        /\/events?\//i,
        /\/festivals?\//i,
        /\/calendar/i,
        /\/blog/i,
        /\/news/i,
        /\/articles?\//i,
        /\/government/i,
        /\/city\.gov/i,
        /\/municipal/i,
        /\/reddit\.com/i,
