import { useEffect } from 'react';

import { supabase } from '@/lib/supabase'; 
import type { PipelineEvent } from '@/lib/types';

/**
 * Manages event subscriptions for pipeline events using Supabase.
 * @example
 * EventSubscriptionManager()
 * @returns {void} This component serves purely for side-effects of subscription management.
 * @description
 *   - Utilizes the Supabase Realtime Client to subscribe and process "pipeline-events".
 *   - Includes an effect cleanup to unsubscribe from events when the component unmounts.
 *   - Current implementation logs events to the console as placeholders.
 */
type RealtimeChannelStatus = 'SUBSCRIBED' | 'CLOSED' | 'CHANNEL_ERROR' | 'TIMED_OUT';

function EventSubscriptionManager() {
  useEffect(() => {
    const handleEvent = (payload: { new: PipelineEvent }) => {
      try {
        const event = payload.new;
        if (event && typeof event === 'object' && 'type' in event) {
          // Process the event based on its type
          switch (event.type) {
            case 'job_started': {
              console.info('Job started:', event.payload);
              break;
            }
            case 'job_completed': {
              console.info('Job completed:', event.payload);
              break;
            }
            case 'data_updated': {
              console.info('Data updated:', event.payload);
              break;
            }
            default: {
              console.warn('Received unknown event type:', event.type);
            }
          }
        } else {
          console.warn('Received malformed event:', payload);
        }
      } catch (error) {
        console.error('Error processing pipeline event:', error);
      }
    };

    const subscription = supabase
      .channel('pipeline-events')
      .on('postgres_changes', { event: 'INSERT', schema: 'public', table: 'pipeline_events' }, handleEvent)
      .subscribe((status: RealtimeChannelStatus, error?: Error) => {
        if (status === 'SUBSCRIBED') {
          console.info('Successfully subscribed to pipeline events.');
        }
        if (status === 'CHANNEL_ERROR' && error) {
          console.error('Pipeline event subscription error:', error);
        }
      });

    return () => {
      void supabase.realtime.removeChannel(subscription);
    };
  }, []);
}

export default EventSubscriptionManager;

import React, { useEffect, useState } from 'react';
import { supabase } from '@/lib/supabase'; 

type RealtimeChannelStatus = 'SUBSCRIBED' | 'CLOSED' | 'CHANNEL_ERROR' | 'TIMED_OUT';

interface SystemStatusResponse {
  status: string;
  data: {
    status: string;
  };
}

/**
* Represents system status indicators in real-time
* @example
* SystemStatusIndicators()
* <div><h3>Current System Status:</h3><span>ONLINE</span></div>
* @returns {JSX.Element} A JSX element displaying the current system status.
* @description
*   - Utilizes Supabase Realtime Client for subscribing to system status updates.
*   - Updates the display with the latest system status retrieved from a realtime database.
*   - Handles subscription clean-up on component unmount to avoid memory leaks.
*/
function SystemStatusIndicators() {
  const [systemStatus, setSystemStatus] = useState('UNKNOWN');

  useEffect(() => {
    const subscription = supabase.realtime.channel('system-status').subscribe((status: RealtimeChannelStatus, payload: unknown) => {
      if (status === 'SUBSCRIBED') {
        const response = payload as SystemStatusResponse;
        if (response && typeof response === 'object' && 'status' in response) {
          const typedResponse = response;
          const responseStatus = typedResponse.status; // Extract status to a variable

          if (typeof responseStatus === 'string' && responseStatus === "success") {
            if (typeof typedResponse.data === 'object' && 'status' in typedResponse.data) {
              setSystemStatus(typedResponse.data.status);
            } else {
              console.error("Invalid data structure in system status response");
            }
          } else {
            console.error("Failed to retrieve system status or invalid response format");
          }
        } else {
          console.error("Failed to retrieve system status or invalid response format");
        }
      }
    });

    return () => {
      void supabase.realtime.removeChannel(subscription);
    };
  }, []);

  return (
    <div>
      <h3>Current System Status:</h3>
      <span>{systemStatus}</span>
    </div>
  );
}

export default SystemStatusIndicators;

'use client';

import React from 'react';
import { Accordion } from '@/components/ui/accordion';
import { TruckAccordionItem } from '@/components/trucks/TruckAccordionItem';
import type { FoodTruck } from '@/lib/types';

interface TruckAccordionContentProps {
  filteredTrucks: FoodTruck[];
  selectedTruckId: string | undefined;
  setSelectedTruckId: (id: string | undefined) => void;
  isOpen: (truck: FoodTruck) => boolean;
  userLocation: { lat: number; lng: number } | undefined;
}

export const TruckAccordionContent: React.FC<TruckAccordionContentProps> = ({
  filteredTrucks,
  selectedTruckId,
  setSelectedTruckId,
  isOpen,
  userLocation,
}) => {
  if (filteredTrucks.length === 0) {
    return (
      <div className="text-center py-8 text-gray-500 dark:text-gray-400">
        <p>No food trucks found in your area.</p>
        <p className="text-sm mt-2">Try expanding your search radius or check back later!</p>
      </div>
    );
  }

  // Smart sorting: prioritize open trucks and specific locations
  const sortedTrucks = [...filteredTrucks].sort((a, b) => {
    const aOpen = isOpen(a);
    const bOpen = isOpen(b);
    const aHasAddress = !!(a.current_location?.address && a.current_location.address.trim());
    const bHasAddress = !!(b.current_location?.address && b.current_location.address.trim());
    
    // First, sort by open/closed status (open first)
    if (aOpen !== bOpen) {
      return aOpen ? -1 : 1;
    }
    
    // Then sort by specific address vs city only (specific first)
    if (aHasAddress !== bHasAddress) {
      return aHasAddress ? -1 : 1;
    }
    
    // Finally, sort alphabetically by name
    return a.name.localeCompare(b.name);
  });

  return (
    <Accordion 
      type="single" 
      collapsible 
