'use client';

import 'leaflet/dist/leaflet.css';
import 'leaflet-defaulticon-compatibility/dist/leaflet-defaulticon-compatibility.css';
import 'leaflet-defaulticon-compatibility';
import './Map.css'; // Enhanced map styling

import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import type { LatLngExpression } from 'leaflet';
import L from 'leaflet';
import { useEffect, useState } from 'react';
import type { FoodTruck } from '@/lib/types';
import { isTruckOpen } from '@/lib/utils/foodTruckHelpers';
import { geocodeAddress, CHARLESTON_FALLBACK } from '@/lib/utils/geocoding';

interface MapComponentProps {
  readonly trucks: FoodTruck[];
  readonly userLocation?: { lat: number; lng: number };
  readonly defaultCenter: LatLngExpression;
  readonly defaultZoom?: number;
  readonly onSelectTruck?: (truckId: string) => void;
  readonly selectedTruckLocation?: LatLngExpression;
  readonly theme?: string;
}

const MapViewUpdater = ({
  center,
  zoom,
}: {
  center: LatLngExpression | undefined;
  zoom?: number;
}) => {
  const map = useMap();
  useEffect(() => {
    if (center) {
      map.flyTo(center, zoom ?? map.getZoom());
    }
  }, [center, zoom, map]);
  return <></>;
};

const getFoodTruckIcon = (isOpen: boolean, theme: string) => {
  const color = isOpen ? 'green' : 'red';
  return new L.Icon({
    iconUrl: '/food-truck-icon.svg',
    iconSize: [64, 64],
    iconAnchor: [32, 52], // Adjusted for better pin positioning
    popupAnchor: [0, -52],
    shadowUrl: undefined, // Use CSS shadow instead
    className: `food-truck-marker-icon glow-${color}`
  });
};

const MapComponent: React.FC<MapComponentProps> = ({
  trucks,
  userLocation,
  defaultCenter,
  defaultZoom = 10,
  onSelectTruck,
  selectedTruckLocation,
  theme,
}) => {
  const [isMounted, setIsMounted] = useState(false);
  const [trucksWithCoords, setTrucksWithCoords] = useState<FoodTruck[]>([]);

  useEffect(() => {
    setIsMounted(true);
  }, []);

  // Process trucks to ensure all have coordinates - no geocoding delays
  useEffect(() => {
    if (!trucks.length) {
      setTrucksWithCoords([]);
      return;
    }
    
    console.log('üó∫Ô∏è Processing', trucks.length, 'trucks for map display');
    
    const processedTrucks: FoodTruck[] = trucks.map(truck => {
      if (!truck.current_location) {
        console.warn('‚ö†Ô∏è Skipping truck with no location:', truck.name);
        return null;
      }
      
      // Ensure truck has valid coordinates, use fallback if needed
      const lat = truck.current_location.lat || CHARLESTON_FALLBACK.lat;
      const lng = truck.current_location.lng || CHARLESTON_FALLBACK.lng;
      
      return {
        ...truck,
        current_location: {
          ...truck.current_location,
          lat,
          lng,
        },
      };
    }).filter((truck): truck is FoodTruck => truck !== null);
    
    console.log('üéØ Processed trucks for map:', processedTrucks.length);
    setTrucksWithCoords(processedTrucks);
  }, [trucks]);

  const validTrucks = trucksWithCoords;

  const initialMapCenter: LatLngExpression =
    userLocation && typeof userLocation.lat === 'number' && typeof userLocation.lng === 'number'
      ? [userLocation.lat, userLocation.lng]
      : defaultCenter;

  // SIMPLE AND EFFECTIVE: Use the crisp OpenStreetMap for both modes
  const isDark = theme === 'dark';
  
  const tileLayerProps = {
    // Use the crisp OpenStreetMap tiles that you like
    attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
    url: 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
    className: isDark ? 'dark-inverted-map' : 'light-crisp-map'
  };

  if (!isMounted) {
    return (
      <div
        className={`map-loading rounded-lg shadow-md ${isDark ? 'dark' : ''}`}
        style={{
          height: '400px',
          width: '100%',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        <div className="flex items-center gap-2">
          <div className="w-5 h-5 rounded-full border-2 border-current border-t-transparent animate-spin" />
          <p style={{ color: isDark ? '#fff' : '#000' }}>Loading map...</p>
        </div>
      </div>
    );
  }

  return (
    <MapContainer
      center={initialMapCenter}
      zoom={defaultZoom}
      scrollWheelZoom={true}
      zoomControl={true}
      doubleClickZoom={true}
      dragging={true}
      style={{ height: '400px', width: '100%' }}
      className="rounded-lg shadow-md"
      // Enhanced performance settings
      preferCanvas={false}
    >
      <TileLayer
        attribution={tileLayerProps.attribution}
        url={tileLayerProps.url}
        maxZoom={19}
        minZoom={1}
        tileSize={256}
        zoomOffset={0}
        // Performance enhancements
        keepBuffer={2}
        updateWhenIdle={true}
        updateWhenZooming={false}
        // Better visual quality
        className={tileLayerProps.className}
      />
      <MapViewUpdater
        center={selectedTruckLocation}
        zoom={selectedTruckLocation ? 13 : undefined}
      />
      {validTrucks.map((truck) => {
        const isOpen = isTruckOpen(truck);
        return (
          <Marker
            key={truck.id}
            position={[truck.current_location.lat, truck.current_location.lng]}
            icon={getFoodTruckIcon(isOpen, theme || 'light')}
            eventHandlers={{
              click: () => {
                if (onSelectTruck) {
                  onSelectTruck(truck.id);
                }
              },
            }}
          >
            <Popup>
              <h4 className="font-bold">{truck.name}</h4>
              {truck.current_location.address && <div>{truck.current_location.address}</div>}
              <div className={isOpen ? 'text-green-600' : 'text-red-600'}>
                {isOpen ? 'Open' : 'Closed'}
              </div>
            </Popup>
          </Marker>
        );
      })}
      {userLocation && (
        <Marker position={[userLocation.lat, userLocation.lng]}>
          <Popup>You are here</Popup>
        </Marker>
      )}
    </MapContainer>
  );
};

export default MapComponent;

import { Clock } from 'lucide-react';
import type { DailyOperatingHours } from '@/lib/supabase/types'; // Import DailyOperatingHours

interface OperatingHoursSectionProps {
  readonly todayHours: DailyOperatingHours | undefined; // Update type
  readonly formatHours: (hours: DailyOperatingHours) => string; // Update type
}

/**
 * Renders today's operating hours section.
 * @example
 * OperatingHoursSection({ todayHours: { closed: false, open: '09:00', close: '17:00' }, formatHours: (hours) => `${hours.open} - ${hours.close}` })
 * // Returns a JSX element with formatted operating hours or 'Closed'.
 * @param {Readonly<OperatingHoursSectionProps>} {todayHours, formatHours} - The properties for today's operating hours and the function to format them.
 * @returns {JSX.Element | undefined} A JSX element displaying today's hours, or nothing if hours are undefined.
 * @description
 *   - The component displays 'Closed' if todayHours indicates closure.
 *   - Utilizes a Clock icon for a visual representation of hours.
 *   - Ensures dark mode compatibility through styling classes.
 */
export function OperatingHoursSection({
  todayHours,
  formatHours,
}: Readonly<OperatingHoursSectionProps>) {
  if (todayHours === undefined) return; // Return null if no hours

  return (
    <div>
      <h4 className="font-medium mb-2 text-sm dark:text-gray-100">Today's Hours</h4>
      <div className="flex items-center gap-2">
        <Clock className="size-3 text-gray-500" />
        <span className="text-sm dark:text-gray-300">
          {todayHours.closed ? 'Closed' : formatHours(todayHours)}
        </span>
      </div>
    </div>
  );
}

