        /\/twitter\.com/i,
        /\/linkedin\.com/i,
        /\/youtube\.com/i,
        /\/yelp\.com/i,
        /\/google\.com\/maps/i,
        /\/foursquare\.com/i,
        /\/tripadvisor\.com/i,
        /\/zomato\.com/i,
        /\/eventbrite\.com/i,
        /\/meetup\.com/i,
    ];
    
    // Also check for blacklist keywords like the discovery engine does
    const blacklistKeywords = [
        'facebook.com',
        'instagram.com',
        'twitter.com',
        'linkedin.com',
        'youtube.com',
        'yelp.com',
        'google.com',
        'maps.google.com',
        'foursquare.com',
        'tripadvisor.com',
        'zomato.com',
        'eventbrite.com',
        'meetup.com',
    ];
    
    const urlLower = url.toLowerCase();
    
    console.log(`Testing URL: ${url}`);
    
    // Check regex patterns first
    for (const pattern of nonFoodTruckPatterns) {
        if (pattern.test(urlLower)) {
            console.log(`  ‚úÖ Matched pattern: ${pattern}`);
            return true;
        }
    }
    
    // Check blacklist keywords like discovery engine does
    for (const keyword of blacklistKeywords) {
        if (urlLower.includes(keyword)) {
            console.log(`  ‚úÖ Matched blacklist keyword: ${keyword}`);
