export class GeminiDataProcessor {
  private _apiKey: string;
  private _baseUrl: string;
  private requestCount: number;
  private tokenCount: number;
  private dailyLimit: { requests: number; tokens: number };

  constructor(apiKey: string) {
    this._apiKey = apiKey;
    this._baseUrl = 'https://generativelanguage.googleapis.com/v1beta';
    this.requestCount = 0;
    this.tokenCount = 0;
    this.dailyLimit = { requests: 1500, tokens: 32_000 };
  }
  async processMenuData(rawMenuText: string): Promise<{ categories: MenuCategory[] }> {
    if (!this.canMakeRequest()) {
      throw new Error('Gemini API rate limit exceeded');
    }

    try {
      const prompt = `
        Parse the following food truck menu text and return a structured JSON format:
        
        ${rawMenuText}
        
        Return format:
        {
          "categories": [
            {
              "name": "category_name",
              "items": [
                {
                  "name": "item_name",
                  "description": "item_description",
                  "price": 0.00,
                  "dietary_tags": ["vegetarian", "vegan", "gluten-free", etc.]
                }
              ]
            }
          ]
        }
        
        Only return valid JSON, no additional text.
      `;

      const response = await this.makeGeminiRequest(prompt);
      this.updateUsageCounters(1, prompt.length + response.length);

      // Ensure type safety for parsed response
      const parsed: unknown = JSON.parse(response);
      if (
        typeof parsed !== 'object' ||
        parsed == undefined ||
        !('categories' in parsed) ||
        !Array.isArray((parsed as { categories: unknown[] }).categories)
      ) {
        throw new Error('Invalid Gemini menu response: missing or malformed categories array');
      }
      return parsed as { categories: MenuCategory[] };
    } catch (error) {
      console.error('Error processing menu data with Gemini:', error);
      throw error;
    }
  }

  async extractLocationFromText(text: string): Promise<GeminiLocationData> {
    if (!this.canMakeRequest()) {
      throw new Error('Gemini API rate limit exceeded');
    }

    try {
      const prompt = `
        Extract location information from the following text and return structured data:
        
        "${text}"
        
        Return format:
        {
          "address": "full_address",
          "city": "city_name",
          "state": "state",
          "coordinates": {
            "lat": 0.0,
            "lng": 0.0
          },
          "confidence": 0.95
        }
        
        If coordinates cannot be determined, set them to undefined. Only return valid JSON.
      `;
      const response = await this.makeGeminiRequest(prompt);
      this.updateUsageCounters(1, prompt.length + response.length);
      const parsedResponse: unknown = JSON.parse(response);
      const validatedResponse = this.validateGeminiLocationResponse(parsedResponse);
      return validatedResponse;
    } catch (error) {
      console.error('Error extracting location with Gemini:', error);
      throw error;
    }
  }

  private validateGeminiLocationResponse(parsedResponse: unknown): GeminiLocationData {
    if (
      parsedResponse == undefined ||
      typeof parsedResponse !== 'object' ||
      !('coordinates' in parsedResponse) ||
      typeof (parsedResponse as Record<string, unknown>).coordinates !== 'object'
    ) {
      throw new Error('Invalid Gemini location response');
    }
    const coordinates = (parsedResponse as { coordinates?: { lat?: unknown; lng?: unknown } })
      .coordinates;
    if (coordinates && typeof coordinates === 'object') {
      if (typeof coordinates.lat !== 'number') {
        (coordinates as { lat?: number }).lat = undefined;
      }
      if (typeof coordinates.lng !== 'number') {
        (coordinates as { lng?: number }).lng = undefined;
      }
    }
    return parsedResponse as GeminiLocationData;
  }

  async standardizeOperatingHours(hoursText: string): Promise<GeminiOperatingHours> {
    if (!this.canMakeRequest()) {
      throw new Error('Gemini API rate limit exceeded');
    }

    try {
      const prompt = `
        Parse the following operating hours text and return standardized format:
        
        "${hoursText}"
        
        Return format:
        {
          "monday": {"open": "HH:MM", "close": "HH:MM", "closed": false},
          "tuesday": {"open": "HH:MM", "close": "HH:MM", "closed": false},
          "wednesday": {"open": "HH:MM", "close": "HH:MM", "closed": false},
          "thursday": {"open": "HH:MM", "close": "HH:MM", "closed": false},
          "friday": {"open": "HH:MM", "close": "HH:MM", "closed": false},
          "saturday": {"open": "HH:MM", "close": "HH:MM", "closed": false},
          "sunday": {"open": "HH:MM", "close": "HH:MM", "closed": false}
        }
          Use 24-hour format. If closed on a day, set "closed": true and omit open/close times.
        Only return valid JSON.
      `;

      const response = await this.makeGeminiRequest(prompt);
      this.updateUsageCounters(1, prompt.length + response.length);
      const parsed: unknown = JSON.parse(response);
      if (parsed == undefined || typeof parsed !== 'object') {
        throw new Error('Invalid Gemini hours response');
      }
      return parsed as GeminiOperatingHours;
    } catch (error) {
      console.error('Error standardizing hours with Gemini:', error);
      throw error;
    }
  }

  async analyzeSentiment(reviewText: string): Promise<GeminiSentimentAnalysis> {
    if (!this.canMakeRequest()) {
      throw new Error('Gemini API rate limit exceeded');
    }

    try {
      const prompt = `
        Analyze the sentiment of this food truck review and extract key insights:
        
        "${reviewText}"
        
        Return format:
        {
          "sentiment": "positive|negative|neutral",
          "score": 0.85,
          "key_topics": ["food_quality", "service", "price", "location"],
          "summary": "brief_summary_of_review"
        }
          Score should be between 0 (very negative) and 1 (very positive).
        Only return valid JSON.
      `;

      const response = await this.makeGeminiRequest(prompt);
      this.updateUsageCounters(1, prompt.length + response.length);
      const parsed: unknown = JSON.parse(response);
      if (parsed == undefined || typeof parsed !== 'object') {
        throw new Error('Invalid Gemini sentiment response');
      }
      return parsed as GeminiSentimentAnalysis;
    } catch (error) {
      console.error('Error analyzing sentiment with Gemini:', error);
      throw error;
    }
  }

  private async makeGeminiRequest(prompt: string): Promise<string> {
    await new Promise((resolve) => setTimeout(resolve, 1000)); // Simulate API delay
    return this.getMockGeminiResponse(prompt);
  }

  private getMockGeminiResponse(prompt: string): string {
    if (prompt.includes('menu text')) {
      return JSON.stringify({
        categories: [
          {
            name: 'Burgers',
            items: [
              {
                name: 'Classic Cheeseburger',
                description: 'Beef patty with cheese, lettuce, tomato',
                price: 12.99,
                dietary_tags: [],
              },
            ],
          },
        ],
      });
    } else if (prompt.includes('location information')) {
      return JSON.stringify({
        address: '123 Market St, San Francisco, CA 94105',
        city: 'San Francisco',
        state: 'CA',
        coordinates: {
          lat: 37.7749,
          lng: -122.4194,
        },
        confidence: 0.95,
      });
    } else if (prompt.includes('operating hours')) {
      return JSON.stringify({
        monday: { open: '11:00', close: '15:00', closed: false },
        tuesday: { open: '11:00', close: '15:00', closed: false },
        wednesday: { open: '11:00', close: '15:00', closed: false },
        thursday: { open: '11:00', close: '15:00', closed: false },
        friday: { open: '11:00', close: '20:00', closed: false },
        saturday: { open: '12:00', close: '20:00', closed: false },
        sunday: { open: '12:00', close: '16:00', closed: false },
      });
    } else if (prompt.includes('sentiment')) {
      return JSON.stringify({
        sentiment: 'positive',
        score: 0.85,
        key_topics: ['food_quality', 'service'],
        summary: 'Customer enjoyed the food and service',
      });
    }
    return '{"processed": true}';
  }

  private canMakeRequest(): boolean {
    return this.requestCount < this.dailyLimit.requests && this.tokenCount < this.dailyLimit.tokens;
  }

  private updateUsageCounters(requests: number, tokens: number): void {
    this.requestCount += requests;
    this.tokenCount += tokens;
  }

  getUsageStats(): {
    requests: { used: number; limit: number; remaining: number };
    tokens: { used: number; limit: number; remaining: number };
  } {
    return {
      requests: {
        used: this.requestCount,
        limit: this.dailyLimit.requests,
        remaining: this.dailyLimit.requests - this.requestCount,
      },
      tokens: {
        used: this.tokenCount,
        limit: this.dailyLimit.tokens,
        remaining: this.dailyLimit.tokens - this.tokenCount,
      },
    };
  }
}

import { NextResponse } from 'next/server';
import { BatchCleanupService } from '@/lib/data-quality/batchCleanup';

interface CleanupSchedule {
  id: string;
  name: string;
  operations: string[];
  schedule: string; // cron expression
  enabled: boolean;
  lastRun?: string;
  nextRun?: string;
  successCount: number;
  errorCount: number;
}

interface CleanupResult {
  id: string;
  timestamp: string;
  operation: string;
  success: boolean;
  details: Record<string, unknown>;
}

interface RunScheduledOptions {
  scheduleId: string;
}

interface RunImmediateOptions {
  operations?: string[];
  batchSize?: number;
  dryRun?: boolean;
}

interface ScheduleCleanupOptions {
  name: string;
  operations: string[];
  schedule: string;
  enabled?: boolean;
}

interface UpdateScheduleOptions {
  scheduleId: string;
  updates: Record<string, unknown>;
}

interface DeleteScheduleOptions {
  scheduleId: string;
}

interface AnalyzeDuplicatesOptions {
  threshold?: number;
}

interface AutomatedCleanupStatus {
  isRunning: boolean;
  lastRun: string | null;
  nextScheduledRun: string | null;
  schedules: CleanupSchedule[];
  recentResults: CleanupResult[];
  statistics: {
    totalRuns: number;
    successfulRuns: number;
    failedRuns: number;
    trucksImproved: number;
    duplicatesRemoved: number;
  };
}

type CleanupOperationType =
  | 'normalize_phone'
  | 'fix_coordinates'
  | 'remove_placeholders'
  | 'update_quality_scores'
  | 'merge_duplicates';

/**
 * Handles post requests by executing specific actions based on the provided body.
 * @example
 * handlePostRequest({ action: 'run_scheduled', options: { key: 'value' } })
 * Returns the result of executing handleRunScheduled with the provided options.
 * @param {Object} body - The body of the request containing action type and optional parameters.
 * @param {string} body.action - The action to perform such as 'run_scheduled', 'update_schedule', etc.
 * @param {Object} [body.options] - Optional parameters for the specified action, default is an empty object.
 * @returns {Promise<Object>} Resolves with the result of the corresponding action handler or an error object for unknown actions.
 * @description
 *   - Supports multiple predefined actions like scheduling, running, and cleanup operations.
 *   - Provides feedback on available actions when an unknown action is specified.
 *   - Uses async functions to perform operations, ensuring asynchronous behavior.
 *   - Returns JSON responses compliant with NextResponse standards.
 */
function isRunScheduledOptions(obj: any): obj is RunScheduledOptions {
  return typeof obj.scheduleId === "string";
}

function isRunImmediateOptions(obj: any): obj is RunImmediateOptions {
  return (
    (obj.operations === undefined || Array.isArray(obj.operations)) &&
    (obj.batchSize === undefined || typeof obj.batchSize === "number") &&
    (obj.dryRun === undefined || typeof obj.dryRun === "boolean")
  );
}

function isScheduleCleanupOptions(obj: any): obj is ScheduleCleanupOptions {
  return (
    typeof obj.name === "string" &&
    Array.isArray(obj.operations) &&
    typeof obj.schedule === "string" &&
    (obj.enabled === undefined || typeof obj.enabled === "boolean")
  );
}

function isUpdateScheduleOptions(obj: any): obj is UpdateScheduleOptions {
  return (
    typeof obj.scheduleId === "string" &&
    typeof obj.updates === "object"
  );
}

function isDeleteScheduleOptions(obj: any): obj is DeleteScheduleOptions {
  return typeof obj.scheduleId === "string";
}

export async function handlePostRequest(body: {
  action: string;
  options?: Record<string, unknown>;
}) {
  const { action, options = {} } = body;

  switch (action) {
    case 'run_scheduled': {
      if (!isRunScheduledOptions(options)) {
        return NextResponse.json({ success: false, error: 'Invalid options for run_scheduled' }, { status: 400 });
      }
      return await handleRunScheduled(options);
    }
    case 'run_immediate': {
      if (!isRunImmediateOptions(options)) {
        return NextResponse.json({ success: false, error: 'Invalid options for run_immediate' }, { status: 400 });
      }
      return await handleRunImmediate(options);
    }
    case 'schedule_cleanup': {
      if (!isScheduleCleanupOptions(options)) {
        return NextResponse.json({ success: false, error: 'Invalid options for schedule_cleanup' }, { status: 400 });
      }
      return await handleScheduleCleanup(options);
    }
    case 'update_schedule': {
      if (!isUpdateScheduleOptions(options)) {
        return NextResponse.json({ success: false, error: 'Invalid options for update_schedule' }, { status: 400 });
      }
      return await handleUpdateSchedule(options);
    }
    case 'delete_schedule': {
      if (!isDeleteScheduleOptions(options)) {
        return NextResponse.json({ success: false, error: 'Invalid options for delete_schedule' }, { status: 400 });
      }
      return await handleDeleteSchedule(options);
    }
    default: {
      return NextResponse.json(
        {
          success: false,
          error: 'Unknown action',
          available_actions: [
            'run_scheduled',
            'run_immediate',
            'schedule_cleanup',
            'update_schedule',
            'delete_schedule',
            'analyze_duplicates',
          ],
        },
        { status: 400 },
      );
    }
  }
}

