export const gemini = new GeminiService();

import { GoogleGenAI } from '@google/genai';

export class GeminiApiClient {
    constructor() {
        this.modelName = 'gemini-2.0-flash-lite-001';
        const apiKey = process.env.GEMINI_API_KEY;
        if (apiKey == undefined || apiKey === '') {
            throw new Error('GEMINI_API_KEY environment variable is not set or is empty.');
        }
        this.genAI = new GoogleGenAI(apiKey);
    }

    /**
     * Classify Gemini API errors for appropriate handling
     */
    classifyError(error) {
        // Check if it's an HTTP error with status code
        if (error && typeof error === 'object') {
            if (error.status) {
                return {
                    type: 'HTTP_ERROR',
                    message: error.message || 'HTTP Error',
                    statusCode: error.status,
                    statusText: error.statusText || 'Unknown status'
                };
            }
            
            // Check for response-related errors
            if (error.message && error.message.includes('JSON')) {
                return {
                    type: 'PARSE_ERROR',
                    message: error.message,
                    statusCode: null
                };
            }
        }
        
        return {
            type: 'UNKNOWN_ERROR',
            message: error instanceof Error ? error.message : 'Unknown error',
            statusCode: null
        };
    }

    /**
     * Determine if an error should be retried
     */
    shouldRetry(errorInfo) {
        // Retry on rate limits, server errors, and service unavailability
        if (errorInfo.statusCode === 429) return true; // Rate limit
        if (errorInfo.statusCode === 500) return true; // Internal server error
        if (errorInfo.statusCode === 503) return true; // Service unavailable
        if (errorInfo.type === 'PARSE_ERROR') return true; // JSON parsing issues
        return false;
    }

    /**
     * Calculate delay with exponential backoff and jitter
     */
    calculateDelay(baseDelay, attempt, errorInfo) {
        const exponentialDelay = baseDelay * Math.pow(2, attempt - 1);
        
        // Add jitter to prevent thundering herd
        const jitter = Math.random() * 0.1 * exponentialDelay;
        
        // For rate limits, use longer delays
        const multiplier = errorInfo.statusCode === 429 ? 2 : 1;
        
        return Math.min(exponentialDelay + jitter, 30000) * multiplier; // Max 30 seconds
    }

    /**
     * Sleep utility
     */
    sleep(ms) {
        return new Promise(resolve => setTimeout(resolve, ms));
    }

    /**
     * Enhanced API request with retry logic
     */
    async makeRequest(prompt, config = {}) {
        let attempt = 0;
        const maxRetries = config.maxRetries ?? 3;
        const baseDelay = config.baseDelay ?? 1000; // 1 second
        
        while (attempt <= maxRetries) {
            try {
                console.log(`Gemini API Request attempt ${attempt + 1}/${maxRetries + 1}`);
                
                const generationConfig = {
                    temperature: config.temperature ?? 0,
                    ...config.generationConfig
                };
                
                const result = await this.genAI.models.generateContent({
                    model: this.modelName,
                    contents: [{ role: 'user', parts: [{ text: prompt }] }],
                    config: generationConfig,
                });
                
                // Extract text from the response
                let textOutput = '';
                if (result.candidates && result.candidates.length > 0) {
                    const candidate = result.candidates[0];
                    if (candidate.content && candidate.content.parts && candidate.content.parts.length > 0) {
                        textOutput = candidate.content.parts[0].text ?? '';
                    }
                }
                
                const tokensUsed = result.usageMetadata?.totalTokenCount ??
                    Math.ceil((prompt.length + textOutput.length) / 4);
                
                // Track usage asynchronously (optional)
                try {
                    const { APIUsageService } = await import('../supabase/services/apiUsageService.js');
                    APIUsageService.trackUsage('gemini', 1, tokensUsed).catch((error) => {
                        console.warn('Failed to track API usage:', error);
                    });
                } catch (importError) {
                    // API usage tracking is optional, continue without it
                    console.debug('API usage tracking not available:', importError.message);
                }
                
                console.log(`Gemini API Request successful after ${attempt + 1} attempt(s)`);
                return {
                    success: true,
                    data: textOutput,
                    tokensUsed,
                };
            }
            catch (error) {
                attempt++;
                const errorInfo = this.classifyError(error);
                
                console.error(`Gemini API Request attempt ${attempt} failed:`, errorInfo);
                
                // Check if retry is appropriate
                if (attempt <= maxRetries && this.shouldRetry(errorInfo)) {
                    const delay = this.calculateDelay(baseDelay, attempt, errorInfo);
                    console.log(`Retrying in ${delay}ms... (attempt ${attempt + 1}/${maxRetries + 1})`);
                    await this.sleep(delay);
                    continue;
                }
                
                // Final error handling
                const tokensUsed = Math.ceil((prompt.length + (error instanceof Error ? error.message.length : String(error).length)) / 4);
                return {
                    success: false,
                    error: errorInfo.message,
                    errorType: errorInfo.type,
                    statusCode: errorInfo.statusCode,
                    tokensUsed,
                };
            }
        }
    }

    /**
     * Clean response data to fix common JSON issues
     */
    cleanResponseData(text) {
        if (!text) return text;
        
        // Remove common problematic patterns
        let cleaned = text
            .trim()
            .replace(/^\s*```(?:json|yaml)?\s*/i, '')
            .replace(/\s*```\s*$/i, '')
            .replace(/^\s*(?:json|yaml):\s*/i, '')
            .trim();
        
        // Fix common JSON escaping issues
        cleaned = cleaned
            .replace(/\\'/g, "'")  // Fix single quote escaping
            .replace(/([^\\])\\"/g, '$1"')  // Fix double quote escaping
            .replace(/\\n/g, '\n')  // Normalize newlines
            .replace(/\\t/g, '\t'); // Normalize tabs
        
        return cleaned;
    }

    /**
     * Enhanced request with parsing and retry logic
     */
    async makeRequestWithParsing(prompt, parser, config = {}) {
        const maxParseRetries = 3;
        let parseAttempt = 0;
        
        while (parseAttempt < maxParseRetries) {
            parseAttempt++;
            
            const response = await this.makeRequest(prompt, config);
            if (!response.success) {
                return response;
            }
            
            try {
                if (!response.data) {
                    return {
                        success: false,
                        error: 'No data received from Gemini API',
                        tokensUsed: response.tokensUsed,
                    };
                }
                
                // Clean the response data
                const cleanedData = this.cleanResponseData(response.data);
                const parsedData = parser(cleanedData);
                
                return {
                    success: true,
                    data: parsedData,
                    tokensUsed: response.tokensUsed,
                };
            }
            catch (parseError) {
                console.warn(`Gemini JSON parsing attempt ${parseAttempt} failed:`, parseError);
                
                // If this is the last attempt, return the error
                if (parseAttempt >= maxParseRetries) {
                    console.warn('Problematic Gemini raw response text:', response.data?.trim() ?? 'No data');
                    return {
                        success: false,
                        error: `Failed to parse Gemini response after ${maxParseRetries} attempts: ${parseError instanceof Error ? parseError.message : String(parseError)}. Response text: ${response.data?.trim().slice(0, 200) ?? 'No data'}...`,
                        tokensUsed: response.tokensUsed,
                    };
                }
                
                // Wait before retry
                await this.sleep(1000 * parseAttempt);
            }
        }
    }
}

import { GoogleGenAI } from '@google/genai';
import { APIUsageService } from '../supabase';
import type { GeminiResponse } from '../types';

export interface GeminiApiConfig {
  temperature?: number;
  maxTokens?: number;
}

export class GeminiApiClient {
  private genAI: GoogleGenAI;
  private modelName: string;

  constructor() {
    const apiKey = process.env.GEMINI_API_KEY;
    if (apiKey == undefined || apiKey === '') {
      throw new Error('GEMINI_API_KEY environment variable is not set or is empty.');
    }
    this.genAI = new GoogleGenAI({ apiKey });
    this.modelName = 'gemini-2.0-flash-lite-001';
  }

  /**
  * Makes an API request to generate content based on the provided prompt and configuration.
  * @example
  * makeRequest('Create a poem about nature', { temperature: 0.7 })
  * Returns a promise that resolves to a GeminiResponse containing the generated text and token usage.
  * @param {string} prompt - The input prompt for content generation.
  * @param {GeminiApiConfig} config - Configuration settings for the API request, such as temperature. Defaults to an empty object.
  * @returns {Promise<GeminiResponse<T>>} A promise that resolves to a GeminiResponse with the generated content or an error message.
  * @description
  *   - The function calculates the number of tokens used for tracking purposes, even in case of an error.
  *   - The `temperature` parameter affects the randomness of the content generation, with higher values resulting in more creative outputs.
  *   - API usage is tracked asynchronously, and warnings are logged if tracking fails.
  *   - Returns an object indicating success status, the generated content or error message, and the number of tokens used.
  */
  async makeRequest<T>(prompt: string, config: GeminiApiConfig = {}): Promise<GeminiResponse<T>> {
    let textOutput: string = '';

    try {
      const sdkResponse = await this.genAI.models.generateContent({
        model: this.modelName,
        contents: [{ role: 'user', parts: [{ text: prompt }] }],
        config: { temperature: config.temperature ?? 0 },
      });

      textOutput = sdkResponse.text ?? '';

      const tokensUsed =
        sdkResponse.usageMetadata?.totalTokenCount ??
        Math.ceil((prompt.length + textOutput.length) / 4);

      // Track usage asynchronously
      APIUsageService.trackUsage('gemini', 1, tokensUsed).catch((error) => {
        console.warn('Failed to track API usage:', error);
      });

      return {
        success: true,
        data: textOutput,
        tokensUsed,
      } as GeminiResponse<T>;
    } catch (error: unknown) {
      const tokensUsed = Math.ceil(
        (prompt.length + (error instanceof Error ? error.message.length : String(error).length)) /
          4,
      );

      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error',
        tokensUsed,
      };
    }
  }

  /**
   * Executes a request using a specified prompt and attempts to parse the response using the provided parser function.
   * @example
   * makeRequestWithParsing("example prompt", (text) => JSON.parse(text))
   * { success: true, data: { key: "value" }, tokensUsed: 100 }
   * @param {string} prompt - The prompt used to make the API request.
   * @param {function} parser - A function to parse the response text into the desired format.
   * @param {GeminiApiConfig} config - Optional configuration settings for the Gemini API request.
   * @returns {Promise<GeminiResponse<T>>} A promise resolving to a GeminiResponse object containing either the parsed data or an error description.
   * @description
   *   - The function makes an asynchronous request using a specific prompt string and configuration.
   *   - If the response is unsuccessful, it returns the original response cast to the expected return type.
   *   - Parses the successful response data using the provided parser function.
   *   - Handles parsing errors gracefully, returning a detailed error message and logs the raw problematic response.
   */
  async makeRequestWithParsing<T>(
    prompt: string,
    parser: (text: string) => T,
    config: GeminiApiConfig = {},
  ): Promise<GeminiResponse<T>> {
    const response = await this.makeRequest<string>(prompt, config);

    if (!response.success) {
      return response as GeminiResponse<T>;
    }

    try {
if (!response.data) {
        return {
          success: false,
          error: 'No data received from Gemini API',
          tokensUsed: response.tokensUsed,
        };
      }
      const parsedData = parser(response.data);
      return {
        success: true,
        data: parsedData,
        tokensUsed: response.tokensUsed,
      };
    } catch (parseError: unknown) {
      console.warn('Gemini json parsing error:', parseError);
      console.warn('Problematic Gemini raw response text:', response.data?.trim() ?? 'No data');

      return {
        success: false,
        error: `Failed to parse Gemini response: ${parseError instanceof Error ? parseError.message : String(parseError)}. Response text: ${response.data?.trim().slice(0, 200) ?? 'No data'}...`,
        tokensUsed: response.tokensUsed,
      };
    }
  }
}

import yaml from 'js-yaml';

export const GeminiResponseParser = {
    parseStructuredData(text) {
        // Clean up the response text for YAML
        let cleanedText = text
            .replaceAll(/```yaml\s*/g, '')
            .replaceAll(/```\s*/g, '')
            .replaceAll(/^\s*yaml\s*/g, '')
            .trim();

        try {
            return yaml.load(cleanedText);
        } catch (yamlError) {
            console.warn("YAML parse failed. Attempting JSON parse.", yamlError);
            // Fallback to JSON parsing if YAML fails
            cleanedText = text
                .replaceAll(/```json\s*/g, '')
                .replaceAll(/```\s*/g, '')
                .replaceAll(/^\s*json\s*/g, '')
                .trim();
            try {
                return JSON.parse(cleanedText);
            } catch (jsonError) {
                console.error("Initial JSON parse failed. Attempting aggressive extraction.", jsonError);
                console.error("Problematic text:", cleanedText);
                console.error("Original raw response text:", text);

                // Attempt to extract JSON using regex for more robustness
                const jsonMatch = cleanedText.match(/(\{[\s\S]*\})|(\[[\s\S]*\])/);
                if (jsonMatch && jsonMatch[0]) {
                    try {
                        return JSON.parse(jsonMatch[0]);
                    } catch (e) {
                        console.error("Aggressive JSON extraction also failed.", e);
                        throw new Error("Failed to parse Gemini response even after aggressive extraction.");
                    }
                }
                throw new Error("Failed to parse Gemini response: No valid YAML or JSON structure found.");
            }
        }
    },
    parseMenuData(text) {
        return this.parseStructuredData(text);
    },
    parseLocationData(text) {
        return this.parseStructuredData(text);
    },
    parseOperatingHours(text) {
        return this.parseStructuredData(text);
    },
    parseSentimentAnalysis(text) {
        return this.parseStructuredData(text);
    },
    parseEnhancedFoodTruckData(text) {
        return this.parseStructuredData(text);
    },
    parseExtractedFoodTruckDetails(text) {
        return this.parseStructuredData(text);
    },
    cleanMarkdownResponse(text) {
        // This function is now less critical as parseStructuredData handles cleaning
        // but keeping it for consistency if other parts of the code use it.
        return text
            .replaceAll(/```yaml\s*/g, '')
            .replaceAll(/```json\s*/g, '')
            .replaceAll(/```\s*/g, '')
            .replaceAll(/^\s*yaml\s*/g, '')
            .replaceAll(/^\s*json\s*/g, '')
            .trim();
    },
};

import { APIUsageService } from '../supabase/services/apiUsageService.js';
import { APIMonitor } from '../monitoring/apiMonitor.js';
export class GeminiUsageLimits {
    static DEFAULT_LIMITS = {
        dailyRequestLimit: 1500,
        dailyTokenLimit: 32_000,
    };
    /**
     * Checks the current usage against predefined limits and determines if further requests can be made.
     * @example
     * checkUsageLimits()
     * { canMakeRequest: true, usage: { requests: { used: 50, limit: 1000, remaining: 950 }, tokens: { used: 200, limit: 5000, remaining: 4800 } } }
     * @param {UsageLimits} limits - Object containing usage limits for requests and tokens.
     * @returns {Promise<UsageCheck>} Object indicating if a request can be made and the current usage statistics.
     * @description
     *   - Attempts to fetch today's usage data from the APIUsageService for 'gemini'.
     *   - If the fetch fails, defaults to allowing the request with a warning.
     *   - Maintains a buffer of 100 tokens to ensure requests are not made when limits are too close.
     */
    static async checkUsageLimits(limits = this.DEFAULT_LIMITS) {
        try {
            const usage = await APIUsageService.getTodayUsage('gemini');
            if (!usage) {
                return { canMakeRequest: true };
            }
            const requestsUsed = usage.requests_count ?? 0;
            const tokensUsed = usage.tokens_used ?? 0;
            const requestsRemaining = limits.dailyRequestLimit - requestsUsed;
            const tokensRemaining = limits.dailyTokenLimit - tokensUsed;
            return {
                canMakeRequest: requestsRemaining > 0 && tokensRemaining > 100, // Keep 100 token buffer
                usage: {
                    requests: {
                        used: requestsUsed,
                        limit: limits.dailyRequestLimit,
                        remaining: requestsRemaining,
                    },
                    tokens: {
                        used: tokensUsed,
                        limit: limits.dailyTokenLimit,
                        remaining: tokensRemaining,
                    },
                },
            };
        }
        catch (error) {
            console.warn('Error checking Gemini usage limits:', error);
            return { canMakeRequest: false };
        }
    }
    /**
    * Checks if a request can be made with monitoring and returns whether the operation is allowed.
    * @example
    * checkWithMonitoring(100, DEFAULT_LIMITS)
    * { allowed: true }
    * @param {number} estimatedTokens - Number of estimated tokens for the request.
    * @param {UsageLimits} _limits - Usage limits configuration for requests (default provided internally).
    * @returns {Promise<{ allowed: boolean; reason?: string; waitTime?: number }>} Object indicating if the request is allowed and why not if denied.
    * @description
    *   - Uses an internal API monitor to check if requests can be made based on predefined limits.
    *   - Wait time is converted from milliseconds to minutes before being returned.
    */
    static async checkWithMonitoring(estimatedTokens, _limits = this.DEFAULT_LIMITS) {
        const canMakeRequest = await APIMonitor.canMakeRequest('gemini', 1, estimatedTokens);
        if (!canMakeRequest.allowed) {
            return {
                allowed: false,
                reason: `API limit reached: ${canMakeRequest.reason}. Wait time: ${Math.ceil((canMakeRequest.waitTime ?? 0) / 1000 / 60)} minutes`,
            };
        }
        return { allowed: true };
    }
}

/**
 * Data mapper for Uprooted Vegan Cuisine onboarding
 * 
 * This utility handles the transformation of Uprooted Vegan Cuisine data
 * to match our FoodTruck schema, including field mappings and defaults.
 */

import type { FoodTruck, MenuCategory, OperatingHours } from '../types';

export interface UprootedVeganData {
  // Core fields
  name: string;
  description?: string;
  cuisine_type: string | string[]; // Handle both single and array formats
  price_range?: '$' | '$$' | '$$$' | '$$$$';
  specialties?: string | string[]; // Handle both single and array formats
  
  // User/ownership fields
  user_id?: string;
  state?: string;
  
  // Location fields
  exact_location?: {
    lat: number;
    lng: number;
    address?: string;
    timestamp?: string;
  };
  city_location?: {
    lat: number;
    lng: number;
    address?: string;
    timestamp?: string;
  };
  current_location?: {
    lat: number;
    lng: number;
    address?: string;
    timestamp?: string;
  };
  
  // Schedule fields
  scheduled_locations?: {
    address?: string;
    city?: string;
    state?: string;
    zip_code?: string;
    lat?: number;
    lng?: number;
    timestamp: string;
    start_time: string;
    end_time: string;
  }[];
  
  // Menu and hours
  operating_hours?: OperatingHours | Record<string, any>;
  menu?: MenuCategory[] | any[];
  
  // Contact information
  contact_info?: {
    phone?: string;
    email?: string;
    website?: string;
  };
  
  // Social media
  social_media?: {
    instagram?: string;
    facebook?: string;
    twitter?: string;
    tiktok?: string;
    yelp?: string;
  };
  
  // Quality and verification
  data_quality_score?: number;
  verification_status?: 'pending' | 'verified' | 'flagged';
  source_urls?: string | string[];
  
  // Timestamps (for mapping purposes)
  last_scraped_at?: string; // Data scraping timestamp
  created_at?: string;
  updated_at?: string;
}

/**
 * Normalizes cuisine_type to always be an array
 */
function normalizeCuisineType(cuisine_type: string | string[]): string[] {
  if (typeof cuisine_type === 'string') {
    // Split on common separators and clean up
    return cuisine_type
      .split(/[,;&]/)
      .map(c => c.trim())
      .filter(c => c.length > 0);
  }
  return Array.isArray(cuisine_type) ? cuisine_type : [];
}

/**
 * Normalizes specialties to always be an array
 */
function normalizeSpecialties(specialties?: string | string[]): string[] {
  if (!specialties) return [];
  if (typeof specialties === 'string') {
    return specialties
      .split(/[,;&]/)
      .map(s => s.trim())
      .filter(s => s.length > 0);
  }
  return Array.isArray(specialties) ? specialties : [];
}

/**
 * Normalizes source URLs to always be an array
 */
function normalizeSourceUrls(source_urls?: string | string[]): string[] {
  if (!source_urls) return [];
  if (typeof source_urls === 'string') {
    return [source_urls];
  }
  return Array.isArray(source_urls) ? source_urls : [];
}

/**
 * Creates default operating hours
 */
function createDefaultOperatingHours(): OperatingHours {
  return {
    monday: undefined,
    tuesday: undefined,
    wednesday: undefined,
    thursday: undefined,
    friday: undefined,
    saturday: undefined,
    sunday: undefined
  };
}

/**
 * Maps Uprooted Vegan Cuisine data to our FoodTruck schema
 */
export function mapUprootedVeganData(data: UprootedVeganData): Partial<FoodTruck> {
  const now = new Date().toISOString();
  
  // Handle location priority: exact_location > current_location > city_location
  const primaryLocation = data.exact_location || data.current_location || data.city_location;
  
  const mapped: Partial<FoodTruck> = {
    // Core fields
    name: data.name,
    description: data.description,
    cuisine_type: normalizeCuisineType(data.cuisine_type),
    price_range: data.price_range,
    specialties: normalizeSpecialties(data.specialties),
    
    // New fields for Uprooted Vegan support
    user_id: data.user_id,
    state: data.state,
    
    // Location fields
    current_location: primaryLocation ? {
      lat: primaryLocation.lat,
      lng: primaryLocation.lng,
      address: primaryLocation.address,
      timestamp: primaryLocation.timestamp || now,
    } : {
      lat: 0,
      lng: 0,
      address: undefined,
      timestamp: now,
    },
    scheduled_locations: data.scheduled_locations,
    
    // Menu and hours
    operating_hours: data.operating_hours as OperatingHours || createDefaultOperatingHours(),
    menu: (data.menu as MenuCategory[]) || [],
    
    // Contact information
    contact_info: data.contact_info || {},
    social_media: data.social_media || {},
    
    // Quality and verification
    data_quality_score: data.data_quality_score ?? 0.5, // Default to medium quality
    verification_status: data.verification_status || 'pending',
    source_urls: normalizeSourceUrls(data.source_urls),
    
    // Handle timestamps
    last_scraped_at: data.last_scraped_at || now,
    created_at: data.created_at || now,
    updated_at: data.updated_at || now,
    
    // Additional helpful defaults
    is_active: true, // Assume new entries are active
    test_run_flag: false // Not a test import
  };
  
  return mapped;
}

/**
 * Validates that required fields are present
 */
export function validateUprootedVeganData(data: UprootedVeganData): { 
  isValid: boolean; 
  errors: string[] 
} {
  const errors: string[] = [];
  
  // Check required fields
  if (!data.name || data.name.trim().length === 0) {
    errors.push('name is required');
  }
  
  if (!data.cuisine_type || 
      (Array.isArray(data.cuisine_type) && data.cuisine_type.length === 0) ||
      (typeof data.cuisine_type === 'string' && data.cuisine_type.trim().length === 0)) {
    errors.push('cuisine_type is required');
  }
  
  // Validate state format if provided
  if (data.state && (typeof data.state !== 'string' || data.state.length !== 2)) {
    errors.push('state must be a 2-character string (e.g., CA, NY, TX)');
  }
  
  // Validate user_id format if provided
  if (data.user_id && typeof data.user_id !== 'string') {
    errors.push('user_id must be a valid UUID string');
  }
  
  // Validate price range if provided
  if (data.price_range && !['$', '$$', '$$$', '$$$$'].includes(data.price_range)) {
    errors.push('price_range must be one of: $, $$, $$$, $$$$');
  }
  
  return {
    isValid: errors.length === 0,
    errors
  };
}

