                      View Logs
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
          </TableBody>
        </Table>
      </CardContent>
    </Card>
  );
}

// Failed Processing Queue Table Component
function FailedProcessingQueueTable({ processingQueue }: {
