export function GET(request: NextRequest) {
  const { searchParams } = new URL(request.url);
  const action = searchParams.get('action');

  try {
    switch (action) {
      case 'status': {
        return NextResponse.json({
          isRunning: schedulerInstance !== undefined,
          tasks: schedulerTasks,
          summary: {
            totalTasks: schedulerTasks.length,
            enabledTasks: schedulerTasks.filter((t) => t.enabled).length,
            runningTasks: schedulerTasks.filter((t) => t.enabled && t.nextRun !== undefined).length,
            totalSuccesses: schedulerTasks.reduce((acc, t) => acc + t.successCount, 0),
            totalErrors: schedulerTasks.reduce((acc, t) => acc + t.errorCount, 0),
          },
        });
      }

      case 'logs': {
        // Return recent task execution logs
        return NextResponse.json({
          logs: [
            {
              timestamp: new Date(Date.now() - 300_000).toISOString(),
              taskId: 'location_update',
              level: 'info',
              message: 'Successfully updated locations for 12 food trucks',
            },
            {
              timestamp: new Date(Date.now() - 900_000).toISOString(),
              taskId: 'instagram_scrape',
              level: 'info',
              message: 'Scraped 3 Instagram accounts, found 8 new posts',
            },
            {
              timestamp: new Date(Date.now() - 1_800_000).toISOString(),
              taskId: 'gemini_processing',
              level: 'error',
              message: 'Rate limit exceeded, skipping AI processing',
            },
            {
              timestamp: new Date(Date.now() - 3_600_000).toISOString(),
              taskId: 'website_crawl',
              level: 'warning',
              message: 'Failed to crawl tacoparadise.com - site temporarily unavailable',
            },
          ],
        });
      }

      default: {
        return NextResponse.json({
          message: 'Task Scheduler API',
          endpoints: [
            'GET /api/scheduler?action=status - Get scheduler status',
            'GET /api/scheduler?action=logs - Get execution logs',
            'POST /api/scheduler - Start/stop scheduler or execute task',
            'PUT /api/scheduler - Update task configuration',
          ],
        });
      }
    }
  } catch (error: unknown) {
    console.error('Scheduler API error:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

export async function POST(request: NextRequest) {
  try {
    const body = (await request.json()) as PostRequestBody;
    const { action, taskId } = body;

    switch (action) {
      case 'start': {
        if (schedulerInstance) {
          return NextResponse.json({ error: 'Scheduler is already running' }, { status: 409 });
        }

        schedulerInstance = { started: new Date().toISOString() };

        return NextResponse.json({
          message: 'Scheduler started successfully',
          status: 'running',
        });
      }

      case 'stop': {
        if (!schedulerInstance) {
          return NextResponse.json({ error: 'Scheduler is not running' }, { status: 409 });
        }

        schedulerInstance = undefined;

        return NextResponse.json({
          message: 'Scheduler stopped successfully',
          status: 'stopped',
        });
      }

      case 'execute': {
        if (taskId === undefined || taskId === '') {
          return NextResponse.json({ error: 'Task ID is required for execution' }, { status: 400 });
        }

        const task = schedulerTasks.find((t) => t.id === taskId);
        if (!task) {
          return NextResponse.json({ error: 'Task not found' }, { status: 404 });
        }

        // Simulate task execution
        task.lastRun = new Date().toISOString();

        // Simulate success/failure
        // eslint-disable-next-line sonarjs/pseudo-random
        if (Math.random() > 0.1) {
          // 90% success rate
          task.lastSuccess = task.lastRun;
          task.successCount++;
          task.lastError = undefined;
        } else {
          task.errorCount++;
          task.lastError = 'Simulated execution error';
        }

        return NextResponse.json({
          message: `Task ${taskId} executed`,
          task: task,
        });
      }

      default: {
        return NextResponse.json({ error: 'Invalid action' }, { status: 400 });
      }
    }
  } catch (error: unknown) {
    console.error('Error in scheduler POST:', error);
    return NextResponse.json({ error: 'Failed to process request' }, { status: 500 });
  }
}

export async function PUT(request: NextRequest) {
  try {
    const body = (await request.json()) as PutRequestBody;
    const { taskId, config } = body;

    if (!taskId) {
      return NextResponse.json({ error: 'Task ID is required' }, { status: 400 });
    }

    const taskIndex = schedulerTasks.findIndex((t) => t.id === taskId);
    if (taskIndex === -1) {
      return NextResponse.json({ error: 'Task not found' }, { status: 404 });
    }

    // Update task configuration
    schedulerTasks[taskIndex] = {
      ...schedulerTasks[taskIndex],
      ...config,
    };

    // Update next run time if interval changed
    if (config.intervalMinutes !== undefined && config.intervalMinutes > 0 && schedulerTasks[taskIndex].enabled) {
      const lastRun = new Date(schedulerTasks[taskIndex].lastRun ?? Date.now());
      const nextRun = new Date(lastRun.getTime() + config.intervalMinutes * 60 * 1000);
      schedulerTasks[taskIndex].nextRun = nextRun.toISOString();
    }

    return NextResponse.json({
      message: 'Task configuration updated',
      task: schedulerTasks[taskIndex],
    });
  } catch (error: unknown) {
    console.error('Error updating task configuration:', error);
    return NextResponse.json({ error: 'Failed to update task configuration' }, { status: 500 });
  }
}

import { type NextRequest, NextResponse } from 'next/server';
import { CachedFoodTruckService } from '@/lib/performance/databaseCache';
import { MenuCategory, MenuItem, OperatingHours, type FoodTruck } from '@/lib/types';

export async function GET(request: NextRequest) {
  const { searchParams } = new URL(request.url);
  const query = searchParams.get('q');
  const cuisine = searchParams.get('cuisine');
  const openNow = searchParams.get('openNow') === 'true';
  const lat = searchParams.get('lat');
  const lng = searchParams.get('lng');
  const radius = searchParams.get('radius') ?? '10';

  try {
    let trucks: FoodTruck[] = [];

    // Get trucks by location if coordinates provided
    if (lat != undefined && lng != undefined) {
      const userLat = Number.parseFloat(lat);
      const userLng = Number.parseFloat(lng);
      const radiusKm = Number.parseFloat(radius);
      trucks = await CachedFoodTruckService.getTrucksByLocationCached(userLat, userLng, radiusKm);
    } else {
      // Get all trucks
      const result = await CachedFoodTruckService.getAllTrucksCached();
      trucks = result.trucks;
    }

    // Apply filters
    let filteredTrucks = trucks;

    // Text search filter
    if (query != undefined && query !== '') {
      filteredTrucks = filteredTrucks.filter(
        (truck: FoodTruck) => truck.name.toLowerCase().includes(query.toLowerCase()) ||
        truck.description?.toLowerCase().includes(query.toLowerCase()) ??
        truck.menu?.some((category: MenuCategory) =>
          category.items?.some(
            (item: MenuItem) =>
              item.name.toLowerCase().includes(query.toLowerCase()) ??
              (item.description?.toLowerCase().includes(query.toLowerCase()) ?? false),
          ) ?? false,
        ) ?? false,
      );
    }

    // Cuisine filter
    if (cuisine != undefined && cuisine != '') {
      filteredTrucks = filteredTrucks.filter((truck: FoodTruck) => truck.menu?.some((category: MenuCategory) =>
        category.name.toLowerCase().includes(cuisine.toLowerCase()),
      ),
      );
    }

    // Open now filter
    if (openNow === true) {
      const now = new Date();
      const daysOfWeek: Array<keyof OperatingHours> = [
        'sunday',
        'monday',
        'tuesday',
        'wednesday',
        'thursday',
        'friday',
        'saturday',
      ];
      const currentDay = daysOfWeek[now.getDay()];
      const currentTime = now.getHours() * 100 + now.getMinutes();

      filteredTrucks = filteredTrucks.filter((truck: FoodTruck) => {
        const hours = truck.operating_hours?.[currentDay];
        if (!hours || hours.closed) return false;

        const openTime = Number.parseInt(hours.open.replace(':', ''));
        const closeTime = Number.parseInt(hours.close.replace(':', ''));
        return currentTime >= openTime && currentTime <= closeTime;
      });
    }

    // Sort by data quality score
    filteredTrucks.sort((a: FoodTruck, b: FoodTruck) => (b.data_quality_score ?? 0) - (a.data_quality_score ?? 0));

    return NextResponse.json({
      trucks: filteredTrucks,
      total: filteredTrucks.length,
      filters: {
        query,
        cuisine,
        openNow,
        location:
          lat != undefined && lng != undefined ? { lat: Number.parseFloat(lat), lng: Number.parseFloat(lng) } : undefined,
        radius: Number.parseFloat(radius),
      },
    });
  } catch (error) {
    console.error('Search error:', error);
    return NextResponse.json({ error: 'Search failed' }, { status: 500 });
  }
}

