export default function LoginPage() {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string>();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const router = useRouter();
  const searchParams = useSearchParams();
  const redirectTo = searchParams.get('redirectedFrom') ?? '/admin';

  const handleEmailLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      setLoading(true);
      setError(undefined);

      const { error } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (error) {
        throw error;
      }

      // Check if user has admin role
      const {
        data: { user },
      } = await supabase.auth.getUser();

      if (user) {
        const { data: profile } = await supabase
          .from('profiles')
          .select('role')
          .eq('id', user.id)
          .single();

        if (profile?.role === 'admin') {
          router.push(redirectTo);
        } else {
          // User exists but is not admin - redirect to access denied
          router.push('/access-denied');
        }
      }
    } catch (error: unknown) {
      console.error('Login error:', error);
      setError(error instanceof Error ? error.message : 'An error occurred during login');
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleLogin = async () => {
    try {
      setLoading(true);
      setError(undefined);

      const { error } = await supabase.auth.signInWithOAuth({
        provider: 'google',
        options: {
          redirectTo: `${globalThis.location.origin}/auth/callback?redirectTo=${encodeURIComponent(redirectTo)}`,
        },
      });

      if (error) {
        throw error;
      }
    } catch (error: unknown) {
      console.error('Login error:', error);
      setError(error instanceof Error ? error.message : 'An error occurred during login');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-background to-secondary/20 p-4">
      <Card className="w-full max-w-md">
        <LoginHeader />
        <CardContent className="space-y-4">
          {error !== undefined && error !== '' && (
            <Alert variant="destructive">
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          <EmailLoginForm
            email={email}
            setEmail={setEmail}
            password={password}
            setPassword={setPassword}
            loading={loading}
            handleEmailLogin={handleEmailLogin}
          />

          <LoginDivider />

          <GoogleLoginButton
            loading={loading}
            handleGoogleLogin={handleGoogleLogin}
          />

          <LoginFooter />
        </CardContent>
      </Card>
    </div>
  );
}

import { NextResponse } from 'next/server';
import type { NextRequest } from 'next/server';
import { createSupabaseMiddlewareClient } from '@/lib/supabaseMiddleware';
import { AuditLogger } from '@/lib/security/auditLogger';

export async function middleware(req: NextRequest) {
  const res = NextResponse.next();
  const supabase = createSupabaseMiddlewareClient(req, res);

  // Extract request metadata for security logging
  const requestMetadata = {
    ip: (req.ip as string | undefined) ?? req.headers.get('x-forwarded-for') ?? req.headers.get('x-real-ip') ?? 'unknown',
    userAgent: req.headers.get('user-agent') ?? 'unknown',
    url: req.nextUrl.pathname,
    method: req.method
  } as {
    ip: string;
    userAgent: string;
    url: string;
    method: string;
  };

  const {
    data: { user },
    error: userError,
  } = await supabase.auth.getUser();

  // Protect admin routes
  if (req.nextUrl.pathname.startsWith('/admin')) {
    if (userError || !user) {
      // Log unauthorized access attempt
      await AuditLogger.logSecurityEvent({
        event_type: 'permission_denied',
        ip_address: requestMetadata.ip,
        user_agent: requestMetadata.userAgent,
        details: {
          attempted_url: requestMetadata.url,
          reason: 'no_session',
          error: userError?.message
        },
        severity: 'warning'
      } as Parameters<typeof AuditLogger.logSecurityEvent>[0]);

      // Redirect unauthenticated users to login
      const redirectUrl = req.nextUrl.clone();
      redirectUrl.pathname = '/login';
      redirectUrl.searchParams.set(`redirectedFrom`, req.nextUrl.pathname);
      return NextResponse.redirect(redirectUrl);
    }

    // Check for admin role (assuming 'profiles' table with 'role' column)
    const { data: profile, error: profileQueryError } = await supabase
      .from('profiles')
      .select('role')
      .eq('id', user.id)
      .single();

    if (profileQueryError || profile?.role !== 'admin') {
      // Log unauthorized admin access attempt
      await AuditLogger.logSecurityEvent({
        event_type: 'permission_denied',
        user_id: user.id,
        user_email: user.email,
        ip_address: requestMetadata.ip,
        user_agent: requestMetadata.userAgent,
        details: {
          attempted_url: requestMetadata.url,
          user_role: profile?.role ?? 'none',
          reason: 'insufficient_privileges',
          error: profileQueryError?.message
        },
        severity: 'error'
      } as Parameters<typeof AuditLogger.logSecurityEvent>[0]);

      // Redirect non-admin users to access denied page
      const redirectUrl = req.nextUrl.clone();
      redirectUrl.pathname = '/access-denied';
      return NextResponse.redirect(redirectUrl);
    }

    // Log successful admin access for audit trail
    if (req.method !== 'GET' || req.nextUrl.pathname.includes('/api/')) {
      await AuditLogger.logDataAccess(
        user.id,
        user.email ?? 'unknown',
        'admin_panel',
        req.nextUrl.pathname,
        req.method === 'GET' ? 'read' : 'write',
        {
          ip: requestMetadata.ip,
          userAgent: requestMetadata.userAgent
        } as Parameters<typeof AuditLogger.logDataAccess>[5]
      );
    }
  }

  return res;
}

