        className="dark:text-gray-300 dark:border-slate-600 dark:hover:bg-slate-700"
      >
        Clear All
      </Button>
    </div>
  );
}

// Cuisine types section component
function CuisineTypesSection({
  filters,
  setFilters,
  cuisineTypes
}: {
  readonly filters: SearchFilters;
  readonly setFilters: (filters: SearchFilters) => void;
  readonly cuisineTypes: readonly string[];
}) {
  return (
    <div>
      <label className="text-sm font-medium mb-2 block dark:text-gray-100">
        Cuisine Type
      </label>
      <div className="flex flex-wrap gap-2">
        {cuisineTypes.map((cuisine) => (
          <Badge
            key={cuisine}
            variant={filters.cuisine === cuisine ? 'default' : 'outline'}
            className="cursor-pointer dark:text-gray-300 dark:border-slate-600 dark:hover:bg-slate-700"
            onClick={() =>
              setFilters({
                ...filters,
                cuisine: filters.cuisine === cuisine ? '' : cuisine,
              })
            }
          >
            {cuisine}
          </Badge>
        ))}
      </div>
    </div>
  );
}

// Distance slider section component
function DistanceSliderSection({
  filters,
  setFilters
}: {
  readonly filters: SearchFilters;
  readonly setFilters: (filters: SearchFilters) => void;
}) {
  return (
    <div>
      <label className="text-sm font-medium mb-2 block dark:text-gray-100">
        Distance: {filters.radius} km
      </label>
      <input
        type="range"
        min="1"
        max="50"
        value={filters.radius}
        onChange={(e) => setFilters({ ...filters, radius: Number(e.target.value) })}
        className="w-full accent-blue-600 dark:accent-blue-500"
      />
      <div className="flex justify-between text-xs text-gray-500 dark:text-gray-400 mt-1">
        <span>1 km</span>
        <span>50 km</span>
      </div>
    </div>
  );
}

interface SearchFiltersProps {
  readonly onSearch: (filters: SearchFilters) => void;
  readonly loading?: boolean;
}

interface SearchFilters {
  query: string;
  cuisine: string;
  openNow: boolean;
  radius: number;
}

export function SearchFilters({ onSearch, loading }: SearchFiltersProps) {
  const [filters, setFilters] = useState<SearchFilters>({
    query: '',
    cuisine: '',
    openNow: false,
    radius: 10,
  });

  const [showAdvanced, setShowAdvanced] = useState(false);

  const cuisineTypes = [
    'Mexican',
    'American',
    'Asian',
    'Italian',
    'BBQ',
    'Burgers',
    'Tacos',
    'Pizza',
    'Sandwiches',
    'Desserts',
  ];

  const handleSearch = () => {
    onSearch(filters);
  };

  const clearFilters = () => {
    const clearedFilters = {
      query: '',
      cuisine: '',
      openNow: false,
      radius: 10,
    };
    setFilters(clearedFilters);
    onSearch(clearedFilters);
  };

  const activeFilterCount = [
    filters.query !== '',
    filters.cuisine !== '',
    filters.openNow === true
  ].filter(Boolean).length;

  return (
    <Card className="dark:bg-slate-800 dark:border-slate-700">
      <CardContent className="p-4">
        <div className="space-y-4">
          <MainSearchSection
            filters={filters}
            setFilters={setFilters}
            loading={loading}
            handleSearch={handleSearch}
            showAdvanced={showAdvanced}
            setShowAdvanced={setShowAdvanced}
            activeFilterCount={activeFilterCount}
          />

          {showAdvanced && (
            <div className="space-y-4 pt-4 border-t dark:border-slate-700">
              <QuickFiltersSection
                filters={filters}
                setFilters={setFilters}
                clearFilters={clearFilters}
              />

              <CuisineTypesSection
                filters={filters}
                setFilters={setFilters}
                cuisineTypes={cuisineTypes}
              />

              <DistanceSliderSection
                filters={filters}
                setFilters={setFilters}
              />
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

