/**
 * SOTA Security Audit Logging System
 * Implements comprehensive audit logging for admin actions and security events
 */

import { supabaseAdmin } from '@/lib/supabase';

// Type alias for severity levels to comply with sonarjs/use-type-alias
type SeverityLevel = 'info' | 'warning' | 'error' | 'critical';

export interface AuditLogEntry {
  user_id: string;
  user_email: string;
  action: string;
  resource_type: string;
  resource_id?: string;
  details?: Record<string, unknown>;
  ip_address?: string;
  user_agent?: string;
  session_id?: string;
  timestamp: string;
  severity: SeverityLevel;
}

export interface SecurityEvent {
  event_type: 'login_attempt' | 'login_success' | 'login_failure' | 'logout' | 'permission_denied' | 'data_access' | 'data_modification' | 'admin_action';
  user_id?: string;
  user_email?: string;
  ip_address?: string;
  user_agent?: string;
  details?: Record<string, unknown>;
  severity: SeverityLevel;
}

/**
 * Audit Logger Service
 */
export class AuditLogger {
  /**
   * Log admin action with full audit trail
   */
  static async logAdminAction(
    userId: string,
    userEmail: string,
    action: string,
    resourceType: string,
    resourceId?: string,
    details?: Record<string, unknown>,
    request?: {
      ip?: string;
      userAgent?: string;
      sessionId?: string;
    }
  ): Promise<void> {
    const auditEntry: AuditLogEntry = {
      user_id: userId,
      user_email: userEmail,
      action,
      resource_type: resourceType,
      resource_id: resourceId,
      details,
      ip_address: request?.ip,
      user_agent: request?.userAgent,
      session_id: request?.sessionId,
      timestamp: new Date().toISOString(),
      severity: this.determineSeverity(action, resourceType)
    };

    await this.writeAuditLog(auditEntry);

    // Log to console for immediate monitoring
    console.info('Admin Action Audit:', {
      user: userEmail,
      action,
      resource: (resourceId === undefined) ? resourceType : `${resourceType}:${resourceId}`,
      timestamp: auditEntry.timestamp
    });
  }

  /**
   * Log security event
   */
  static async logSecurityEvent(event: SecurityEvent): Promise<void> {
    const logEntry = {
      event_type: event.event_type,
      user_id: event.user_id,
      user_email: event.user_email,
      ip_address: event.ip_address,
      user_agent: event.user_agent,
      details: event.details,
      severity: event.severity,
      timestamp: new Date().toISOString()
    };

    try {
      if (supabaseAdmin) {
        await supabaseAdmin
          .from('security_events')
          .insert(logEntry);
      }
    } catch (error) {
      console.error('Failed to log security event:', error);
    }

    // Always log security events to console
    console.info('Security Event:', logEntry);

    // Alert on critical security events
    if (event.severity === 'critical' || event.severity === 'error') {
      console.warn('SECURITY ALERT:', logEntry);
    }
  }

  /**
   * Log authentication events
   */
  static async logAuthEvent(
    eventType: 'login_attempt' | 'login_success' | 'login_failure' | 'logout',
    userEmail?: string,
    userId?: string,
    request?: {
      ip?: string;
      userAgent?: string;
    },
    details?: Record<string, unknown>
  ): Promise<void> {
    const severity = eventType === 'login_failure' ? 'warning' : 'info';

    await this.logSecurityEvent({
      event_type: eventType,
      user_id: userId,
      user_email: userEmail,
      ip_address: request?.ip,
      user_agent: request?.userAgent,
      details,
      severity
    });
  }

  /**
   * Log data access events
   */
  static async logDataAccess(
    userId: string,
    userEmail: string,
    resourceType: string,
    resourceId?: string,
    action: 'read' | 'search' | 'export' = 'read',
    request?: {
      ip?: string;
      userAgent?: string;
    }
  ): Promise<void> {
    await this.logSecurityEvent({
      event_type: 'data_access',
      user_id: userId,
      user_email: userEmail,
      ip_address: request?.ip,
      user_agent: request?.userAgent,
      details: {
        resource_type: resourceType,
        resource_id: resourceId,
        action
      },
      severity: 'info'
    });
  }

  /**
   * Write audit log to database
   */
  private static async writeAuditLog(entry: AuditLogEntry): Promise<void> {
    try {
      if (supabaseAdmin) {
        await supabaseAdmin
          .from('audit_logs')
          .insert(entry);
      }
    } catch (error) {
      console.error('Failed to write audit log:', error);
      // Don't throw - audit logging should not break application flow
    }
  }

  /**
   * Determine severity based on action and resource type
   */
  private static determineSeverity(action: string, resourceType: string): SeverityLevel {
    // Critical actions
    if (action.includes('delete') || action.includes('remove')) {
      return 'critical';
    }

    // Warning actions
    if (action.includes('update') || action.includes('modify') || action.includes('change')) {
      return 'warning';
    }

    // Admin-specific actions
    if (resourceType === 'user' || resourceType === 'admin' || resourceType === 'system') {
      return 'warning';
    }

    // Default to info
    return 'info';
  }

  /**
   * Get audit logs for a specific user
   */
  static async getUserAuditLogs(
    userId: string,
    limit: number = 100,
    offset: number = 0
  ): Promise<AuditLogEntry[]> {
    try {
      if (!supabaseAdmin) {
        return [];
      }

      const { data, error } = await supabaseAdmin
        .from('audit_logs')
        .select('*')
        .eq('user_id', userId)
        .order('timestamp', { ascending: false })
        .range(offset, offset + limit - 1);

      if (error) {
        console.error('Failed to fetch user audit logs:', error);
        return [];
      }

      // eslint-disable-next-line @typescript-eslint/no-unsafe-return
      return data ?? [];
    } catch (error) {
      console.error('Error fetching user audit logs:', error);
      return [];
    }
  }

  /**
   * Get recent security events
   */
  static async getRecentSecurityEvents(
    hours: number = 24,
    severity?: SeverityLevel
  ): Promise<Record<string, unknown>[]> {
    try {
      if (!supabaseAdmin) {
        return [];
      }

      const startTime = new Date();
      startTime.setHours(startTime.getHours() - hours);

      let query = supabaseAdmin
        .from('security_events')
        .select('*')
        .gte('timestamp', startTime.toISOString())
        .order('timestamp', { ascending: false });

      if (severity) {
        query = query.eq('severity', severity);
      }

      const { data, error } = await query.limit(500);

      if (error) {
        console.error('Failed to fetch security events:', error);
        return [];
      }

      // eslint-disable-next-line @typescript-eslint/no-unsafe-return
      return data ?? [];
    } catch (error) {
      console.error('Error fetching security events:', error);
      return [];
    }
  }
}

/**
 * Security monitoring utilities
 */
export const SecurityMonitor = {
  /**
   * Check for suspicious activity patterns
   */
  async checkSuspiciousActivity(userId: string): Promise<{
    suspicious: boolean;
    reasons: string[];
    riskLevel: 'low' | 'medium' | 'high';
  }> {
    const reasons: string[] = [];
    let riskLevel: 'low' | 'medium' | 'high' = 'low';

    try {
      // Check recent failed login attempts
      const recentEvents = await AuditLogger.getRecentSecurityEvents(1, 'warning');
      const failedLogins = recentEvents.filter(

        event => event.event_type === 'login_failure' && event.user_id === userId
      );

      if (failedLogins.length > 5) {
        reasons.push('Multiple failed login attempts');
        riskLevel = 'high';
      } else if (failedLogins.length > 2) {
        reasons.push('Recent failed login attempts');
        riskLevel = 'medium';
      }

      // Check for unusual access patterns
      const auditLogs = await AuditLogger.getUserAuditLogs(userId, 50);
      const recentActions = auditLogs.filter(
        log => new Date(log.timestamp) > new Date(Date.now() - 60 * 60 * 1000) // Last hour
      );

      if (recentActions.length > 20) {
        reasons.push('High activity volume');
        riskLevel = riskLevel === 'high' ? 'high' : 'medium';
      }

      return {
        suspicious: reasons.length > 0,
        reasons,
        riskLevel
      };
    } catch (error) {
      console.error('Error checking suspicious activity:', error);
      return {
        suspicious: false,
        reasons: [],
        riskLevel: 'low'
      };
    }
  },
};

/**
 * SOTA Rate Limiting Implementation
 * Prevents brute force attacks and API abuse with intelligent rate limiting
 */

interface RateLimitEntry {
  count: number;
  resetTime: number;
  blocked: boolean;
  blockUntil?: number;
}


// In-memory rate limit store (in production, use Redis)
const rateLimitStore = new Map<string, RateLimitEntry>();

// Rate limit configurations for different endpoints
export const RATE_LIMIT_CONFIGS = {
  // Authentication endpoints - strict limits
  auth: {
    windowMs: 15 * 60 * 1000, // 15 minutes
    maxRequests: 5, // 5 attempts per 15 minutes
    blockDurationMs: 30 * 60 * 1000, // Block for 30 minutes
    skipSuccessfulRequests: true
  },
  // API endpoints - moderate limits
  api: {
    windowMs: 60 * 1000, // 1 minute
    maxRequests: 60, // 60 requests per minute
    blockDurationMs: 5 * 60 * 1000, // Block for 5 minutes
    skipSuccessfulRequests: false
  },
  // Admin endpoints - very strict limits
  admin: {
    windowMs: 60 * 1000, // 1 minute
    maxRequests: 20, // 20 requests per minute
    blockDurationMs: 15 * 60 * 1000, // Block for 15 minutes
    skipSuccessfulRequests: false
  }
} as const;

export type RateLimitType = keyof typeof RATE_LIMIT_CONFIGS;

/**
 * Rate Limiter Service
 */
export class RateLimiter {
  /**
   * Check if request should be rate limited
   */
  static checkRateLimit(
    identifier: string,
    type: RateLimitType = 'api'
  ): {
    allowed: boolean;
    remaining: number;
    resetTime: number;
    retryAfter?: number;
  } {
    const config = RATE_LIMIT_CONFIGS[type];
    const now = Date.now();
    const key = `${type}:${identifier}`;

    // Clean up expired entries
    this.cleanupExpiredEntries();

    let entry = rateLimitStore.get(key);

    // Initialize entry if it doesn't exist
    if (!entry) {
      entry = {
        count: 0,
        resetTime: now + config.windowMs,
        blocked: false
      };
      rateLimitStore.set(key, entry);
    }

    // Check if currently blocked
    if (entry.blocked && (entry.blockUntil != undefined) && now < entry.blockUntil) {
      return {
        allowed: false,
        remaining: 0,
        resetTime: entry.resetTime,
        retryAfter: Math.ceil((entry.blockUntil - now) / 1000)
      };
    }

    // Reset window if expired
    if (now >= entry.resetTime) {
      entry.count = 0;
      entry.resetTime = now + config.windowMs;
      entry.blocked = false;
      entry.blockUntil = undefined;
    }

    // Check if limit exceeded
    if (entry.count >= config.maxRequests) {
      entry.blocked = true;
      entry.blockUntil = now + config.blockDurationMs;

      return {
        allowed: false,
        remaining: 0,
        resetTime: entry.resetTime,
        retryAfter: Math.ceil(config.blockDurationMs / 1000)
      };
    }

    // Increment counter
    entry.count++;
    rateLimitStore.set(key, entry);

    return {
      allowed: true,
      remaining: config.maxRequests - entry.count,
      resetTime: entry.resetTime
    };
  }

  /**
   * Record successful request (for auth endpoints)
   */
  static recordSuccess(identifier: string, type: RateLimitType = 'api'): void {
    const config = RATE_LIMIT_CONFIGS[type];
    if (!config.skipSuccessfulRequests) return;

    const key = `${type}:${identifier}`;
    const entry = rateLimitStore.get(key);

    if (entry) {
      // Reset counter on successful auth
      entry.count = 0;
      entry.blocked = false;
      entry.blockUntil = undefined;
      rateLimitStore.set(key, entry);
    }
  }

  /**
   * Get rate limit status without incrementing
   */
  static getStatus(
    identifier: string,
    type: RateLimitType = 'api'
  ): {
    remaining: number;
    resetTime: number;
    blocked: boolean;
    retryAfter?: number;
  } {
    const config = RATE_LIMIT_CONFIGS[type];
    const now = Date.now();
    const key = `${type}:${identifier}`;
    const entry = rateLimitStore.get(key);

    if (!entry) {
      return {
        remaining: config.maxRequests,
        resetTime: now + config.windowMs,
        blocked: false
      };
    }

    // Check if blocked
    if (entry.blocked && (entry.blockUntil != undefined) && now < entry.blockUntil) {
      return {
        remaining: 0,
        resetTime: entry.resetTime,
        blocked: true,
        retryAfter: Math.ceil((entry.blockUntil - now) / 1000)
      };
    }

    // Check if window expired
    if (now >= entry.resetTime) {
      return {
        remaining: config.maxRequests,
        resetTime: now + config.windowMs,
        blocked: false
      };
    }

    return {
      remaining: Math.max(0, config.maxRequests - entry.count),
      resetTime: entry.resetTime,
      blocked: false
    };
  }

  /**
   * Clean up expired entries to prevent memory leaks
   */
  private static cleanupExpiredEntries(): void {
    const now = Date.now();

    for (const [key, entry] of rateLimitStore.entries()) {
      // Remove entries that are expired and not blocked
      if (now >= entry.resetTime && (!entry.blocked || (entry.blockUntil == undefined) || now >= entry.blockUntil)) {
        rateLimitStore.delete(key);
      }
    }
  }

  /**
   * Clear all rate limit data for an identifier
   */
  static clearLimits(identifier: string, type?: RateLimitType): void {
    if (type) {
      const key = `${type}:${identifier}`;
      rateLimitStore.delete(key);
    } else {
      // Clear all types for this identifier
      for (const limitType of Object.keys(RATE_LIMIT_CONFIGS)) {
        const key = `${limitType}:${identifier}`;
        rateLimitStore.delete(key);
      }
    }
  }

  /**
   * Get rate limit statistics
   */
  static getStats(): {
    totalEntries: number;
    blockedEntries: number;
    entriesByType: Record<string, number>;
  } {
    const stats = {
      totalEntries: rateLimitStore.size,
      blockedEntries: 0,
      entriesByType: {} as Record<string, number>
    };

    for (const [key, entry] of rateLimitStore.entries()) {
      const type = key.split(':')[0];
      stats.entriesByType[type] = (stats.entriesByType[type] ?? 0) + 1;

      if (entry.blocked) {
        stats.blockedEntries++;
      }
    }

    return stats;
  }
}

/**
 * Utility function to get client identifier from request
 */
export function getClientIdentifier(request: Request): string {
  // Try to get IP address from various headers
  const forwarded = request.headers.get('x-forwarded-for');
  const realIp = request.headers.get('x-real-ip');
  const cfConnectingIp = request.headers.get('cf-connecting-ip');

  // Use the first available IP
  const ip = (forwarded?.split(',')[0]?.trim() ?? realIp ?? cfConnectingIp) ?? 'unknown';

  // Include user agent for more specific identification
  const userAgent = request.headers.get('user-agent') ?? 'unknown';

  // Create a hash of IP + User Agent for better identification
  return `${ip}:${userAgent.slice(0, 50)}`;
}

/**
 * Rate limiting middleware for API routes
 */
export function withRateLimit(
  handler: (request: Request) => Promise<Response>,
  type: RateLimitType = 'api'
) {
  return async (request: Request): Promise<Response> => {
    const identifier = getClientIdentifier(request);
    const result = RateLimiter.checkRateLimit(identifier, type);

    if (result.allowed === false) {
      const headers = new Headers({
        'X-RateLimit-Limit': RATE_LIMIT_CONFIGS[type].maxRequests.toString(),
        'X-RateLimit-Remaining': '0',
        'X-RateLimit-Reset': new Date(result.resetTime).toISOString(),
      });

      if (result.retryAfter != undefined) {
        headers.set('Retry-After', result.retryAfter.toString());
      }

      return new Response(
        JSON.stringify({
          error: 'Rate limit exceeded',
          message: (result.retryAfter == undefined) ? 'Too many requests. Try again later.' : `Too many requests. Try again in ${result.retryAfter} seconds.`,
          retryAfter: result.retryAfter
        }),
        {
          status: 429,
          headers: {
            'Content-Type': 'application/json',
            ...Object.fromEntries(headers.entries())
          }
        }
      );
    }

    // Add rate limit headers to successful responses
    const response = await handler(request);

    response.headers.set('X-RateLimit-Limit', RATE_LIMIT_CONFIGS[type].maxRequests.toString());
    response.headers.set('X-RateLimit-Remaining', result.remaining.toString());
    response.headers.set('X-RateLimit-Reset', new Date(result.resetTime).toISOString());

    return response;
  };
}

/**
 * SOTA Quality Scoring Utilities
 * Centralized quality scoring functions to eliminate duplication
 * and provide consistent quality metrics across the application
 */

import { isNonEmptyString, isValidNumber, safeNumber, safeString } from './typeGuards';

// Quality score weights (must sum to 1.0)
export const QUALITY_WEIGHTS = {
  CORE_DATA: 0.35,      // 35% - name, description, location
  LOCATION_DATA: 0.25,  // 25% - coordinates, address
  CONTACT_DATA: 0.2,    // 20% - phone, email, website
  MENU_DATA: 0.1,       // 10% - menu items, pricing
  OPERATIONAL_DATA: 0.05, // 5% - hours, social media
  VERIFICATION: 0.05    // 5% - verification status
} as const;

// Quality thresholds
export const QUALITY_THRESHOLDS = {
  HIGH: 0.8,    // 80%+
  MEDIUM: 0.6,  // 60-79%
  LOW: 0       // <60%
} as const;

// Core data quality scoring
export function scoreCoreData(truck: Record<string, unknown>): number {
  let score = 0;
  let maxScore = 0;

  // Name (required - 40% of core score)
  maxScore += 0.4;
  if (isNonEmptyString(truck.name)) {
    score += 0.4;
  }

  // Description (30% of core score)
  maxScore += 0.3;
  if (isNonEmptyString(truck.description)) {
    const desc = safeString(truck.description);
    if (desc.length > 50) {
      score += 0.3;
    } else if (desc.length > 20) {
      score += 0.15;
    }
  }

  // Cuisine type (20% of core score)
  maxScore += 0.2;
  if (isNonEmptyString(truck.cuisine_type)) {
    score += 0.2;
  }

  // Image URL (10% of core score)
  maxScore += 0.1;
  if (isNonEmptyString(truck.image_url)) {
    score += 0.1;
  }

  return maxScore > 0 ? score / maxScore : 0;
}

// Location data quality scoring
export function scoreLocationData(truck: Record<string, unknown>): number {
  let score = 0;
  let maxScore = 0;

  // Coordinates (50% of location score)
  maxScore += 0.5;
  const latitude = safeNumber(truck.latitude);
  const longitude = safeNumber(truck.longitude);
  if (isValidNumber(latitude) && isValidNumber(longitude) && 
      latitude !== 0 && longitude !== 0) {
    score += 0.5;
  }

  // Address (30% of location score)
  maxScore += 0.3;
  if (isNonEmptyString(truck.address)) {
    score += 0.3;
  }

  // City/State (20% of location score)
  maxScore += 0.2;
  if (isNonEmptyString(truck.city) || isNonEmptyString(truck.state)) {
    score += 0.2;
  }

  return maxScore > 0 ? score / maxScore : 0;
}

// Contact data quality scoring
export function scoreContactData(truck: Record<string, unknown>): number {
  let score = 0;
  let maxScore = 0;

  // Phone (40% of contact score)
  maxScore += 0.4;
  if (isNonEmptyString(truck.phone)) {
    const phone = safeString(truck.phone);
    // Basic phone validation - simplified to avoid backtracking
    if (/^\+?[\d\s\-()]{10,}$/.test(phone)) {
      score += 0.4;
    } else if (phone.length > 5) {
      score += 0.2;
    }
  }

  // Email (30% of contact score)
  maxScore += 0.3;
  if (isNonEmptyString(truck.email)) {
    const email = safeString(truck.email);
    // Basic email validation
    if (/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      score += 0.3;
    }
  }

  // Website (30% of contact score)
  maxScore += 0.3;
  if (isNonEmptyString(truck.website)) {
    const website = safeString(truck.website);
    // Basic URL validation
    if (/^https?:\/\/.+\..+/.test(website)) {
      score += 0.3;
    }
  }

  return maxScore > 0 ? score / maxScore : 0;
}

// Menu data quality scoring
export function scoreMenuData(truck: Record<string, unknown>): number {
  let score = 0;
  let maxScore = 0;

  // Menu items (70% of menu score)
  maxScore += 0.7;
  if (isNonEmptyString(truck.menu_items)) {
    const menuItems = safeString(truck.menu_items);
    if (menuItems.length > 100) {
      score += 0.7;
    } else if (menuItems.length > 20) {
      score += 0.35;
    }
  }

  // Price range (30% of menu score)
  maxScore += 0.3;
  if (isNonEmptyString(truck.price_range)) {
    score += 0.3;
  }

  return maxScore > 0 ? score / maxScore : 0;
}

// Operational data quality scoring
export function scoreOperationalData(truck: Record<string, unknown>): number {
  let score = 0;
  let maxScore = 0;

  // Operating hours (60% of operational score)
  maxScore += 0.6;
  if (isNonEmptyString(truck.operating_hours)) {
    score += 0.6;
  }

  // Social media (40% of operational score)
  maxScore += 0.4;
  const socialFields = ['facebook_url', 'twitter_url', 'instagram_url'];
  const socialCount = socialFields.filter(field => isNonEmptyString(truck[field])).length;
  score += (socialCount / socialFields.length) * 0.4;

  return maxScore > 0 ? score / maxScore : 0;
}

// Verification status scoring
export function scoreVerificationStatus(truck: Record<string, unknown>): number {
  const status = safeString(truck.verification_status);
  switch (status) {
    case 'verified': {
      return 1;
    }
    case 'pending': {
      return 0.5;
    }
    default: {
      return 0;
    }
  }
}

// Calculate overall quality score
export function calculateOverallQualityScore(truck: Record<string, unknown>): number {
  const coreScore = scoreCoreData(truck);
  const locationScore = scoreLocationData(truck);
  const contactScore = scoreContactData(truck);
  const menuScore = scoreMenuData(truck);
  const operationalScore = scoreOperationalData(truck);
  const verificationScore = scoreVerificationStatus(truck);

  return (
    coreScore * QUALITY_WEIGHTS.CORE_DATA +
    locationScore * QUALITY_WEIGHTS.LOCATION_DATA +
    contactScore * QUALITY_WEIGHTS.CONTACT_DATA +
    menuScore * QUALITY_WEIGHTS.MENU_DATA +
    operationalScore * QUALITY_WEIGHTS.OPERATIONAL_DATA +
    verificationScore * QUALITY_WEIGHTS.VERIFICATION
  );
}

// Get quality category
export function getQualityCategory(score: number): 'high' | 'medium' | 'low' {
  if (score >= QUALITY_THRESHOLDS.HIGH) return 'high';
  if (score >= QUALITY_THRESHOLDS.MEDIUM) return 'medium';
  return 'low';
}

// Get quality label
export function getQualityLabel(score: number): string {
  const category = getQualityCategory(score);
  switch (category) {
    case 'high': {
      return 'Excellent';
    }
    case 'medium': {
      return 'Good';
    }
    case 'low': {
      return 'Needs Work';
    }
  }
}

// Get quality color
export function getQualityColor(score: number): string {
  const category = getQualityCategory(score);
  switch (category) {
    case 'high': {
      return '#22c55e';
    }
    case 'medium': {
      return '#f59e0b';
    }
    case 'low': {
      return '#ef4444';
    }
  }
}

#!/usr/bin/env node

/**
 * ESLint Error Pattern Analyzer
 * Analyzes current ESLint errors to identify automation opportunities
 */

const { execSync } = require('node:child_process');
const fs = require('node:fs');

class ErrorPatternAnalyzer {
  constructor() {
    this.errorCounts = {};
    this.fileErrorCounts = {};
    this.automationCandidates = [];
  }

  /**
   * Get current ESLint results
   */
  getESLintResults() {
    try {
      console.log('ðŸ” Running ESLint analysis...');
      const output = execSync('npx eslint . --format json', {
        encoding: 'utf8',
        stdio: ['pipe', 'pipe', 'pipe'],
        timeout: 120_000
      });
      return JSON.parse(output);
    } catch (error) {
      // Try to parse output even if ESLint failed
      const stdout = error.stdout ? error.stdout.toString() : '';
      if (stdout) {
        try {
          return JSON.parse(stdout);
        } catch (parseError) {
          console.error('Failed to parse ESLint output:', parseError.message);
          return [];
        }
      }
      console.error('ESLint failed:', error.message);
      return [];
    }
  }

  /**
   * Analyze error patterns from ESLint results
   */
  analyzePatterns(results) {
    console.log('ðŸ“Š Analyzing error patterns...');

    for (const file of results) {
      const fileName = file.filePath.replace(process.cwd(), '').replaceAll('\\', '/');
      let fileErrorCount = 0;

      for (const msg of file.messages) {
        if (msg.severity === 2) { // Error (not warning)
          const ruleId = msg.ruleId || 'unknown';
          this.errorCounts[ruleId] = (this.errorCounts[ruleId] || 0) + 1;
          fileErrorCount++;
        }
      }

      if (fileErrorCount > 0) {
        this.fileErrorCounts[fileName] = fileErrorCount;
      }
    }
  }

  /**
   * Identify automation candidates based on frequency and safety
   */
  identifyAutomationCandidates() {
    console.log('ðŸŽ¯ Identifying automation candidates...');

    // Define automation-friendly rules
    const automationRules = {
      // High confidence - safe to automate
      '@typescript-eslint/no-unused-vars': {
        confidence: 'HIGH',
        method: 'eslint-autofix',
        description: 'Remove unused variables and imports',
        estimatedReduction: '90%'
      },
      'sonarjs/unused-import': {
        confidence: 'HIGH', 
        method: 'eslint-autofix',
        description: 'Remove unused imports',
        estimatedReduction: '95%'
      },
      'unicorn/no-null': {
        confidence: 'HIGH',
        method: 'pattern-replacement',
        description: 'Replace null with undefined',
        estimatedReduction: '85%'
      },

      // Medium confidence - needs careful automation
      '@typescript-eslint/no-explicit-any': {
        confidence: 'MEDIUM',
        method: 'pattern-replacement',
        description: 'Replace any with unknown in safe contexts',
        estimatedReduction: '40%'
      },
      '@typescript-eslint/no-unsafe-assignment': {
        confidence: 'MEDIUM',
        method: 'type-annotation',
        description: 'Add type annotations for unsafe assignments',
        estimatedReduction: '30%'
      },
      'sonarjs/no-dead-store': {
        confidence: 'MEDIUM',
        method: 'pattern-replacement',
        description: 'Remove dead store assignments',
        estimatedReduction: '70%'
      },

      // Low confidence - manual review needed
      '@typescript-eslint/strict-boolean-expressions': {
        confidence: 'LOW',
        method: 'manual-review',
        description: 'Complex boolean expression fixes',
        estimatedReduction: '10%'
      },
      'max-lines-per-function': {
        confidence: 'MANUAL_ONLY',
        method: 'manual-ide-refactor',
        description: 'RESEARCH-PROVEN UNSAFE FOR AUTOMATION: Function extraction requires human judgment. Use VS Code Extract Method only.',
        estimatedReduction: '0%',
        automationRisk: 'HIGH',
        researchEvidence: 'Academic studies show 47% failure rate, semantic errors common'
      }
    };

    // Calculate automation potential
    for (const [ruleId, count] of Object.entries(this.errorCounts)) {
      if (automationRules[ruleId] && count >= 5) { // Only consider rules with 5+ occurrences
        const rule = automationRules[ruleId];
        const estimatedFixes = Math.floor(count * (Number.parseInt(rule.estimatedReduction) / 100));

        this.automationCandidates.push({
          ruleId,
          count,
          confidence: rule.confidence,
          method: rule.method,
          description: rule.description,
          estimatedFixes,
          priority: this.calculatePriority(count, rule.confidence, estimatedFixes)
        });
      }
    }

    // Sort by priority (highest first)
    this.automationCandidates.sort((a, b) => b.priority - a.priority);
  }

  /**
   * Calculate automation priority score
   */
  calculatePriority(count, confidence, estimatedFixes) {
    const confidenceMultiplier = {
      'HIGH': 3,
      'MEDIUM': 2,
      'LOW': 1
    };

    return estimatedFixes * confidenceMultiplier[confidence];
  }

  /**
   * Generate automation recommendations
   */
  generateRecommendations() {
    console.log('ðŸ’¡ Generating automation recommendations...');

    const highConfidenceCandidates = this.automationCandidates.filter(c => c.confidence === 'HIGH');
    const mediumConfidenceCandidates = this.automationCandidates.filter(c => c.confidence === 'MEDIUM');

    const totalHighConfidenceFixes = highConfidenceCandidates.reduce((sum, c) => sum + c.estimatedFixes, 0);
    const totalMediumConfidenceFixes = mediumConfidenceCandidates.reduce((sum, c) => sum + c.estimatedFixes, 0);

    return {
      highConfidence: {
        candidates: highConfidenceCandidates,
        totalFixes: totalHighConfidenceFixes,
        methods: [...new Set(highConfidenceCandidates.map(c => c.method))]
      },
      mediumConfidence: {
        candidates: mediumConfidenceCandidates,
        totalFixes: totalMediumConfidenceFixes,
        methods: [...new Set(mediumConfidenceCandidates.map(c => c.method))]
      }
    };
  }

  /**
   * Print analysis results
   */
  printResults() {
    const totalErrors = Object.values(this.errorCounts).reduce((sum, count) => sum + count, 0);
    const sortedErrors = Object.entries(this.errorCounts).sort((a, b) => b[1] - a[1]);
    const recommendations = this.generateRecommendations();

    console.log('\nðŸ“ˆ ERROR PATTERN ANALYSIS RESULTS');
    console.log('=====================================');
    console.log(`Total errors: ${totalErrors}`);
    console.log(`Unique error types: ${Object.keys(this.errorCounts).length}`);
    console.log(`Files with errors: ${Object.keys(this.fileErrorCounts).length}`);

    console.log('\nðŸ” TOP 15 ERROR TYPES:');
    console.log('----------------------');
    for (const [index, [rule, count]] of sortedErrors.slice(0, 15).entries()) {
      const percentage = ((count / totalErrors) * 100).toFixed(1);
      console.log(`${index + 1}. ${rule}: ${count} (${percentage}%)`);
    }

    console.log('\nðŸŽ¯ HIGH CONFIDENCE AUTOMATION CANDIDATES:');
    console.log('------------------------------------------');
    if (recommendations.highConfidence.candidates.length > 0) {
      for (const candidate of recommendations.highConfidence.candidates) {
        console.log(`âœ… ${candidate.ruleId}: ${candidate.count} errors â†’ ~${candidate.estimatedFixes} fixes (${candidate.method})`);
        console.log(`   ${candidate.description}`);
      }
      console.log(`\nðŸš€ TOTAL HIGH CONFIDENCE FIXES: ${recommendations.highConfidence.totalFixes}`);
    } else {
      console.log('No high confidence automation candidates found.');
    }

    console.log('\nâš ï¸  MEDIUM CONFIDENCE AUTOMATION CANDIDATES:');
    console.log('--------------------------------------------');
    if (recommendations.mediumConfidence.candidates.length > 0) {
      for (const candidate of recommendations.mediumConfidence.candidates) {
        console.log(`ðŸ”¶ ${candidate.ruleId}: ${candidate.count} errors â†’ ~${candidate.estimatedFixes} fixes (${candidate.method})`);
        console.log(`   ${candidate.description}`);
      }
      console.log(`\nâš¡ TOTAL MEDIUM CONFIDENCE FIXES: ${recommendations.mediumConfidence.totalFixes}`);
    } else {
      console.log('No medium confidence automation candidates found.');
    }

    const totalAutomationPotential = recommendations.highConfidence.totalFixes + recommendations.mediumConfidence.totalFixes;
    const automationPercentage = ((totalAutomationPotential / totalErrors) * 100).toFixed(1);

    console.log('\nðŸ“Š AUTOMATION SUMMARY:');
    console.log('----------------------');
    console.log(`Current errors: ${totalErrors}`);
    console.log(`Automation potential: ${totalAutomationPotential} fixes (${automationPercentage}%)`);
    console.log(`Remaining manual work: ${totalErrors - totalAutomationPotential} errors`);

    console.log('\nðŸ› ï¸  RECOMMENDED AUTOMATION METHODS:');
    console.log('-----------------------------------');
    const allMethods = [...recommendations.highConfidence.methods, ...recommendations.mediumConfidence.methods];
    for (const method of new Set(allMethods)) {
      console.log(`â€¢ ${method}`);
    }

    return recommendations;
  }

  /**
   * Run the complete analysis
   */
  async run() {
    const results = this.getESLintResults();
    this.analyzePatterns(results);
    this.identifyAutomationCandidates();
    return this.printResults();
  }
}

// CLI interface
if (require.main === module) {
  const analyzer = new ErrorPatternAnalyzer();
  analyzer.run().then(recommendations => {
    // Save results for other scripts to use
    const output = {
      timestamp: new Date().toISOString(),
      totalErrors: Object.values(analyzer.errorCounts).reduce((sum, count) => sum + count, 0),
      errorCounts: analyzer.errorCounts,
      automationCandidates: analyzer.automationCandidates,
      recommendations
    };

    fs.writeFileSync('error-analysis.json', JSON.stringify(output, null, 2));
    console.log('\nðŸ’¾ Analysis saved to error-analysis.json');

    process.exit(0);
  }).catch(error => {
    console.error('Analysis failed:', error);
    process.exit(1);
  });
}

module.exports = ErrorPatternAnalyzer;

#!/usr/bin/env node

/**
 * Async Function Cleaner
 * Fixes @typescript-eslint/require-await errors by removing unnecessary async keywords
 * 
 * SAFE PATTERNS (auto-fix):
 * - async function with no await â†’ remove async
 * - async arrow function with no await â†’ remove async
 * - async method with no await â†’ remove async
 * 
 * SAFETY MEASURES:
 * - Only removes async if no await expressions found
 * - Preserves function signatures and return types
 * - Maintains code formatting
 */

const fs = require('node:fs');
const path = require('node:path');
const { execSync } = require('node:child_process');

class AsyncFunctionCleaner {
  constructor() {
    this.stats = {
      filesProcessed: 0,
      asyncKeywordsRemoved: 0,
      errors: []
    };
  }

  /**
   * Get current error count
   */
  getCurrentErrorCount() {
    try {
      const output = execSync('node scripts/count-errors.cjs', { encoding: 'utf8' });
      const lines = output.trim().split('\n');
      return Number.parseInt(lines.at(-1)) || 0;
    } catch (error) {
      console.warn('Could not get current error count:', error.message);
      return 0;
    }
  }

  /**
   * Check if a function body contains await expressions
   */
  hasAwaitExpressions(functionBody) {
    // Simple regex to find await expressions
    // This is conservative - if we're not sure, we don't remove async
    const awaitPattern = /\bawait\s+/g;
    return awaitPattern.test(functionBody);
  }

  /**
   * Fix async functions without await in a file
   */
  fixAsyncFunctions(content) {
    let fixed = content;
    let changes = 0;

    // Pattern 1: async function declarations
    const functionPattern = /async\s+(function\s+[a-zA-Z_$][a-zA-Z0-9_$]*\s*\([^)]*\)\s*\{[^}]*\})/g;
    fixed = fixed.replaceAll(functionPattern, (match, functionDef) => {
      if (!this.hasAwaitExpressions(functionDef)) {
        changes++;
        return functionDef; // Remove 'async' keyword
      }
      return match;
    });

    // Pattern 2: async arrow functions
    const arrowPattern = /async\s+(\([^)]*\)\s*=>\s*\{[^}]*\})/g;
    fixed = fixed.replaceAll(arrowPattern, (match, arrowDef) => {
      if (!this.hasAwaitExpressions(arrowDef)) {
        changes++;
        return arrowDef; // Remove 'async' keyword
      }
      return match;
    });

    // Pattern 3: async methods in classes/objects
    const methodPattern = /async\s+([a-zA-Z_$][a-zA-Z0-9_$]*\s*\([^)]*\)\s*\{[^}]*\})/g;
    fixed = fixed.replaceAll(methodPattern, (match, methodDef) => {
      if (!this.hasAwaitExpressions(methodDef)) {
        changes++;
        return methodDef; // Remove 'async' keyword
      }
      return match;
    });

    return { fixed, changes };
  }

  /**
   * Process a single file
   */
  processFile(filePath) {
    try {
      const content = fs.readFileSync(filePath, 'utf8');
      const result = this.fixAsyncFunctions(content);

      if (result.changes > 0) {
        fs.writeFileSync(filePath, result.fixed);
        this.stats.asyncKeywordsRemoved += result.changes;
        console.log(`âœ… Updated: ${filePath} (${result.changes} async keywords removed)`);
        return true;
      } else {
        console.log(`â­ï¸  No changes: ${filePath}`);
        return false;
      }
    } catch (error) {
      this.stats.errors.push({ file: filePath, error: error.message });
      console.error(`âŒ Error processing ${filePath}: ${error.message}`);
      return false;
    }
  }

  /**
   * Find TypeScript files
   */
  findTSFiles(directories = ['app', 'components', 'lib']) {
    const files = [];

    for (const dir of directories) {
      if (fs.existsSync(dir)) {
        const findFiles = (currentDir) => {
          const items = fs.readdirSync(currentDir);
          for (const item of items) {
            const fullPath = path.join(currentDir, item);
            const stat = fs.statSync(fullPath);

            if (stat.isDirectory() && !item.startsWith('.')) {
              findFiles(fullPath);
            } else if (stat.isFile() && (item.endsWith('.ts') || item.endsWith('.tsx'))) {
              files.push(fullPath.replaceAll('\\', '/'));
            }
          }
        };
        findFiles(dir);
      }
    }

    return files;
  }

  /**
   * Run the async function cleaning process
   */
  async run(options = {}) {
    const { 
      maxFiles = null,
      dryRun = false 
    } = options;

    console.log('ðŸš€ Starting Async Function Cleaning');
    console.log('===================================');

    if (dryRun) {
      console.log('ðŸ” DRY RUN MODE - No files will be modified');
    }

    // Get baseline error count
    const initialErrors = this.getCurrentErrorCount();
    console.log(`ðŸ“Š Initial error count: ${initialErrors}`);

    // Find files to process
    const allFiles = this.findTSFiles();
    const filesToProcess = maxFiles ? allFiles.slice(0, maxFiles) : allFiles;

    console.log(`ðŸ“ Found ${allFiles.length} TypeScript files`);
    console.log(`ðŸŽ¯ Processing ${filesToProcess.length} files`);
    console.log('');

    // Process files
    let filesChanged = 0;
    for (const file of filesToProcess) {
      console.log(`Processing: ${file}`);

      if (dryRun) {
        // Dry run - just analyze
        const content = fs.readFileSync(file, 'utf8');
        const result = this.fixAsyncFunctions(content);

        if (result.changes > 0) {
          console.log(`  Would remove ${result.changes} async keyword(s)`);
          filesChanged++;
        }
      } else {
        const changed = this.processFile(file);
        if (changed) filesChanged++;
      }

      this.stats.filesProcessed++;
    }

    // Get final error count
    const finalErrors = dryRun ? initialErrors : this.getCurrentErrorCount();
    const errorReduction = initialErrors - finalErrors;

    // Print summary
    console.log('');
    console.log('ðŸ“ˆ ASYNC FUNCTION CLEANING SUMMARY');
    console.log('==================================');
    console.log(`Files processed: ${this.stats.filesProcessed}`);
    console.log(`Files changed: ${filesChanged}`);
    console.log(`Async keywords removed: ${this.stats.asyncKeywordsRemoved}`);
    console.log(`Errors encountered: ${this.stats.errors.length}`);
    console.log('');
    console.log(`Initial errors: ${initialErrors}`);
    console.log(`Final errors: ${finalErrors}`);
    console.log(`Error reduction: ${errorReduction} (${((errorReduction/initialErrors)*100).toFixed(1)}%)`);

    if (this.stats.errors.length > 0) {
      console.log('');
      console.log('âŒ ERRORS:');
      for (const err of this.stats.errors) {
        console.log(`  ${err.file}: ${err.error}`);
      }
    }

    return {
      filesChanged,
      asyncKeywordsRemoved: this.stats.asyncKeywordsRemoved,
      errorReduction,
      success: this.stats.errors.length === 0
    };
  }
}

// CLI interface
if (require.main === module) {
  const args = process.argv.slice(2);
  const options = {};

  // Parse command line arguments
  if (args.includes('--dry-run')) options.dryRun = true;
  if (args.includes('--max-files')) {
    const maxIndex = args.indexOf('--max-files');
    options.maxFiles = Number.parseInt(args[maxIndex + 1]) || 10;
  }

  const cleaner = new AsyncFunctionCleaner();
  cleaner.run(options).then(result => {
    process.exit(result.success ? 0 : 1);
  }).catch(error => {
    console.error('Fatal error:', error);
    process.exit(1);
  });
}

module.exports = AsyncFunctionCleaner;

#!/usr/bin/env node

/**
 * Automated Nullish Coalescing Converter
 * Safely converts || patterns to ?? for @typescript-eslint/prefer-nullish-coalescing fixes
 *
 * SAFE PATTERNS (auto-convert):
 * - value || defaultValue
 * - data?.field || fallback
 * - array || []
 * - object || {}
 * - string || 'default'
 * - number || 0
 *
 * UNSAFE PATTERNS (manual review needed):
 * - if (condition1 || condition2)
 * - error || !user
 * - boolean || boolean
 */

const fs = require('node:fs');
const path = require('node:path');
const { execSync } = require('node:child_process');

class NullishCoalescingConverter {
  constructor() {
    this.safePatterns = [
      // Simple variable || default patterns
      /(\w+(?:\?\.\w+)*)\s*\|\|\s*(\[|\{|'[^']*'|"[^"]*"|\d+|null|undefined)/g,
      // Property access with fallbacks
      /(\w+(?:\?\.\w+)+)\s*\|\|\s*(\w+|\[|\{|'[^']*'|"[^"]*"|\d+|null|undefined)/g,
      // Function call results with defaults
      /(\w+\([^)]*\)(?:\?\.\w+)*)\s*\|\|\s*(\[|\{|'[^']*'|"[^"]*"|\d+|null|undefined)/g,
      // Array/object access with defaults
      /([\w.]+\[[^\]]+\](?:\?\.\w+)*)\s*\|\|\s*(\[|\{|'[^']*'|"[^"]*"|\d+|null|undefined)/g,
      // Complex property chains
      /([\w.]+(?:\?\.\w+)+)\s*\|\|\s*(\w+)/g,
    ];

    this.unsafePatterns = [
      // Boolean conditions in if statements
      /if\s*\([^)]*\|\|[^)]*\)/gi,
      // While/for conditions
      /(while|for)\s*\([^)]*\|\|[^)]*\)/gi,
      // Error handling patterns
      /(error|err)\s*\|\|\s*!/gi,
      // Boolean variables
      /\b(is|has|can|should|will|was|were)\w*\s*\|\|\s*\b(is|has|can|should|will|was|were)/gi,
      // Return statements with boolean logic
      /return\s+\w+\s*\|\|\s*\w+\s*$/gi,
    ];

    this.stats = {
      filesProcessed: 0,
      conversionsApplied: 0,
      unsafePatternsSaved: 0,
      errors: []
    };
  }

  /**
   * Check if a line contains unsafe patterns that should not be auto-converted
   */
  containsUnsafePattern(line) {
    return this.unsafePatterns.some(pattern => pattern.test(line));
  }

  /**
   * Convert safe || patterns to ?? in a single line
   */
  convertLine(line, lineNumber) {
    if (this.containsUnsafePattern(line)) {
      this.stats.unsafePatternsSaved++;
      return { converted: line, changed: false, reason: 'Contains unsafe boolean logic' };
    }

    let converted = line;
    let changed = false;
    let conversions = 0;

    // Apply safe pattern conversions
    for (const pattern of this.safePatterns) {
      const matches = converted.match(pattern);
      if (matches) {
        converted = converted.replace(pattern, (match, left, right) => {
          conversions++;
          return `${left} ?? ${right}`;
        });
        changed = true;
      }
    }

    if (changed) {
      this.stats.conversionsApplied += conversions;
    }

    return { converted, changed, conversions };
  }

  /**
   * Process a single TypeScript/TSX file
   */
  processFile(filePath) {
    try {
      const content = fs.readFileSync(filePath, 'utf8');
      const lines = content.split('\n');
      let hasChanges = false;
      const results = [];

      for (const [index, line] of lines.entries()) {
        const result = this.convertLine(line, index + 1);
        results.push(result.converted);
        if (result.changed) {
          hasChanges = true;
          console.log(`  Line ${index + 1}: ${result.conversions} conversion(s)`);
        }
      }

      if (hasChanges) {
        fs.writeFileSync(filePath, results.join('\n'));
        console.log(`âœ… Updated: ${filePath}`);
        return true;
      } else {
        console.log(`â­ï¸  No changes: ${filePath}`);
        return false;
      }
    } catch (error) {
      this.stats.errors.push({ file: filePath, error: error.message });
      console.error(`âŒ Error processing ${filePath}: ${error.message}`);
      return false;
    }
  }

  /**
   * Find all TypeScript/TSX files in specified directories
   */
  findTSFiles(directories = ['app', 'components', 'lib']) {
    const files = [];

    for (const dir of directories) {
      if (fs.existsSync(dir)) {
        const findFiles = (currentDir) => {
          const items = fs.readdirSync(currentDir);
          for (const item of items) {
            const fullPath = path.join(currentDir, item);
            const stat = fs.statSync(fullPath);

            if (stat.isDirectory() && !item.startsWith('.') && item !== 'node_modules') {
              findFiles(fullPath);
            } else if (stat.isFile() && (item.endsWith('.ts') || item.endsWith('.tsx'))) {
              files.push(fullPath);
            }
          }
        };
        findFiles(dir);
      }
    }

    return files;
  }

  /**
   * Get current error count for comparison
   */
  getCurrentErrorCount() {
    try {
      const output = execSync('node scripts/count-errors.cjs', { encoding: 'utf8' });
      const lines = output.trim().split('\n');
      return Number.parseInt(lines.at(-1)) || 0;
    } catch (error) {
      console.warn('Could not get current error count:', error.message);
      return 0;
    }
  }

  /**
   * Run the conversion process
   */
  async run(options = {}) {
    const { 
      directories = ['app', 'components', 'lib'],
      dryRun = false,
      maxFiles = null 
    } = options;

    console.info('ðŸš€ Starting Automated Nullish Coalescing Converter');
    console.info('================================================');

    if (dryRun) {
      console.info('ðŸ” DRY RUN MODE - No files will be modified');
    }

    // Get baseline error count
    const initialErrors = this.getCurrentErrorCount();
    console.info(`ðŸ“Š Initial error count: ${initialErrors}`);

    // Find files to process
    const files = this.findTSFiles(directories);
    const filesToProcess = maxFiles ? files.slice(0, maxFiles) : files;

    console.info(`ðŸ“ Found ${files.length} TypeScript files`);
    console.info(`ðŸŽ¯ Processing ${filesToProcess.length} files`);
    console.info('');

    // Process files
    let filesChanged = 0;
    for (const file of filesToProcess) {
      console.log(`Processing: ${file}`);

      if (dryRun) {
        // Dry run - just analyze
        const content = fs.readFileSync(file, 'utf8');
        const lines = content.split('\n');
        let potentialChanges = 0;

        for (const [index, line] of lines.entries()) {
          const result = this.convertLine(line, index + 1);
          if (result.changed) potentialChanges += result.conversions;
        }

        if (potentialChanges > 0) {
          console.info(`  Would make ${potentialChanges} conversion(s)`);
          filesChanged++;
        }
      } else {
        const changed = this.processFile(file);
        if (changed) filesChanged++;
      }

      this.stats.filesProcessed++;
    }

    // Get final error count
    const finalErrors = dryRun ? initialErrors : this.getCurrentErrorCount();
    const errorReduction = initialErrors - finalErrors;

    // Print summary
    console.info('');
    console.info('ðŸ“ˆ CONVERSION SUMMARY');
    console.info('====================');
    console.info(`Files processed: ${this.stats.filesProcessed}`);
    console.info(`Files changed: ${filesChanged}`);
    console.info(`Total conversions: ${this.stats.conversionsApplied}`);
    console.info(`Unsafe patterns preserved: ${this.stats.unsafePatternsSaved}`);
    console.info(`Errors encountered: ${this.stats.errors.length}`);
    console.info('');
    console.info(`Initial errors: ${initialErrors}`);
    console.info(`Final errors: ${finalErrors}`);
