  }

  // Process files and sort by error count
  const files = data
    .filter(f => f.errorCount > 0)
    .map(f => ({
      file: f.filePath.replace(process.cwd(), '').replaceAll('\\', '/'),
      errors: f.errorCount
    }))
    .sort((a, b) => b.errors - a.errors);

  console.log('\nðŸŽ¯ HIGH-IMPACT FILES (20+ errors):');
  console.log('=' .repeat(50));
  const highImpact = files.filter(f => f.errors >= 20);
  if (highImpact.length > 0) {
    for (const f of highImpact) console.log(`${f.errors} errors: ${f.file}`);
  } else {
    console.log('No files with 20+ errors found.');
  }

  console.log('\nðŸ“Š MEDIUM-IMPACT FILES (10-19 errors):');
  console.log('=' .repeat(50));
  const mediumImpact = files.filter(f => f.errors >= 10 && f.errors < 20);
  if (mediumImpact.length > 0) {
    for (const f of mediumImpact) console.log(`${f.errors} errors: ${f.file}`);
  } else {
    console.log('No files with 10-19 errors found.');
  }

  console.log('\nðŸ“ˆ LOWER-IMPACT FILES (5-9 errors):');
  console.log('=' .repeat(50));
  const lowerImpact = files.filter(f => f.errors >= 5 && f.errors < 10);
  if (lowerImpact.length > 0) {
    for (const f of lowerImpact) console.log(`${f.errors} errors: ${f.file}`);
  } else {
    console.log('No files with 5-9 errors found.');
  }

  console.log('\nðŸ“‹ SUMMARY:');
  console.log('=' .repeat(50));
  console.log(`Total files with errors: ${files.length}`);
  console.log(`High-impact files (20+): ${highImpact.length}`);
  console.log(`Medium-impact files (10-19): ${mediumImpact.length}`);
  console.log(`Lower-impact files (5-9): ${lowerImpact.length}`);
  console.log(`Files with <5 errors: ${files.filter(f => f.errors < 5).length}`);

} catch (error) { // Catch any other unexpected errors during processing
  console.error('Error processing ESLint data:', error.message);
  process.exit(1);
}

#!/usr/bin/env node

/**
 * Revert Boolean Fixer Damage
 * Fixes the malformed expressions created by the targeted-boolean-expression-fixer.cjs
 */

const fs = require('node:fs');
const path = require('node:path');
const { execSync } = require('node:child_process');

class BooleanFixerReverter {
  constructor() {
    this.stats = {
      filesProcessed: 0,
      fixesApplied: 0,
      errors: []
    };
  }

  /**
   * Get current error count
   */
  getCurrentErrorCount() {
    try {
      const output = execSync('node scripts/count-errors.cjs', { encoding: 'utf8' });
      const lines = output.trim().split('\n');
      return Number.parseInt(lines.at(-1)) || 0;
    } catch (error) {
      console.warn('Could not get current error count:', error.message);
      return 0;
    }
  }

  /**
   * Fix malformed boolean expressions in a line
   */
  fixMalformedExpressions(line) {
    let fixed = line;
    let changes = 0;

    // Pattern 1: Fix instanceof expressions
    // "error instanceof Error != null" â†’ "error instanceof Error"
    const instanceofPattern = /(\w+\s+instanceof\s+\w+)\s*!=\s*null/g;
    fixed = fixed.replaceAll(instanceofPattern, (match, instanceofExpr) => {
      changes++;
      return instanceofExpr;
    });

    // Pattern 2: Fix boolean ternary expressions
    // "booleanVar != null ? a : b" â†’ "booleanVar ? a : b" (for known boolean patterns)
    const booleanTernaryPattern = /(is\w+|has\w+|can\w+|should\w+|success|loading|error|active|enabled|disabled|visible|hidden)\s*!=\s*null\s*\?\s*([^:]+)\s*:\s*([^;,}]+)/g;
    fixed = fixed.replaceAll(booleanTernaryPattern, (match, booleanVar, trueExpr, falseExpr) => {
      changes++;
      return `${booleanVar} ? ${trueExpr} : ${falseExpr}`;
    });

    // Pattern 3: Fix boolean if conditions
    // "if (booleanVar != null)" â†’ "if (booleanVar)"
    const booleanIfPattern = /if\s*\(\s*(is\w+|has\w+|can\w+|should\w+|success|loading|error|active|enabled|disabled|visible|hidden)\s*!=\s*null\s*\)/g;
    fixed = fixed.replaceAll(booleanIfPattern, (match, booleanVar) => {
      changes++;
      return `if (${booleanVar})`;
    });

    // Pattern 4: Fix boolean && expressions
    // "booleanVar != null &&" â†’ "booleanVar &&"
    const booleanAndPattern = /(is\w+|has\w+|can\w+|should\w+|success|loading|error|active|enabled|disabled|visible|hidden)\s*!=\s*null\s*&&/g;
    fixed = fixed.replaceAll(booleanAndPattern, (match, booleanVar) => {
      changes++;
      return `${booleanVar} &&`;
    });

    // Pattern 5: Fix specific known patterns that should be nullable checks
    // Keep these as != null for actual nullable values like dates, objects, etc.
    // But fix the malformed ones where the property access was broken

    return { fixed, changes };
  }

  /**
   * Process a single file
   */
  processFile(filePath) {
    try {
      const content = fs.readFileSync(filePath, 'utf8');
      const lines = content.split('\n');
      let hasChanges = false;
      const results = [];

      for (const [index, line] of lines.entries()) {
        const result = this.fixMalformedExpressions(line);
        results.push(result.fixed);
        if (result.changes > 0) {
          hasChanges = true;
          this.stats.fixesApplied += result.changes;
          console.log(`  Line ${index + 1}: ${result.changes} fix(es)`);
        }
      }

      if (hasChanges) {
        fs.writeFileSync(filePath, results.join('\n'));
        console.log(`âœ… Updated: ${filePath}`);
        return true;
      } else {
        console.log(`â­ï¸  No changes: ${filePath}`);
        return false;
      }
    } catch (error) {
      this.stats.errors.push({ file: filePath, error: error.message });
      console.error(`âŒ Error processing ${filePath}: ${error.message}`);
      return false;
    }
  }

  /**
   * Find files with malformed patterns
   */
  findFilesWithMalformedPatterns() {
    try {
      // Use PowerShell to find files with the malformed patterns
      const output = execSync(String.raw`Get-ChildItem -Path . -Include "*.ts", "*.tsx" -Recurse | Select-String "!= null \?" | Select-Object -ExpandProperty Filename | Sort-Object | Get-Unique`, {
        encoding: 'utf8',
        shell: 'powershell'
      });

      return output.trim().split('\n').filter(file => file.trim()).map(file => file.trim());
    } catch {
      console.warn('Could not find files with malformed patterns, using fallback');
      // Fallback to known affected files
      return [
        'app/admin/auto-scraping/page.tsx',
        'app/admin/events/page.tsx', 
        'app/admin/food-trucks/[id]/page.tsx',
        'app/admin/pipeline/page.tsx',
        'app/admin/test-pipeline/page.tsx',
        'app/api/admin/automated-cleanup/route.ts',
        'app/api/admin/data-cleanup/route.ts',
        'app/api/admin/data-quality/route.ts'
      ];
    }
  }

  /**
   * Run the reversion process
   */
  async run() {
    console.log('ðŸ”„ Starting Boolean Fixer Damage Reversion');
    console.log('==========================================');

    // Get baseline error count
    const initialErrors = this.getCurrentErrorCount();
    console.log(`ðŸ“Š Initial error count: ${initialErrors}`);

    // Find files to process
    const filesToProcess = this.findFilesWithMalformedPatterns();

    console.log(`ðŸ“ Found ${filesToProcess.length} files to check`);
    console.log('');

    // Process files
    let filesChanged = 0;
    for (const file of filesToProcess) {
      if (fs.existsSync(file)) {
        console.log(`Processing: ${file}`);
        const changed = this.processFile(file);
        if (changed) filesChanged++;
        this.stats.filesProcessed++;
      }
    }

    // Get final error count
    const finalErrors = this.getCurrentErrorCount();
    const errorReduction = initialErrors - finalErrors;

    // Print summary
    console.log('');
    console.log('ðŸ“ˆ REVERSION SUMMARY');
    console.log('===================');
    console.log(`Files processed: ${this.stats.filesProcessed}`);
    console.log(`Files changed: ${filesChanged}`);
    console.log(`Fixes applied: ${this.stats.fixesApplied}`);
    console.log(`Errors encountered: ${this.stats.errors.length}`);
    console.log('');
    console.log(`Initial errors: ${initialErrors}`);
    console.log(`Final errors: ${finalErrors}`);
    console.log(`Error reduction: ${errorReduction} (${((errorReduction/initialErrors)*100).toFixed(1)}%)`);

    if (this.stats.errors.length > 0) {
      console.log('');
      console.log('âŒ ERRORS:');
      for (const err of this.stats.errors) {
        console.log(`  ${err.file}: ${err.error}`);
      }
    }

    return {
      filesChanged,
      fixesApplied: this.stats.fixesApplied,
      errorReduction,
      success: this.stats.errors.length === 0
    };
  }
}

// CLI interface
if (require.main === module) {
  const reverter = new BooleanFixerReverter();
  reverter.run().then(result => {
    process.exit(result.success ? 0 : 1);
  }).catch(error => {
    console.error('Fatal error:', error);
    process.exit(1);
  });
}

module.exports = BooleanFixerReverter;

#!/usr/bin/env node

/**
 * OAuth Flow Testing Script
 *
 * This script provides automated testing for the Google OAuth flow
 * and generates test reports for verification.
 *
 * Usage: node scripts/test-oauth-flow.js [--env=development|production]
 */

import https from 'node:https';
import http from 'node:http';
import fs from 'node:fs';
import path from 'node:path';
import { fileURLToPath } from 'node:url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Configuration
const SUPABASE_PROJECT_ID = 'zkwliyjjkdnigizidlln';
const DEV_URL = 'http://localhost:3000';
const PROD_URL = 'https://food-truck-finder-poc-git-feat-s-20ec1c-codedeficients-projects.vercel.app';

class OAuthTester {
  constructor(environment = 'development') {
    this.environment = environment;
    this.baseUrl = environment === 'production' ? PROD_URL : DEV_URL;
    this.results = {
      tests: [],
      passed: 0,
      failed: 0,
      warnings: 0
    };
    this.loadEnvironment();
  }

  loadEnvironment() {
    const envPath = path.join(process.cwd(), '.env.local');
    if (fs.existsSync(envPath)) {
      const envContent = fs.readFileSync(envPath, 'utf8');
      for (const line of envContent.split('\n')) {
        const [key, value] = line.split('=');
        if (key && value) {
          process.env[key] = value.replaceAll('"', '');
        }
      }
    }
  }

  log(message, type = 'info') {
    const colors = {
      info: '\u001B[36m',
      success: '\u001B[32m',
      warning: '\u001B[33m',
      error: '\u001B[31m',
      reset: '\u001B[0m'
    };

    const timestamp = new Date().toISOString();
    console.log(`${colors[type]}[${timestamp}] ${message}${colors.reset}`);
  }

  async runTest(name, testFn) {
    this.log(`Running test: ${name}`, 'info');

    try {
      const result = await testFn();
      this.results.tests.push({
        name,
        status: 'passed',
        result,
        timestamp: new Date().toISOString()
      });
      this.results.passed++;
      this.log(`âœ… ${name} - PASSED`, 'success');
      return result;
    } catch (error) {
      this.results.tests.push({
        name,
        status: 'failed',
        error: error.message,
        timestamp: new Date().toISOString()
      });
      this.results.failed++;
      this.log(`âŒ ${name} - FAILED: ${error.message}`, 'error');
      throw error;
    }
  }

  async testLoginPageAccessibility() {
    return this.runTest('Login Page Accessibility', async () => {
      const response = await this.makeRequest(`${this.baseUrl}/login`);

      if (response.statusCode !== 200) {
        throw new Error(`Login page returned status ${response.statusCode}`);
      }

      if (!response.body.includes('Google')) {
        throw new Error('Google login button not found on login page');
      }

      return {
        statusCode: response.statusCode,
        hasGoogleButton: response.body.includes('Google'),
        contentLength: response.body.length
      };
    });
  }

  async testAuthCallbackRoute() {
    return this.runTest('Auth Callback Route', async () => {
      // Test callback route without parameters (should handle gracefully)
      const response = await this.makeRequest(`${this.baseUrl}/auth/callback`);

      // Should redirect to login or handle missing code gracefully
      if (response.statusCode !== 302 && response.statusCode !== 200) {
        throw new Error(`Callback route returned unexpected status ${response.statusCode}`);
      }

      return {
        statusCode: response.statusCode,
        location: response.headers.location || 'No redirect',
        handlesGracefully: true
      };
    });
  }

  async testSupabaseConnection() {
    return this.runTest('Supabase Connection', async () => {
      const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
      if (!supabaseUrl) {
        throw new Error('NEXT_PUBLIC_SUPABASE_URL not configured');
      }

      const response = await this.makeRequest(`${supabaseUrl}/rest/v1/`);

      if (response.statusCode !== 200 && response.statusCode !== 401) {
        throw new Error(`Supabase connection failed with status ${response.statusCode}`);
      }

      return {
        url: supabaseUrl,
        statusCode: response.statusCode,
        connected: true
      };
    });
  }

  async testSupabaseAuthSettings() {
    return this.runTest('Supabase Auth Settings', async () => {
      const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
      const settingsUrl = `${supabaseUrl}/auth/v1/settings`;

      try {
        const response = await this.makeRequest(settingsUrl);
        const settings = JSON.parse(response.body);

        return {
          googleEnabled: settings.external?.google || false,
          signupDisabled: settings.disable_signup || false,
          autoconfirm: settings.autoconfirm || false,
          settingsAccessible: true
        };
      } catch {
        // Settings endpoint might require auth, which is normal
        this.log('âš ï¸  Auth settings endpoint requires authentication (normal)', 'warning');
        this.results.warnings++;

        return {
          settingsAccessible: false,
          reason: 'Authentication required'
        };
      }
    });
  }

  async testEnvironmentConfiguration() {
    return this.runTest('Environment Configuration', async () => {
      const requiredVars = [
        'NEXT_PUBLIC_SUPABASE_URL',
        'NEXT_PUBLIC_SUPABASE_ANON_KEY',
        'SUPABASE_SERVICE_ROLE_KEY'
      ];

      const missing = requiredVars.filter(varName => !process.env[varName]);

      if (missing.length > 0) {
        throw new Error(`Missing environment variables: ${missing.join(', ')}`);
      }

      return {
        allVariablesPresent: true,
        supabaseProjectId: SUPABASE_PROJECT_ID,
        environment: this.environment
      };
    });
  }

  async testOAuthRedirectUrls() {
    return this.runTest('OAuth Redirect URLs', async () => {
      const expectedUrls = [
        `${this.baseUrl}/auth/callback`,
        `${PROD_URL}/auth/callback`,
        'http://localhost:3000/auth/callback'
      ];

      // This test validates the expected URLs are properly formatted
      const validUrls = expectedUrls.filter(url => {
        try {
          new URL(url);
          return true;
        } catch {
          return false;
        }
      });

      if (validUrls.length !== expectedUrls.length) {
        throw new Error('Some redirect URLs are malformed');
      }

      return {
        expectedUrls,
        validUrls: validUrls.length,
        currentEnvironment: this.baseUrl
      };
    });
  }

  async makeRequest(url, options = {}) {
    return new Promise((resolve, reject) => {
      const urlObj = new URL(url);
      const isHttps = urlObj.protocol === 'https:';
      const client = isHttps ? https : http;

      const requestOptions = {
        hostname: urlObj.hostname,
        port: urlObj.port || (isHttps ? 443 : 80),
        path: urlObj.pathname + urlObj.search,
        method: options.method || 'GET',
        headers: {
          'User-Agent': 'OAuth-Tester/1.0',
          ...options.headers
        }
      };

      const req = client.request(requestOptions, (res) => {
        let body = '';
        res.on('data', chunk => body += chunk);
        res.on('end', () => {
          resolve({
            statusCode: res.statusCode,
            headers: res.headers,
            body
          });
        });
      });

      req.on('error', reject);
      req.setTimeout(10_000, () => {
        req.destroy();
        reject(new Error('Request timeout'));
      });

      if (options.body) {
        req.write(options.body);
      }

      req.end();
    });
  }

  generateReport() {
    const report = {
      summary: {
        environment: this.environment,
        baseUrl: this.baseUrl,
        timestamp: new Date().toISOString(),
        totalTests: this.results.tests.length,
        passed: this.results.passed,
        failed: this.results.failed,
        warnings: this.results.warnings,
        successRate: `${Math.round((this.results.passed / this.results.tests.length) * 100)}%`
      },
      tests: this.results.tests,
      recommendations: this.generateRecommendations()
    };

    // Save report to file
    const reportPath = path.join(process.cwd(), 'reports', `oauth-test-${this.environment}-${Date.now()}.json`);
    const reportsDir = path.dirname(reportPath);

    if (!fs.existsSync(reportsDir)) {
      fs.mkdirSync(reportsDir, { recursive: true });
    }

    fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));

    return { report, reportPath };
  }

  generateRecommendations() {
    const recommendations = [];

    if (this.results.failed > 0) {
      recommendations.push('âŒ Fix failed tests before proceeding with OAuth setup');
    }

    if (this.results.warnings > 0) {
      recommendations.push('âš ï¸  Review warnings - they may indicate configuration issues');
    }

    if (this.results.passed === this.results.tests.length) {
      recommendations.push('âœ… All tests passed! Ready for manual OAuth configuration', 'ðŸ“‹ Next: Configure Google Cloud Console and Supabase OAuth settings');
    }

    recommendations.push('ðŸ“– See docs/GOOGLE_OAUTH_SETUP_GUIDE.md for detailed setup instructions');

    return recommendations;
  }

  async run() {
    this.log(`ðŸ§ª Starting OAuth Flow Tests (${this.environment})`, 'info');
    this.log(`Base URL: ${this.baseUrl}`, 'info');

    try {
      await this.testEnvironmentConfiguration();
      await this.testSupabaseConnection();
      await this.testSupabaseAuthSettings();
      await this.testLoginPageAccessibility();
      await this.testAuthCallbackRoute();
      await this.testOAuthRedirectUrls();

      const { report, reportPath } = this.generateReport();

      this.log('\nðŸ“Š TEST SUMMARY:', 'info');
      this.log(`Total Tests: ${report.summary.totalTests}`, 'info');
      this.log(`Passed: ${report.summary.passed}`, 'success');
      this.log(`Failed: ${report.summary.failed}`, 'error');
      this.log(`Warnings: ${report.summary.warnings}`, 'warning');
      this.log(`Success Rate: ${report.summary.successRate}`, 'info');

      this.log('\nðŸ’¡ RECOMMENDATIONS:', 'info');
      for (const rec of report.recommendations) this.log(rec, 'info');

      this.log(`\nðŸ“„ Report saved: ${reportPath}`, 'info');

      return report;

    } catch (error) {
      this.log(`Test suite failed: ${error.message}`, 'error');
      throw error;
    }
  }
}

// CLI interface
const args = process.argv.slice(2);
const envArg = args.find(arg => arg.startsWith('--env='));
const environment = envArg ? envArg.split('=')[1] : 'development';

if (!['development', 'production'].includes(environment)) {
  console.error('Invalid environment. Use --env=development or --env=production');
  process.exit(1);
}

const tester = new OAuthTester(environment);
tester.run().catch(error => {
  console.error('Testing failed:', error);
  process.exit(1);
});

export default OAuthTester;

