import { createClient, type PostgrestSingleResponse, type PostgrestResponse } from "@supabase/supabase-js"

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL!
const supabaseKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!

export const supabase = createClient(supabaseUrl, supabaseKey)

// Server-side client with service role key for admin operations
export const supabaseAdmin = createClient(supabaseUrl, process.env.SUPABASE_SERVICE_ROLE_KEY!)

// Database types
export interface FoodTruck {
  id: string
  name: string
  description?: string
  current_location: {
    lat: number
    lng: number
    address: string
    timestamp: string
  }
  scheduled_locations: Array<{
    lat: number
    lng: number
    start_time: string
    end_time: string
  }>
  operating_hours: OperatingHours
  menu: Array<{
    category: string
    items: Array<{
      name: string
      description?: string
      price: number
      dietary_tags: string[]
    }>
  }>
  contact_info: {
    phone?: string
    email?: string
    website?: string
  }
  social_media: {
    instagram?: string
    facebook?: string
    twitter?: string
  }
  data_quality_score: number
  verification_status: "pending" | "verified" | "flagged"
  source_urls: string[]
  cuisine_type?: string[]; // Added
  price_range?: string; // Added
  created_at: string
  updated_at: string
  last_scraped_at?: string
}

export interface ScrapingJob {
  id: string
  job_type: string
  target_url?: string
  target_handle?: string
  platform?: string
  status: "pending" | "running" | "completed" | "failed"
  priority: number
  scheduled_at: string
  started_at?: string
  completed_at?: string;
  data_collected?: Record<string, unknown>;
  errors?: string[]
  retry_count: number
  max_retries: number
  created_at: string
}

export interface DataProcessingQueue {
  id: string
  truck_id: string
  processing_type: string
  raw_data: Record<string, unknown>
  processed_data?: Record<string, unknown>
  gemini_tokens_used: number
  status: "pending" | "processing" | "completed" | "failed"
  priority: number
  created_at: string
  processed_at?: string
}

interface PipelineRequestBody {
  target_url: string;
  job_type?: string;
  priority?: number;
}

// Food truck operations
export const FoodTruckService = {
  async getAllTrucks(limit = 50, offset = 0): Promise<{ trucks: FoodTruck[] | null; total: number | null }> {
    // Construct the query
    const query = supabase
      .from("food_trucks")
      .select("*", { count: "exact" })
      // Filter by address containing ", SC" or " South Carolina" (case-insensitive)
      // Assumes 'current_location' is a JSONB field with an 'address' key.
      .or("current_location->>address.ilike.%, SC%,current_location->>address.ilike.% South Carolina%")
      .order("updated_at", { ascending: false })
      .range(offset, offset + limit - 1);

    const { data, error, count } = await query as PostgrestResponse<FoodTruck>;

    if (error) {
      throw error;
    }
    return { trucks: data, total: count };
  },

  async getTruckById(id: string): Promise<FoodTruck | null> {
    const { data, error }: PostgrestSingleResponse<FoodTruck> = await supabase.from("food_trucks").select("*").eq("id", id).single()

    if (error) throw error
    return data
  },

  async getTrucksByLocation(lat: number, lng: number, radius = 5): Promise<FoodTruck[] | null> {
    // Using PostGIS functions for geospatial queries
    const { data, error }: PostgrestResponse<FoodTruck> = await supabase.rpc("get_trucks_near_location", {
      user_lat: lat,
      user_lng: lng,
      radius_km: radius,
    })

    if (error) throw error
    return data
  },

  async createTruck(truckData: Partial<FoodTruck>): Promise<FoodTruck | null> {
    const { data, error }: PostgrestSingleResponse<FoodTruck> = await supabaseAdmin.from("food_trucks").insert([truckData]).select().single()

    if (error) throw error
    return data
  },

  async updateTruck(id: string, updates: Partial<FoodTruck>): Promise<FoodTruck | null> {
    const { data, error }: PostgrestSingleResponse<FoodTruck> = await supabaseAdmin.from("food_trucks").update(updates).eq("id", id).select().single()

    if (error) throw error
    return data
  },

  async updateDataQuality(id: string, score: number, _issues: string[]): Promise<FoodTruck | null> {
    const { data, error }: PostgrestSingleResponse<FoodTruck> = await supabaseAdmin
      .from("food_trucks")
      .update({
        data_quality_score: score,
        updated_at: new Date().toISOString(),
      })
      .eq("id", id)
      .select()
      .single()

    if (error) throw error
    return data
  },
};

// --------- API ROUTE HANDLERS ---------

import { type NextRequest, NextResponse } from "next/server";
import { ScraperEngine, WebsiteScrapeData } from "@/lib/ScraperEngine"; // Using path alias
import { gemini } from "@/lib/gemini"; // Using path alias
import { EnhancedFoodTruckData, OperatingHours } from "@/lib/types"; // Import the new type
// FoodTruckService, ScrapingJobService, etc. are already defined in this file.

export async function POST(request: NextRequest) {
    try {
        const body = await request.json() as PipelineRequestBody;
        const { target_url, job_type = "website_scrape", priority = 1 } = body;

        if (!target_url) {
            return NextResponse.json({ error: "target_url is required" }, { status: 400 });
        }

        // Basic URL validation
        try {
            new URL(target_url);
        } catch {
            return NextResponse.json({ error: "Invalid target_url format" }, { status: 400 });
        }

        // 1. Create a new scraping job
        const job: ScrapingJob | null = await ScrapingJobService.createJob({
            target_url,
            job_type,
            status: "running", // Simplified: set to running immediately
            priority,
            scheduled_at: new Date().toISOString(),
        });

        if (!job) {
            return NextResponse.json({ error: "Failed to create scraping job" }, { status: 500 });
        }

        try {
            // 2. Initiate scraping
            const scraper = new ScraperEngine();
            // Using a generic selector to get the main content of the page for Gemini processing
            const scrapeResult = await scraper.scrapeWebsite(target_url, { page_content: "body" });

            if (!scrapeResult.success || !scrapeResult.data) {
                await ScrapingJobService.updateJobStatus(job.id, "failed", { errors: [scrapeResult.error || "Scraping failed"] });
                return NextResponse.json({ error: "Scraping failed", job_id: job.id, details: scrapeResult.error }, { status: 500 });
            }

            // 3. Process scraped content with Gemini
            // Pass the scraped content (assuming it's somewhat structured or just raw text/HTML)
            // The current `enhanceFoodTruckData` expects a JSON object as `rawData`.
            // The scraper with `page_content: "body"` will return `data: { page_content: "<html>..." }`. This fits.
            // Ensure scrapeResult.data is WebsiteScrapeData before passing to Gemini
            const scrapedWebsiteData: WebsiteScrapeData = scrapeResult.data as WebsiteScrapeData; // Type assertion
            const geminiResponse = await gemini.enhanceFoodTruckData({
              scraped_data: scrapedWebsiteData, // Pass the output of scrapeWebsite
              source_url: target_url
            });

            if (!geminiResponse.success || !geminiResponse.data) {
                await ScrapingJobService.updateJobStatus(job.id, "failed", { errors: [geminiResponse.error || "Gemini processing failed"] });
                return NextResponse.json({ error: "Gemini processing failed", job_id: job.id, details: geminiResponse.error }, { status: 500 });
            }
            const processedData: EnhancedFoodTruckData = geminiResponse.data;

            // 4. Save processed data to FoodTrucks table
            // Map EnhancedFoodTruckData to Partial<FoodTruck> with defensive defaults
            const currentLocationData = {
                address: "Address not specified", // EnhancedFoodTruckData does not contain current_location
                lat: 0, // Default to 0.0 as location is not in EnhancedFoodTruckData
                lng: 0, // Default to 0.0 as location is not in EnhancedFoodTruckData
                timestamp: new Date().toISOString(),
            };

            const contactInfoData = {
                phone: processedData.cleaned_contact?.phone || undefined,
                email: processedData.cleaned_contact?.email || undefined,
                website: processedData.cleaned_contact?.website || undefined,
            };

            const socialMediaData = {
              instagram: undefined, // EnhancedFoodTruckData does not contain social_media
              facebook: undefined, // EnhancedFoodTruckData does not contain social_media
              twitter: undefined, // EnhancedFoodTruckData does not contain social_media
            };
            const truckPayload: Partial<FoodTruck> = {
                name: processedData.name || "Unknown Truck Name",
                description: processedData.description || "No description available.",
                current_location: currentLocationData,
                scheduled_locations: [], // Not in EnhancedFoodTruckData
                operating_hours: { // Ensure all days are present for OperatingHours type
                    monday: processedData.standardized_hours?.monday || { closed: true },
                    tuesday: processedData.standardized_hours?.tuesday || { closed: true },
                    wednesday: processedData.standardized_hours?.wednesday || { closed: true },
                    thursday: processedData.standardized_hours?.thursday || { closed: true },
                    friday: processedData.standardized_hours?.friday || { closed: true },
                    saturday: processedData.standardized_hours?.saturday || { closed: true },
                    sunday: processedData.standardized_hours?.sunday || { closed: true },
                },
                menu: processedData.enhanced_menu?.categories.map(category => ({ // Map to FoodTruck menu structure
                    category: category.name,
                    items: category.items.map(item => ({
                        name: item.name,
                        description: item.description || "", // Ensure description is always string
                        price: typeof item.price === 'number' ? item.price : 0, // Ensure price is number
                        dietary_tags: item.dietary_tags || []
                    }))
                })) || [],
                contact_info: contactInfoData,
                social_media: socialMediaData,

                cuisine_type: processedData.cuisine_type || [],
                price_range: processedData.price_range || undefined,
                // payment_methods: Array.isArray(processedData.payment_methods) ? processedData.payment_methods : [],
                // images: Array.isArray(processedData.images) ? processedData.images : [],
                // dietary_tags: Array.isArray(processedData.dietary_tags) ? processedData.dietary_tags : [],

                source_urls: [target_url],
                data_quality_score: processedData.confidence_score || 0.5, // Use confidence_score from EnhancedFoodTruckData
                verification_status: "pending",
                last_scraped_at: new Date().toISOString(),
            };

            const savedTruck: FoodTruck | null = await FoodTruckService.createTruck(truckPayload);

            if (!savedTruck) {
                await ScrapingJobService.updateJobStatus(job.id, "failed", { errors: ["Failed to save food truck data."] });
                return NextResponse.json({ error: "Failed to save food truck data", job_id: job.id }, { status: 500 });
            }

            // 5. Update job status to "completed"
            await ScrapingJobService.updateJobStatus(job.id, "completed", {
              data_collected: {
                truck_id: savedTruck.id,
                truck_name: savedTruck.name
              }
            });

            return NextResponse.json({
              message: "Pipeline triggered and processed successfully",
              job_id: job.id,
              truck_id: savedTruck.id
            });

        } catch (pipelineError: unknown) {
            console.error(`Pipeline error for job ${job.id} and URL ${target_url}:`, pipelineError);
            const errorMessage = pipelineError instanceof Error ? pipelineError.message : "Unknown pipeline error";
            await ScrapingJobService.updateJobStatus(job.id, "failed", { errors: [errorMessage] });
            return NextResponse.json({ error: "Pipeline processing error", job_id: job.id, details: errorMessage }, { status: 500 });
        }

    } catch (error: unknown) {
        console.error("Error in pipeline trigger endpoint:", error);
        const errorMessage = error instanceof Error ? error.message : "An unexpected error occurred.";
        return NextResponse.json({ error: "Failed to trigger pipeline", details: errorMessage }, { status: 500 });
    }
}

// Scraping job operations
export const ScrapingJobService = {
  async createJob(jobData: Partial<ScrapingJob>): Promise<ScrapingJob | null> {
    const { data, error }: PostgrestSingleResponse<ScrapingJob> = await supabaseAdmin
      .from("scraping_jobs")
      .insert([
        {
          ...jobData,
          status: "pending",
          retry_count: 0,
          max_retries: 3,
          created_at: new Date().toISOString(),
        },
      ])
      .select()
      .single()

    if (error) throw error
    return data
  },

  async getJobsByStatus(status: string): Promise<ScrapingJob[] | null> {
    const { data, error }: PostgrestResponse<ScrapingJob> = await supabase
      .from("scraping_jobs")
      .select("*")
      .eq("status", status)
      .order("priority", { ascending: false })
      .order("scheduled_at", { ascending: true })

    if (error) throw error
    return data
  },

  async updateJobStatus(id: string, status: string, updates: Partial<ScrapingJob> = {}): Promise<ScrapingJob | null> {
    const { data, error }: PostgrestSingleResponse<ScrapingJob> = await supabaseAdmin
      .from("scraping_jobs")
      .update({
        status,
        ...updates,
        ...(status === "running" && { started_at: new Date().toISOString() }),
        ...(status === "completed" && { completed_at: new Date().toISOString() }),
      })
      .eq("id", id)
      .select()
      .single()

    if (error) throw error
    return data
  },
  async incrementRetryCount(id: string): Promise<ScrapingJob | null> {
    // Get current retry count first
    const { data: currentJob, error: fetchError }: PostgrestSingleResponse<{ retry_count: number }> = await supabaseAdmin
      .from("scraping_jobs")
      .select("retry_count")
      .eq("id", id)
      .single()

    if (fetchError) throw fetchError

    const { data, error }: PostgrestSingleResponse<ScrapingJob> = await supabaseAdmin
      .from("scraping_jobs")
      .update({
        retry_count: (currentJob?.retry_count || 0) + 1,
      })
      .eq("id", id)
      .select()
      .single()

    if (error) throw error
    return data
  },
};

// Data processing queue operations
export const DataProcessingService = {
  async addToQueue(queueData: Partial<DataProcessingQueue>): Promise<DataProcessingQueue | null> {
    const { data, error }: PostgrestSingleResponse<DataProcessingQueue> = await supabaseAdmin
      .from("data_processing_queue")
      .insert([
        {
          ...queueData,
          status: "pending",
          gemini_tokens_used: 0,
          created_at: new Date().toISOString(),
        },
      ])
      .select()
      .single()

    if (error) throw error
    return data
  },

  async getNextQueueItem(): Promise<DataProcessingQueue | null> {
    const { data, error }: PostgrestSingleResponse<DataProcessingQueue> = await supabaseAdmin
      .from("data_processing_queue")
      .select("*")
      .eq("status", "pending")
      .order("priority", { ascending: false })
      .order("created_at", { ascending: true })
      .limit(1)
      .single()

    if (error && error.code !== "PGRST116") throw error
    return data
  },

  async updateQueueItem(id: string, updates: Partial<DataProcessingQueue>): Promise<DataProcessingQueue | null> {
    const { data, error }: PostgrestSingleResponse<DataProcessingQueue> = await supabaseAdmin
      .from("data_processing_queue")
      .update({
        ...updates,
        ...(updates.status === "completed" && { processed_at: new Date().toISOString() }),
      })
      .eq("id", id)
      .select()
      .single()

    if (error) throw error
    return data
  },
};

// API usage tracking
export const APIUsageService = {
  async trackUsage(serviceName: string, requests: number, tokens: number): Promise<{ id: string; service_name: string; usage_date: string; requests_count: number; tokens_used: number } | null> {
    const today = new Date().toISOString().split("T")[0]

    // Try to get existing record first
    const { data: existing, error: fetchError }: PostgrestSingleResponse<{ requests_count: number; tokens_used: number }> = await supabaseAdmin
      .from("api_usage")
      .select("requests_count, tokens_used")
      .eq("service_name", serviceName)
      .eq("usage_date", today)
      .single()

    if (fetchError && fetchError.code !== "PGRST116") throw fetchError

    const { data, error }: PostgrestSingleResponse<{ id: string; service_name: string; usage_date: string; requests_count: number; tokens_used: number }> = await supabaseAdmin
      .from("api_usage")
      .upsert(
        {
          service_name: serviceName,
          usage_date: today,
          requests_count: (existing?.requests_count || 0) + requests,
          tokens_used: (existing?.tokens_used || 0) + tokens,
        },
        {
          onConflict: "service_name,usage_date",
        },
      )
      .select()
      .single()

    if (error) throw error
    return data
  },

  async getTodayUsage(serviceName: string): Promise<{ id: string; service_name: string; usage_date: string; requests_count: number; tokens_used: number } | null> {
    const today = new Date().toISOString().split("T")[0]

    const { data, error }: PostgrestSingleResponse<{ id: string; service_name: string; usage_date: string; requests_count: number; tokens_used: number }> = await supabase
      .from("api_usage")
      .select("*")
      .eq("service_name", serviceName)
      .eq("usage_date", today)
      .single()

    if (error && error.code !== "PGRST116") throw error
    return data
  },
};

import { type NextRequest, NextResponse } from "next/server"

// Mock scheduler instance and tasks
interface SchedulerTask {
  id: string;
  name: string;
  enabled: boolean;
  intervalMinutes: number;
  lastRun: string;
  lastSuccess: string;
  successCount: number;
  errorCount: number;
  nextRun: string | null;
  lastError?: string;
}

interface PostRequestBody {
  action: "start" | "stop" | "execute";
  taskId?: string;
}

interface PutRequestBody {
  taskId: string;
  config: Partial<SchedulerTask>;
}

let schedulerInstance: { started: string } | undefined;
const schedulerTasks: SchedulerTask[] = [
  {
    id: "instagram_scrape",
    name: "Instagram Data Scraping",
    enabled: true,
    intervalMinutes: 120,
    lastRun: new Date(Date.now() - 1_800_000).toISOString(),
    lastSuccess: new Date(Date.now() - 1_800_000).toISOString(),
    successCount: 45,
    errorCount: 3,
    nextRun: new Date(Date.now() + 5_400_000).toISOString(),
  },
  {
    id: "website_crawl",
    name: "Website Crawling",
    enabled: true,
    intervalMinutes: 360,
    lastRun: new Date(Date.now() - 7_200_000).toISOString(),
    lastSuccess: new Date(Date.now() - 7_200_000).toISOString(),
    successCount: 23,
    errorCount: 1,
    nextRun: new Date(Date.now() + 14_400_000).toISOString(),
  },
  {
    id: "data_quality_check",
    name: "Data Quality Assessment",
    enabled: true,
    intervalMinutes: 720,
    lastRun: new Date(Date.now() - 21_600_000).toISOString(),
    lastSuccess: new Date(Date.now() - 21_600_000).toISOString(),
    successCount: 12,
    errorCount: 0,
    nextRun: new Date(Date.now() + 21_600_000).toISOString(),
  },
  {
    id: "gemini_processing",
    name: "AI Data Processing",
    enabled: false,
    intervalMinutes: 480,
    lastRun: new Date(Date.now() - 28_800_000).toISOString(),
    lastSuccess: new Date(Date.now() - 28_800_000).toISOString(),
    successCount: 8,
    errorCount: 2,
    lastError: "Rate limit exceeded",
    nextRun: null,
  },
  {
    id: "location_update",
    name: "Real-time Location Updates",
    enabled: true,
    intervalMinutes: 30,
    lastRun: new Date(Date.now() - 900_000).toISOString(),
    lastSuccess: new Date(Date.now() - 900_000).toISOString(),
    successCount: 156,
    errorCount: 8,
    nextRun: new Date(Date.now() + 900_000).toISOString(),
  },
];

export function GET(request: NextRequest) {
  const { searchParams } = new URL(request.url);
  const action = searchParams.get("action");

  try {
    switch (action) {
      case "status": {
        return NextResponse.json({
          isRunning: schedulerInstance !== undefined,
          tasks: schedulerTasks,
          summary: {
            totalTasks: schedulerTasks.length,
            enabledTasks: schedulerTasks.filter((t) => t.enabled).length,
            runningTasks: schedulerTasks.filter((t) => t.enabled && t.nextRun).length,
            totalSuccesses: schedulerTasks.reduce((acc, t) => acc + t.successCount, 0),
            totalErrors: schedulerTasks.reduce((acc, t) => acc + t.errorCount, 0),
          },
        });
      }

      case "logs": {
        // Return recent task execution logs
        return NextResponse.json({
          logs: [
            {
              timestamp: new Date(Date.now() - 300_000).toISOString(),
              taskId: "location_update",
              level: "info",
              message: "Successfully updated locations for 12 food trucks",
            },
            {
              timestamp: new Date(Date.now() - 900_000).toISOString(),
              taskId: "instagram_scrape",
              level: "info",
              message: "Scraped 3 Instagram accounts, found 8 new posts",
            },
            {
              timestamp: new Date(Date.now() - 1_800_000).toISOString(),
              taskId: "gemini_processing",
              level: "error",
              message: "Rate limit exceeded, skipping AI processing",
            },
            {
              timestamp: new Date(Date.now() - 3_600_000).toISOString(),
              taskId: "website_crawl",
              level: "warning",
              message: "Failed to crawl tacoparadise.com - site temporarily unavailable",
            },
          ],
        });
      }

      default: {
        return NextResponse.json({
          message: "Task Scheduler API",
          endpoints: [
            "GET /api/scheduler?action=status - Get scheduler status",
            "GET /api/scheduler?action=logs - Get execution logs",
            "POST /api/scheduler - Start/stop scheduler or execute task",
            "PUT /api/scheduler - Update task configuration",
          ],
        });
      }
    }
  } catch (error: unknown) {
    console.error("Scheduler API error:", error);
    return NextResponse.json({ error: "Internal server error" }, { status: 500 });
  }
}

export async function POST(request: NextRequest) {
  try {
    const body: PostRequestBody = await request.json();
    const { action, taskId } = body;

    switch (action) {
      case "start": {
        if (schedulerInstance) {
          return NextResponse.json({ error: "Scheduler is already running" }, { status: 409 });
        }

        schedulerInstance = { started: new Date().toISOString() };

        return NextResponse.json({
          message: "Scheduler started successfully",
          status: "running",
        });
      }

      case "stop": {
        if (!schedulerInstance) {
          return NextResponse.json({ error: "Scheduler is not running" }, { status: 409 });
        }

        schedulerInstance = undefined;

        return NextResponse.json({
          message: "Scheduler stopped successfully",
          status: "stopped",
        });
      }

      case "execute": {
        if (!taskId) {
          return NextResponse.json({ error: "Task ID is required for execution" }, { status: 400 });
        }

        const task = schedulerTasks.find((t) => t.id === taskId);
        if (!task) {
          return NextResponse.json({ error: "Task not found" }, { status: 404 });
        }

        // Simulate task execution
        task.lastRun = new Date().toISOString();

        // Simulate success/failure
        if (Math.random() > 0.1) {
          // 90% success rate
          task.lastSuccess = task.lastRun;
          task.successCount++;
          task.lastError = undefined;
        } else {
          task.errorCount++;
          task.lastError = "Simulated execution error";
        }

        return NextResponse.json({
          message: `Task ${taskId} executed`,
          task: task,
        });
      }

      default: {
        return NextResponse.json({ error: "Invalid action" }, { status: 400 });
      }
    }
  } catch (error: unknown) {
    console.error("Error in scheduler POST:", error);
    return NextResponse.json({ error: "Failed to process request" }, { status: 500 });
  }
}

export async function PUT(request: NextRequest) {
  try {
    const body: PutRequestBody = await request.json();
    const { taskId, config } = body;

    if (!taskId) {
      return NextResponse.json({ error: "Task ID is required" }, { status: 400 });
    }

    const taskIndex = schedulerTasks.findIndex((t) => t.id === taskId);
    if (taskIndex === -1) {
      return NextResponse.json({ error: "Task not found" }, { status: 404 });
    }

    // Update task configuration
    schedulerTasks[taskIndex] = {
      ...schedulerTasks[taskIndex],
      ...config,
    };

    // Update next run time if interval changed
    if (config.intervalMinutes && schedulerTasks[taskIndex].enabled) {
      const lastRun = new Date(schedulerTasks[taskIndex].lastRun || Date.now());
      const nextRun = new Date(lastRun.getTime() + config.intervalMinutes * 60 * 1000);
      schedulerTasks[taskIndex].nextRun = nextRun.toISOString();
    }

    return NextResponse.json({
      message: "Task configuration updated",
      task: schedulerTasks[taskIndex],
    });
  } catch (error: unknown) {
    console.error("Error updating task configuration:", error);
    return NextResponse.json({ error: "Failed to update task configuration" }, { status: 500 });
  }
}

import { type NextRequest, NextResponse } from "next/server"
