"use client"

import "leaflet/dist/leaflet.css"
import "leaflet-defaulticon-compatibility/dist/leaflet-defaulticon-compatibility.css"
import "leaflet-defaulticon-compatibility"

import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet"
import type { LatLngExpression } from "leaflet"
import L from "leaflet"

interface MapDisplayProps {
  trucks: Array<{
    id: string
    name: string
    current_location: {
      lat?: number
      lng?: number
      address?: string
    }
  }>
  userLocation?: { lat: number; lng: number } | null
  defaultCenter?: LatLngExpression
  defaultZoom?: number
  onSelectTruck?: (truckId: string) => void
}

// Custom food truck icon (public/placeholder-logo.png or use your own svg/png)
const foodTruckIcon = new L.Icon({
  iconUrl: "/placeholder-logo.png",
  iconSize: [36, 36],
  iconAnchor: [18, 36],
  popupAnchor: [0, -36],
  className: "food-truck-marker-icon"
})

const MapDisplay = ({
  trucks,
  userLocation,
  defaultCenter = [37.7749, -122.4194], // Default to San Francisco
  defaultZoom = 10,
  onSelectTruck,
}: MapDisplayProps) => {
  const validTrucks = trucks.filter(
    (truck) =>
      truck.current_location &&
      typeof truck.current_location.lat === "number" &&
      typeof truck.current_location.lng === "number",
  )

  const mapCenter: LatLngExpression = userLocation
    ? [userLocation.lat, userLocation.lng]
    : validTrucks.length > 0 && validTrucks[0].current_location.lat && validTrucks[0].current_location.lng
      ? [validTrucks[0].current_location.lat, validTrucks[0].current_location.lng]
      : defaultCenter

  return (
    <MapContainer
      center={mapCenter}
      zoom={defaultZoom}
      scrollWheelZoom={true}
      style={{ height: "100%", width: "100%" }}
      className="rounded-lg"
    >
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      {validTrucks.map((truck) => (
        <Marker
          key={truck.id}
          position={[truck.current_location.lat!, truck.current_location.lng!]}
          icon={foodTruckIcon}
          eventHandlers={{
            click: () => {
              if (onSelectTruck) {
                onSelectTruck(truck.id)
              }
            },
          }}
        >
          <Popup>
            <h4 className="font-bold">{truck.name}</h4>
            <div>{truck.current_location.address || "Location not available"}</div>
          </Popup>
        </Marker>
      ))}
      {userLocation && (
        <Marker position={[userLocation.lat, userLocation.lng]}>
          <Popup>You are here</Popup>
        </Marker>
      )}
    </MapContainer>
  )
}

export default MapDisplay

"use client"

import { useState } from "react"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Badge } from "@/components/ui/badge"
import { Card, CardContent } from "@/components/ui/card"
import { Search, Filter, Clock } from "lucide-react"

interface SearchFiltersProps {
  onSearch: (filters: SearchFilters) => void
  loading?: boolean
}

interface SearchFilters {
  query: string
  cuisine: string
  openNow: boolean
  radius: number
}

export function SearchFilters({ onSearch, loading }: SearchFiltersProps) {
  const [filters, setFilters] = useState<SearchFilters>({
    query: "",
    cuisine: "",
    openNow: false,
    radius: 10,
  })

  const [showAdvanced, setShowAdvanced] = useState(false)

  const cuisineTypes = [
    "Mexican",
    "American",
    "Asian",
    "Italian",
    "BBQ",
    "Burgers",
    "Tacos",
    "Pizza",
    "Sandwiches",
    "Desserts",
  ]

  const handleSearch = () => {
    onSearch(filters)
  }

  const clearFilters = () => {
    const clearedFilters = {
      query: "",
      cuisine: "",
      openNow: false,
      radius: 10,
    }
    setFilters(clearedFilters)
    onSearch(clearedFilters)
  }

  const activeFilterCount = [filters.query, filters.cuisine, filters.openNow].filter(Boolean).length

  return (
    <Card className="dark:bg-slate-800 dark:border-slate-700">
      <CardContent className="p-4">
        <div className="space-y-4">
          {/* Main Search */}
          <div className="flex flex-wrap items-center gap-2 sm:space-x-2"> {/* Allow wrapping and adjust gap */}
            <div className="relative flex-grow min-w-[200px]"> {/* Use flex-grow and min-w for input section */}
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 dark:text-gray-500 h-4 w-4" />
              <Input
                placeholder="Search food trucks, cuisine, or menu items..."
                value={filters.query}
                onChange={(e) => setFilters({ ...filters, query: e.target.value })}
                className="pl-10 w-full dark:bg-slate-700 dark:text-gray-100 dark:placeholder-gray-400" // Ensure input takes full width of its container
                onKeyDown={(e) => {
                  if (e.key === "Enter") {
                    handleSearch();
                  }
                }}
              />
            </div>
            <Button onClick={handleSearch} disabled={loading} className="flex-shrink-0"> {/* Prevent button from shrinking excessively */}
              <Search className="h-4 w-4 mr-2" />
              Search
            </Button>
            <Button variant="outline" onClick={() => setShowAdvanced(!showAdvanced)} className="dark:text-gray-300 dark:border-slate-600 dark:hover:bg-slate-700 flex-shrink-0"> {/* Prevent button from shrinking */}
              <Filter className="h-4 w-4 mr-2" />
              Filters
              {activeFilterCount > 0 && (
                <Badge variant="secondary" className="ml-2 dark:bg-slate-700 dark:text-gray-300">
                  {activeFilterCount}
                </Badge>
              )}
            </Button>
          </div>

          {/* Advanced Filters */}
          {showAdvanced && (
            <div className="space-y-4 pt-4 border-t dark:border-slate-700">
              {/* Quick Filters */}
              <div className="flex items-center space-x-2">
                <Button
                  variant={filters.openNow ? "default" : "outline"}
                  size="sm"
                  onClick={() => setFilters({ ...filters, openNow: !filters.openNow })}
                  className="dark:text-gray-300 dark:border-slate-600 dark:hover:bg-slate-700"
                >
                  <Clock className="h-4 w-4 mr-1" />
                  Open Now
                </Button>
                <Button variant="outline" size="sm" onClick={clearFilters} className="dark:text-gray-300 dark:border-slate-600 dark:hover:bg-slate-700">
                  Clear All
                </Button>
              </div>

              {/* Cuisine Types */}
              <div>
                <label className="text-sm font-medium mb-2 block dark:text-gray-100">Cuisine Type</label>
                <div className="flex flex-wrap gap-2">
                  {cuisineTypes.map((cuisine) => (
                    <Badge
                      key={cuisine}
                      variant={filters.cuisine === cuisine ? "default" : "outline"}
                      className="cursor-pointer dark:text-gray-300 dark:border-slate-600 dark:hover:bg-slate-700"
                      onClick={() =>
                        setFilters({
                          ...filters,
                          cuisine: filters.cuisine === cuisine ? "" : cuisine,
                        })
                      }
                    >
                      {cuisine}
                    </Badge>
                  ))}
                </div>
              </div>

              {/* Distance */}
              <div>
                <label className="text-sm font-medium mb-2 block dark:text-gray-100">Distance: {filters.radius} km</label>
                <input
                  type="range"
                  min="1"
                  max="50"
                  value={filters.radius}
                  onChange={(e) => setFilters({ ...filters, radius: Number(e.target.value) })}
                  className="w-full accent-blue-600 dark:accent-blue-500"
                />
                <div className="flex justify-between text-xs text-gray-500 dark:text-gray-400 mt-1">
                  <span>1 km</span>
                  <span>50 km</span>
                </div>
              </div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  )
}

"use client"

import * as React from "react"
import { ThemeProvider as NextThemesProvider, useTheme } from "next-themes"
import { type ThemeProviderProps } from "next-themes/dist/types"

export interface CustomThemeProviderProps extends ThemeProviderProps {
  children: React.ReactNode;
}

export function ThemeProvider({ children, ...props }: CustomThemeProviderProps) {
  return (
    <NextThemesProvider
      attribute="class"
      defaultTheme="system"
      enableSystem
      disableTransitionOnChange
      storageKey="vite-ui-theme" // Default storage key used by next-themes, can be customized
      {...props}
