  return context
}

"use client"

import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { MapPin, Phone, ExternalLink, Star } from "lucide-react"

interface FoodTruck {
  id: string
  name: string
  description: string
  current_location?: {
    address: string
  }
  operating_hours?: Record<string, { open: string; close: string; closed: boolean }>
  contact_info?: {
    phone?: string
    website?: string
  }
  data_quality_score: number
  verification_status: string
  menu?: Array<{
    category: string
    items: Array<{ name: string; price: number }>
  }>
}

interface TruckCardProps {
  truck: FoodTruck
  isOpen: boolean
  onSelectTruck: () => void
  formatPrice: (price: number) => string
  userLocation?: { lat: number; lng: number } | undefined;
}

export function TruckCard({ truck, isOpen, onSelectTruck, formatPrice }: TruckCardProps) {
  const getPopularItems = () => {
    if (!truck.menu || truck.menu.length === 0) return []
    return truck.menu[0]?.items?.slice(0, 3) || []
  }

  // Helper to determine price range fallback
  const getPriceRange = () => {
    // Flatten all prices from all menu items
    const prices = (truck.menu || [])
      .flatMap((cat) => cat.items)
      .map((item) => typeof item.price === 'number' ? item.price : undefined)
      .filter((p): p is number => p !== undefined && !isNaN(p))
    if (prices.length === 0) return null
    const min = Math.min(...prices)
    const max = Math.max(...prices)
    if (max < 10) return '$'
    if (min >= 10 && max <= 20) return '$$'
    if (min > 20) return '$$$'
    if (min < 10 && max > 20) return '$-$$$'
    if (min < 10 && max <= 20) return '$-$$'
    if (min >= 10 && max > 20) return '$$-$$$'
    return '$'
  }

  const popularItems = getPopularItems()
  const priceRange = getPriceRange()

  return (
    <Card className="hover:shadow-md transition-shadow cursor-pointer dark:bg-slate-800 dark:border-slate-700" onClick={onSelectTruck}>
      <CardHeader>
        <div className="flex justify-between items-start">
          <div className="flex-1">
            <CardTitle className="text-lg dark:text-gray-100">{truck.name}</CardTitle>
            <CardDescription className="flex items-center mt-1 dark:text-gray-400">
              <MapPin className="h-4 w-4 mr-1" />
              {truck.current_location?.address || "Location not available"}
            </CardDescription>
          </div>
          <div className="flex flex-col items-end space-y-1">
            <Badge variant={isOpen ? "default" : "secondary"}>{isOpen ? "Open" : "Closed"}</Badge>
            {/* Show price range fallback if no explicit prices */}
            {(popularItems.every(item => !item.price)) && priceRange && (
              <Badge variant="outline" className="mt-1">{priceRange}</Badge>
            )}
          </div>
        </div>
      </CardHeader>
      <CardContent>
        <p className="text-gray-600 dark:text-gray-400 mb-4 line-clamp-2">{truck.description}</p>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <h4 className="font-medium mb-2 text-sm dark:text-gray-100">Popular Items</h4>
            <div className="space-y-1">
              {popularItems.map((item, idx) => (
                <div key={idx} className="flex justify-between text-sm dark:text-gray-300">
                  <span className="truncate dark:text-gray-200">{item.name}</span>
                  {typeof item.price === 'number' ? (
                    <span className="text-green-600 dark:text-green-400 ml-2">{formatPrice(item.price)}</span>
                  ) : (
                    <span className="text-gray-400 ml-2">N/A</span>
                  )}
                </div>
              ))}
              {popularItems.length === 0 && <p className="text-gray-500 dark:text-gray-400 text-sm">Menu not available</p>}
            </div>
          </div>

          <div>
            <h4 className="font-medium mb-2 text-sm dark:text-gray-100">Contact</h4>
            <div className="space-y-1 dark:text-gray-300">
              {truck.contact_info?.phone && (
                <div className="flex items-center text-sm">
                  <Phone className="h-3 w-3 mr-1" />
                  <span className="truncate">{truck.contact_info.phone}</span>
                </div>
              )}
              {truck.contact_info?.website && (
                <div className="flex items-center text-sm">
                  <ExternalLink className="h-3 w-3 mr-1" />
                  <span className="truncate">Website</span>
                </div>
              )}
              <div className="flex items-center text-xs text-gray-500 dark:text-gray-400">
                <Star className="h-3 w-3 mr-1" />
                <span>{truck.verification_status}</span>
              </div>
            </div>
          </div>
        </div>
        <div className="mt-2">
          <p className="text-xs text-gray-500 dark:text-gray-400">
            <Badge variant={truck.verification_status === "Verified" ? "default" : "secondary"}>
              {truck.verification_status}
            </Badge>
          </p>
        </div>
      </CardContent>
      {truck.verification_status === "verified" && (
        <div className="px-4 py-2 border-t border-gray-200 dark:border-gray-700">
          <Button className="w-full" variant="default" disabled>
            Book Me
          </Button>
        </div>
      )}
    </Card>
  )
}

"use client"

import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { VariantProps, cva } from "class-variance-authority"
import { PanelLeft } from "lucide-react"

import { useIsMobile } from "@/hooks/UseMobile"
import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Separator } from "@/components/ui/Separator"
import { Sheet, SheetContent } from "@/components/ui/Sheet"
import { Skeleton } from "@/components/ui/Skeleton"
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/Tooltip"

const SIDEBAR_COOKIE_NAME = "sidebar:state"
const SIDEBAR_COOKIE_MAX_AGE = 60 * 60 * 24 * 7
const SIDEBAR_WIDTH = "16rem"
const SIDEBAR_WIDTH_MOBILE = "18rem"
const SIDEBAR_WIDTH_ICON = "3rem"
const SIDEBAR_KEYBOARD_SHORTCUT = "b"

type SidebarContext = {
  state: "expanded" | "collapsed"
  open: boolean
  setOpen: (open: boolean) => void
  openMobile: boolean
  setOpenMobile: (open: boolean) => void
  isMobile: boolean
  toggleSidebar: () => void
}

const SidebarContext = React.createContext<SidebarContext | undefined>(undefined)

function useSidebar() {
  const context = React.useContext(SidebarContext)
  if (!context) {
    throw new Error("useSidebar must be used within a SidebarProvider.")
  }

  return context
}

const SidebarProvider = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> & {
    defaultOpen?: boolean
    open?: boolean
    onOpenChange?: (open: boolean) => void
  }
>(
  (
    {
      defaultOpen = true,
      open: openProp,
      onOpenChange: setOpenProp,
      className,
      style,
      children,
      ...props
    },
    ref
  ) => {
    const isMobile = useIsMobile()
    const [openMobile, setOpenMobile] = React.useState(false)

    // This is the internal state of the sidebar.
    // We use openProp and setOpenProp for control from outside the component.
    const [_open, _setOpen] = React.useState(defaultOpen)
    const open = openProp ?? _open
    const setOpen = React.useCallback(
      (value: boolean | ((value: boolean) => boolean)) => {
        const openState = typeof value === "function" ? value(open) : value
        if (setOpenProp) {
          setOpenProp(openState)
        } else {
          _setOpen(openState)
        }

        // This sets the cookie to keep the sidebar state.
        globalThis.document.cookie = `${SIDEBAR_COOKIE_NAME}=${openState}; path=/; max-age=${SIDEBAR_COOKIE_MAX_AGE}`
      },
      [setOpenProp, open]
    )

    // Helper to toggle the sidebar.
    const toggleSidebar = React.useCallback(() => {
      return isMobile
        ? setOpenMobile((open) => !open)
        : setOpen((open) => !open)
    }, [isMobile, setOpen, setOpenMobile])

    // Adds a keyboard shortcut to toggle the sidebar.
    React.useEffect(() => {
      const handleKeyDown = (event: KeyboardEvent) => {
        if (
          event.key === SIDEBAR_KEYBOARD_SHORTCUT &&
          (event.metaKey || event.ctrlKey)
        ) {
          event.preventDefault()
          toggleSidebar()
        }
      }

      globalThis.addEventListener("keydown", handleKeyDown)
      return () => globalThis.removeEventListener("keydown", handleKeyDown)
    }, [toggleSidebar])

    // We add a state so that we can do data-state="expanded" or "collapsed".
    // This makes it easier to style the sidebar with Tailwind classes.
    const state = open ? "expanded" : "collapsed"

    const contextValue = React.useMemo<SidebarContext>(
      () => ({
        state,
        open,
        setOpen,
        isMobile,
        openMobile,
        setOpenMobile,
        toggleSidebar,
      }),
      [state, open, setOpen, isMobile, openMobile, setOpenMobile, toggleSidebar]
    )

    return (
      <SidebarContext.Provider value={contextValue}>
        <TooltipProvider delayDuration={0}>
          <div
            style={
              {
                "--sidebar-width": SIDEBAR_WIDTH,
                "--sidebar-width-icon": SIDEBAR_WIDTH_ICON,
                ...style,
              } as React.CSSProperties
            }
            className={cn(
              "group/sidebar-wrapper flex min-h-svh w-full has-[[data-variant=inset]]:bg-sidebar",
              className
            )}
            ref={ref}
            {...props}
          >
            {children}
