export default ContactInfoCard;

import { useEffect } from 'react';

import { supabase } from '@/lib/supabase'; 
import { PipelineEvent } from '@/lib/types';

/**
 * Manages event subscriptions for pipeline events using Supabase.
 * @example
 * EventSubscriptionManager()
 * @returns {void} This component serves purely for side-effects of subscription management.
 * @description
 *   - Utilizes the Supabase Realtime Client to subscribe and process "pipeline-events".
 *   - Includes an effect cleanup to unsubscribe from events when the component unmounts.
 *   - Current implementation logs events to the console as placeholders.
 */
type RealtimeChannelStatus = 'SUBSCRIBED' | 'CLOSED' | 'CHANNEL_ERROR' | 'TIMED_OUT';

function EventSubscriptionManager() {
  useEffect(() => {
    const handleEvent = (payload: { new: PipelineEvent }) => {
      try {
        const event = payload.new;
        if (event && typeof event === 'object' && 'type' in event) {
          // Process the event based on its type
          switch (event.type) {
            case 'job_started': {
              console.info('Job started:', event.payload);
              break;
            }
            case 'job_completed': {
              console.info('Job completed:', event.payload);
              break;
            }
            case 'data_updated': {
              console.info('Data updated:', event.payload);
              break;
            }
            default: {
              console.warn('Received unknown event type:', event.type);
            }
          }
        } else {
          console.warn('Received malformed event:', payload);
        }
      } catch (error) {
        console.error('Error processing pipeline event:', error);
      }
    };

    const subscription = supabase
      .channel('pipeline-events')
      .on('postgres_changes', { event: 'INSERT', schema: 'public', table: 'pipeline_events' }, handleEvent)
      .subscribe((status: RealtimeChannelStatus, error?: Error) => {
        if (status === 'SUBSCRIBED') {
          console.info('Successfully subscribed to pipeline events.');
        }
        if (status === 'CHANNEL_ERROR' && error) {
          console.error('Pipeline event subscription error:', error);
        }
      });

    return () => {
      void supabase.realtime.removeChannel(subscription);
    };
  }, []);
}

export default EventSubscriptionManager;

import React, { useEffect, useState } from 'react';
import { supabase } from '@/lib/supabase'; 

type RealtimeChannelStatus = 'SUBSCRIBED' | 'CLOSED' | 'CHANNEL_ERROR' | 'TIMED_OUT';

interface SystemStatusResponse {
  status: string;
  data: {
    status: string;
  };
}

/**
* Represents system status indicators in real-time
* @example
* SystemStatusIndicators()
* <div><h3>Current System Status:</h3><span>ONLINE</span></div>
* @returns {JSX.Element} A JSX element displaying the current system status.
* @description
*   - Utilizes Supabase Realtime Client for subscribing to system status updates.
*   - Updates the display with the latest system status retrieved from a realtime database.
*   - Handles subscription clean-up on component unmount to avoid memory leaks.
*/
function SystemStatusIndicators() {
  const [systemStatus, setSystemStatus] = useState('UNKNOWN');

  useEffect(() => {
    const subscription = supabase.realtime.channel('system-status').subscribe((status: RealtimeChannelStatus, payload: unknown) => {
      if (status === 'SUBSCRIBED') {
        const response = payload as SystemStatusResponse;
        if (response && typeof response === 'object' && 'status' in response) {
          const typedResponse = response;
          const responseStatus = typedResponse.status; // Extract status to a variable

          if (typeof responseStatus === 'string' && responseStatus === "success") {
            if (typeof typedResponse.data === 'object' && 'status' in typedResponse.data) {
              setSystemStatus(typedResponse.data.status);
            } else {
              console.error("Invalid data structure in system status response");
            }
          } else {
            console.error("Failed to retrieve system status or invalid response format");
          }
        } else {
          console.error("Failed to retrieve system status or invalid response format");
        }
      }
    });

    return () => {
      void supabase.realtime.removeChannel(subscription);
    };
  }, []);

  return (
    <div>
      <h3>Current System Status:</h3>
      <span>{systemStatus}</span>
    </div>
  );
}

export default SystemStatusIndicators;

'use client';

import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import { LatLngExpression } from 'leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import { FoodTruck } from '@/lib/types';

// Fix for default marker icon issue with Webpack
// @ts-expect-error: Leaflet's default icon handling with Webpack can be problematic.
// This is a common workaround to ensure marker icons load correctly.
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'leaflet/images/marker-icon-2x.png',
  iconUrl: 'leaflet/images/marker-icon.png',
  shadowUrl: 'leaflet/images/marker-shadow.png',
});

interface MapComponentProps {
  readonly trucks: FoodTruck[];
  readonly userLocation?: { lat: number; lng: number };
  readonly defaultCenter: LatLngExpression;
  readonly defaultZoom?: number;
  readonly onSelectTruck?: (truckId: string) => void;
  readonly selectedTruckLocation?: LatLngExpression;
}

// Component to handle map view changes
function MapViewHandler({ center, zoom }: { center: LatLngExpression; zoom: number }) {
  const map = useMap();
  useEffect(() => {
    map.setView(center, zoom);
  }, [center, zoom, map]);
  return null;
}

const MapComponent: React.FC<MapComponentProps> = ({
  trucks,
  userLocation,
  defaultCenter,
  defaultZoom = 13,
  onSelectTruck,
  selectedTruckLocation,
}) => {
  const [isMounted, setIsMounted] = useState(false);

  useEffect(() => {
    setIsMounted(true);
    return () => {
      setIsMounted(false);
    };
  }, []);

  const center: LatLngExpression = selectedTruckLocation || userLocation
    ? [userLocation?.lat ?? 0, userLocation?.lng ?? 0]
    : defaultCenter;

  if (!isMounted) {
    return null; // Render nothing until mounted on client
  }

  return (
    <MapContainer
      center={center}
      zoom={defaultZoom}
      scrollWheelZoom={false}
      style={{ height: '100%', width: '100%' }}
    >
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      <MapViewHandler center={center} zoom={defaultZoom} />

      {userLocation && (
        <Marker position={[userLocation.lat, userLocation.lng]}>
          <Popup>Your Location</Popup>
        </Marker>
      )}

      {trucks.map((truck) => (
        truck.current_location?.lat && truck.current_location?.lng && (
          <Marker
            key={truck.id}
            position={[truck.current_location.lat, truck.current_location.lng]}
            eventHandlers={{
              click: () => {
                onSelectTruck?.(truck.id);
              },
            }}
          >
            <Popup>{truck.name}</Popup>
          </Marker>
        )
      ))}
    </MapContainer>
  );
};

export default MapComponent;

import { Clock } from 'lucide-react';
import { DailyOperatingHours } from '@/lib/supabase/types'; // Import DailyOperatingHours

interface OperatingHoursSectionProps {
  readonly todayHours: DailyOperatingHours | undefined; // Update type
  readonly formatHours: (hours: DailyOperatingHours) => string; // Update type
}

/**
 * Renders today's operating hours section.
 * @example
 * OperatingHoursSection({ todayHours: { closed: false, open: '09:00', close: '17:00' }, formatHours: (hours) => `${hours.open} - ${hours.close}` })
 * // Returns a JSX element with formatted operating hours or 'Closed'.
 * @param {Readonly<OperatingHoursSectionProps>} {todayHours, formatHours} - The properties for today's operating hours and the function to format them.
 * @returns {JSX.Element | undefined} A JSX element displaying today's hours, or nothing if hours are undefined.
 * @description
 *   - The component displays 'Closed' if todayHours indicates closure.
 *   - Utilizes a Clock icon for a visual representation of hours.
 *   - Ensures dark mode compatibility through styling classes.
 */
export function OperatingHoursSection({
  todayHours,
  formatHours,
}: Readonly<OperatingHoursSectionProps>) {
  if (todayHours === undefined) return; // Return null if no hours

  return (
    <div>
      <h4 className="font-medium mb-2 text-sm dark:text-gray-100">Today's Hours</h4>
      <div className="flex items-center gap-2">
        <Clock className="size-3 text-gray-500" />
        <span className="text-sm dark:text-gray-300">
          {todayHours.closed ? 'Closed' : formatHours(todayHours)}
        </span>
      </div>
    </div>
  );
}

import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Phone, Mail, Globe } from 'lucide-react';
import type { FoodTruck } from '@/lib/supabase';

interface TruckContactInfoProps {
  readonly truck: FoodTruck;
}

interface ContactFieldProps {
  readonly icon: React.ComponentType<{ className?: string }>;
  readonly label: string;
  readonly value?: string | null;
  readonly href?: string;
  readonly unavailableText: string;
}

/**
 * Renders a contact field with icon, label, and value, handling both available and unavailable states.
 * @example
 * ContactField({
 *   icon: EmailIcon,
 *   label: 'Email',
 *   value: 'example@example.com',
 *   href: 'mailto:example@example.com',
 *   unavailableText: 'Not Available'
 * })
 * <div>...</div>
 * @param {React.ComponentType} icon - The icon component to display in the contact field.
 * @param {string} label - The label to display above the contact value.
 * @param {string} value - The contact value to display; if undefined or empty, displays unavailableText instead.
 * @param {string} [href] - The link associated with the contact value for redirection. Determines link behavior based on URL format.
 * @param {string} unavailableText - Text displayed when value is unavailable or empty.
 * @returns {JSX.Element} JSX structure for the contact field.
 * @description
 *   - Opens external links in a new tab with 'noopener noreferrer' for security.
 *   - Conditionally renders either a hyperlink or plain text based on value and href.
 *   - Applies specific styling based on the availability of the value.
 */
function ContactField({
  icon: Icon,
  label,
  value,
  href,
  unavailableText,
}: Readonly<ContactFieldProps>) {
  if (value == undefined || value.length === 0) {
    // Handles null, undefined, and empty string
    return (
      <div className="flex items-center gap-3 text-gray-400">
        <Icon className="size-4" />
        <span className="text-sm">{unavailableText}</span>
      </div>
    );
  }

  return (
    <div className="flex items-center gap-3">
      <Icon className="size-4 text-gray-500" />
      <div>
        <label className="text-sm font-medium text-gray-500 dark:text-gray-400">{label}</label>
        {href !== undefined && href !== '' ? (
          <a
            href={href}
            target={href.startsWith('http') ? '_blank' : undefined}
            rel={href.startsWith('http') ? 'noopener noreferrer' : undefined}
            className="block text-blue-600 hover:text-blue-800 dark:text-blue-400 dark:hover:text-blue-300 underline"
          >
            {href.startsWith('http') ? 'Visit Website' : value}
          </a>
        ) : (
          <p className="text-gray-900 dark:text-gray-200">{value}</p>
        )}
      </div>
    </div>
  );
}

/**
 * Renders social media links based on provided handles.
 * @example
 * SocialMediaLinks({ socialMedia: { instagram: 'insta_user', twitter: 'twit_user' } })
 * Renders and displays links for Instagram and Twitter.
 * @param {Object} {socialMedia} - Contains platform keys mapped to user handles.
 * @returns {JSX.Element|undefined} A JSX element displaying social media links or undefined if no valid links exist.
 * @description
 *   - Checks for undefined or empty socialMedia input to prevent unnecessary rendering.
 *   - Supports Instagram, Facebook, and Twitter platforms with specific styles.
 *   - Handles links are generated dynamically based on platform keys and user handles.
 *   - Applies consistent styling for light and dark themes using color properties.
 */
function SocialMediaLinks({
  socialMedia,
}: Readonly<{ readonly socialMedia?: Record<string, string> }>) {
  if (socialMedia == undefined || Object.keys(socialMedia).length === 0) {
    return;
  }

  const socialPlatforms = [
    { key: 'instagram', name: 'Instagram', baseUrl: 'https://instagram.com/', color: 'pink' },
    { key: 'facebook', name: 'Facebook', baseUrl: 'https://facebook.com/', color: 'blue' },
    { key: 'twitter', name: 'Twitter', baseUrl: 'https://twitter.com/', color: 'sky' },
  ];

  return (
    <div>
      <label className="text-sm font-medium text-gray-500 dark:text-gray-400">Social Media</label>
      <div className="flex flex-wrap gap-2 mt-2">
        {socialPlatforms.map(({ key, name, baseUrl, color }) => {
          const handle = socialMedia[key];
          if (handle == undefined || handle.length === 0) return; // Handles null, undefined, and empty string

          return (
            <a
              key={key}
              href={`${baseUrl}${handle}`}
              target="_blank"
              rel="noopener noreferrer"
              className={`flex items-center gap-1 px-2 py-1 bg-${color}-100 text-${color}-800 rounded-md text-sm hover:bg-${color}-200 dark:bg-${color}-900 dark:text-${color}-200`}
            >
              <Globe className="size-3" />
              {name}
            </a>
          );
        })}
      </div>
    </div>
  );
}

/**
 * Renders a truck's contact information within a styled card component.
 * @example
 * TruckContactInfo({ truck: sampleTruckObject })
 * <Card>...</Card>
 * @param {Readonly<TruckContactInfoProps>} {truck} - Object containing truck's contact information and social media links.
 * @returns {JSX.Element} A card component displaying various contact fields and social media links.
 * @description
 *   - Utilizes conditional rendering to display availability of contact information.
 *   - Applies Tailwind CSS classes for dark mode styling.
 *   - Integrates icons alongside contact labels for visual aid.
 *   - SocialMediaLinks component is used to list the truck's social media presence.
 */
export function TruckContactInfo({ truck }: Readonly<TruckContactInfoProps>) {
  return (
    <Card className="dark:bg-slate-800 dark:border-slate-700">
      <CardHeader>
        <CardTitle className="flex items-center gap-2 dark:text-gray-100">
          <Phone className="size-5" />
          Contact Information
        </CardTitle>
        <CardDescription className="dark:text-gray-400">Get in touch</CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <ContactField
          icon={Phone}
          label="Phone"
          value={truck.contact_info?.phone}
          href={
            truck.contact_info?.phone !== undefined && truck.contact_info.phone !== ''
              ? `tel:${truck.contact_info.phone}`
              : undefined
          }
          unavailableText="No phone number available"
        />

        <ContactField
          icon={Mail}
          label="Email"
          value={truck.contact_info?.email}
          href={
            truck.contact_info?.email !== undefined && truck.contact_info.email !== ''
              ? `mailto:${truck.contact_info.email}`
              : undefined
          }
          unavailableText="No email available"
        />

        <ContactField
          icon={Globe}
          label="Website"
          value={truck.contact_info?.website}
          href={truck.contact_info?.website}
          unavailableText="No website available"
        />

        <SocialMediaLinks socialMedia={truck.social_media} />
      </CardContent>
    </Card>
  );
}

'use client';

import * as React from 'react';
import * as AlertDialogPrimitive from '@radix-ui/react-alert-dialog';

import { cn } from '@/lib/utils';
import { buttonVariants } from '@/components/ui/button';

const AlertDialog = AlertDialogPrimitive.Root;

const AlertDialogTrigger = AlertDialogPrimitive.Trigger;

const AlertDialogPortal = AlertDialogPrimitive.Portal;

const AlertDialogOverlay = React.forwardRef<
  HTMLDivElement,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Overlay>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Overlay
    className={cn(
      'fixed inset-0 z-50 bg-black/80  data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0',
      className,
    )}
    {...props}
    ref={ref}
  />
));
AlertDialogOverlay.displayName = AlertDialogPrimitive.Overlay.displayName;

const AlertDialogContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Content>
>(({ className, ...props }, ref) => (
  <AlertDialogPortal>
    <AlertDialogOverlay />
    <AlertDialogPrimitive.Content
      ref={ref}
      className={cn(
        'fixed left-[50%] top-[50%] z-50 grid w-full max-w-lg translate-x-[-50%] translate-y-[-50%] gap-4 border bg-background p-6 shadow-lg duration-200 data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[state=closed]:slide-out-to-left-1/2 data-[state=closed]:slide-out-to-top-[48%] data-[state=open]:slide-in-from-left-1/2 data-[state=open]:slide-in-from-top-[48%] sm:rounded-lg',
        className,
      )}
      {...props}
    />
  </AlertDialogPortal>
));
AlertDialogContent.displayName = AlertDialogPrimitive.Content.displayName;

const AlertDialogHeader = ({ className, ...props }: React.HTMLAttributes<HTMLDivElement>) => (
  <div className={cn('flex flex-col space-y-2 text-center sm:text-left', className)} {...props} />
);
AlertDialogHeader.displayName = 'AlertDialogHeader';

const AlertDialogFooter = ({ className, ...props }: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn('flex flex-col-reverse sm:flex-row sm:justify-end sm:space-x-2', className)}
    {...props}
  />
);
AlertDialogFooter.displayName = 'AlertDialogFooter';

const AlertDialogTitle = React.forwardRef<
  HTMLHeadingElement,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Title>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Title
    ref={ref}
    className={cn('text-lg font-semibold', className)}
    {...props}
  />
));
AlertDialogTitle.displayName = AlertDialogPrimitive.Title.displayName;

const AlertDialogDescription = React.forwardRef<
  HTMLParagraphElement,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Description>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Description
    ref={ref}
    className={cn('text-sm text-muted-foreground', className)}
    {...props}
  />
));
AlertDialogDescription.displayName = AlertDialogPrimitive.Description.displayName;

const AlertDialogAction = React.forwardRef<
  HTMLButtonElement,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Action>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Action ref={ref} className={cn(buttonVariants(), className)} {...props} />
));
AlertDialogAction.displayName = AlertDialogPrimitive.Action.displayName;

const AlertDialogCancel = React.forwardRef<
  HTMLButtonElement,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Cancel>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Cancel
    ref={ref}
    className={cn(buttonVariants({ variant: 'outline' }), 'mt-2 sm:mt-0', className)}
    {...props}
  />
));
AlertDialogCancel.displayName = AlertDialogPrimitive.Cancel.displayName;

export {
  AlertDialog,
  AlertDialogPortal,
  AlertDialogOverlay,
  AlertDialogTrigger,
  AlertDialogContent,
  AlertDialogHeader,
  AlertDialogFooter,
  AlertDialogTitle,
  AlertDialogDescription,
  AlertDialogAction,
  AlertDialogCancel,
};

import * as React from 'react';
import * as NavigationMenuPrimitive from '@radix-ui/react-navigation-menu';
import { cva } from 'class-variance-authority';
import { ChevronDown } from 'lucide-react';

import { cn } from '@/lib/utils';

const NavigationMenu = React.forwardRef<
  HTMLDivElement,
  React.ComponentPropsWithoutRef<typeof NavigationMenuPrimitive.Root>
>(({ className, children, ...props }, ref) => (
  <NavigationMenuPrimitive.Root
    ref={ref}
    className={cn('relative z-10 flex max-w-max flex-1 items-center justify-center', className)}
    {...props}
  >
    {children}
    <NavigationMenuViewport />
  </NavigationMenuPrimitive.Root>
));
NavigationMenu.displayName = NavigationMenuPrimitive.Root.displayName;

const NavigationMenuList = React.forwardRef<
  HTMLUListElement,
  React.ComponentPropsWithoutRef<typeof NavigationMenuPrimitive.List>
>(({ className, ...props }, ref) => (
  <NavigationMenuPrimitive.List
    ref={ref}
    className={cn('group flex flex-1 list-none items-center justify-center space-x-1', className)}
    {...props}
  />
));
NavigationMenuList.displayName = NavigationMenuPrimitive.List.displayName;

const NavigationMenuItem = NavigationMenuPrimitive.Item;

const navigationMenuTriggerStyle = cva(
  'group inline-flex h-10 w-max items-center justify-center rounded-md bg-background px-4 py-2 text-sm font-medium transition-colors hover:bg-accent hover:text-accent-foreground focus:bg-accent focus:text-accent-foreground focus:outline-none disabled:pointer-events-none disabled:opacity-50 data-[active]:bg-accent/50 data-[state=open]:bg-accent/50',
);

const NavigationMenuTrigger = React.forwardRef<
  HTMLButtonElement,
  React.ComponentPropsWithoutRef<typeof NavigationMenuPrimitive.Trigger>
>(({ className, children, ...props }, ref) => (
  <NavigationMenuPrimitive.Trigger
    ref={ref}
    className={cn(navigationMenuTriggerStyle(), 'group', className)}
    {...props}
  >
    {children}{' '}
    <ChevronDown
      className="relative top-[1px] ml-1 size-3 transition duration-200 group-data-[state=open]:rotate-180"
      aria-hidden="true"
    />
  </NavigationMenuPrimitive.Trigger>
));
NavigationMenuTrigger.displayName = NavigationMenuPrimitive.Trigger.displayName;

const NavigationMenuContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentPropsWithoutRef<typeof NavigationMenuPrimitive.Content>
>(({ className, ...props }, ref) => (
  <NavigationMenuPrimitive.Content
    ref={ref}
    className={cn(
      'left-0 top-0 w-full data-[motion^=from-]:animate-in data-[motion^=to-]:animate-out data-[motion^=from-]:fade-in data-[motion^=to-]:fade-out data-[motion=from-end]:slide-in-from-right-52 data-[motion=from-start]:slide-in-from-left-52 data-[motion=to-end]:slide-out-to-right-52 data-[motion=to-start]:slide-out-to-left-52 md:absolute md:w-auto ',
      className,
    )}
    {...props}
  />
));
NavigationMenuContent.displayName = NavigationMenuPrimitive.Content.displayName;

const NavigationMenuLink = NavigationMenuPrimitive.Link;

const NavigationMenuViewport = React.forwardRef<
  HTMLDivElement,
  React.ComponentPropsWithoutRef<typeof NavigationMenuPrimitive.Viewport>
