import { NextResponse, NextRequest } from 'next/server';
import { runTestPipeline } from '@/lib/api/test-integration/pipelineRunner';

export async function POST(request: NextRequest) {
  const logs: string[] = [];
  logs.push('Test pipeline run started.');

  interface TestPipelineRequestBody {
    url?: string;
    rawText?: string;
    isDryRun?: boolean;
  }

  try {
    const rawBody: unknown = await request.json();

    if (typeof rawBody !== 'object' || rawBody === null) {
      logs.push('Invalid request body: expected an object.');
      return NextResponse.json({ error: 'Invalid request body: expected an object.' }, { status: 400 });
    }

    const body: TestPipelineRequestBody = {};
    if ('url' in rawBody && typeof (rawBody as { url?: unknown }).url === 'string') {
      body.url = (rawBody as { url: string }).url;
    }
    if ('rawText' in rawBody && typeof (rawBody as { rawText?: unknown }).rawText === 'string') {
      body.rawText = (rawBody as { rawText: string }).rawText;
    }
    if ('isDryRun' in rawBody && typeof (rawBody as { isDryRun?: unknown }).isDryRun === 'boolean') {
      body.isDryRun = (rawBody as { isDryRun: boolean }).isDryRun;
    }
    // Default isDryRun if not provided, as per original logic where isDryRun = true was default in runTestPipeline
    if (body.isDryRun === undefined) {
        body.isDryRun = true;
    }

    const results = await runTestPipeline(body, logs);

    // Explicitly cast to Record<string, unknown> for NextResponse.json
    // to handle the complex/union types within PipelineRunResult's StageResult.data
    return NextResponse.json({ results: results as Record<string, unknown> }, { status: 200 });
  } catch (error: unknown) {
    const errorMessage =
      error instanceof Error
        ? error.message
        : 'An unknown error occurred during overall test pipeline run.';
    logs.push(`Overall test pipeline error: ${errorMessage}`);
    return NextResponse.json(
      {
        message: 'Test pipeline run failed.',
        error: errorMessage,
        results: {
          logs,
          overallStatus: 'Error',
        },
      },
      { status: 200 },
    );
  }
}

import React from 'react';
// Removed Mail, Phone, Globe imports as they are not directly used here

interface ContactFieldProps {
  readonly icon: React.ComponentType<{ className?: string }>;
  readonly label: string;
  readonly value?: string;
  readonly href?: string;
  readonly unavailableText: string;
}

export function ContactField({
  icon: Icon,
  label,
  value,
  href,
  unavailableText
}: Readonly<ContactFieldProps>) {
  if (value == undefined || value === '') {
    return (
      <div className="flex items-center gap-3 text-gray-400">
        <Icon className="h-4 w-4" />
        <span className="text-sm">{unavailableText}</span>
      </div>
    );
  }

  return (
    <div className="flex items-center gap-3">
      <Icon className="h-4 w-4 text-gray-500" />
      <div>
        <label className="text-sm font-medium text-gray-500">{label}</label>
        {(href != null && href !== '') ? (
          <a
            href={href}
            target="_blank"
            rel="noopener noreferrer"
            className="text-blue-600 hover:text-blue-800 underline"
          >
            {value}
          </a>
        ) : (
          <p className="text-gray-900">{value}</p>
        )}
      </div>
    </div>
  );
}

import React from 'react';
import {
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from '@/components/ui/accordion';
import { Badge } from '@/components/ui/badge';
import { TruckCard } from '@/components/TruckCard';
import { FoodTruck } from '@/lib/types/foodTruck';

interface TruckAccordionItemProps {
  readonly truck: FoodTruck;
  readonly selectedTruckId: string | undefined;
  readonly setSelectedTruckId: (id: string | undefined) => void;
  readonly isOpen: (truck: FoodTruck) => boolean;
  readonly userLocation: { lat: number; lng: number } | undefined;
}

export function TruckAccordionItem({
  truck,
  selectedTruckId,
  setSelectedTruckId,
  isOpen,
  userLocation,
}: TruckAccordionItemProps) {
  return (
    <AccordionItem value={truck.id} key={truck.id}>
      <AccordionTrigger className="flex items-center justify-between p-4 hover:bg-gray-50 dark:hover:bg-slate-700 rounded-md">
        <div className="flex-1 text-left">
          <h4 className="font-medium dark:text-gray-100">{truck.name}</h4>
          {truck.current_location?.address && (
            <p className="text-sm text-gray-600 dark:text-gray-400 line-clamp-1">
              {truck.current_location.address}
            </p>
          )}
        </div>
        <Badge variant={isOpen(truck) ? 'default' : 'secondary'}>
          {isOpen(truck) ? 'Open' : 'Closed'}
        </Badge>
      </AccordionTrigger>
      <AccordionContent>
        <TruckCard
          truck={truck}
          isOpen={isOpen(truck)}
          onSelectTruck={() => setSelectedTruckId(truck.id)}
          userLocation={userLocation}
          hideHeader={true}
        />
      </AccordionContent>
    </AccordionItem>
  );
}

import React from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import type { FoodTruck } from '@/lib/supabase';

