import React from 'react';
import { Badge } from '@/components/ui/badge';
import { FoodTruck, DailyOperatingHours, MenuItem } from '@/lib/types';
import { RatingSection } from './RatingSection';
import { MenuSection } from './MenuSection';
import { ContactSection } from './ContactSection';
import { SocialMediaSection } from './SocialMediaSection';
import { OperatingHoursSection } from './OperatingHoursSection';
import { formatHours } from '@/lib/utils/foodTruckHelpers';

interface TruckCardContentProps {
  readonly truck: FoodTruck;
  readonly todayHours?: DailyOperatingHours;
  readonly popularItems: MenuItem[]; // Use the full MenuItem type
}

export function TruckCardContent({
  truck,
  todayHours,
  popularItems,
}: TruckCardContentProps) {
  return (
    <>
      {truck.description && (
        <p className="text-gray-600 dark:text-gray-400 mb-4 line-clamp-2">{truck.description}</p>
      )}
      <div className="space-y-4">
        {/* Ratings & Hours Row */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <RatingSection averageRating={truck.average_rating} reviewCount={truck.review_count} />
          <OperatingHoursSection todayHours={todayHours} formatHours={formatHours} />
        </div>

        {/* Menu & Contact Row */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <MenuSection popularItems={popularItems} />
          <ContactSection contactInfo={truck.contact_info} verificationStatus={truck.verification_status} />
        </div>

        <SocialMediaSection socialMedia={truck.social_media} />
      </div>
      {truck.verification_status && (
        <div className="mt-2">
          <Badge variant={truck.verification_status === 'verified' ? 'default' : 'secondary'}>
            <span className="capitalize">{truck.verification_status}</span>
          </Badge>
        </div>
      )}
    </>
  );
}

import React from 'react';
import { CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { MapPin } from 'lucide-react';
import { FoodTruck } from '@/lib/types';

interface TruckCardHeaderProps {
  readonly truck: FoodTruck;
  readonly isOpen: boolean;
  readonly popularItems: Array<{ name: string; price: number | string | undefined }>;
  readonly priceRange: string | undefined;
}

export function TruckCardHeader({
  truck,
  isOpen,
  popularItems,
  priceRange
}: TruckCardHeaderProps) {
  return (
    <CardHeader>
      <div className="flex justify-between items-start">
        <div className="flex-1">
          <CardTitle className="text-lg dark:text-gray-100">{truck.name}</CardTitle>
          {(truck.current_location?.address != undefined) && (
            <CardDescription className="flex items-center mt-1 dark:text-gray-400">
              <MapPin className="h-4 w-4 mr-1" />
              {truck.current_location.address}
            </CardDescription>
          )}
        </div>
        <div className="flex flex-col items-end space-y-1">
          <Badge variant={isOpen ? 'default' : 'secondary'}>{isOpen ? 'Open' : 'Closed'}</Badge>
          {/* Show price range fallback if no explicit prices */}
          {popularItems.every((item) => item.price == null || item.price === 0 || (typeof item.price === 'string' && item.price.trim() === '')) && (priceRange != null && priceRange !== '') && (
            <Badge variant="outline" className="mt-1">
              {priceRange}
            </Badge>
          )}
        </div>
      </div>
    </CardHeader>
  );
}

import React from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Phone, Mail, Globe } from 'lucide-react';
import type { FoodTruck } from '@/lib/supabase';

interface TruckContactInfoProps {
  readonly truck: FoodTruck;
}

interface ContactFieldProps {
  readonly icon: React.ComponentType<{ className?: string }>;
  readonly label: string;
  readonly value?: string | null;
  readonly href?: string;
  readonly unavailableText: string;
}

function ContactField({ 
  icon: Icon, 
  label, 
  value, 
  href, 
  unavailableText 
}: Readonly<ContactFieldProps>) {
  if (value == undefined || value.length === 0) { // Handles null, undefined, and empty string
    return (
      <div className="flex items-center gap-3 text-gray-400">
        <Icon className="h-4 w-4" />
        <span className="text-sm">{unavailableText}</span>
      </div>
    );
  }

  return (
    <div className="flex items-center gap-3">
      <Icon className="h-4 w-4 text-gray-500" />
      <div>
        <label className="text-sm font-medium text-gray-500 dark:text-gray-400">{label}</label>
        {(href != null && href !== '') ? (
          <a 
            href={href}
            target={href.startsWith('http') ? '_blank' : undefined}
            rel={href.startsWith('http') ? 'noopener noreferrer' : undefined}
            className="block text-blue-600 hover:text-blue-800 dark:text-blue-400 dark:hover:text-blue-300 underline"
          >
            {href.startsWith('http') ? 'Visit Website' : value}
          </a>
        ) : (
          <p className="text-gray-900 dark:text-gray-200">{value}</p>
        )}
      </div>
    </div>
  );
}

function SocialMediaLinks({ socialMedia }: Readonly<{ readonly socialMedia?: Record<string, string> }>) {
  if (socialMedia == undefined || Object.keys(socialMedia).length === 0) {
    return;
  }

  const socialPlatforms = [
    { key: 'instagram', name: 'Instagram', baseUrl: 'https://instagram.com/', color: 'pink' },
    { key: 'facebook', name: 'Facebook', baseUrl: 'https://facebook.com/', color: 'blue' },
    { key: 'twitter', name: 'Twitter', baseUrl: 'https://twitter.com/', color: 'sky' },
  ];

  return (
    <div>
      <label className="text-sm font-medium text-gray-500 dark:text-gray-400">Social Media</label>
      <div className="flex flex-wrap gap-2 mt-2">
        {socialPlatforms.map(({ key, name, baseUrl, color }) => {
          const handle = socialMedia[key];
          if (handle == undefined || handle.length === 0) return; // Handles null, undefined, and empty string

          return (
            <a
              key={key}
              href={`${baseUrl}${handle}`}
              target="_blank"
              rel="noopener noreferrer"
              className={`flex items-center gap-1 px-2 py-1 bg-${color}-100 text-${color}-800 rounded-md text-sm hover:bg-${color}-200 dark:bg-${color}-900 dark:text-${color}-200`}
            >
              <Globe className="h-3 w-3" />
              {name}
            </a>
          );
        })}
      </div>
    </div>
  );
}

export function TruckContactInfo({ truck }: Readonly<TruckContactInfoProps>) {
  return (
    <Card className="dark:bg-slate-800 dark:border-slate-700">
      <CardHeader>
        <CardTitle className="flex items-center gap-2 dark:text-gray-100">
          <Phone className="h-5 w-5" />
          Contact Information
        </CardTitle>
        <CardDescription className="dark:text-gray-400">Get in touch</CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <ContactField
          icon={Phone}
          label="Phone"
          value={truck.contact_info?.phone}
          href={(truck.contact_info?.phone != null && truck.contact_info.phone !== '') ? `tel:${truck.contact_info.phone}` : undefined}
          unavailableText="No phone number available"
        />

        <ContactField
          icon={Mail}
          label="Email"
          value={truck.contact_info?.email}
          href={(truck.contact_info?.email != null && truck.contact_info.email !== '') ? `mailto:${truck.contact_info.email}` : undefined}
          unavailableText="No email available"
        />

        <ContactField
          icon={Globe}
          label="Website"
          value={truck.contact_info?.website}
          href={(truck.contact_info?.website != null && truck.contact_info.website !== '') ? truck.contact_info.website : undefined}
          unavailableText="No website available"
        />

        <SocialMediaLinks socialMedia={truck.social_media} />
      </CardContent>
    </Card>
  );
}

import React from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Clock } from 'lucide-react';
import type { FoodTruck } from '@/lib/supabase';

interface TruckOperatingHoursProps {
  readonly truck: FoodTruck;
}

interface DayData {
  closed?: boolean;
  open?: string;
  close?: string;
}

function DaySchedule({ day, dayData }: Readonly<{ day: string; dayData?: DayData }>) {
  const dayName = day.charAt(0).toUpperCase() + day.slice(1);

  return (
    <div className="flex justify-between items-center">
      <span className="text-sm font-medium dark:text-gray-200">{dayName}</span>
      <span className="text-sm text-gray-600 dark:text-gray-400">
        {dayData?.closed === true ? 'Closed' : `${dayData?.open ?? 'N/A'} - ${dayData?.close ?? 'N/A'}`}
      </span>
    </div>
  );
}

export function TruckOperatingHours({ truck }: TruckOperatingHoursProps) {
  const daysOfWeek = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'];
  const hasOperatingHours = truck.operating_hours && Object.keys(truck.operating_hours).length > 0;

  return (
    <Card className="dark:bg-slate-800 dark:border-slate-700">
      <CardHeader>
        <CardTitle className="flex items-center gap-2 dark:text-gray-100">
          <Clock className="h-5 w-5" />
          Operating Hours
        </CardTitle>
        <CardDescription className="dark:text-gray-400">Daily schedule</CardDescription>
      </CardHeader>
      <CardContent>
        {hasOperatingHours === true ? (
          <div className="space-y-2">
            {daysOfWeek.map((day) => {
              const dayData = truck.operating_hours?.[day as keyof typeof truck.operating_hours] as DayData | undefined;
              return (
                <DaySchedule key={day} day={day} dayData={dayData} />
              );
            })}
          </div>
        ) : (
          <p className="text-gray-400 text-sm">Operating hours not available</p>
        )}
      </CardContent>
    </Card>
  );
}

'use client';

import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { RefreshCw, Settings, Loader2 } from 'lucide-react';

interface SimpleQualityPanelProps {
  readonly onRefresh?: () => void;
}

interface RecalculateAllResult {
  success: boolean;
  data?: {
    updated: number;
    errors: number;
  };
  error?: string;
}

const recalculateAllScores = async (): Promise<RecalculateAllResult> => {
  const response = await fetch('/api/admin/data-quality', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
      action: 'recalculate_all',
    }),
  });

  const result: unknown = await response.json();

  if (typeof result === 'object' && result !== null && 'success' in result) {
    return result.success === true ? {
        success: true,
        data: {
          updated: (result as { data?: { updated?: number } }).data?.updated ?? 0,
          errors: (result as { data?: { errors?: number } }).data?.errors ?? 0,
        },
      } : {
        success: false,
        error: (result as { error?: string }).error ?? 'Failed to recalculate quality scores',
      };
  }
  return { success: false, error: 'Invalid response format' };
};

const QualityScoreThresholds: React.FC = () => (
  <div className="border rounded-lg p-4 bg-blue-50/50">
    <h4 className="font-medium mb-2 text-blue-900">Quality Score Thresholds</h4>
    <div className="space-y-1 text-sm text-blue-800">
      <div className="flex justify-between">
        <span>High Quality:</span>
        <Badge className="bg-green-100 text-green-800"> 80%</Badge>
      </div>
      <div className="flex justify-between">
        <span>Medium Quality:</span>
        <Badge className="bg-yellow-100 text-yellow-800">60% - 79%</Badge>
      </div>
      <div className="flex justify-between">
        <span>Low Quality:</span>
        <Badge className="bg-red-100 text-red-800">{`< 60%`}</Badge>
      </div>
    </div>
  </div>
);

export const SimpleQualityPanel: React.FC<SimpleQualityPanelProps> = ({ onRefresh }) => {
  const [isRecalculating, setIsRecalculating] = useState(false);

  const handleRecalculateAll = async () => {
    setIsRecalculating(true);

    try {
      const result = await recalculateAllScores();
      if (result.success) {
        alert(`Quality scores updated successfully! ${result.data?.updated} trucks updated, ${result.data?.errors} errors.`);
        onRefresh?.();
      } else {
        throw new Error(result.error);
      }
    } catch (error) {
      console.error('Error recalculating quality scores:', error);
      alert('Failed to recalculate quality scores. Please try again.');
    } finally {
      setIsRecalculating(false);
    }
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Settings className="h-5 w-5" />
          Quality Management Operations
        </CardTitle>
        <CardDescription>
          Bulk operations for managing data quality scores across all food trucks
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <Button
            onClick={handleRecalculateAll}
            disabled={isRecalculating}
            className="flex items-center gap-2"
            variant="default"
          >
            {isRecalculating ? (
              <Loader2 className="h-4 w-4 animate-spin" />
            ) : (
              <RefreshCw className="h-4 w-4" />
            )}
            Recalculate All Scores
          </Button>
        </div>

        <QualityScoreThresholds />

        <div className="text-xs text-muted-foreground">
          <p>
            <strong>Recalculate All:</strong> Updates quality scores for all food trucks using the latest algorithm.
          </p>
        </div>
      </CardContent>
    </Card>
  );
};

export default SimpleQualityPanel;

'use client';

import * as React from 'react';
import * as RechartsPrimitive from 'recharts';
import {
  NameType,
  Payload,
  ValueType,
} from 'recharts/types/component/DefaultTooltipContent';

import { cn } from '@/lib/utils';
import { useTooltipLabel } from './chart/useTooltipLabel';
import { TooltipIndicator } from './chart/TooltipIndicator';
import { TooltipItemContent } from './chart/TooltipItemContent';
import { getPayloadConfigFromPayload } from './chart/getPayloadConfigFromPayload';

// Format: { THEME_NAME: CSS_SELECTOR }
const THEMES = { light: '', dark: '.dark' } as const;

export type ChartConfig = {
  [k in string]: {
    label?: React.ReactNode;
    icon?: React.ComponentType;
  } & (
    | { color?: string; theme?: never }
    | { color?: never; theme: Record<keyof typeof THEMES, string> }
  );
};

type ChartContextProps = {
  config: ChartConfig;
};

const ChartContext = React.createContext<ChartContextProps | undefined>(undefined);

function useChart() {
  const context = React.useContext(ChartContext);
  if (context === undefined) {
    throw new Error('useChart must be used within a <ChartContainer />');
  }
  return context;
}

const ChartContainer = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<'div'> & {
    readonly config: ChartConfig;
    readonly children: React.ComponentProps<typeof RechartsPrimitive.ResponsiveContainer>['children'];
  }
>(({ id, className, children, config, ...props }, ref) => {
  const uniqueId = React.useId();
  const chartId = `chart-${id ?? uniqueId.replaceAll(':', '')}`;

  return (
    <ChartContext.Provider value={{ config }}>
      <div
        data-chart={chartId}
        ref={ref}
        className={cn(
          "flex aspect-video justify-center text-xs [&_.recharts-cartesian-axis-tick_text]:fill-muted-foreground [&_.recharts-cartesian-grid_line[stroke='#ccc']]:stroke-border/50 [&_.recharts-curve.recharts-tooltip-cursor]:stroke-border [&_.recharts-dot[stroke='#fff']]:stroke-transparent [&_.recharts-layer]:outline-none [&_.recharts-polar-grid_[stroke='#ccc']]:stroke-border [&_.recharts-radial-bar-background-sector]:fill-muted [&_.recharts-rectangle.recharts-tooltip-cursor]:fill-muted [&_.recharts-reference-line_[stroke='#ccc']]:stroke-border [&_.recharts-sector[stroke='#fff']]:stroke-transparent [&_.recharts-sector]:outline-none [&_.recharts-surface]:outline-none",
          className,
        )}
        {...props}
      >
        <ChartStyle id={chartId} config={config} />
        <RechartsPrimitive.ResponsiveContainer>{children}</RechartsPrimitive.ResponsiveContainer>
      </div>
    </ChartContext.Provider>
  );
});
ChartContainer.displayName = 'Chart';

const ChartStyle = ({ id, config }: { readonly id: string; readonly config: ChartConfig }) => {
  const colorConfig = Object.entries(config).filter(([_, itemConfig]) => (itemConfig.theme ?? itemConfig.color) !== undefined);
  if (colorConfig.length === 0) {
    return;
  }

  return (
    <style
      dangerouslySetInnerHTML={{
        __html: Object.entries(THEMES)
          .map(
            ([theme, prefix]) => `
${prefix} [data-chart=${id}] {
${colorConfig
  .map(([key, itemConfig]) => {
    const color = itemConfig.theme?.[theme as keyof typeof itemConfig.theme] ?? itemConfig.color;
    return color !== undefined && color !== '' ? `  --color-${key}: ${color};` : undefined;
  })
  .join('\n')}
}
`,
          )
          .join('\n'),
      }}
    />
  );
};

const ChartTooltip = RechartsPrimitive.Tooltip;

interface ChartTooltipContentProps {
  readonly hideLabel?: boolean;
  readonly hideIndicator?: boolean;
  readonly indicator?: 'line' | 'dot' | 'dashed';
  readonly nameKey?: string;
  readonly labelKey?: string;
  // We can add any other specific props for ChartTooltipContent here
  // and ensure they are distinct from RechartsPrimitive.Tooltip props
  // and standard div props to avoid conflicts.
}

function isNonEmptyArray<T>(arr: T[] | undefined): arr is T[] {
  return Array.isArray(arr) && arr.length > 0;
}

type TooltipFormatter = (value: number, name: string, item: unknown, index: number, payload: Record<string, unknown>[]) => React.ReactNode;
type TooltipItemData = { name?: string; dataKey?: string; payload?: Record<string, unknown>; color?: string; value?: number };


type ChartTooltipIndicatorAndContentProps = {
  indicator: 'line' | 'dot' | 'dashed';
  hideIndicator: boolean;
