// NOTE: React.MutableRefObject is flagged as deprecated by some linters, but is still the standard type for refs created by useRef in React 18+.
// See: https://github.com/DefinitelyTyped/DefinitelyTyped/issues/66808

interface DisconnectEventSourceParams {
  eventSourceRef: React.RefObject<EventSource | undefined>;
  reconnectTimeoutRef: React.RefObject<NodeJS.Timeout | undefined>;
  isManuallyDisconnectedRef: React.RefObject<boolean>;
  setIsConnected: (connected: boolean) => void;
  setIsConnecting: (connecting: boolean) => void;
  setConnectionError: (error: string | undefined) => void;
}

export function disconnectEventSource({
  eventSourceRef,
  reconnectTimeoutRef,
  isManuallyDisconnectedRef,
  setIsConnected,
  setIsConnecting,
  setConnectionError
}: DisconnectEventSourceParams) {
  (isManuallyDisconnectedRef.current as boolean) = true;

  if (reconnectTimeoutRef.current) {
    clearTimeout(reconnectTimeoutRef.current);
    (reconnectTimeoutRef.current as NodeJS.Timeout | undefined) = undefined;
  }

  if (eventSourceRef.current) {
    eventSourceRef.current.close();
    (eventSourceRef.current as EventSource | undefined) = undefined;
  }

  setIsConnected(false);
  setIsConnecting(false);
  setConnectionError(undefined);
}

export function clearRecentEvents(
  setRecentEvents: (events: RealtimeEvent[]) => void
) {
  setRecentEvents([]);
}

import React from 'react';
import { RealtimeEvent } from '../useRealtimeAdminEvents.types';
import { useConnectionState } from './useConnectionState';

interface ConnectionStateActions {
  setIsConnected: (connected: boolean) => void;
  setIsConnecting: (connecting: boolean) => void;
  setConnectionError: (error: string | undefined) => void;
  setConnectionAttempts: (attempts: number | ((prev: number) => number)) => void;
}

interface EventSourceRefs {
  // eslint-disable-next-line sonarjs/deprecation -- MutableRefObject is used intentionally here for mutable ref values.
  isManuallyDisconnectedRef: React.MutableRefObject<boolean | null>;
  // eslint-disable-next-line sonarjs/deprecation -- MutableRefObject is used intentionally here for mutable ref values.
  reconnectTimeoutRef: React.MutableRefObject<NodeJS.Timeout | undefined | null>;
}

interface ReconnectLogicParams extends EventSourceRefs {
  connectionAttempts: number;
  maxReconnectAttempts: number;
  reconnectInterval: number;
  connect: () => void;
  setConnectionAttempts: (attempts: number | ((prev: number) => number)) => void;
  setConnectionError: (error: string | undefined) => void;
}

const handleOpen = (connectionState: ConnectionStateActions) => () => {
  console.info('Real-time admin events connected');
  connectionState.setIsConnected(true);
  connectionState.setIsConnecting(false);
  connectionState.setConnectionError(undefined);
  connectionState.setConnectionAttempts(0);
};

const handleMessage = (handleEvent: (event: RealtimeEvent) => void, setConnectionError: (error: string | undefined) => void) => (event: MessageEvent) => {
  try {
    const parsedData = JSON.parse(event.data as string);
    // Add a basic check for type property, assuming it's a key indicator of RealtimeEvent
    if (typeof parsedData === 'object' && parsedData !== null && 'type' in parsedData) {
      const adminEvent: RealtimeEvent = parsedData as RealtimeEvent;
      handleEvent(adminEvent);
    } else {
      console.error('Parsed event data does not match RealtimeEvent structure:', parsedData);
      setConnectionError('Received malformed event data');
    }
  } catch (error) {
    console.error('Error parsing event data:', error);
    setConnectionError('Error parsing event data');
  }
};

const handleReconnectLogic = (refs: EventSourceRefs, reconnectParams: ReconnectLogicParams) => {
  if (refs.isManuallyDisconnectedRef.current !== true && reconnectParams.connectionAttempts < reconnectParams.maxReconnectAttempts) {
    reconnectParams.setConnectionAttempts((prev) => prev + 1);

    if (refs.reconnectTimeoutRef.current !== undefined) { // Check if undefined before assigning
      refs.reconnectTimeoutRef.current = setTimeout(() => {
        if (refs.isManuallyDisconnectedRef.current !== true) {
          reconnectParams.connect();
        }
      }, reconnectParams.reconnectInterval);
    }
  } else if (reconnectParams.connectionAttempts >= reconnectParams.maxReconnectAttempts) {
    reconnectParams.setConnectionError('Max reconnection attempts reached');
  }
};

const handleError = (connectionState: ConnectionStateActions, refs: EventSourceRefs, reconnectParams: ReconnectLogicParams) => (error: Event) => {
  console.error('Real-time admin events error:', error);
  connectionState.setIsConnected(false);
  connectionState.setIsConnecting(false);
  connectionState.setConnectionError('Connection error occurred');

  handleReconnectLogic(refs, reconnectParams);
};

interface SetupEventSourceListenersParams {
  eventSource: EventSource;
  handleEvent: (event: RealtimeEvent) => void;
  connectionState: ReturnType<typeof useConnectionState>;
  // eslint-disable-next-line sonarjs/deprecation -- MutableRefObject is used intentionally here for mutable ref values.
  isManuallyDisconnectedRef: React.MutableRefObject<boolean | null>;
  connectionAttempts: number;
  maxReconnectAttempts: number;
  reconnectInterval: number;
  // eslint-disable-next-line sonarjs/deprecation -- MutableRefObject is used intentionally here for mutable ref values.
  reconnectTimeoutRef: React.MutableRefObject<NodeJS.Timeout | undefined | null>;
  connect: () => void;
}

function createReconnectLogicParams(
  params: Omit<SetupEventSourceListenersParams, 'eventSource' | 'handleEvent' | 'connectionState'>,
  connectionState: ReturnType<typeof useConnectionState>
): ReconnectLogicParams {
  return {
    ...params,
    setConnectionAttempts: connectionState.setConnectionAttempts,
    setConnectionError: connectionState.setConnectionError,
  };
}

function addEventListeners(
  eventSource: EventSource,
  params: SetupEventSourceListenersParams,
  connectionActions: ConnectionStateActions,
  reconnectLogicParams: ReconnectLogicParams
) {
  const { handleEvent, isManuallyDisconnectedRef, reconnectTimeoutRef } = params;
  const refs: EventSourceRefs = { isManuallyDisconnectedRef, reconnectTimeoutRef };

  eventSource.addEventListener('open', handleOpen(connectionActions));
  eventSource.addEventListener('message', handleMessage(handleEvent, connectionActions.setConnectionError));
  eventSource.addEventListener('error', handleError(connectionActions, refs, reconnectLogicParams));
}

export function setupEventSourceListeners(params: SetupEventSourceListenersParams) {
  const { eventSource, connectionState } = params;
  const { setIsConnected, setIsConnecting, setConnectionError, setConnectionAttempts } = connectionState;
  const connectionActions: ConnectionStateActions = { setIsConnected, setIsConnecting, setConnectionError, setConnectionAttempts };
  const reconnectLogicParams = createReconnectLogicParams(params, connectionState);

  addEventListeners(eventSource, params, connectionActions, reconnectLogicParams);
}

import { useEffect, useRef } from 'react';
import { RealtimeEvent, RealtimeMetrics } from '../useRealtimeAdminEvents.types';
import { useConnectionState } from './useConnectionState';
import { useEventHandlers } from './useEventHandlers';
import { useConnectionManagement } from './useConnectionManagement';
import { useAutoConnect } from './useAutoConnect';

interface UseRealtimeAdminEventsOptions {
  autoConnect?: boolean;
  reconnectInterval?: number;
  maxReconnectAttempts?: number;
  eventFilter?: (event: RealtimeEvent) => boolean;
}

interface UseRealtimeAdminEventsReturn {
  // Connection state
  isConnected: boolean;
  isConnecting: boolean;
  connectionError: string | undefined;

  // Data
  latestMetrics: RealtimeMetrics | undefined;
  recentEvents: RealtimeEvent[];

  // Controls
  connect: () => void;
  disconnect: () => void;
  clearEvents: () => void;

  // Statistics
  connectionAttempts: number;
  lastEventTime: Date | undefined;
}

// eslint-disable-next-line max-lines-per-function -- This hook orchestrates multiple other hooks and manages their state/dependencies.
export function useRealtimeAdminEventsLogic(
  options: UseRealtimeAdminEventsOptions = {}
): UseRealtimeAdminEventsReturn {
  const {
    autoConnect = true,
    reconnectInterval = 5000,
    maxReconnectAttempts = 10,
    eventFilter
  } = options;

  // State management
  const connectionState = useConnectionState();
  const {
    isConnected,
    isConnecting,
    connectionError,
    latestMetrics,
    recentEvents,
    connectionAttempts,
    lastEventTime
  } = connectionState;

  // Refs
  const eventSourceRef = useRef<EventSource | undefined>(undefined);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout | undefined>(undefined);
  const isManuallyDisconnectedRef = useRef(false);

  // Event handlers
  const handleEvent = useEventHandlers(
    eventFilter,
    connectionState.setLastEventTime,
    connectionState.setLatestMetrics,
    connectionState.setRecentEvents
  );

  // Connection management
  const { connect, disconnect, clearEvents } = useConnectionManagement(
    eventSourceRef,
    reconnectTimeoutRef,
    isManuallyDisconnectedRef,
    connectionState,
    handleEvent,
    connectionAttempts,
    maxReconnectAttempts,
    reconnectInterval,
    isConnecting
  );

  // Auto-connect on mount
  useAutoConnect(autoConnect, connect, disconnect);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
      }
      if (eventSourceRef.current) {
        eventSourceRef.current.close();
      }
    };
  }, []);

  return {
    // Connection state
    isConnected,
    isConnecting,
    connectionError,

    // Data
    latestMetrics,
    recentEvents,

    // Controls
    connect,
    disconnect,
    clearEvents,

    // Statistics
    connectionAttempts,
    lastEventTime
  };
}

import { useState, useCallback } from 'react';
import { supabase } from '@/lib/supabase';
import { useRouter } from 'next/navigation';

interface UseAuthHandlersReturn {
  handleEmailLogin: (e: React.FormEvent) => Promise<void>;
  handleGoogleLogin: () => Promise<void>;
  loading: boolean;
  error: string | undefined;
  email: string;
  setEmail: (email: string) => void;
  password: string;
  setPassword: (password: string) => void;
}

export function useAuthHandlers(redirectTo: string): UseAuthHandlersReturn {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | undefined>();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const router = useRouter();

  const performEmailLogin = async (currentEmail: string, currentPassword: string) => {
    const { error: signInError } = await supabase.auth.signInWithPassword({
      email: currentEmail,
      password: currentPassword,
    });
    if (signInError) throw signInError;

    const { data: { user }, error: userError } = await supabase.auth.getUser();
    if (userError) throw userError;

    if (user) {
      const { data: profile, error: profileError } = await supabase
        .from('profiles')
        .select('role')
        .eq('id', user.id)
        .single();
      if (profileError) throw profileError;

      if (profile?.role === 'admin') {
        router.push(redirectTo);
      } else {
        router.push('/access-denied');
      }
    }
  };

  const handleEmailLogin = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError(undefined);
    try {
      await performEmailLogin(email, password);
    } catch (error_: unknown) {
      console.error('Email login error:', error_);
      setError(error_ instanceof Error ? error_.message : 'An error occurred during email login');
    } finally {
      setLoading(false);
    }
  }, [email, password, router, redirectTo]); // eslint-disable-line react-hooks/exhaustive-deps 
  // router, redirectTo are stable, email/password are dependencies for performEmailLogin call.

  const performGoogleLogin = async () => {
    const { error: signInError } = await supabase.auth.signInWithOAuth({
      provider: 'google',
      options: {
        redirectTo: `${globalThis.location.origin}/auth/callback?redirectTo=${encodeURIComponent(redirectTo)}`,
      },
    });
    if (signInError) throw signInError;
  };

  const handleGoogleLogin = useCallback(async () => {
    setLoading(true);
    setError(undefined);
    try {
      await performGoogleLogin();
    } catch (error_: unknown) {
      console.error('Google login error:', error_);
      setError(error_ instanceof Error ? error_.message : 'An error occurred during Google login');
    } finally {
      setLoading(false);
    }
  }, [router, redirectTo]); // eslint-disable-line react-hooks/exhaustive-deps
  // router, redirectTo are stable.

  return {
    handleEmailLogin,
    handleGoogleLogin,
    loading,
    error,
    email,
    setEmail,
    password,
    setPassword,
  };
}

'use client';

import { useState } from 'react';

export interface CleanupOperation {
  type: string;
  description: string;
  affectedCount: number;
  successCount: number;
  errorCount: number;
  errors: string[];
}

export interface CleanupResult {
  totalProcessed: number;
  operations: CleanupOperation[];
  summary: {
    trucksImproved: number;
    duplicatesRemoved: number;
    qualityScoreImprovement: number;
    placeholdersRemoved: number;
  };
  duration: number;
}

export function useDataCleanup() {
  const [isRunning, setIsRunning] = useState(false);
  const [lastResult, setLastResult] = useState<CleanupResult | undefined>();
  const [previewData, setPreviewData] = useState<unknown>();
  const [selectedOperations, setSelectedOperations] = useState<string[]>([
    'remove_placeholders',
    'normalize_phone',
    'fix_coordinates',
    'update_quality_scores'
  ]);

  const performCleanupApiCall = async (dryRun: boolean, operations: string[]): Promise<{ success: boolean; result?: CleanupResult; error?: string }> => {
    const response = await fetch('/api/admin/data-cleanup', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        action: dryRun ? 'dry-run' : 'full-cleanup',
        options: {
          operations: operations,
          batchSize: 50,
          dryRun
        }
      })
    });
    return response.json() as Promise<{ success: boolean; result?: CleanupResult; error?: string }>;
  };

  const runCleanup = async (dryRun: boolean = false) => {
    setIsRunning(true);
    try {
      const data = await performCleanupApiCall(dryRun, selectedOperations);
      if (data.success === true) {
        setLastResult(data.result);
      } else {
        console.error('Cleanup failed:', data.error);
      }
    } catch (error) {
      console.error('Error running cleanup:', error);
    } finally {
      setIsRunning(false);
    }
  };

  const performLoadPreviewApiCall = async (): Promise<{ success: boolean; preview?: unknown }> => {
    const response = await fetch('/api/admin/data-cleanup?action=preview');
    return response.json() as Promise<{ success: boolean; preview?: unknown }>;
  };

  const loadPreview = async () => {
    try {
      const data = await performLoadPreviewApiCall();
      if (data.success === true) {
        setPreviewData(data.preview);
      } else {
        // It's good practice to also handle the case where data.success is false from the API
        console.error('Failed to load preview data:', (data as { error?: string }).error);
      }
    } catch (error) {
      console.error('Error loading preview:', error);
    }
  };

  const toggleOperation = (operation: string) => {
    setSelectedOperations(prev => 
      prev.includes(operation)
        ? prev.filter(op => op !== operation)
        : [...prev, operation]
    );
  };

  return {
    isRunning,
    lastResult,
    previewData,
    selectedOperations,
    runCleanup,
    loadPreview,
    toggleOperation
  };
}

