      return handleUpdateSingle(truckId);
    }
    case 'batch-update': {
      return await handleBatchUpdate();
    }
    case 'recalculate-all': {
      return await handleRecalculateAll();
    }
    default: {
      return NextResponse.json({ success: false, error: `Unknown action: ${action}` }, { status: 400 });
    }
  }
}

