import React from 'react';
// Removed Mail, Phone, Globe imports as they are not directly used here

interface ContactFieldProps {
  readonly icon: React.ComponentType<{ className?: string }>;
  readonly label: string;
  readonly value?: string;
  readonly href?: string;
  readonly unavailableText: string;
}

export function ContactField({
  icon: Icon,
  label,
  value,
  href,
  unavailableText
}: Readonly<ContactFieldProps>) {
  if (value == undefined || value.length === 0) {
    return (
      <div className="flex items-center gap-3 text-gray-400">
        <Icon className="h-4 w-4" />
        <span className="text-sm">{unavailableText}</span>
      </div>
    );
  }

  return (
    <div className="flex items-center gap-3">
      <Icon className="h-4 w-4 text-gray-500" />
      <div>
        <label className="text-sm font-medium text-gray-500">{label}</label>
        {href ? (
          <a
            href={href}
            target="_blank"
            rel="noopener noreferrer"
            className="text-blue-600 hover:text-blue-800 underline"
          >
            {value}
          </a>
        ) : (
          <p className="text-gray-900">{value}</p>
        )}
      </div>
    </div>
  );
}

import React from 'react';
import {
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from '@/components/ui/accordion';
import { Badge } from '@/components/ui/badge';
import { TruckCard } from '@/components/TruckCard';
import { FoodTruck } from '@/lib/types/foodTruck';

interface TruckAccordionItemProps {
  readonly truck: FoodTruck;
  readonly selectedTruckId: string | undefined;
  readonly setSelectedTruckId: (id: string | undefined) => void;
  readonly isOpen: (truck: FoodTruck) => boolean;
  readonly userLocation: { lat: number; lng: number } | undefined;
}

export function TruckAccordionItem({
  truck,
  selectedTruckId,
  setSelectedTruckId,
  isOpen,
  userLocation,
}: TruckAccordionItemProps) {
  return (
    <AccordionItem value={truck.id} key={truck.id}>
      <AccordionTrigger className="flex items-center justify-between p-4 hover:bg-gray-50 dark:hover:bg-slate-700 rounded-md">
        <div className="flex-1 text-left">
          <h4 className="font-medium dark:text-gray-100">{truck.name}</h4>
          {truck.current_location?.address && (
            <p className="text-sm text-gray-600 dark:text-gray-400 line-clamp-1">
              {truck.current_location.address}
            </p>
          )}
        </div>
        <Badge variant={isOpen(truck) ? 'default' : 'secondary'}>
          {isOpen(truck) ? 'Open' : 'Closed'}
        </Badge>
      </AccordionTrigger>
      <AccordionContent>
        <TruckCard
          truck={truck}
          isOpen={isOpen(truck)}
          onSelectTruck={() => setSelectedTruckId(truck.id)}
          userLocation={userLocation}
          hideHeader={true}
        />
      </AccordionContent>
    </AccordionItem>
  );
}

import React from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import type { FoodTruck } from '@/lib/supabase';

interface TruckBasicInfoProps {
  readonly truck: FoodTruck;
}

export function TruckBasicInfo({ truck }: Readonly<TruckBasicInfoProps>) {
  return (
    <Card className="dark:bg-slate-800 dark:border-slate-700">
      <CardHeader>
        <CardTitle className="dark:text-gray-100">About</CardTitle>
        <CardDescription className="dark:text-gray-400">Food truck information</CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <div>
          <label className="text-sm font-medium text-gray-500 dark:text-gray-400">Name</label>
          <p className="text-lg font-semibold dark:text-gray-100">{truck.name}</p>
        </div>
        
        {truck.description != undefined && (
          <div>
            <label className="text-sm font-medium text-gray-500 dark:text-gray-400">Description</label>
            <p className="text-gray-900 dark:text-gray-200">{truck.description}</p>
          </div>
        )}

        {truck.cuisine_type && (
          <div>
            <label className="text-sm font-medium text-gray-500 dark:text-gray-400">Cuisine Type</label>
            <p className="text-gray-900 dark:text-gray-200">{truck.cuisine_type}</p>
          </div>
        )}

        {truck.price_range && (
          <div>
            <label className="text-sm font-medium text-gray-500 dark:text-gray-400">Price Range</label>
            <Badge variant="outline" className="ml-2">
              {truck.price_range}
            </Badge>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

import React from 'react';
import { Badge } from '@/components/ui/badge';
import { FoodTruck, DailyOperatingHours, MenuItem } from '@/lib/types';
import { RatingSection } from './RatingSection';
import { MenuSection } from './MenuSection';
import { ContactSection } from './ContactSection';
import { SocialMediaSection } from './SocialMediaSection';
import { OperatingHoursSection } from './OperatingHoursSection';
import { formatHours } from '@/lib/utils/foodTruckHelpers';

interface TruckCardContentProps {
  readonly truck: FoodTruck;
  readonly todayHours?: DailyOperatingHours;
  readonly popularItems: MenuItem[]; // Use the full MenuItem type
}

export function TruckCardContent({
  truck,
  todayHours,
  popularItems,
}: TruckCardContentProps) {
  return (
    <>
      {truck.description && (
        <p className="text-gray-600 dark:text-gray-400 mb-4 line-clamp-2">{truck.description}</p>
      )}
      <div className="space-y-4">
        {/* Ratings & Hours Row */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <RatingSection averageRating={truck.average_rating} reviewCount={truck.review_count} />
          <OperatingHoursSection todayHours={todayHours} formatHours={formatHours} />
        </div>

        {/* Menu & Contact Row */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <MenuSection popularItems={popularItems} />
          <ContactSection contactInfo={truck.contact_info} verificationStatus={truck.verification_status} />
        </div>

        <SocialMediaSection socialMedia={truck.social_media} />
      </div>
      {truck.verification_status && (
        <div className="mt-2">
          <Badge variant={truck.verification_status === 'verified' ? 'default' : 'secondary'}>
            <span className="capitalize">{truck.verification_status}</span>
          </Badge>
        </div>
      )}
    </>
  );
}

import React from 'react';
import { CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { MapPin } from 'lucide-react';
import { FoodTruck } from '@/lib/types';

interface TruckCardHeaderProps {
  readonly truck: FoodTruck;
  readonly isOpen: boolean;
  readonly popularItems: Array<{ name: string; price: number | string | undefined }>;
  readonly priceRange: string | undefined;
}

export function TruckCardHeader({
  truck,
  isOpen,
  popularItems,
  priceRange
}: TruckCardHeaderProps) {
  return (
    <CardHeader>
      <div className="flex justify-between items-start">
        <div className="flex-1">
          <CardTitle className="text-lg dark:text-gray-100">{truck.name}</CardTitle>
          {(truck.current_location?.address != undefined) && (
            <CardDescription className="flex items-center mt-1 dark:text-gray-400">
              <MapPin className="h-4 w-4 mr-1" />
              {truck.current_location.address}
            </CardDescription>
          )}
        </div>
        <div className="flex flex-col items-end space-y-1">
          <Badge variant={isOpen ? 'default' : 'secondary'}>{isOpen ? 'Open' : 'Closed'}</Badge>
          {/* Show price range fallback if no explicit prices */}
          {popularItems.every((item) => !item.price) && priceRange && (
            <Badge variant="outline" className="mt-1">
              {priceRange}
            </Badge>
          )}
        </div>
      </div>
    </CardHeader>
  );
}

import React from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Phone, Mail, Globe } from 'lucide-react';
import type { FoodTruck } from '@/lib/supabase';

interface TruckContactInfoProps {
  readonly truck: FoodTruck;
}

interface ContactFieldProps {
  readonly icon: React.ComponentType<{ className?: string }>;
  readonly label: string;
  readonly value?: string | null;
  readonly href?: string;
  readonly unavailableText: string;
}

function ContactField({ 
  icon: Icon, 
  label, 
  value, 
  href, 
  unavailableText 
}: Readonly<ContactFieldProps>) {
  if (value == undefined || value.length === 0) { // Handles null, undefined, and empty string
    return (
      <div className="flex items-center gap-3 text-gray-400">
        <Icon className="h-4 w-4" />
        <span className="text-sm">{unavailableText}</span>
      </div>
    );
  }

  return (
    <div className="flex items-center gap-3">
      <Icon className="h-4 w-4 text-gray-500" />
      <div>
        <label className="text-sm font-medium text-gray-500 dark:text-gray-400">{label}</label>
        {href ? (
          <a 
            href={href}
            target={href.startsWith('http') ? '_blank' : undefined}
            rel={href.startsWith('http') ? 'noopener noreferrer' : undefined}
            className="block text-blue-600 hover:text-blue-800 dark:text-blue-400 dark:hover:text-blue-300 underline"
          >
            {href.startsWith('http') ? 'Visit Website' : value}
          </a>
        ) : (
          <p className="text-gray-900 dark:text-gray-200">{value}</p>
        )}
      </div>
    </div>
  );
}

function SocialMediaLinks({ socialMedia }: Readonly<{ readonly socialMedia?: Record<string, string> }>) {
  if (socialMedia == undefined || Object.keys(socialMedia).length === 0) {
    return;
  }

  const socialPlatforms = [
    { key: 'instagram', name: 'Instagram', baseUrl: 'https://instagram.com/', color: 'pink' },
    { key: 'facebook', name: 'Facebook', baseUrl: 'https://facebook.com/', color: 'blue' },
    { key: 'twitter', name: 'Twitter', baseUrl: 'https://twitter.com/', color: 'sky' },
  ];

  return (
    <div>
      <label className="text-sm font-medium text-gray-500 dark:text-gray-400">Social Media</label>
      <div className="flex flex-wrap gap-2 mt-2">
        {socialPlatforms.map(({ key, name, baseUrl, color }) => {
          const handle = socialMedia[key];
          if (handle == undefined || handle.length === 0) return; // Handles null, undefined, and empty string

          return (
            <a
              key={key}
              href={`${baseUrl}${handle}`}
              target="_blank"
              rel="noopener noreferrer"
              className={`flex items-center gap-1 px-2 py-1 bg-${color}-100 text-${color}-800 rounded-md text-sm hover:bg-${color}-200 dark:bg-${color}-900 dark:text-${color}-200`}
            >
              <Globe className="h-3 w-3" />
              {name}
            </a>
          );
        })}
      </div>
    </div>
  );
}

export function TruckContactInfo({ truck }: Readonly<TruckContactInfoProps>) {
  return (
    <Card className="dark:bg-slate-800 dark:border-slate-700">
      <CardHeader>
        <CardTitle className="flex items-center gap-2 dark:text-gray-100">
          <Phone className="h-5 w-5" />
          Contact Information
        </CardTitle>
        <CardDescription className="dark:text-gray-400">Get in touch</CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <ContactField
          icon={Phone}
          label="Phone"
          value={truck.contact_info?.phone}
          href={truck.contact_info?.phone ? `tel:${truck.contact_info.phone}` : undefined}
          unavailableText="No phone number available"
        />

        <ContactField
          icon={Mail}
          label="Email"
          value={truck.contact_info?.email}
          href={truck.contact_info?.email ? `mailto:${truck.contact_info.email}` : undefined}
          unavailableText="No email available"
        />

        <ContactField
          icon={Globe}
          label="Website"
          value={truck.contact_info?.website}
          href={truck.contact_info?.website}
          unavailableText="No website available"
        />

        <SocialMediaLinks socialMedia={truck.social_media} />
      </CardContent>
    </Card>
  );
}

import React from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Clock } from 'lucide-react';
import type { FoodTruck } from '@/lib/supabase';

interface TruckOperatingHoursProps {
  readonly truck: FoodTruck;
}

interface DayData {
  closed?: boolean;
  open?: string;
  close?: string;
}

function DaySchedule({ day, dayData }: Readonly<{ day: string; dayData?: DayData }>) {
  const dayName = day.charAt(0).toUpperCase() + day.slice(1);
  
  return (
    <div className="flex justify-between items-center">
      <span className="text-sm font-medium dark:text-gray-200">{dayName}</span>
      <span className="text-sm text-gray-600 dark:text-gray-400">
        {dayData?.closed === true ? 'Closed' : `${dayData?.open ?? 'N/A'} - ${dayData?.close ?? 'N/A'}`}
      </span>
    </div>
  );
}

export function TruckOperatingHours({ truck }: TruckOperatingHoursProps) {
  const daysOfWeek = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'];
  const hasOperatingHours = truck.operating_hours && Object.keys(truck.operating_hours).length > 0;

  return (
    <Card className="dark:bg-slate-800 dark:border-slate-700">
      <CardHeader>
        <CardTitle className="flex items-center gap-2 dark:text-gray-100">
          <Clock className="h-5 w-5" />
          Operating Hours
        </CardTitle>
        <CardDescription className="dark:text-gray-400">Daily schedule</CardDescription>
      </CardHeader>
      <CardContent>
        {hasOperatingHours ? (
          <div className="space-y-2">
            {daysOfWeek.map((day) => {
              const dayData = truck.operating_hours?.[day as keyof typeof truck.operating_hours] as DayData | undefined;
              return (
                <DaySchedule key={day} day={day} dayData={dayData} />
              );
            })}
          </div>
        ) : (
          <p className="text-gray-400 text-sm">Operating hours not available</p>
        )}
      </CardContent>
    </Card>
  );
}

/* eslint-disable max-params */
'use client';

import * as React from 'react';
import * as RechartsPrimitive from 'recharts';
import {
  NameType,
  Payload,
  ValueType,
} from 'recharts/types/component/DefaultTooltipContent';

import { cn } from '@/lib/utils';
import { useTooltipLabel } from './chart/useTooltipLabel';
import { TooltipIndicator } from './chart/TooltipIndicator';
import { TooltipItemContent } from './chart/TooltipItemContent';
import { getPayloadConfigFromPayload } from './chart/getPayloadConfigFromPayload';

// Format: { THEME_NAME: CSS_SELECTOR }
const THEMES = { light: '', dark: '.dark' } as const;

export type ChartConfig = {
  [k in string]: {
    label?: React.ReactNode;
    icon?: React.ComponentType;
  } & (
    | { color?: string; theme?: never }
    | { color?: never; theme: Record<keyof typeof THEMES, string> }
  );
};

type ChartContextProps = {
  config: ChartConfig;
};

const ChartContext = React.createContext<ChartContextProps | undefined>(undefined);

function useChart() {
  const context = React.useContext(ChartContext);
  if (context === undefined) {
    throw new Error('useChart must be used within a <ChartContainer />');
  }
  return context;
}

const ChartContainer = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<'div'> & {
    readonly config: ChartConfig;
    readonly children: React.ComponentProps<typeof RechartsPrimitive.ResponsiveContainer>['children'];
  }
>(({ id, className, children, config, ...props }, ref) => {
  const uniqueId = React.useId();
  const chartId = `chart-${id ?? uniqueId.replaceAll(':', '')}`;

  return (
    <ChartContext.Provider value={{ config }}>
      <div
        data-chart={chartId}
        ref={ref}
        className={cn(
          "flex aspect-video justify-center text-xs [&_.recharts-cartesian-axis-tick_text]:fill-muted-foreground [&_.recharts-cartesian-grid_line[stroke='#ccc']]:stroke-border/50 [&_.recharts-curve.recharts-tooltip-cursor]:stroke-border [&_.recharts-dot[stroke='#fff']]:stroke-transparent [&_.recharts-layer]:outline-none [&_.recharts-polar-grid_[stroke='#ccc']]:stroke-border [&_.recharts-radial-bar-background-sector]:fill-muted [&_.recharts-rectangle.recharts-tooltip-cursor]:fill-muted [&_.recharts-reference-line_[stroke='#ccc']]:stroke-border [&_.recharts-sector[stroke='#fff']]:stroke-transparent [&_.recharts-sector]:outline-none [&_.recharts-surface]:outline-none",
          className,
        )}
        {...props}
      >
        <ChartStyle id={chartId} config={config} />
        <RechartsPrimitive.ResponsiveContainer>{children}</RechartsPrimitive.ResponsiveContainer>
      </div>
    </ChartContext.Provider>
  );
});
ChartContainer.displayName = 'Chart';

const ChartStyle = ({ id, config }: { readonly id: string; readonly config: ChartConfig }) => {
  const colorConfig = Object.entries(config).filter(([_, itemConfig]) => (itemConfig.theme ?? itemConfig.color) !== undefined);
  if (colorConfig.length === 0) {
    return;
  }

  return (
    <style
      dangerouslySetInnerHTML={{
        __html: Object.entries(THEMES)
          .map(
            ([theme, prefix]) => `
${prefix} [data-chart=${id}] {
${colorConfig
  .map(([key, itemConfig]) => {
    const color = itemConfig.theme?.[theme as keyof typeof itemConfig.theme] ?? itemConfig.color;
    return color !== undefined && color !== '' ? `  --color-${key}: ${color};` : undefined;
  })
  .join('\n')}
}
`,
          )
          .join('\n'),
      }}
    />
  );
};

const ChartTooltip = RechartsPrimitive.Tooltip;

function isNonEmptyArray<T>(arr: T[] | undefined): arr is T[] {
  return Array.isArray(arr) && arr.length > 0;
}

type TooltipFormatter = (value: number, name: string, item: unknown, index: number, payload: Record<string, unknown>[]) => React.ReactNode;
type TooltipItemData = { name?: string; dataKey?: string; payload?: Record<string, unknown>; color?: string; value?: number };


type ChartTooltipIndicatorAndContentProps = {
  indicator: 'line' | 'dot' | 'dashed';
  hideIndicator: boolean;
  indicatorColor?: string;
  nestLabel: boolean;
  itemConfig: Record<string, unknown>;
  formatter: TooltipFormatter | undefined;
  itemData: TooltipItemData;
  item: Payload<ValueType, NameType>;
  index: number;
  tooltipLabel: React.ReactNode;
};

function ChartTooltipIndicatorAndContent(props: Readonly<ChartTooltipIndicatorAndContentProps>) {
  const {
    indicator,
    hideIndicator,
    indicatorColor,
    nestLabel,
    itemConfig,
    formatter,
    itemData,
    item,
    index,
    tooltipLabel,
  } = props;
  return (
    <>
      <TooltipIndicator
        indicator={indicator}
        hideIndicator={hideIndicator}
        indicatorColor={indicatorColor}
        nestLabel={nestLabel}
        itemConfig={itemConfig}
      />
      <TooltipItemContent
        formatter={formatter}
        itemData={itemData}
        item={item}
        index={index}
        itemConfig={itemConfig}
        nestLabel={nestLabel}
        tooltipLabel={tooltipLabel}
      />
    </>
  );
}

type ChartTooltipItemProps = {
  item: Payload<ValueType, NameType>;
  index: number;
  indicatorProps: {
    indicator: 'line' | 'dot' | 'dashed';
    hideIndicator: boolean;
    color?: string;
    nestLabel: boolean;
    config: ChartConfig;
    nameKey?: string;
    tooltipLabel: React.ReactNode;
    formatter: TooltipFormatter | undefined;
  };
};

function ChartTooltipItem({ item, index, indicatorProps }: Readonly<ChartTooltipItemProps>) {
  const { indicator, hideIndicator, color, nestLabel, config, nameKey, tooltipLabel, formatter } = indicatorProps;
  const dataKey = typeof item.dataKey === 'string' ? item.dataKey : undefined;

  const itemData: TooltipItemData = {
    name: item.name === undefined ? undefined : String(item.name),
    dataKey,
    payload: typeof item.payload === 'object' && item.payload ? (item.payload as Record<string, unknown>) : undefined,
    color: typeof item.color === 'string' ? item.color : undefined,
    value: typeof item.value === 'number' ? item.value : undefined,
  };
  const key = nameKey ?? itemData.name ?? itemData.dataKey ?? 'value';
  const itemConfig = getPayloadConfigFromPayload(config, item, key) as Record<string, unknown>;
  const indicatorColor =
    color ??
    (itemData.payload !== undefined && typeof itemData.payload === 'object' && 'fill' in itemData.payload
      ? String(itemData.payload.fill)
      : undefined) ??
    itemData.color;
  return (
    <div
      key={itemData.dataKey ?? index}
      className={cn(
        'flex w-full flex-wrap items-stretch gap-2 [&>svg]:h-2.5 [&>svg]:w-2.5 [&>svg]:text-muted-foreground',
        indicator === 'dot' && 'items-center',
      )}
    >
      <ChartTooltipIndicatorAndContent
        indicator={indicator}
        hideIndicator={hideIndicator}
        indicatorColor={indicatorColor}
        nestLabel={nestLabel}
        itemConfig={itemConfig}
        formatter={formatter}
        itemData={itemData}
        item={item}
        index={index}
        tooltipLabel={tooltipLabel}
      />
    </div>
  );
}

type ChartTooltipItemsProps = {
  safePayload: Payload<ValueType, NameType>[];
  indicatorProps: {
    indicator: 'line' | 'dot' | 'dashed';
    hideIndicator: boolean;
    formatter: TooltipFormatter | undefined;
    nameKey?: string;
    color?: string;
    tooltipLabel: React.ReactNode;
    config: ChartConfig;
    nestLabel: boolean;
  };
};

function ChartTooltipItems(props: Readonly<ChartTooltipItemsProps>) {
  const { safePayload, indicatorProps } = props;
  return (
    <div className="grid gap-1.5">
      {safePayload.map((item, index) => (
        <ChartTooltipItem
          key={item.dataKey ?? index}
          item={item}
          index={index}
          indicatorProps={indicatorProps}
        />
      ))}
    </div>
  );
}

const ChartTooltipContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof RechartsPrimitive.Tooltip> &
    React.ComponentProps<'div'> & {
      readonly hideLabel?: boolean;
      readonly hideIndicator?: boolean;
      readonly indicator?: 'line' | 'dot' | 'dashed';
      readonly nameKey?: string;
      readonly labelKey?: string;
    }
>(({
  active,
  payload,
  className,
  indicator = 'dot',
  hideLabel = false,
  hideIndicator = false,
  label,
  labelFormatter,
  labelClassName,
  formatter,
  color,
  nameKey,
  labelKey,
}, ref) => {
    const { config } = useChart();
    const safePayload: Payload<ValueType, NameType>[] = isNonEmptyArray(payload) ? payload : [];
    const tooltipLabel = useTooltipLabel({
      hideLabel,
      payload: safePayload,
      label,
      labelFormatter,
      labelClassName,
      config,
      labelKey
    });

    if (active === false || !isNonEmptyArray(safePayload)) {
      return;
    }

    const nestLabel = safePayload.length === 1 && indicator !== 'dot';

    return (
      <div
        ref={ref}
        className={cn(
          'grid min-w-[8rem] items-start gap-1.5 rounded-lg border border-border/50 bg-background px-2.5 py-1.5 text-xs shadow-xl',
          className,
        )}
      >
        {!nestLabel && tooltipLabel}
        <ChartTooltipItems
          safePayload={safePayload}
          indicatorProps={{
            indicator,
            hideIndicator,
            formatter: formatter as TooltipFormatter | undefined,
            nameKey,
            color,
            tooltipLabel,
            config,
            nestLabel,
          }}
        />
      </div>
    );
  },
);
ChartTooltipContent.displayName = 'ChartTooltip';

const ChartLegend = RechartsPrimitive.Legend;

interface ChartLegendItemProps {
  item: Payload<ValueType, NameType>;
  idx: number;
  hideIcon: boolean;
  nameKey?: string;
  config: ChartConfig;
}

function ChartLegendItem({ item, idx, hideIcon, nameKey, config }: Readonly<ChartLegendItemProps>) {
  const dataKey = item.dataKey?.toString();
  const itemData = {
    dataKey,
    value: item.value?.toString(),
    color: item.color?.toString(),
  };
  const key = nameKey ?? dataKey ?? 'value';
  const itemConfig = getPayloadConfigFromPayload(config, item, key);

  return (
    <div
      key={itemData.value ?? idx}
      className={cn(
