async function fetchRealtimeMetrics(): Promise<RealtimeMetrics> {
  try {
    const recentJobs = await ScrapingJobService.getJobsByStatus('all');
    const scrapingMetrics = {
      active: recentJobs.filter((job: ScrapingJob) => job.status === 'running').length,
      completed: recentJobs.filter((job: ScrapingJob) => job.status === 'completed').length,
      failed: recentJobs.filter((job: ScrapingJob) => job.status === 'failed').length,
      pending: recentJobs.filter((job: ScrapingJob) => job.status === 'pending').length
    };

    const qualityStats = await FoodTruckService.getDataQualityStats();
    const dataQualityMetrics = {
      averageScore: qualityStats.avg_quality_score ?? 0,
      totalTrucks: qualityStats.total_trucks ?? 0,
      recentChanges: 0
    };

    const systemHealth = {
      status: 'healthy' as const,
      uptime: process.uptime(),
      lastUpdate: new Date().toISOString()
    };

    return {
      scrapingJobs: scrapingMetrics,
      dataQuality: dataQualityMetrics,
      systemHealth
    };
  } catch (error) {
    console.error('Error fetching realtime metrics:', error);
    return {
      scrapingJobs: { active: 0, completed: 0, failed: 0, pending: 0 },
      dataQuality: { averageScore: 0, totalTrucks: 0, recentChanges: 0 },
      systemHealth: {
        status: 'error',
        uptime: 0,
        lastUpdate: new Date().toISOString()
      }
    };
  }
}

function isScrapingJob(obj: unknown): obj is ScrapingJob {
  return typeof obj === 'object' && obj !== null && 'id' in obj && 'status' in obj;
}

// Removed isFoodTruck function as it is unused.
// function isFoodTruck(obj: unknown): obj is FoodTruck {
//   return typeof obj === 'object' && obj !== null && 'id' in obj && 'name' in obj;
// }

async function sendScrapingUpdateEvent(
  controller: ReadableStreamDefaultController<Uint8Array>,
  encoder: TextEncoder
): Promise<void> {
  const recentJobs = await ScrapingJobService.getJobsByStatus('all');

  if (Array.isArray(recentJobs) && recentJobs.length > 0) {
    const event: AdminEvent = {
      id: generateEventId(),
      type: 'scraping_update',
      timestamp: new Date().toISOString(),
      data: {
        recentJobs: recentJobs.filter((job) => isScrapingJob(job)).map((job) => ({ // Fixed unicorn/no-array-callback-reference
          id: job.id,
          status: job.status,
          started_at: job.started_at,
          completed_at: job.completed_at
        })),
        count: recentJobs.length
      },
      severity: 'info'
    };

    controller.enqueue(encoder.encode(formatSSEMessage(event)));
  }
}

async function sendDataQualityChangeEvent(
  controller: ReadableStreamDefaultController<Uint8Array>,
  encoder: TextEncoder
): Promise<void> {
  const recentTrucksResult = await FoodTruckService.getAllTrucks(10, 0);
  const recentlyUpdated = recentTrucksResult.trucks.filter((truck: FoodTruck) => {
    if (!truck.updated_at) return false;
    const updatedAt = new Date(truck.updated_at);
    const oneMinuteAgo = new Date(Date.now() - 60_000);
    return updatedAt > oneMinuteAgo;
  });

  if (recentlyUpdated.length > 0) {
    const event: AdminEvent = {
      id: generateEventId(),
      type: 'data_quality_change',
      timestamp: new Date().toISOString(),
      data: {
        updatedTrucks: recentlyUpdated.map(truck => ({
          id: truck.id,
          name: truck.name,
          data_quality_score: truck.data_quality_score,
          updated_at: truck.updated_at
        })),
        count: recentlyUpdated.length
      },
      severity: 'info'
    };

    controller.enqueue(encoder.encode(formatSSEMessage(event)));
  }
}

async function monitorDataChanges(
  controller: ReadableStreamDefaultController<Uint8Array>,
  encoder: TextEncoder
): Promise<void> {
  try {
    await sendScrapingUpdateEvent(controller, encoder);
    await sendDataQualityChangeEvent(controller, encoder);
  } catch (error) {
    console.error('Error monitoring data changes:', error);
  }
}

function formatSSEMessage(event: AdminEvent): string {
  return `id: ${event.id}\nevent: ${event.type}\ndata: ${JSON.stringify(event)}\n\n`;
}

function generateEventId(): string {
  // eslint-disable-next-line sonarjs/pseudo-random -- Math.random is acceptable for generating non-security-sensitive event IDs.
  return `${Date.now()}-${Math.random().toString(36).slice(2, 11)}`;
}

async function handleHealthCheck(): Promise<Response> {
  const metrics = await fetchRealtimeMetrics();
  return new Response(JSON.stringify({
    success: true,
    status: 'healthy',
    metrics,
    timestamp: new Date().toISOString()
  }), {
    headers: { 'Content-Type': 'application/json' }
  });
}

function handleTriggerTestEvent(): Response {
  return new Response(JSON.stringify({
    success: true,
    message: 'Test event triggered',
    timestamp: new Date().toISOString()
  }), {
    headers: { 'Content-Type': 'application/json' }
  });
}

import { NextRequest, NextResponse } from 'next/server';
import { autoScraper } from '@/lib/autoScraper';
import { scheduler } from '@/lib/scheduler';
import { logActivity } from '@/lib/activityLogger';
import { AutoScrapeResult } from './types';

function verifyCronSecret(request: NextRequest): NextResponse | null {
  const authHeader = request.headers.get('authorization');
  const cronSecret = process.env.CRON_SECRET;

  if (cronSecret === undefined || cronSecret === '') {
    console.error('CRON_SECRET not configured');
    return NextResponse.json({ error: 'Server configuration error' }, { status: 500 });
  }

  if (authHeader !== `Bearer ${cronSecret}`) {
    console.error('Unauthorized cron attempt:', authHeader);
    return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
  }
  return undefined;
}

function logAutoScrapeStart() {
  console.info('Starting automated scraping job...');
  logActivity({
    type: 'cron_job',
    action: 'auto_scrape_started',
    details: { timestamp: new Date().toISOString() },
  });
}

function logAutoScrapeCompletion(result: AutoScrapeResult) {
  logActivity({
    type: 'cron_job',
    action: 'auto_scrape_completed',
    details: {
      timestamp: new Date().toISOString(),
      trucksProcessed: result.trucksProcessed,
      newTrucksFound: result.newTrucksFound,
      errorsCount: result.errors?.length ?? 0,
    },
  });
  console.info('Automated scraping job completed successfully');
}

function logAutoScrapeFailure(error: unknown) {
  console.error('Auto-scraping cron job failed:', error);
  logActivity({
    type: 'cron_job',
    action: 'auto_scrape_failed',
    details: {
      timestamp: new Date().toISOString(),
      error: error instanceof Error ? error.message : 'Unknown error',
    },
  });
}

export async function handlePostRequest(request: NextRequest) {
  try {
    const authResponse = verifyCronSecret(request);
    if (authResponse) {
      return authResponse;
    }

    logAutoScrapeStart();

    const rawResult = await autoScraper.runAutoScraping();
    // Map errors to string[] for compatibility
    const result: AutoScrapeResult = {
      trucksProcessed: rawResult.trucksProcessed,
      newTrucksFound: rawResult.newTrucksFound,
      errors: rawResult.errors?.map(e => e.url + (e.details ? `: ${e.details}` : '')),
    };
    scheduler.scheduleFollowUpTasks(result);
    logAutoScrapeCompletion(result);

    return NextResponse.json({
      success: true,
      message: 'Auto-scraping completed successfully',
      data: {
        trucksProcessed: result.trucksProcessed,
        newTrucksFound: result.newTrucksFound,
        timestamp: new Date().toISOString(),
      },
    });
  } catch (error) {
    logAutoScrapeFailure(error);
    return NextResponse.json(
      {
        success: false,
        error: 'Auto-scraping failed',
        message: error instanceof Error ? error.message : 'Unknown error',
      },
      { status: 500 },
    );
  }
}

export function handleGetRequest() {
  return NextResponse.json(
    { error: 'Method not allowed. Use POST for cron jobs.' },
    { status: 405 },
  );
}

import {
  ExtractedFoodTruckDetails,
  FoodTruckSchema,
  MenuCategory,
  MenuItem,
} from '@/lib/types';

export function mapExtractedDataToTruckSchema(
  extractedData: ExtractedFoodTruckDetails,
  sourceUrl: string,
  isDryRun: boolean,
): FoodTruckSchema {
  if (!extractedData || typeof extractedData !== 'object') {
    throw new Error('Invalid extractedData for mapping.');
  }

  const name = extractedData.name ?? 'Unknown Test Truck';
  const locationData = extractedData.current_location ?? {};
  const fullAddress = [
    locationData.address,
    locationData.city,
    locationData.state,
    locationData.zip_code,
  ]
    .filter(Boolean)
    .join(', ');

  return {
    name: name,
    description: extractedData.description ?? undefined,
    current_location: {
      lat: locationData.lat ?? 0,
      lng: locationData.lng ?? 0,
      address: fullAddress ?? (locationData.raw_text ?? undefined),
      timestamp: new Date().toISOString(),
    },
    scheduled_locations: extractedData.scheduled_locations ?? undefined,
    operating_hours: extractedData.operating_hours ?? undefined,
    menu: (extractedData.menu ?? []).map((category: MenuCategory) => ({
      name: category.name ?? 'Uncategorized',
      items: (category.items ?? []).map((item: MenuItem) => ({
        name: item.name ?? 'Unknown Item',
        description: item.description ?? undefined,
        price:
          typeof item.price === 'number' || typeof item.price === 'string'
            ? item.price
            : undefined,
        dietary_tags: item.dietary_tags ?? [],
      })),
    })),
    contact_info: extractedData.contact_info ?? undefined,
    social_media: extractedData.social_media ?? undefined,
    cuisine_type: extractedData.cuisine_type ?? [],
    price_range: extractedData.price_range ?? undefined,
    specialties: extractedData.specialties ?? [],
    data_quality_score: isDryRun ? 0.5 : 0.6, // Differentiate test/dry run
    verification_status: 'pending',
    source_urls: [sourceUrl].filter(Boolean),
    last_scraped_at: new Date().toISOString(),
    ...(isDryRun && { test_run_flag: true }), // Add a flag for actual test saves if needed
  };
}

/**
 * SOTA Batch Data Cleanup System
 * Implements automated data quality improvements and cleanup operations
 */

import { FoodTruckService, type FoodTruck, DataQualityService } from '@/lib/supabase';
import { DuplicatePreventionService } from './duplicatePrevention';
import { getPlaceholderPatterns, processTruckForPlaceholders } from './placeholderUtils';

export interface CleanupOperation {
  type: 'normalize_phone' | 'fix_coordinates' | 'remove_placeholders' | 'update_quality_scores' | 'merge_duplicates';
  description: string;
  affectedCount: number;
  successCount: number;
  errorCount: number;
  errors: string[];
}

export interface BatchCleanupResult {
  totalProcessed: number;
  operations: CleanupOperation[];
  summary: {
    trucksImproved: number;
    duplicatesRemoved: number;
    qualityScoreImprovement: number;
    placeholdersRemoved: number;
  };
  duration: number;
}

interface MergeOperationContext {
  operation: CleanupOperation;
  processedIds: Set<string>;
}

interface CoordinateProcessContext {
  defaultLat: number;
  defaultLng: number;
  dryRun: boolean;
  operation: CleanupOperation;
}

/**
 * Automated Data Quality Cleanup Service
 */
export class BatchCleanupService {
  
  /**
   * Run comprehensive data cleanup operations
   */
  static async runFullCleanup(options: {
    batchSize?: number;
    dryRun?: boolean;
    operations?: CleanupOperation['type'][];
  } = {}): Promise<BatchCleanupResult> {
    const startTime = Date.now();
    const { batchSize = 50, dryRun = false, operations = [
      'remove_placeholders',
      'normalize_phone',
      'fix_coordinates',
      'update_quality_scores',
      'merge_duplicates'
    ] } = options;

    console.info(`Starting batch cleanup (${dryRun ? 'DRY RUN' : 'LIVE'})...`);
    const result = this.initializeCleanupResult();
    try {
      const allTrucks = await FoodTruckService.getAllTrucks();
      result.totalProcessed = allTrucks.total;
      await this.processTrucksInBatches(allTrucks.trucks, {
        batchSize,
        operations,
        dryRun,
        result
      });
      return this.finalizeCleanupResult(result, startTime);
    } catch (error) {
      console.error('Batch cleanup failed:', error);
      throw error;
    }
  }


  private static initializeCleanupResult(): BatchCleanupResult {
    return {
      totalProcessed: 0,
      operations: [],
      summary: {
        trucksImproved: 0,
        duplicatesRemoved: 0,
        qualityScoreImprovement: 0,
        placeholdersRemoved: 0
      },
      duration: 0
    };
  }

  private static finalizeCleanupResult(result: BatchCleanupResult, startTime: number): BatchCleanupResult {
    result.summary = this.calculateSummary(result.operations);
    result.duration = Date.now() - startTime;

    console.info(`Batch cleanup completed in ${result.duration}ms`);
    return result;
  }

  private static async processTrucksInBatches(
    trucks: FoodTruck[],
    { batchSize, operations, dryRun, result }: {
      batchSize: number;
      operations: CleanupOperation['type'][];
      dryRun: boolean;
      result: BatchCleanupResult;
    }
  ): Promise<void> {
    for (let i = 0; i < trucks.length; i += batchSize) {
      const batch = trucks.slice(i, i + batchSize);
      for (const op of operations) {
        const opResult = await this.runOperation(op, batch, dryRun);
        result.operations.push(opResult);
      }
    }
  }
  
  /**
   * Run a specific cleanup operation
   */
  private static async runOperation(
    type: CleanupOperation['type'],
    trucks: FoodTruck[],
    dryRun: boolean
  ): Promise<CleanupOperation> {
    const operation: CleanupOperation = {
      type,
      description: this.getOperationDescription(type),
      affectedCount: 0,
      successCount: 0,
      errorCount: 0,
      errors: []
    };
    
    const operationRunners = {
      remove_placeholders: this.runRemovePlaceholders,
      normalize_phone: this.runNormalizePhoneNumbers,
      fix_coordinates: this.runFixCoordinates,
      update_quality_scores: this.runUpdateQualityScores,
      merge_duplicates: this.runMergeDuplicates,
    };

    try {
      const runner = operationRunners[type];
      if (runner) {
        return await runner.call(this, trucks, dryRun, operation);
      } else {
        operation.errors.push(`Unknown operation type: ${String(type)}`);
        return operation;
      }
    } catch (error) {
      operation.errors.push(`Operation failed: ${error instanceof Error ? error.message : String(error)}`);
      return operation;
    }
  }

  private static runRemovePlaceholders = async (trucks: FoodTruck[], dryRun: boolean, operation: CleanupOperation): Promise<CleanupOperation> => {
    return await this.removePlaceholders(trucks, dryRun, operation);
  }

  private static runNormalizePhoneNumbers = async (trucks: FoodTruck[], dryRun: boolean, operation: CleanupOperation): Promise<CleanupOperation> => {
    return await this.normalizePhoneNumbers(trucks, dryRun, operation);
  }

  private static runFixCoordinates = async (trucks: FoodTruck[], dryRun: boolean, operation: CleanupOperation): Promise<CleanupOperation> => {
    return await this.fixCoordinates(trucks, dryRun, operation);
  }

  private static runUpdateQualityScores = async (trucks: FoodTruck[], dryRun: boolean, operation: CleanupOperation): Promise<CleanupOperation> => {
    return await this.updateQualityScores(trucks, dryRun, operation);
  }

  private static runMergeDuplicates = async (trucks: FoodTruck[], dryRun: boolean, operation: CleanupOperation): Promise<CleanupOperation> => {
    return await this.mergeDuplicates(trucks, dryRun, operation);
  }
  

  /**
   * Remove placeholder and mock data
   */
  private static async removePlaceholders(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<CleanupOperation> {
    const placeholderPatterns = getPlaceholderPatterns();
    const promises = trucks.map(truck => 
      this.processSingleTruckForPlaceholders(truck, placeholderPatterns, dryRun, operation)
    );
    await Promise.all(promises);
    return operation;
  }

  private static async processSingleTruckForPlaceholders(
    truck: FoodTruck,
    patterns: RegExp[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<void> {
    const updates = processTruckForPlaceholders(truck, patterns);
    if (updates && Object.keys(updates).length > 0) {
      await this.performUpdateOperation(truck.id, updates, dryRun, operation);
    }
  }

  private static async performUpdateOperation(
    truckId: string,
    updates: Partial<FoodTruck>,
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<void> {
    operation.affectedCount++;
    if (dryRun) {
      operation.successCount++;
    } else {
      try {
        await FoodTruckService.updateTruck(truckId, updates);
        operation.successCount++;
      } catch (error) {
        operation.errorCount++;
        operation.errors.push(`Failed to update truck ${truckId}: ${error instanceof Error ? error.message : String(error)}`);
      }
    }
  }
  
  /**
   * Normalize phone numbers to consistent format
   */
  private static async normalizePhoneNumbers(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<CleanupOperation> {
    const promises = trucks.map(truck => {
      if (truck.contact_info?.phone !== undefined) {
        const originalPhone = truck.contact_info.phone;
        const normalizedPhone = this.normalizePhone(originalPhone);

        if (normalizedPhone !== undefined && normalizedPhone !== originalPhone) {
          return this.applyPhoneNormalizationUpdate(truck, normalizedPhone, dryRun, operation);
        }
      }
      return Promise.resolve();
    });
    await Promise.all(promises);
    return operation;
  }

  private static async applyPhoneNormalizationUpdate(
    truck: FoodTruck,
    normalizedPhone: string,
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<void> {
    operation.affectedCount++;
    if (dryRun) {
      operation.successCount++;
    } else {
      try {
        await FoodTruckService.updateTruck(truck.id, {
          contact_info: {
            ...truck.contact_info,
            phone: normalizedPhone
          }
        });
        operation.successCount++;
      } catch (error) {
        operation.errorCount++;
        operation.errors.push(`Failed to normalize phone for truck ${truck.id}: ${error instanceof Error ? error.message : String(error)}`);
      }
    }
  }
  
  /**
   * Helper to determine if coordinates need fixing and provide updates
   */
  private static getFixedCoordinates(
    lat: number | undefined,
    lng: number | undefined,
    defaultLat: number,
    defaultLng: number
  ): Partial<FoodTruck['current_location']> | undefined {
    // Fix invalid coordinates (0,0 or undefined)
    if (lat === undefined || lng === undefined || lat === 0 || lng === 0) {
      return { lat: defaultLat, lng: defaultLng };
    }
    // Fix coordinates outside reasonable bounds for Charleston area
    if (lat < 32 || lat > 34 || lng > -79 || lng < -81) {
      return { lat: defaultLat, lng: defaultLng };
    }
    return undefined;
  }

  /**
   * Fix invalid GPS coordinates
   */
  private static async fixCoordinates(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<CleanupOperation> {
    const context: Omit<CoordinateProcessContext, 'operation'> = {
      defaultLat: 32.7767,
      defaultLng: -79.9311,
      dryRun,
    };

    const promises = trucks.map(truck => 
      this.processSingleTruckCoordinates(truck, { ...context, operation })
    );
    await Promise.all(promises);
    return operation;
  }

  private static async processSingleTruckCoordinates(
    truck: FoodTruck,
    context: CoordinateProcessContext
  ): Promise<void> {
    if (!truck.current_location) return;
    const { defaultLat, defaultLng, dryRun, operation } = context;
    const { lat, lng } = truck.current_location;
    const updates = this.getFixedCoordinates(lat, lng, defaultLat, defaultLng);
    if (updates) {
      await this.applyCoordinateFixUpdate(truck, updates, dryRun, operation);
    }
  }

  private static async applyCoordinateFixUpdate(
    truck: FoodTruck,
    updates: Partial<FoodTruck['current_location']>,
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<void> {
    operation.affectedCount++;
    if (dryRun) {
      operation.successCount++;
    } else {
      try {
        await FoodTruckService.updateTruck(truck.id, {
          current_location: {
            ...truck.current_location,
            ...updates,
          }
        });
        operation.successCount++;
      } catch (error) {
        operation.errorCount++;
        operation.errors.push(`Failed to fix coordinates for truck ${truck.id}: ${error instanceof Error ? error.message : String(error)}`);
      }
    }
  }

  /**
   * Update quality scores for all trucks
   */
  private static async updateQualityScores(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<CleanupOperation> {
    const promises = trucks.map(truck => 
      this.processSingleTruckForQualityScore(truck, dryRun, operation)
    );
    await Promise.all(promises);
    return operation;
  }

  private static async processSingleTruckForQualityScore(
    truck: FoodTruck,
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<void> {
    const qualityAssessment = DataQualityService.calculateQualityScore(truck);
    if (qualityAssessment) {
      const newScore = qualityAssessment.score;
      const currentScore = truck.data_quality_score ?? 0;
      // Only update if score changed significantly (>5% difference)
      if (typeof newScore === 'number' && Math.abs(newScore - currentScore) > 0.05) {
        await this.applyQualityScoreUpdate(truck, dryRun, operation);
      }
    }
  }

  private static async applyQualityScoreUpdate(
    truck: FoodTruck,
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<void> {
    operation.affectedCount++;
    if (dryRun) {
      operation.successCount++;
    } else {
      try {
        const updateResult = await DataQualityService.updateTruckQualityScore(truck.id);
        if ('error' in updateResult) {
          throw new Error(updateResult.error);
        }
        operation.successCount++;
      } catch (error) {
        operation.errorCount++;
        operation.errors.push(`Failed to update quality score for truck ${truck.id}: ${error instanceof Error ? error.message : String(error)}`);
      }
    }
  }
  
  /**
   * Identify and merge duplicate trucks
   */
  private static async mergeDuplicates(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<CleanupOperation> {
    const processedIds = new Set<string>();
    for (const truck of trucks) {
      await this.processSingleTruckForDuplicates(truck, dryRun, { operation, processedIds });
    }
    return operation;
  }

  private static async processSingleTruckForDuplicates(
    truck: FoodTruck,
    dryRun: boolean,
    context: MergeOperationContext
  ): Promise<void> {
    const { processedIds } = context;
    if (processedIds.has(truck.id)) return;

    const duplicateCheck = await DuplicatePreventionService.checkForDuplicates(truck);
    if (duplicateCheck.isDuplicate && duplicateCheck.bestMatch?.confidence === 'high' && duplicateCheck.bestMatch.recommendation === 'merge') {
      await this.applyMergeOperation(truck, duplicateCheck.bestMatch.existingTruck, dryRun, context);
    }
    processedIds.add(truck.id);
  }

  private static async applyMergeOperation(
    truck: FoodTruck,
    existingTruck: FoodTruck,
    dryRun: boolean,
    context: MergeOperationContext
  ): Promise<void> {
    const { operation, processedIds } = context;
    operation.affectedCount++;
    if (dryRun) {
      operation.successCount++;
    } else {
      try {
        const mergeResult = await DuplicatePreventionService.mergeDuplicates(truck.id, existingTruck.id);
        if ('error' in mergeResult) {
          throw new Error(mergeResult.error);
        }
        processedIds.add(existingTruck.id);
        operation.successCount++;
      } catch (error) {
        operation.errorCount++;
        operation.errors.push(`Failed to merge duplicates ${truck.id} and ${existingTruck.id}: ${error instanceof Error ? error.message : String(error)}`);
      }
    }
  }
  
  /**
   * Normalize phone number format
   */
  private static normalizePhone(phone: string): string | undefined {
    if (!phone) return undefined;
    
    // Remove all non-digit characters
    const digits = phone.replaceAll(/\D/g, '');
    
    // Handle US phone numbers
    if (digits.length === 10) {
      return `(${digits.slice(0, 3)}) ${digits.slice(3, 6)}-${digits.slice(6)}`;
    }
    if (digits.length === 11 && digits.startsWith('1')) {
      return `+1 (${digits.slice(1, 4)}) ${digits.slice(4, 7)}-${digits.slice(7)}`;
    }
    
    // Return original if can't normalize
    return phone;
  }
  
  /**
   * Get operation description
   */
  private static getOperationDescription(type: CleanupOperation['type']): string {
    const descriptions = {
      remove_placeholders: 'Remove placeholder and mock data values',
      normalize_phone: 'Normalize phone numbers to consistent format',
      fix_coordinates: 'Fix invalid GPS coordinates',
      update_quality_scores: 'Recalculate data quality scores',
      merge_duplicates: 'Identify and merge duplicate truck entries'
    };
    
    return descriptions[type] ?? 'Unknown operation';
  }
  
  /**
   * Calculate cleanup summary
   */
  private static calculateSummary(operations: CleanupOperation[]): BatchCleanupResult['summary'] {
    return {
      trucksImproved: operations.reduce((sum, op) => sum + op.successCount, 0),
      duplicatesRemoved: operations.find(op => op.type === 'merge_duplicates')?.successCount ?? 0,
      qualityScoreImprovement: operations.find(op => op.type === 'update_quality_scores')?.successCount ?? 0,
      placeholdersRemoved: operations.find(op => op.type === 'remove_placeholders')?.successCount ?? 0
    };
  }
}

/**
 * SOTA Duplicate Prevention System
 * Implements intelligent duplicate detection and prevention for food truck data
 */

import { FoodTruckService, type FoodTruck } from '@/lib/supabase';

// Duplicate detection configuration
export const DUPLICATE_DETECTION_CONFIG = {
  // Similarity thresholds (0.0 = no match, 1.0 = exact match)
  thresholds: {
    name: 0.85,        // High threshold for name matching
    location: 0.9,     // Very high threshold for location matching
    phone: 1,        // Exact match for phone numbers
    website: 1,      // Exact match for websites
    overall: 0.8       // Overall similarity threshold
  },
  
  // Weight factors for different fields
  weights: {
    name: 0.4,         // 40% weight for name similarity
    location: 0.3,     // 30% weight for location similarity
    contact: 0.2,      // 20% weight for contact info similarity
    menu: 0.1          // 10% weight for menu similarity
  }
} as const;

export interface DuplicateMatch {
  existingTruck: FoodTruck;
  similarity: number;
  matchedFields: string[];
  confidence: 'high' | 'medium' | 'low';
  recommendation: 'merge' | 'update' | 'skip' | 'manual_review';
}

export interface DuplicateDetectionResult {
  isDuplicate: boolean;
  matches: DuplicateMatch[];
  bestMatch?: DuplicateMatch;
  action: 'create' | 'update' | 'merge' | 'manual_review';
  reason: string;
}

/**
 * Advanced Duplicate Prevention Service
 */
export class DuplicatePreventionService {
  
  /**
   * Check if a food truck is a duplicate of existing trucks
   */
  static async checkForDuplicates(candidateTruck: Partial<FoodTruck>): Promise<DuplicateDetectionResult> {
    try {
      // Get all existing trucks for comparison
      const existingTrucksResult = await FoodTruckService.getAllTrucks();

      if ('error' in existingTrucksResult) {
        console.error('Error fetching existing trucks:', existingTrucksResult.error);
        return {
          isDuplicate: false,
          matches: [],
          action: 'create',
          reason: `Error fetching existing trucks: ${existingTrucksResult.error}`
        };
      }

      const existingTrucks = existingTrucksResult.trucks;
      const matches: DuplicateMatch[] = [];

      for (const existingTruck of existingTrucks) {
        const similarity = this.calculateSimilarity(candidateTruck, existingTruck);

        if (similarity.overall >= DUPLICATE_DETECTION_CONFIG.thresholds.overall) {
          matches.push({
            existingTruck,
            similarity: similarity.overall,
            matchedFields: similarity.matchedFields,
            confidence: this.getConfidenceLevel(similarity.overall),
            recommendation: this.getRecommendation(similarity)
          });
        }
      }

      return this.processDuplicateMatches(matches, candidateTruck);

    } catch (error) {
      console.error('Error checking for duplicates:', error);
      return {
        isDuplicate: false,
        matches: [],
        action: 'create',
        reason: 'An unexpected error occurred during duplicate detection - proceeding with creation'
      };
    }
  }

  /**
   * Processes the matches found during duplicate detection and returns the result.
   */
  private static processDuplicateMatches(matches: DuplicateMatch[], candidateTruck: Partial<FoodTruck>): DuplicateDetectionResult {
    // Sort matches by similarity (highest first)
    matches.sort((a, b) => b.similarity - a.similarity);

    const bestMatch = matches.length > 0 ? matches[0] : undefined;
    const isDuplicate = matches.length > 0;

    return {
      isDuplicate,
      matches,
      bestMatch,
      action: this.determineAction(matches, candidateTruck),
      reason: this.generateReason(matches, candidateTruck)
    };
  }
  
  /**
   * Calculate similarity between two food trucks
   */
  private static calculateSimilarity(
    candidate: Partial<FoodTruck>, 
    existing: FoodTruck
  ): { overall: number; matchedFields: string[]; breakdown: Record<string, number> } {
    const breakdown: Record<string, number> = {};
    const matchedFields: string[] = [];
    
    // Name similarity
    const nameSimilarity = this.calculateStringSimilarity(
      candidate.name ?? '', 
      existing.name ?? ''
    );
    breakdown.name = nameSimilarity;
    if (nameSimilarity >= DUPLICATE_DETECTION_CONFIG.thresholds.name) {
      matchedFields.push('name');
    }
    
    // Location similarity
    const locationSimilarity = this.calculateLocationSimilarity(
      candidate.current_location,
      existing.current_location
    );
    breakdown.location = locationSimilarity;
    if (locationSimilarity >= DUPLICATE_DETECTION_CONFIG.thresholds.location) {
      matchedFields.push('location');
    }
    
    // Contact similarity
    const contactSimilarity = this.calculateContactSimilarity(
      candidate.contact_info,
      existing.contact_info
    );
    breakdown.contact = contactSimilarity;
    if (contactSimilarity >= DUPLICATE_DETECTION_CONFIG.thresholds.phone) {
      matchedFields.push('contact');
    }
    
    // Menu similarity (basic)
    const menuSimilarity = this.calculateMenuSimilarity(
      candidate.menu,
      existing.menu
    );
    breakdown.menu = menuSimilarity;
    if (menuSimilarity > 0.7) {
      matchedFields.push('menu');
    }
    
    // Calculate weighted overall similarity
    const overall = (
      nameSimilarity * DUPLICATE_DETECTION_CONFIG.weights.name +
      locationSimilarity * DUPLICATE_DETECTION_CONFIG.weights.location +
      contactSimilarity * DUPLICATE_DETECTION_CONFIG.weights.contact +
      menuSimilarity * DUPLICATE_DETECTION_CONFIG.weights.menu
    );
    
    return { overall, matchedFields, breakdown };
  }
  
  /**
   * Calculate string similarity using Levenshtein distance
   */
  private static calculateStringSimilarity(str1: string, str2: string): number {
    if (!str1 || !str2) return 0;
    
    // Normalize strings
    const s1 = str1.toLowerCase().trim();
    const s2 = str2.toLowerCase().trim();
    
    if (s1 === s2) return 1;
    
    // Calculate Levenshtein distance
    const matrix: number[][] = [];
    const len1 = s1.length;
    const len2 = s2.length;
    
    for (let i = 0; i <= len1; i++) {
      matrix[i] = [i];
    }
    
    for (let j = 0; j <= len2; j++) {
      matrix[0][j] = j;
    }
    
    for (let i = 1; i <= len1; i++) {
      for (let j = 1; j <= len2; j++) {
        const cost = s1[i - 1] === s2[j - 1] ? 0 : 1;
        matrix[i][j] = Math.min(
          matrix[i - 1][j] + 1,      // deletion
          matrix[i][j - 1] + 1,      // insertion
          matrix[i - 1][j - 1] + cost // substitution
        );
      }
    }
    
    const distance = matrix[len1][len2];
    const maxLength = Math.max(len1, len2);
    
    return maxLength === 0 ? 1 : 1 - (distance / maxLength);
  }
  
  /**
   * Calculate location similarity
   */
  private static calculateLocationSimilarity(
    loc1: FoodTruck['current_location'] | undefined | null,
    loc2: FoodTruck['current_location'] | undefined | null
  ): number {
    if (!loc1 || !loc2) return 0;
    
    let similarity = 0;
    let factors = 0;
    
    // Address similarity
    if (loc1.address && loc2.address) {
      similarity += this.calculateStringSimilarity(loc1.address, loc2.address);
      factors++;
    }
    
    // GPS coordinate similarity (within 100 meters = high similarity)
    if (
      loc1.lat &&
      loc1.lng &&
      loc2.lat &&
      loc2.lng
    ) {
      const distance = this.calculateGPSDistance(
        loc1.lat, loc1.lng,
        loc2.lat, loc2.lng
      );
      
      // Distance similarity (closer = higher similarity)
      const distanceSimilarity = distance <= 0.1 ? 1 : Math.max(0, 1 - (distance / 1)); // 1km max
      similarity += distanceSimilarity;
      factors++;
    }
    
    return factors > 0 ? similarity / factors : 0;
  }
  
  /**
   * Calculate GPS distance in kilometers
   */
  private static calculateGPSDistance(lat1: number, lng1: number, lat2: number, lng2: number): number {
    const R = 6371; // Earth's radius in kilometers
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLng = (lng2 - lng1) * Math.PI / 180;
    const a = 
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLng / 2) * Math.sin(dLng / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  }
  
  /**
   * Calculate contact similarity
   */
  private static calculateContactSimilarity(
    contact1: FoodTruck['contact_info'] | undefined | null,
    contact2: FoodTruck['contact_info'] | undefined | null
  ): number {
    if (!contact1 || !contact2) return 0;
    
    let matches = 0;
    let total = 0;
    
    // Phone number exact match
    if (contact1.phone != undefined && contact2.phone != undefined) {
      const phone1 = contact1.phone.replaceAll(/\D/g, ''); // Remove non-digits
      const phone2 = contact2.phone.replaceAll(/\D/g, '');
      if (phone1 === phone2) matches++;
      total++;
    }
    
    // Website exact match
    if (contact1.website != undefined && contact2.website != undefined) {
      const url1 = contact1.website.toLowerCase().replace(/^https?:\/\//, '').replace(/\/$/, '');
      const url2 = contact2.website.toLowerCase().replace(/^https?:\/\//, '').replace(/\/$/, '');
      if (url1 === url2) matches++;
      total++;
    }
    
    // Email similarity
    if (contact1.email != undefined && contact2.email != undefined) {
      if (contact1.email.toLowerCase() === contact2.email.toLowerCase()) matches++;
      total++;
    }
    
    return total > 0 ? matches / total : 0;
  }
  
  /**
   * Calculate menu similarity (basic implementation)
   */
  private static calculateMenuSimilarity(
    menu1: FoodTruck['menu'] | undefined | null,
    menu2: FoodTruck['menu'] | undefined | null
  ): number {
    if (!menu1 || !menu2 || menu1.length === 0 || menu2.length === 0) return 0;
    
    // Simple category name matching
    const categories1 = menu1.map(cat => (cat as { category?: string }).category?.toLowerCase() ?? '').filter(Boolean);
    const categories2 = menu2.map(cat => (cat as { category?: string }).category?.toLowerCase() ?? '').filter(Boolean);
    
    const commonCategories = categories1.filter(cat => categories2.includes(cat));
    const totalCategories = new Set([...categories1, ...categories2]).size;
    
    return totalCategories > 0 ? commonCategories.length / totalCategories : 0;
  }
  
  /**
   * Get confidence level based on similarity score
   */
  private static getConfidenceLevel(similarity: number): 'high' | 'medium' | 'low' {
    if (similarity >= 0.95) return 'high';
    if (similarity >= 0.85) return 'medium';
    return 'low';
  }
  
  /**
   * Get recommendation based on similarity analysis
   */
  private static getRecommendation(similarity: { overall: number; matchedFields: string[] }): 'merge' | 'update' | 'skip' | 'manual_review' {
    if (similarity.overall >= 0.95) return 'merge';
    if (similarity.overall >= 0.9) return 'update';
    if (similarity.overall >= 0.8) return 'manual_review';
    return 'skip';
  }
  
  /**
   * Determine action based on matches
   */
  private static determineAction(matches: DuplicateMatch[], _candidate: Partial<FoodTruck>): 'create' | 'update' | 'merge' | 'manual_review' {
    if (matches.length === 0) return 'create';
    
    const bestMatch = matches[0];
    
    if (bestMatch.confidence === 'high') {
      const recommendation = bestMatch.recommendation;
      if (recommendation === 'merge' || recommendation === 'update') {
        return recommendation;
      }
      return 'manual_review';
    }
    
    return 'manual_review';
  }
  
  /**
   * Generate human-readable reason
   */
  private static generateReason(matches: DuplicateMatch[], _candidate: Partial<FoodTruck>): string {
    if (matches.length === 0) {
      return 'No duplicates found - safe to create new truck entry';
    }
    
    const bestMatch = matches[0];
    const similarity = Math.round(bestMatch.similarity * 100);
    
    return `Found ${matches.length} potential duplicate(s). Best match: ${similarity}% similarity with "${bestMatch.existingTruck.name}" (matched: ${bestMatch.matchedFields.join(', ')})`;
  }
  
  /**
   * Merge duplicate truck data intelligently
   */
  static async mergeDuplicates(targetId: string, sourceId: string): Promise<FoodTruck | { error: string }> {
    const targetResult = await FoodTruckService.getTruckById(targetId);
    const sourceResult = await FoodTruckService.getTruckById(sourceId);

    if ('error' in targetResult) {
      return { error: `Failed to retrieve target truck with ID ${targetId}: ${targetResult.error}` };
    }
    if ('error' in sourceResult) {
      return { error: `Failed to retrieve source truck with ID ${sourceId}: ${sourceResult.error}` };
    }

    const target = targetResult;
    const source = sourceResult;

    // Merge logic: prefer non-null, more complete data
    const mergedData: Partial<FoodTruck> = {
      name: target.name ?? source.name,
      description: target.description ?? source.description,
      cuisine_type: (target.cuisine_type?.length ?? 0) > 0 ? target.cuisine_type : source.cuisine_type,
      price_range: target.price_range ?? source.price_range,
      current_location: target.current_location ?? source.current_location,
      contact_info: {
        ...source.contact_info,
        ...target.contact_info // Target takes precedence
      },
      operating_hours: target.operating_hours ?? source.operating_hours,
      menu: (target.menu?.length ?? 0) > 0 ? target.menu : source.menu,
      social_media: {
        ...source.social_media,
        ...target.social_media
      },
      source_urls: [...new Set([...(target.source_urls ?? []), ...(source.source_urls ?? [])])],
      last_scraped_at: new Date().toISOString()
    };

    // Update target with merged data
    const updatedTruckResult = await FoodTruckService.updateTruck(targetId, mergedData);

    if ('error' in updatedTruckResult) {
      return { error: `Failed to update target truck with merged data: ${updatedTruckResult.error}` };
    }

    console.info(`Merged truck ${sourceId} into ${targetId}`);

    return updatedTruckResult;
  }
}

import { APIUsageService } from '../supabase';
import { APIMonitor } from '../monitoring/apiMonitor';

export interface UsageLimits {
  dailyRequestLimit: number;
  dailyTokenLimit: number;
}

export interface UsageCheck {
  canMakeRequest: boolean;
  usage?: {
    requests: { used: number; limit: number; remaining: number };
    tokens: { used: number; limit: number; remaining: number };
  };
}

export class GeminiUsageLimits {
  private static readonly DEFAULT_LIMITS: UsageLimits = {
    dailyRequestLimit: 1500,
    dailyTokenLimit: 32_000,
  };

  static async checkUsageLimits(limits: UsageLimits = this.DEFAULT_LIMITS): Promise<UsageCheck> {
    try {
      const usage = await APIUsageService.getTodayUsage('gemini');

      if (!usage) {
        return { canMakeRequest: true };
      }

      const requestsUsed = usage.requests_count ?? 0;
      const tokensUsed = usage.tokens_used ?? 0;

      const requestsRemaining = limits.dailyRequestLimit - requestsUsed;
      const tokensRemaining = limits.dailyTokenLimit - tokensUsed;

      return {
        canMakeRequest: requestsRemaining > 0 && tokensRemaining > 100, // Keep 100 token buffer
        usage: {
          requests: {
            used: requestsUsed,
            limit: limits.dailyRequestLimit,
            remaining: requestsRemaining,
          },
          tokens: {
            used: tokensUsed,
            limit: limits.dailyTokenLimit,
            remaining: tokensRemaining,
          },
        },
      };
    } catch (error: unknown) {
      console.warn('Error checking Gemini usage limits:', error);
      return { canMakeRequest: false };
    }
  }

  static async checkWithMonitoring(
    estimatedTokens: number,
    limits: UsageLimits = this.DEFAULT_LIMITS
  ): Promise<{ allowed: boolean; reason?: string; waitTime?: number }> {
    const canMakeRequest = await APIMonitor.canMakeRequest('gemini', 1, estimatedTokens);

    if (!canMakeRequest.allowed) {
      return {
        allowed: false,
        reason: `API limit reached: ${canMakeRequest.reason}. Wait time: ${Math.ceil((canMakeRequest.waitTime ?? 0) / 1000 / 60)} minutes`,
      };
    }

    return { allowed: true };
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { AuditLogger, SecurityEvent } from '@/lib/security/auditLogger';
import { createSupabaseMiddlewareClient } from '@/lib/supabaseMiddleware';

interface RequestMetadata {
  ip: string;
  userAgent: string;
  url: string;
  method: string;
}

interface SupabaseUser {
  id: string;
  email?: string;
}

interface SupabaseProfile {
  role?: string;
}

interface LogAndRedirectParams {
  req: NextRequest;
  res: NextResponse;
  requestMetadata: RequestMetadata;
  reason: string;
  userError?: { message?: string };
}

interface LogSecurityEventParams {
  event_type: SecurityEvent['event_type'];
  ip_address: string;
  user_agent: string;
  details: Record<string, any>;
  severity: SecurityEvent['severity'];
  user_id?: string;
  user_email?: string;
}

interface LogSecurityEventAndRedirectParams {
  req: NextRequest;
  res: NextResponse;
  logParams: LogSecurityEventParams;
  redirectPath: string;
  redirectFromPath?: string;
}

async function logSecurityEventAndRedirect({
  req,
  res,
  logParams,
  redirectPath,
  redirectFromPath,
}: LogSecurityEventAndRedirectParams) {
  await AuditLogger.logSecurityEvent(logParams);
  const redirectUrl = req.nextUrl.clone();
  redirectUrl.pathname = redirectPath;
  if (redirectFromPath) {
    redirectUrl.searchParams.set(`redirectedFrom`, redirectFromPath);
  }
  return NextResponse.redirect(redirectUrl);
}

async function logAndRedirect({ req, res, requestMetadata, reason, userError }: LogAndRedirectParams) {
  return logSecurityEventAndRedirect({
    req,
    res,
    logParams: {
      event_type: 'permission_denied',
      ip_address: requestMetadata.ip,
      user_agent: requestMetadata.userAgent,
      details: {
        attempted_url: requestMetadata.url,
        reason,
        error: userError?.message,
      },
      severity: 'warning',
    },
    redirectPath: '/login',
    redirectFromPath: req.nextUrl.pathname
  });
}

interface LogAndRedirectDeniedParams {
  req: NextRequest;
  res: NextResponse;
  requestMetadata: RequestMetadata;
  user: SupabaseUser;
  profile: SupabaseProfile | null;
  profileQueryError?: { message?: string } | null; // Changed to allow null
}

async function logAndRedirectDenied({ req, res, requestMetadata, user, profile, profileQueryError }: LogAndRedirectDeniedParams) {
  return logSecurityEventAndRedirect({
    req,
    res,
    logParams: {
      event_type: 'permission_denied',
      user_id: user.id,
      user_email: user.email ?? undefined,
      ip_address: requestMetadata.ip,
      user_agent: requestMetadata.userAgent,
      details: {
        attempted_url: requestMetadata.url,
        user_role: profile?.role ?? 'none',
        reason: 'insufficient_privileges',
        error: profileQueryError?.message,
      },
      severity: 'error',
    },
    redirectPath: '/access-denied'
  });
}

export async function protectAdminRoutes(req: NextRequest, res: NextResponse, requestMetadata: RequestMetadata) {
  const supabase = createSupabaseMiddlewareClient(req, res);
  const { data, error: userError } = await supabase.auth.getUser();
  const user = data?.user;

  if (userError || !user) {
    return logAndRedirect({ req, res, requestMetadata, reason: 'no_session', userError: userError ?? undefined });
  }
  // Explicitly type the result of the Supabase query
  const { data: profile, error: profileQueryError } = await supabase
    .from('profiles')
    .select('role')
    .eq('id', user.id)
    .single() as { data: SupabaseProfile | null; error: { message?: string } | null };
  if (profileQueryError || (profile && profile.role !== 'admin')) {
    return logAndRedirectDenied({ req, res, requestMetadata, user, profile: profile ?? null, profileQueryError: profileQueryError ?? undefined });
  }
  if (req.method !== 'GET' || req.nextUrl.pathname.includes('/api/')) {
    await AuditLogger.logDataAccess({
      userId: user.id,
      userEmail: user.email ?? 'unknown',
      resourceType: 'admin_panel',
      resourceId: req.nextUrl.pathname,
      action: req.method === 'GET' ? 'read' : 'admin_access',
      request: {
        ip: requestMetadata.ip,
        userAgent: requestMetadata.userAgent,
      },
    });
  }
  return res;
}

/**
 * SOTA Database Query Optimization and Caching
 * Implements Next.js unstable_cache for optimal performance
 */

import { unstable_cache } from 'next/cache';
import { supabaseAdmin, FoodTruckService } from '@/lib/supabase';
import type { FoodTruck } from '@/lib/types';

// Cache configuration
const CACHE_CONFIG = {
  // Short-term cache for frequently changing data
  SHORT_TTL: 60 * 5, // 5 minutes
  // Medium-term cache for semi-static data
  MEDIUM_TTL: 60 * 30, // 30 minutes
  // Long-term cache for static data
  LONG_TTL: 60 * 60 * 24, // 24 hours
} as const;

/**
 * Cached food truck queries with optimized database access
 */
export const CachedFoodTruckService = {
  /**
   * Get all food trucks with caching
   * Cache for 30 minutes since truck data changes moderately
   */
  getAllTrucksCached : unstable_cache(
    async (): Promise<{ trucks: FoodTruck[]; count: number }> => {
      console.info('CachedFoodTruckService: Cache miss - fetching all trucks from database');
      const result = await FoodTruckService.getAllTrucks();
      if ('error' in result) {
        throw new Error(`Failed to fetch all trucks: ${result.error}`);
      }
      return { trucks: result.trucks, count: result.total };
    },
    ['all-trucks'],
    {
      revalidate: CACHE_CONFIG.MEDIUM_TTL,
      tags: ['food-trucks', 'all-trucks']
    }
  ),

  /**
   * Get trucks by location with caching
   * Cache for 5 minutes since location-based queries are time-sensitive
   */
  getTrucksByLocationCached : unstable_cache(
    async (lat: number, lng: number, radiusKm: number): Promise<FoodTruck[]> => {
      console.info(`CachedFoodTruckService: Cache miss - fetching trucks near ${lat},${lng} (${radiusKm}km)`);
      const result = await FoodTruckService.getTrucksByLocation(lat, lng, radiusKm);
      if ('error' in result) {
        throw new Error(`Failed to fetch trucks by location: ${result.error}`);
      }
      return result as FoodTruck[];
    },
    ['trucks-by-location'],
    {
      revalidate: CACHE_CONFIG.SHORT_TTL,
      tags: ['food-trucks', 'location-search']
    }
  ),

  /**
   * Get truck by ID with caching
   * Cache for 30 minutes since individual truck data is relatively stable
   */
  getTruckByIdCached : unstable_cache(
    async (id: string): Promise<FoodTruck | null> => {
      console.info(`CachedFoodTruckService: Cache miss - fetching truck ${id} from database`);
      const result = await FoodTruckService.getTruckById(id);
      if ('error' in result) {
        return null;
      }
      return result as FoodTruck;
    },
    ['truck-by-id'],
    {
      revalidate: CACHE_CONFIG.MEDIUM_TTL,
      tags: ['food-trucks', 'truck-details']
    }
  ),

  /**
   * Search trucks with caching
   * Cache for 5 minutes since search results should be relatively fresh
   */
  searchTrucksCached : unstable_cache(
    async (query: string, filters?: {
      cuisine?: string;
      openNow?: boolean;
      lat?: number;
      lng?: number;
      radius?: number;
    }): Promise<FoodTruck[]> => {
      console.info(`CachedFoodTruckService: Cache miss - searching trucks for "${query}"`);

      if (supabaseAdmin == undefined) {
        throw new Error('Supabase admin client not available');
      }

      let dbQuery = supabaseAdmin
        .from('food_trucks')
        .select('*')
        .or(`name.ilike.%${query}%,description.ilike.%${query}%,cuisine_type.cs.{${query}}`);

      // Apply filters
      if (filters?.cuisine != undefined && filters.cuisine !== '') {
        dbQuery = dbQuery.contains('cuisine_type', [filters.cuisine]);
      }

      const { data, error }: { data: FoodTruck[] | null; error: any } = await dbQuery.limit(50);
      const trucks: FoodTruck[] = data ?? [];

      if (error != undefined) {
        throw new Error(`Search query failed: ${error.message}`);
      }

      let results: FoodTruck[] = trucks ?? [];

      // Apply location filter if provided
      if (filters?.lat != undefined && filters?.lng != undefined && filters?.radius != undefined) {
        results = results.filter((truck: FoodTruck): truck is FoodTruck => {
          if (truck.current_location?.lat == undefined || truck.current_location?.lng == undefined) {
            return false;
          }
          const distance = calculateDistance(
            filters.lat!, // Assert as number, as it's checked by the outer if condition
            filters.lng!, // Assert as number, as it's checked by the outer if condition
            truck.current_location.lat,
            truck.current_location.lng
          );
          return distance <= (filters.radius ?? 10);
        });
      }

      // Apply openNow filter if provided
      if (filters?.openNow === true) {
        const now = new Date();
        const currentDay = now.toLocaleDateString('en-US', { weekday: 'short' }).toLowerCase(); // 'mon', 'tue', etc.
        const currentTime = now.getHours() * 100 + now.getMinutes(); // HHMM format

        results = results.filter((truck: FoodTruck): truck is FoodTruck => {
          const hours = truck.operating_hours?.[currentDay as keyof typeof truck.operating_hours] as { closed?: boolean; open?: string; close?: string } | undefined;
          if (hours == undefined || hours.closed === true) return false;

          const openTime = parseTimeString(hours.open ?? '');
          const closeTime = parseTimeString(hours.close ?? '');

          return currentTime >= openTime && currentTime <= closeTime;
        });
      }

      return results;
    },
    ['search-trucks'],
    {
      revalidate: CACHE_CONFIG.SHORT_TTL,
      tags: ['food-trucks', 'search']
    }
  ),

  /**
   * Get data quality statistics with caching
   * Cache for 24 hours since quality stats change slowly
   */
  getDataQualityStatsCached : unstable_cache(
    async (): Promise<{
      averageScore: number;
      distribution: Record<string, number>;
      totalTrucks: number;
    }> => {
      console.info('CachedFoodTruckService: Cache miss - calculating data quality stats');

      if (supabaseAdmin == undefined) {
        throw new Error('Supabase admin client not available');
      }

      const { data: trucks, error } = await supabaseAdmin
        .from('food_trucks')
        .select('data_quality_score');

      if (error) {
        throw new Error(`Quality stats query failed: ${error.message}`);
      }

      const scores = trucks == undefined ? [] : trucks.map((t: { data_quality_score?: number }) => t.data_quality_score ?? 0);
      const averageScore = scores.length > 0
        ? scores.reduce((sum: number, score: number) => sum + score, 0) / scores.length
        : 0;

      const distribution = {
        high: scores.filter((s: number) => s >= 0.8).length,
        medium: scores.filter((s: number) => s >= 0.6 && s < 0.8).length,
        low: scores.filter((s: number) => s < 0.6).length
      };

      return {
        averageScore: Math.round(averageScore * 100) / 100,
        distribution,
        totalTrucks: scores.length
      };
    },
    ['data-quality-stats'],
    {
      revalidate: CACHE_CONFIG.LONG_TTL,
      tags: ['food-trucks', 'data-quality']
    }
  ),
};

