import { useEffect } from 'react';

import { supabase } from '@/lib/supabase'; 
import { PipelineEvent } from '@/lib/types';

/**
 * Manages event subscriptions for pipeline events using Supabase.
 * @example
 * EventSubscriptionManager()
 * @returns {void} This component serves purely for side-effects of subscription management.
 * @description
 *   - Utilizes the Supabase Realtime Client to subscribe and process "pipeline-events".
 *   - Includes an effect cleanup to unsubscribe from events when the component unmounts.
 *   - Current implementation logs events to the console as placeholders.
 */
type RealtimeChannelStatus = 'SUBSCRIBED' | 'CLOSED' | 'CHANNEL_ERROR' | 'TIMED_OUT';

function EventSubscriptionManager() {
  useEffect(() => {
    const handleEvent = (payload: { new: PipelineEvent }) => {
      try {
        const event = payload.new;
        if (event && typeof event === 'object' && 'type' in event) {
          // Process the event based on its type
          switch (event.type) {
            case 'job_started': {
              console.info('Job started:', event.payload);
              break;
            }
            case 'job_completed': {
              console.info('Job completed:', event.payload);
              break;
            }
            case 'data_updated': {
              console.info('Data updated:', event.payload);
              break;
            }
            default: {
              console.warn('Received unknown event type:', event.type);
            }
          }
        } else {
          console.warn('Received malformed event:', payload);
        }
      } catch (error) {
        console.error('Error processing pipeline event:', error);
      }
    };

    const subscription = supabase
      .channel('pipeline-events')
      .on('postgres_changes', { event: 'INSERT', schema: 'public', table: 'pipeline_events' }, handleEvent)
      .subscribe((status: RealtimeChannelStatus, error?: Error) => {
        if (status === 'SUBSCRIBED') {
          console.log('Successfully subscribed to pipeline events.');
        }
        if (status === 'CHANNEL_ERROR' && error) {
          console.error('Pipeline event subscription error:', error);
        }
      });

    return () => {
      void supabase.realtime.removeChannel(subscription);
    };
  }, []);
}

export default EventSubscriptionManager;

'use client';


import { StatusMetric } from './useSystemMetrics.tsx'; // Import StatusMetric type
import { getStatusColor, getStatusIcon, getTrendIcon } from './StatusHelpers'; // Import helper functions

type Status = 'healthy' | 'warning' | 'error' | 'unknown';
type Trend = 'up' | 'down' | 'stable' | undefined;

interface SystemMetricsGridProps {
  readonly metrics: StatusMetric[];
}

/**
* Displays a grid of system metrics using provided data.
* @example
* SystemMetricsGrid({ metrics: sampleMetricsArray })
* <div className="grid">...</div>
* @param {SystemMetricsGridProps} {metrics} - An array of metric objects to be displayed in the grid.
* @returns {JSX.Element} A JSX element representing a styled grid displaying system metrics.
* @description
*   - Each metric is rendered with icons representing its status and trend.
*   - Metrics are styled based on their status, indicating their current state.
*   - Responsive grid layout adapts based on screen size.
*/
export function SystemMetricsGrid({ metrics }: SystemMetricsGridProps) {
  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
      {metrics.map((metric, index) => (
        <div
          key={index}
          className={`p-3 rounded-lg border ${getStatusColor(metric.status as Status)}`}
        >
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center gap-2">
              {metric.icon}
              <span className="text-sm font-medium">{metric.label}</span>
            </div>
            {getStatusIcon(metric.status as Status)}
          </div>
          <div className="flex items-center gap-2">
            <span className="text-lg font-bold">
              {metric.value}
              {metric.unit}
            </span>
            {getTrendIcon(metric.trend)}
          </div>
        </div>
      ))}
    </div>
  );
}

import TruckCard from './TruckCard';
import { formatPrice } from '@/lib/utils/foodTruckHelpers';
import { MenuSection, SocialMediaSection, ContactSection } from '.';

interface MenuItem {
  name: string;
  price_cents?: number; // Make price_cents optional
}

/**
* Renders the card content for a food truck including its name, operating hours, popular menu items, and contact details.
* @example
* TruckCardContent({
*   truck: { name: 'Tasty Truck', address: '123 Street', phone_number: '123-456-7890' },
*   todayHours: { open: '8:00 AM', close: '5:00 PM', closed: false },
*   popularItems: [{ name: 'Burger', price_cents: 500 }],
* })
* // returns JSX containing truck's card
* @param {object} truck - The truck object containing name, address, and phone number.
* @param {object} todayHours - An optional object specifying today's opening and closing times and whether it is closed.
* @param {MenuItem[]} popularItems - List of popular menu items to display.
* @returns {JSX.Element} The JSX content for rendering the truck's card.
* @description
*   - Uses default values for missing truck properties to ensure components render safely.
*   - Calculates the average price of popular items and formats it to two decimal places.
*   - Validates the existence of social media and contact information before rendering related sections.
*/
export function TruckCardContent({
  truck,
  todayHours,
  popularItems = [],
}: {
  readonly truck: { name: string; address?: string; phone_number?: string; social_media?: Record<string, string> };
  readonly todayHours?: { open?: string; close?: string; closed?: boolean };
  readonly popularItems: MenuItem[];
}) {
  const {
    name = 'Unnamed Truck',
    social_media = {},
    address = '', // Provide safe defaults
    phone_number = '',
  } = truck;

  const avgDailyPrice = (
    popularItems.reduce((acc, item) => acc + (item.price_cents ?? 0), 0) /
    popularItems.length ?? 0
  ).toFixed(2);

  return (
    <main className="ui-truck-container">
      {name && (
        <>
          <TruckCard title={name} avgCost={`$${avgDailyPrice}`} />
          {todayHours !== undefined && !todayHours.closed && (
            <div className="hours-display">
              <strong>Today:</strong> {todayHours.open} - {todayHours.close}
            </div>
          )}
        </>
      )}

      {popularItems.length > 0 && (
        <MenuSection
          items={popularItems.map(item => ({
            name: item.name,
            price: formatPrice(item.price_cents),
          }))}
        />
      )}

      {/* SocialMediaSection and ContactSection require validation */}
      {Object.keys(social_media).length > 0 && (typeof social_media === 'object')&& (
        <SocialMediaSection social_media={social_media} />
      )}

      {/* Refactored ContactSection - handle undefined values explicitly*/}
      {(address || phone_number) && (
        <ContactSection address={address} phone_number={phone_number} />
      )}
    </main>
  );
}

