import React, { useEffect, useState } from 'react';
import { SupabaseRealtimeClient } from '@/lib/supabase'; 

interface SystemStatusResponse {
  status: string;
  data: {
    status: string;
  };
}

/**
* Represents system status indicators in real-time
* @example
* SystemStatusIndicators()
* <div><h3>Current System Status:</h3><span>ONLINE</span></div>
* @returns {JSX.Element} A JSX element displaying the current system status.
* @description
*   - Utilizes Supabase Realtime Client for subscribing to system status updates.
*   - Updates the display with the latest system status retrieved from a realtime database.
*   - Handles subscription clean-up on component unmount to avoid memory leaks.
*/
function SystemStatusIndicators() {
  const [systemStatus, setSystemStatus] = useState('UNKNOWN');

  useEffect(() => {
    const subscription = SupabaseRealtimeClient.subscribe('system-status', (response: unknown) => {
      if (typeof response === 'object' && response !== null && 'status' in response) {
        const typedResponse = response as SystemStatusResponse;
        const responseStatus = typedResponse.status; // Extract status to a variable

        if (typeof responseStatus === 'string' && responseStatus === "success") { // eslint-disable-next-line sonarjs/different-types-comparison
          if (typeof typedResponse.data === 'object' && typedResponse.data !== null && 'status' in typedResponse.data) {
            setSystemStatus(typedResponse.data.status);
          } else {
            console.error("Invalid data structure in system status response");
          }
        } else {
          console.error("Failed to retrieve system status or invalid response format");
        }
      } else {
        console.error("Failed to retrieve system status or invalid response format");
      }
    });

    return () => subscription.unsubscribe() as void;
  }, []);

  return (
    <div>
      <h3>Current System Status:</h3>
      <span>{systemStatus}</span>
    </div>
  );
}

export default SystemStatusIndicators;

import React from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import type { FoodTruck } from '@/lib/supabase';

interface TruckBasicInfoProps {
  readonly truck: FoodTruck;
}

/**
* Renders basic information about a food truck within a styled card component.
* @example
* TruckBasicInfo({ truck: { name: "Tasty Truck", description: "Best burgers in town", cuisine_type: "American", price_range: "$$$" } })
* // Will return a React component displaying the food truck's name, description, cuisine type, and price range in a formatted card layout.
* @param {object} truck - An object containing food truck information, including name, description, cuisine type, and price range.
* @returns {JSX.Element} A styled React component displaying the provided food truck information.
* @description
*   - Applies styling for both light and dark mode themes.
*   - Only displays fields if corresponding values are provided within the truck object.
*/
export function TruckBasicInfo({ truck }: Readonly<TruckBasicInfoProps>) {
  return (
    <Card className="dark:bg-slate-800 dark:border-slate-700">
      <CardHeader>
        <CardTitle className="dark:text-gray-100">About</CardTitle>
        <CardDescription className="dark:text-gray-400">Food truck information</CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <div>
          <label className="text-sm font-medium text-gray-500 dark:text-gray-400">Name</label>
          <p className="text-lg font-semibold dark:text-gray-100">{truck.name}</p>
        </div>

        {truck.description != undefined && (
          <div>
            <label className="text-sm font-medium text-gray-500 dark:text-gray-400">
              Description
            </label>
            <p className="text-gray-900 dark:text-gray-200">{truck.description}</p>
          </div>
        )}

        {truck.cuisine_type && (
          <div>
            <label className="text-sm font-medium text-gray-500 dark:text-gray-400">
              Cuisine Type
            </label>
            <p className="text-gray-900 dark:text-gray-200">{truck.cuisine_type}</p>
          </div>
        )}

        {truck.price_range && (
          <div>
            <label className="text-sm font-medium text-gray-500 dark:text-gray-400">
              Price Range
            </label>
            <Badge variant="outline" className="ml-2">
              {truck.price_range}
            </Badge>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

import React from 'react';
import { CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { MapPin } from 'lucide-react';
import { FoodTruck } from '@/lib/types';

interface TruckCardHeaderProps {
  readonly truck: FoodTruck;
  readonly isOpen: boolean;
  readonly popularItems: Array<{ name: string; price: number | string | undefined }>;
  readonly priceRange: string | undefined;
}

/**
 * Renders a header component for a truck card, displaying truck details and status.
 * @example
 * TruckCardHeader({
 *   truck: { name: 'Food Truck', current_location: { address: '123 Street' } },
 *   isOpen: true,
 *   popularItems: [{ name: 'Burger', price: undefined }],
 *   priceRange: '$$'
 * })
 * <CardHeader>...</CardHeader>
 * @param {Object} truck - Object containing information about the truck, including name and current location.
 * @param {boolean} isOpen - Indicates whether the truck is currently open or closed.
 * @param {Array} popularItems - Array containing popular items offered by the truck, typically objects with a name and optional price.
 * @param {string} priceRange - String indicating the general price range of items if explicit prices are unavailable.
 * @returns {JSX.Element} JSX representation of the card header for a truck.
 * @description
 *   - Displays the truck's name and current address if available.
 *   - Shows the open/closed status with a badge, changing appearance according to the status.
 *   - Fallback badge displaying price range shows only if no prices are available for popular items.
 */
export function TruckCardHeader({ truck, isOpen, popularItems, priceRange }: TruckCardHeaderProps) {
  return (
    <CardHeader>
      <div className="flex justify-between items-start">
        <div className="flex-1">
          <CardTitle className="text-lg dark:text-gray-100">{truck.name}</CardTitle>
          {truck.current_location?.address != undefined && (
            <CardDescription className="flex items-center mt-1 dark:text-gray-400">
              <MapPin className="size-4 mr-1" />
              {truck.current_location.address}
            </CardDescription>
          )}
        </div>
        <div className="flex flex-col items-end space-y-1">
          <Badge variant={isOpen ? 'default' : 'secondary'}>{isOpen ? 'Open' : 'Closed'}</Badge>
          {/* Show price range fallback if no explicit prices */}
          {popularItems.every((item) => !item.price) && priceRange && (
            <Badge variant="outline" className="mt-1">
              {priceRange}
            </Badge>
          )}
        </div>
      </div>
    </CardHeader>
  );
}

import React from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Phone, Mail, Globe } from 'lucide-react';
import type { FoodTruck } from '@/lib/supabase';

interface TruckContactInfoProps {
  readonly truck: FoodTruck;
}

interface ContactFieldProps {
  readonly icon: React.ComponentType<{ className?: string }>;
  readonly label: string;
  readonly value?: string | null;
  readonly href?: string;
  readonly unavailableText: string;
}

/**
 * Renders a contact field with icon, label, and value, handling both available and unavailable states.
 * @example
 * ContactField({
 *   icon: EmailIcon,
 *   label: 'Email',
 *   value: 'example@example.com',
 *   href: 'mailto:example@example.com',
 *   unavailableText: 'Not Available'
 * })
 * <div>...</div>
 * @param {React.ComponentType} icon - The icon component to display in the contact field.
 * @param {string} label - The label to display above the contact value.
 * @param {string} value - The contact value to display; if undefined or empty, displays unavailableText instead.
 * @param {string} [href] - The link associated with the contact value for redirection. Determines link behavior based on URL format.
 * @param {string} unavailableText - Text displayed when value is unavailable or empty.
 * @returns {JSX.Element} JSX structure for the contact field.
 * @description
 *   - Opens external links in a new tab with 'noopener noreferrer' for security.
 *   - Conditionally renders either a hyperlink or plain text based on value and href.
 *   - Applies specific styling based on the availability of the value.
 */
function ContactField({
  icon: Icon,
  label,
  value,
  href,
  unavailableText,
}: Readonly<ContactFieldProps>) {
  if (value == undefined || value.length === 0) {
    // Handles null, undefined, and empty string
    return (
      <div className="flex items-center gap-3 text-gray-400">
        <Icon className="size-4" />
        <span className="text-sm">{unavailableText}</span>
      </div>
    );
  }

  return (
    <div className="flex items-center gap-3">
      <Icon className="size-4 text-gray-500" />
      <div>
        <label className="text-sm font-medium text-gray-500 dark:text-gray-400">{label}</label>
        {href ? (
          <a
            href={href}
            target={href.startsWith('http') ? '_blank' : undefined}
            rel={href.startsWith('http') ? 'noopener noreferrer' : undefined}
            className="block text-blue-600 hover:text-blue-800 dark:text-blue-400 dark:hover:text-blue-300 underline"
          >
            {href.startsWith('http') ? 'Visit Website' : value}
          </a>
        ) : (
          <p className="text-gray-900 dark:text-gray-200">{value}</p>
        )}
      </div>
    </div>
  );
}

/**
 * Renders social media links based on provided handles.
 * @example
 * SocialMediaLinks({ socialMedia: { instagram: 'insta_user', twitter: 'twit_user' } })
 * Renders and displays links for Instagram and Twitter.
 * @param {Object} {socialMedia} - Contains platform keys mapped to user handles.
 * @returns {JSX.Element|undefined} A JSX element displaying social media links or undefined if no valid links exist.
 * @description
 *   - Checks for undefined or empty socialMedia input to prevent unnecessary rendering.
 *   - Supports Instagram, Facebook, and Twitter platforms with specific styles.
 *   - Handles links are generated dynamically based on platform keys and user handles.
 *   - Applies consistent styling for light and dark themes using color properties.
 */
function SocialMediaLinks({
  socialMedia,
}: Readonly<{ readonly socialMedia?: Record<string, string> }>) {
  if (socialMedia == undefined || Object.keys(socialMedia).length === 0) {
    return;
  }

  const socialPlatforms = [
    { key: 'instagram', name: 'Instagram', baseUrl: 'https://instagram.com/', color: 'pink' },
    { key: 'facebook', name: 'Facebook', baseUrl: 'https://facebook.com/', color: 'blue' },
    { key: 'twitter', name: 'Twitter', baseUrl: 'https://twitter.com/', color: 'sky' },
  ];

  return (
    <div>
      <label className="text-sm font-medium text-gray-500 dark:text-gray-400">Social Media</label>
      <div className="flex flex-wrap gap-2 mt-2">
        {socialPlatforms.map(({ key, name, baseUrl, color }) => {
          const handle = socialMedia[key];
          if (handle == undefined || handle.length === 0) return; // Handles null, undefined, and empty string

          return (
            <a
              key={key}
              href={`${baseUrl}${handle}`}
              target="_blank"
              rel="noopener noreferrer"
              className={`flex items-center gap-1 px-2 py-1 bg-${color}-100 text-${color}-800 rounded-md text-sm hover:bg-${color}-200 dark:bg-${color}-900 dark:text-${color}-200`}
            >
              <Globe className="size-3" />
              {name}
            </a>
          );
        })}
      </div>
    </div>
  );
}

/**
 * Renders a truck's contact information within a styled card component.
 * @example
 * TruckContactInfo({ truck: sampleTruckObject })
 * <Card>...</Card>
 * @param {Readonly<TruckContactInfoProps>} {truck} - Object containing truck's contact information and social media links.
 * @returns {JSX.Element} A card component displaying various contact fields and social media links.
 * @description
 *   - Utilizes conditional rendering to display availability of contact information.
 *   - Applies Tailwind CSS classes for dark mode styling.
 *   - Integrates icons alongside contact labels for visual aid.
 *   - SocialMediaLinks component is used to list the truck's social media presence.
 */
export function TruckContactInfo({ truck }: Readonly<TruckContactInfoProps>) {
  return (
    <Card className="dark:bg-slate-800 dark:border-slate-700">
      <CardHeader>
        <CardTitle className="flex items-center gap-2 dark:text-gray-100">
          <Phone className="size-5" />
          Contact Information
        </CardTitle>
        <CardDescription className="dark:text-gray-400">Get in touch</CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <ContactField
          icon={Phone}
          label="Phone"
          value={truck.contact_info?.phone}
          href={truck.contact_info?.phone ? `tel:${truck.contact_info.phone}` : undefined}
          unavailableText="No phone number available"
        />

        <ContactField
          icon={Mail}
          label="Email"
          value={truck.contact_info?.email}
          href={truck.contact_info?.email ? `mailto:${truck.contact_info.email}` : undefined}
          unavailableText="No email available"
        />

        <ContactField
          icon={Globe}
          label="Website"
          value={truck.contact_info?.website}
          href={truck.contact_info?.website}
          unavailableText="No website available"
        />

        <SocialMediaLinks socialMedia={truck.social_media} />
      </CardContent>
    </Card>
  );
}

import React from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Clock } from 'lucide-react';
import type { FoodTruck } from '@/lib/supabase';

interface TruckOperatingHoursProps {
  readonly truck: FoodTruck;
}

interface DayData {
  closed?: boolean;
  open?: string;
  close?: string;
}

/**
 * Displays the schedule for a specific day.
 * @example
 * DaySchedule({ day: 'monday', dayData: { open: '8:00 AM', close: '5:00 PM', closed: false } })
 * <div>...</div>
 * @param {Object} props - The properties for the schedule component.
 * @param {string} props.day - The name of the day (e.g., 'monday').
 * @param {DayData} [props.dayData] - Optional data object containing the opening and closing hours and a closed status.
 * @returns {JSX.Element} A React component representing the schedule for the given day.
 * @description
 *   - Capitalizes the first letter of the provided day's name.
 *   - Displays 'Closed' if dayData.closed is true, otherwise displays opening and closing times.
 *   - 'N/A' is shown if opening or closing time is not defined.
 */
function DaySchedule({ day, dayData }: Readonly<{ day: string; dayData?: DayData }>) {
  const dayName = day.charAt(0).toUpperCase() + day.slice(1);

  return (
    <div className="flex justify-between items-center">
      <span className="text-sm font-medium dark:text-gray-200">{dayName}</span>
      <span className="text-sm text-gray-600 dark:text-gray-400">
        {dayData?.closed === true
          ? 'Closed'
          : `${dayData?.open ?? 'N/A'} - ${dayData?.close ?? 'N/A'}`}
      </span>
    </div>
  );
}

/**
 * Renders a truck's operating hours in a card format.
 * @example
 * TruckOperatingHours({ truck: { operating_hours: { monday: { open: "9:00 AM", close: "5:00 PM" } } } })
 * <Card>...</Card>
 * @param {TruckOperatingHoursProps} {truck} - Object containing truck details including operating hours.
 * @returns {JSX.Element} A JSX element representing truck operating hours card.
 * @description
 *   - Utilizes a Card component for displaying the information visually.
 *   - Iterates through the days of the week to display operating hours for each day.
 *   - Displays a message when operating hours are not available.
 */
export function TruckOperatingHours({ truck }: TruckOperatingHoursProps) {
  const daysOfWeek = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'];
  const hasOperatingHours = truck.operating_hours && Object.keys(truck.operating_hours).length > 0;

  return (
    <Card className="dark:bg-slate-800 dark:border-slate-700">
      <CardHeader>
        <CardTitle className="flex items-center gap-2 dark:text-gray-100">
          <Clock className="size-5" />
          Operating Hours
        </CardTitle>
        <CardDescription className="dark:text-gray-400">Daily schedule</CardDescription>
      </CardHeader>
      <CardContent>
        {hasOperatingHours ? (
          <div className="space-y-2">
            {daysOfWeek.map((day) => {
              const dayData = truck.operating_hours?.[day as keyof typeof truck.operating_hours] as
                | DayData
                | undefined;
              return <DaySchedule key={day} day={day} dayData={dayData} />;
            })}
          </div>
        ) : (
          <p className="text-gray-400 text-sm">Operating hours not available</p>
        )}
      </CardContent>
    </Card>
  );
}

import React from 'react';
import { TruckRating } from '@/lib/types';

/**
 * Displays truck ratings and calculates average score.
 * @example
 * renderRatings({ ratings: [{ id: 1, title: 'Service', score: 4.5 }, { id: 2, title: 'Quality', score: 4.0 }] })
 * <div>A rendered ratings breakdown</div>
 * @param {{ ratings: Array<{ id: number, title: string, score: number }> }} ratings - An array of truck ratings, each containing an id, a title, and a score.
 * @returns {JSX.Element} A JSX element displaying the list of ratings with an average score.
 * @description
 *   - Returns a message that no ratings are available if the input array is empty or undefined.
 *   - Calculates the average rating score using all ratings in the array.
 *   - Renders individual ratings dynamically within the returned JSX.
 */
const RatingSection = ({ ratings }: { ratings: TruckRating[] }) => {
  if (!ratings || ratings.length === 0) return <p>No ratings yet</p>;

  const avgScore = ratings.reduce((total, rating) => total + rating.score, 0) / ratings.length;

  return (
    <div className="rating-container">
      <h3>Ratings Breakdown</h3>
      <div className="average-rating">Average: {avgScore.toFixed(1)}</div>
      {ratings.map(rating => (
        <div key={rating.id} className="individual-rating">
          <p>{rating.title}: {rating.score}</p>
        </div>
      ))}
    </div>
  );
};

export default RatingSection;

