async function getOAuthStatus(): Promise<OAuthStatus> {
  const status: OAuthStatus = {
    timestamp: new Date().toISOString(),
    environment: process.env.NODE_ENV === 'production' ? 'production' : 'development',
    supabase: {
      connected: false,
      projectId: 'zkwliyjjkdnigizidlln' as string,
    },
    environment_variables: {
      supabaseUrl: process.env.NEXT_PUBLIC_SUPABASE_URL !== undefined,
      supabaseAnonKey: process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY !== undefined,
      supabaseServiceKey: process.env.SUPABASE_SERVICE_ROLE_KEY !== undefined,
    },
    oauth_flow: {
      loginPageExists: true,
      callbackRouteExists: true,
      authProviderConfigured: false,
    },
    recommendations: [],
    overall_status: 'not_configured',
  };

  await checkSupabaseConnection(status, supabase);
  await checkSupabaseAuthSettings(status);
  await testOAuthProvider(status, supabase);

  status.recommendations = generateRecommendations(status);
  status.overall_status = determineOverallStatus(status);

  return status;
}

async function checkSupabaseConnection(status: OAuthStatus, supabase: SupabaseClient) {
  try {
    const { error } = await supabase.from('profiles').select('count').limit(1);
    if (error === null) {
      status.supabase.connected = true;
    } else {
      status.supabase.error = error.message;
    }
  } catch (error: unknown) {
    status.supabase.error = error instanceof Error ? error.message : 'Unknown connection error';
  }
}

async function checkSupabaseAuthSettings(status: OAuthStatus) {
  try {
    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
    if (typeof supabaseUrl === 'string' && supabaseUrl.length > 0) {
      // Explicit check for undefined and empty string
      const settingsResponse = await fetch(`${supabaseUrl}/auth/v1/settings`);
      if (settingsResponse.ok === true) {
        const settings: {
          external?: { google?: boolean };
          disable_signup?: boolean;
          autoconfirm?: boolean;
        } = (await settingsResponse.json()) as {
          external?: { google?: boolean };
          disable_signup?: boolean;
          autoconfirm?: boolean;
        };
        status.supabase.authSettings = {
          googleEnabled: settings.external?.google ?? false,
          signupEnabled: settings.disable_signup === false,
          autoconfirm: settings.autoconfirm ?? false,
        };
        if (settings.external?.google !== undefined) {
          // Explicit check for undefined
          status.oauth_flow.authProviderConfigured = true;
        }
      }
    }
  } catch {
    console.info('Auth settings endpoint requires authentication (normal)');
  }
}

async function testOAuthProvider(status: OAuthStatus, supabase: SupabaseClient) {
  try {
    const { error: oauthError } = await supabase.auth.signInWithOAuth({
      provider: 'google',
      options: {
        redirectTo: 'http://localhost:3000/auth/callback',
        skipBrowserRedirect: true,
      },
    });
    if (oauthError !== null && oauthError.message !== 'Provider not found') {
      // Explicitly check for oauthError existence
      status.oauth_flow.authProviderConfigured = true;
    }
  } catch (error: unknown) {
    console.info('OAuth provider test failed (may be normal):', error);
  }
}

function generateRecommendations(status: OAuthStatus): string[] {
  const recommendations: string[] = [];

  if (!status.environment_variables.supabaseUrl) {
    recommendations.push(' Configure NEXT_PUBLIC_SUPABASE_URL environment variable');
  }
  if (!status.environment_variables.supabaseAnonKey) {
    recommendations.push(' Configure NEXT_PUBLIC_SUPABASE_ANON_KEY environment variable');
  }
  if (!status.environment_variables.supabaseServiceKey) {
    recommendations.push(' Configure SUPABASE_SERVICE_ROLE_KEY environment variable');
  }

  if (!status.supabase.connected) {
    recommendations.push(' Fix Supabase connection issue');
    if (typeof status.supabase.error === 'string' && status.supabase.error.length > 0) {
      recommendations.push(`   Error: ${status.supabase.error}`);
    }
  }

  if (status.supabase.authSettings?.googleEnabled === true) {
    recommendations.push(' Google OAuth provider is enabled');
  } else {
    recommendations.push(
      ' Enable Google OAuth provider in Supabase Dashboard',
      '   Go to: Authentication > Providers > Google',
    );
  }

  if (status.overall_status === 'ready') {
    recommendations.push(' OAuth configuration is complete!', ' Test the login flow at /login');
  }

  if (recommendations.length > 1) {
    recommendations.push(
      ' See docs/GOOGLE_OAUTH_SETUP_GUIDE.md for detailed instructions',
      ' Run: npm run oauth:verify for automated checks',
    );
  }

  return recommendations;
}

function determineOverallStatus(
  status: OAuthStatus,
): 'ready' | 'partial' | 'not_configured' | 'error' {
  // eslint-disable-next-line sonarjs/different-types-comparison
  if (!status.supabase.connected || status.supabase.error !== null) {
    return 'error';
  }

  const envVarsComplete = Object.values(status.environment_variables).every(Boolean);
  if (!envVarsComplete) {
    return 'not_configured';
  }

  if (status.supabase.authSettings?.googleEnabled && status.oauth_flow.authProviderConfigured) {
    return 'ready';
  }

  if (status.supabase.connected && envVarsComplete) {
    return 'partial';
  }

  return 'not_configured';
}

function getStatusMessage(status: string): string {
  switch (status) {
    case 'ready': {
      return 'Google OAuth is fully configured and ready to use';
    }
    case 'partial': {
      return 'Basic configuration complete, OAuth provider needs setup';
    }
    case 'not_configured': {
      return 'Google OAuth is not configured';
    }
    case 'error': {
      return 'Configuration error detected';
    }
    default: {
      return 'Unknown configuration status';
    }
  }
}

/* eslint-disable @typescript-eslint/no-misused-promises */
import { NextRequest } from 'next/server';
import {
  supabase,
  supabaseAdmin,
  ScrapingJobService,
  FoodTruckService,
  type ScrapingJob,
  type FoodTruck,
} from '@/lib/supabase';
import { AdminEvent } from './types';

interface RealtimeMetrics {
  scrapingJobs: {
    active: number;
    completed: number;
    failed: number;
    pending: number;
  };
  dataQuality: {
    averageScore: number;
    totalTrucks: number;
    recentChanges: number;
  };
  systemHealth: {
    status: 'healthy' | 'warning' | 'error';
    uptime: number;
    lastUpdate: string;
  };
}

export async function verifyAdminAccess(request: NextRequest): Promise<boolean> {
  try {
    const authHeader = request.headers.get('authorization');
    if (!authHeader?.startsWith('Bearer ')) {
      return false;
    }

    const token = authHeader.slice(7);
    const { data, error } = await supabase.auth.getUser(token);

    // eslint-disable-next-line sonarjs/different-types-comparison
    if (error || data.user === null) {
      return false;
    }
    const user = data.user;

    if (!supabaseAdmin) {
      return false;
    }

    const { data: profile } = await supabaseAdmin
      .from('profiles')
      .select('role')
      .eq('id', user.id)
      .single();

    return profile?.role === 'admin';
  } catch {
    return false;
  }
}

export function handleGetRequest(request: NextRequest): Response {
  const stream = new ReadableStream({
    start(controller: ReadableStreamDefaultController<Uint8Array>) {
      const encoder = new TextEncoder();

      const connectionEvent: AdminEvent = {
        id: generateEventId(),
        type: 'heartbeat',
        timestamp: new Date().toISOString(),
        data: {
          message: 'Real-time admin dashboard connected',
          connectionId: generateEventId(),
        },
      };

      controller.enqueue(encoder.encode(formatSSEMessage(connectionEvent)));

      const intervalId = setInterval(async () => {
        await sendHeartbeatEvent(controller, encoder);
      }, 5000);

      const changeMonitorId = setupDataChangeMonitor(controller, encoder);

      request.signal.addEventListener('abort', () => {
        clearInterval(intervalId);
        clearInterval(changeMonitorId);
        controller.close();
      });
    },
  });

  return new Response(stream, {
    headers: {
      'Content-Type': 'text/event-stream',
      'Cache-Control': 'no-cache',
      Connection: 'keep-alive',
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Headers': 'Cache-Control',
    },
  });
}

async function sendHeartbeatEvent(
  controller: ReadableStreamDefaultController<Uint8Array>,
  encoder: TextEncoder,
): Promise<void> {
  try {
    const metrics = await fetchRealtimeMetrics();
    const event: AdminEvent = {
      id: generateEventId(),
      type: 'heartbeat',
      timestamp: new Date().toISOString(),
      data: { ...metrics },
    };

    controller.enqueue(encoder.encode(formatSSEMessage(event)));
  } catch (error) {
    console.error('Error fetching realtime metrics:', error);

    const errorEvent: AdminEvent = {
      id: generateEventId(),
      type: 'system_alert',
      timestamp: new Date().toISOString(),
      data: {
        error: 'Failed to fetch metrics',
        details: error instanceof Error ? error.message : 'Unknown error',
      },
      severity: 'error',
    };

    controller.enqueue(encoder.encode(formatSSEMessage(errorEvent)));
  }
}

function setupDataChangeMonitor(
  controller: ReadableStreamDefaultController<Uint8Array>,
  encoder: TextEncoder,
): NodeJS.Timeout {
  return setInterval(async () => {
    try {
      await monitorDataChanges(controller, encoder);
    } catch (error) {
      console.error('Error monitoring data changes:', error);
    }
  }, 10_000);
}

export async function handlePostRequest(request: NextRequest): Promise<Response> {
  try {
    const body: unknown = await request.json(); // Explicitly type body as unknown
    let action: string;

    // Type guard to ensure 'body' has 'action' property and is a string
    if (
      typeof body === 'object' &&
      body !== null &&
      'action' in body &&
      typeof (body as { action: string }).action === 'string'
    ) {
      action = (body as { action: string }).action;
    } else {
      return new Response(
        JSON.stringify({
          success: false,
          error: "Invalid request body: 'action' property is missing or not a string.",
        }),
        {
          status: 400,
          headers: { 'Content-Type': 'application/json' },
        },
      );
    }

    switch (action) {
      case 'health_check': {
        return await handleHealthCheck();
      }

      case 'trigger_test_event': {
        return handleTriggerTestEvent();
      }

      default: {
        return new Response(
          JSON.stringify({
            success: false,
            error: "That didn't work, please try again later.",
          }),
          {
            status: 400,
            headers: { 'Content-Type': 'application/json' },
          },
        );
      }
    }
  } catch (error) {
    console.error('Realtime events POST error:', error);
    return new Response(
      JSON.stringify({
        success: false,
        error: "That didn't work, please try again later.",
      }),
      {
        status: 500,
        headers: { 'Content-Type': 'application/json' },
      },
    );
  }
}

async function fetchRealtimeMetrics(): Promise<RealtimeMetrics> {
  try {
    const recentJobs = await ScrapingJobService.getJobsByStatus('all');
    const scrapingMetrics = {
      active: recentJobs.filter((job: ScrapingJob) => job.status === 'running').length,
      completed: recentJobs.filter((job: ScrapingJob) => job.status === 'completed').length,
      failed: recentJobs.filter((job: ScrapingJob) => job.status === 'failed').length,
      pending: recentJobs.filter((job: ScrapingJob) => job.status === 'pending').length,
    };

    const qualityStats = await FoodTruckService.getDataQualityStats();
    const dataQualityMetrics = {
      averageScore: qualityStats.avg_quality_score ?? 0,
      totalTrucks: qualityStats.total_trucks ?? 0,
      recentChanges: 0,
    };

    const systemHealth = {
      status: 'healthy' as const,
      uptime: process.uptime(),
      lastUpdate: new Date().toISOString(),
    };

    return {
      scrapingJobs: scrapingMetrics,
      dataQuality: dataQualityMetrics,
      systemHealth,
    };
  } catch (error) {
    console.error('Error fetching realtime metrics:', error);
    return {
      scrapingJobs: { active: 0, completed: 0, failed: 0, pending: 0 },
      dataQuality: { averageScore: 0, totalTrucks: 0, recentChanges: 0 },
      systemHealth: {
        status: 'error',
        uptime: 0,
        lastUpdate: new Date().toISOString(),
      },
    };
  }
}

function isScrapingJob(obj: unknown): obj is ScrapingJob {
  return typeof obj === 'object' && obj !== null && 'id' in obj && 'status' in obj;
}

// Removed isFoodTruck function as it is unused.
// function isFoodTruck(obj: unknown): obj is FoodTruck {
//   return typeof obj === 'object' && obj !== null && 'id' in obj && 'name' in obj;
// }

async function sendScrapingUpdateEvent(
  controller: ReadableStreamDefaultController<Uint8Array>,
  encoder: TextEncoder,
): Promise<void> {
  const recentJobs = await ScrapingJobService.getJobsByStatus('all');

  if (Array.isArray(recentJobs) && recentJobs.length > 0) {
    const event: AdminEvent = {
      id: generateEventId(),
      type: 'scraping_update',
      timestamp: new Date().toISOString(),
      data: {
        recentJobs: recentJobs
          .filter((job) => isScrapingJob(job))
          .map((job) => ({
            // Fixed unicorn/no-array-callback-reference
            id: job.id,
            status: job.status,
            started_at: job.started_at,
            completed_at: job.completed_at,
          })),
        count: recentJobs.length,
      },
      severity: 'info',
    };

    controller.enqueue(encoder.encode(formatSSEMessage(event)));
  }
}

async function sendDataQualityChangeEvent(
  controller: ReadableStreamDefaultController<Uint8Array>,
  encoder: TextEncoder,
): Promise<void> {
  const recentTrucksResult = await FoodTruckService.getAllTrucks(10, 0);
  const recentlyUpdated = recentTrucksResult.trucks.filter((truck: FoodTruck) => {
    if (!truck.updated_at) return false;
    const updatedAt = new Date(truck.updated_at);
    const oneMinuteAgo = new Date(Date.now() - 60_000);
    return updatedAt > oneMinuteAgo;
  });

  if (recentlyUpdated.length > 0) {
    const event: AdminEvent = {
      id: generateEventId(),
      type: 'data_quality_change',
      timestamp: new Date().toISOString(),
      data: {
        updatedTrucks: recentlyUpdated.map((truck) => ({
          id: truck.id,
          name: truck.name,
          data_quality_score: truck.data_quality_score,
          updated_at: truck.updated_at,
        })),
        count: recentlyUpdated.length,
      },
      severity: 'info',
    };

    controller.enqueue(encoder.encode(formatSSEMessage(event)));
  }
}

async function monitorDataChanges(
  controller: ReadableStreamDefaultController<Uint8Array>,
  encoder: TextEncoder,
): Promise<void> {
  try {
    await sendScrapingUpdateEvent(controller, encoder);
    await sendDataQualityChangeEvent(controller, encoder);
  } catch (error) {
    console.error('Error monitoring data changes:', error);
  }
}

function formatSSEMessage(event: AdminEvent): string {
  return `id: ${event.id}\nevent: ${event.type}\ndata: ${JSON.stringify(event)}\n\n`;
}

function generateEventId(): string {
  // eslint-disable-next-line sonarjs/pseudo-random -- Math.random is acceptable for generating non-security-sensitive event IDs.
  return `${Date.now()}-${Math.random().toString(36).slice(2, 11)}`;
}

async function handleHealthCheck(): Promise<Response> {
  const metrics = await fetchRealtimeMetrics();
  return new Response(
    JSON.stringify({
      success: true,
      status: 'healthy',
      metrics,
      timestamp: new Date().toISOString(),
    }),
    {
      headers: { 'Content-Type': 'application/json' },
    },
  );
}

function handleTriggerTestEvent(): Response {
  return new Response(
    JSON.stringify({
      success: true,
      message: 'Test event triggered',
      timestamp: new Date().toISOString(),
    }),
    {
      headers: { 'Content-Type': 'application/json' },
    },
  );
}

import { NextRequest, NextResponse } from 'next/server';
import { autoScraper } from '@/lib/autoScraper';
import { scheduler } from '@/lib/scheduler';
import { logActivity } from '@/lib/activityLogger';
import { AutoScrapeResult } from './types';

function verifyCronSecret(request: NextRequest): NextResponse | null {
  const authHeader = request.headers.get('authorization');
  const cronSecret = process.env.CRON_SECRET;

  if (cronSecret === undefined || cronSecret === '') {
    console.error('CRON_SECRET not configured');
    return NextResponse.json({ error: 'Server configuration error' }, { status: 500 });
  }

  if (authHeader !== `Bearer ${cronSecret}`) {
    console.error('Unauthorized cron attempt:', authHeader);
    return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
  }
  return undefined;
}

function logAutoScrapeStart() {
  console.info('Starting automated scraping job...');
  logActivity({
    type: 'cron_job',
    action: 'auto_scrape_started',
    details: { timestamp: new Date().toISOString() },
  });
}

function logAutoScrapeCompletion(result: AutoScrapeResult) {
  logActivity({
    type: 'cron_job',
    action: 'auto_scrape_completed',
    details: {
      timestamp: new Date().toISOString(),
      trucksProcessed: result.trucksProcessed,
      newTrucksFound: result.newTrucksFound,
      errorsCount: result.errors?.length ?? 0,
    },
  });
  console.info('Automated scraping job completed successfully');
}

function logAutoScrapeFailure(error: unknown) {
  console.error('Auto-scraping cron job failed:', error);
  logActivity({
    type: 'cron_job',
    action: 'auto_scrape_failed',
    details: {
      timestamp: new Date().toISOString(),
      error: error instanceof Error ? error.message : 'Unknown error',
    },
  });
}

export async function handlePostRequest(request: NextRequest) {
  try {
    const authResponse = verifyCronSecret(request);
    if (authResponse) {
      return authResponse;
    }

    logAutoScrapeStart();

    const rawResult = await autoScraper.runAutoScraping();
    // Map errors to string[] for compatibility
    const result: AutoScrapeResult = {
      trucksProcessed: rawResult.trucksProcessed,
      newTrucksFound: rawResult.newTrucksFound,
      errors: rawResult.errors?.map((e) => e.url + (e.details ? `: ${e.details}` : '')),
    };
    scheduler.scheduleFollowUpTasks(result);
    logAutoScrapeCompletion(result);

    return NextResponse.json({
      success: true,
      message: 'Auto-scraping completed successfully',
      data: {
        trucksProcessed: result.trucksProcessed,
        newTrucksFound: result.newTrucksFound,
        timestamp: new Date().toISOString(),
      },
    });
  } catch (error) {
    logAutoScrapeFailure(error);
    return NextResponse.json(
      {
        success: false,
        error: 'Auto-scraping failed',
        message: error instanceof Error ? error.message : 'Unknown error',
      },
      { status: 500 },
    );
  }
}

export function handleGetRequest() {
  return NextResponse.json(
    { error: 'Method not allowed. Use POST for cron jobs.' },
    { status: 405 },
  );
}

import { ExtractedFoodTruckDetails, FoodTruckSchema, MenuCategory, MenuItem } from '@/lib/types';

export function mapExtractedDataToTruckSchema(
  extractedData: ExtractedFoodTruckDetails,
  sourceUrl: string,
  isDryRun: boolean,
): FoodTruckSchema {
  if (!extractedData || typeof extractedData !== 'object') {
    throw new Error('Invalid extractedData for mapping.');
  }

  const name = extractedData.name ?? 'Unknown Test Truck';
  const locationData = extractedData.current_location ?? {};
  const fullAddress = [
    locationData.address,
    locationData.city,
    locationData.state,
    locationData.zip_code,
  ]
    .filter(Boolean)
    .join(', ');

  return {
    name: name,
    description: extractedData.description ?? undefined,
    current_location: {
      lat: locationData.lat ?? 0,
      lng: locationData.lng ?? 0,
      address: fullAddress ?? locationData.raw_text ?? undefined,
      timestamp: new Date().toISOString(),
    },
    scheduled_locations: extractedData.scheduled_locations ?? undefined,
    operating_hours: extractedData.operating_hours ?? undefined,
    menu: (extractedData.menu ?? []).map((category: MenuCategory) => ({
      name: category.name ?? 'Uncategorized',
      items: (category.items ?? []).map((item: MenuItem) => ({
        name: item.name ?? 'Unknown Item',
        description: item.description ?? undefined,
        price:
          typeof item.price === 'number' || typeof item.price === 'string' ? item.price : undefined,
        dietary_tags: item.dietary_tags ?? [],
      })),
    })),
    contact_info: extractedData.contact_info ?? undefined,
    social_media: extractedData.social_media ?? undefined,
    cuisine_type: extractedData.cuisine_type ?? [],
    price_range: extractedData.price_range ?? undefined,
    specialties: extractedData.specialties ?? [],
    data_quality_score: isDryRun ? 0.5 : 0.6, // Differentiate test/dry run
    verification_status: 'pending',
    source_urls: [sourceUrl].filter(Boolean),
    last_scraped_at: new Date().toISOString(),
    ...(isDryRun && { test_run_flag: true }), // Add a flag for actual test saves if needed
  };
}

/**
 * SOTA Batch Data Cleanup System
 * Implements automated data quality improvements and cleanup operations
 */

import { FoodTruckService, type FoodTruck, DataQualityService } from '@/lib/supabase';
import { DuplicatePreventionService } from './duplicatePrevention';
import { getPlaceholderPatterns, processTruckForPlaceholders } from './placeholderUtils';

export interface CleanupOperation {
  type:
    | 'normalize_phone'
    | 'fix_coordinates'
    | 'remove_placeholders'
    | 'update_quality_scores'
    | 'merge_duplicates';
  description: string;
  affectedCount: number;
  successCount: number;
  errorCount: number;
  errors: string[];
}

export interface BatchCleanupResult {
  totalProcessed: number;
  operations: CleanupOperation[];
  summary: {
    trucksImproved: number;
    duplicatesRemoved: number;
    qualityScoreImprovement: number;
    placeholdersRemoved: number;
  };
  duration: number;
}

interface MergeOperationContext {
  operation: CleanupOperation;
  processedIds: Set<string>;
}

interface CoordinateProcessContext {
  defaultLat: number;
  defaultLng: number;
  dryRun: boolean;
  operation: CleanupOperation;
}

/**
 * Automated Data Quality Cleanup Service
 */
export class BatchCleanupService {
  /**
   * Run comprehensive data cleanup operations
   */
  static async runFullCleanup(
    options: {
      batchSize?: number;
      dryRun?: boolean;
      operations?: CleanupOperation['type'][];
    } = {},
  ): Promise<BatchCleanupResult> {
    const startTime = Date.now();
    const {
      batchSize = 50,
      dryRun = false,
      operations = [
        'remove_placeholders',
        'normalize_phone',
        'fix_coordinates',
        'update_quality_scores',
        'merge_duplicates',
      ],
    } = options;

    console.info(`Starting batch cleanup (${dryRun ? 'DRY RUN' : 'LIVE'})...`);
    const result = this.initializeCleanupResult();
    try {
      const allTrucks = await FoodTruckService.getAllTrucks();
      result.totalProcessed = allTrucks.total;
      await this.processTrucksInBatches(allTrucks.trucks, {
        batchSize,
        operations,
        dryRun,
        result,
      });
      return this.finalizeCleanupResult(result, startTime);
    } catch (error) {
      console.error('Batch cleanup failed:', error);
      throw error;
    }
  }

  private static initializeCleanupResult(): BatchCleanupResult {
    return {
      totalProcessed: 0,
      operations: [],
      summary: {
        trucksImproved: 0,
        duplicatesRemoved: 0,
        qualityScoreImprovement: 0,
        placeholdersRemoved: 0,
      },
      duration: 0,
    };
  }

  private static finalizeCleanupResult(
    result: BatchCleanupResult,
    startTime: number,
  ): BatchCleanupResult {
    result.summary = this.calculateSummary(result.operations);
    result.duration = Date.now() - startTime;

    console.info(`Batch cleanup completed in ${result.duration}ms`);
    return result;
  }

  private static async processTrucksInBatches(
    trucks: FoodTruck[],
    {
      batchSize,
      operations,
      dryRun,
      result,
    }: {
      batchSize: number;
      operations: CleanupOperation['type'][];
      dryRun: boolean;
      result: BatchCleanupResult;
    },
  ): Promise<void> {
    for (let i = 0; i < trucks.length; i += batchSize) {
      const batch = trucks.slice(i, i + batchSize);
      for (const op of operations) {
        const opResult = await this.runOperation(op, batch, dryRun);
        result.operations.push(opResult);
      }
    }
  }

  /**
   * Run a specific cleanup operation
   */
  private static async runOperation(
    type: CleanupOperation['type'],
    trucks: FoodTruck[],
    dryRun: boolean,
  ): Promise<CleanupOperation> {
    const operation: CleanupOperation = {
      type,
      description: this.getOperationDescription(type),
      affectedCount: 0,
      successCount: 0,
      errorCount: 0,
      errors: [],
    };

    const operationRunners = {
      remove_placeholders: this.runRemovePlaceholders,
      normalize_phone: this.runNormalizePhoneNumbers,
      fix_coordinates: this.runFixCoordinates,
      update_quality_scores: this.runUpdateQualityScores,
      merge_duplicates: this.runMergeDuplicates,
    };

    try {
      const runner = operationRunners[type];
      if (runner) {
        return await runner.call(this, trucks, dryRun, operation);
      } else {
        operation.errors.push(`Unknown operation type: ${String(type)}`);
        return operation;
      }
    } catch (error) {
      operation.errors.push(
        `Operation failed: ${error instanceof Error ? error.message : String(error)}`,
      );
      return operation;
    }
  }

  private static runRemovePlaceholders = async (
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<CleanupOperation> => {
    return await this.removePlaceholders(trucks, dryRun, operation);
  };

  private static runNormalizePhoneNumbers = async (
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<CleanupOperation> => {
    return await this.normalizePhoneNumbers(trucks, dryRun, operation);
  };

  private static runFixCoordinates = async (
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<CleanupOperation> => {
    return await this.fixCoordinates(trucks, dryRun, operation);
  };

  private static runUpdateQualityScores = async (
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<CleanupOperation> => {
    return await this.updateQualityScores(trucks, dryRun, operation);
  };

  private static runMergeDuplicates = async (
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<CleanupOperation> => {
    return await this.mergeDuplicates(trucks, dryRun, operation);
  };

  /**
   * Remove placeholder and mock data
   */
  private static async removePlaceholders(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<CleanupOperation> {
    const placeholderPatterns = getPlaceholderPatterns();
    const promises = trucks.map((truck) =>
      this.processSingleTruckForPlaceholders(truck, placeholderPatterns, dryRun, operation),
    );
    await Promise.all(promises);
    return operation;
  }

  private static async processSingleTruckForPlaceholders(
    truck: FoodTruck,
    patterns: RegExp[],
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<void> {
    const updates = processTruckForPlaceholders(truck, patterns);
    if (updates && Object.keys(updates).length > 0) {
      await this.performUpdateOperation(truck.id, updates, dryRun, operation);
    }
  }

  private static async performUpdateOperation(
    truckId: string,
    updates: Partial<FoodTruck>,
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<void> {
    operation.affectedCount++;
    if (dryRun) {
      operation.successCount++;
    } else {
      try {
        await FoodTruckService.updateTruck(truckId, updates);
        operation.successCount++;
      } catch (error) {
        operation.errorCount++;
        operation.errors.push(
          `Failed to update truck ${truckId}: ${error instanceof Error ? error.message : String(error)}`,
        );
      }
    }
  }

  /**
   * Normalize phone numbers to consistent format
   */
  private static async normalizePhoneNumbers(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<CleanupOperation> {
    const promises = trucks.map((truck) => {
      if (truck.contact_info?.phone !== undefined) {
        const originalPhone = truck.contact_info.phone;
        const normalizedPhone = this.normalizePhone(originalPhone);

        if (normalizedPhone !== undefined && normalizedPhone !== originalPhone) {
          return this.applyPhoneNormalizationUpdate(truck, normalizedPhone, dryRun, operation);
        }
      }
      return Promise.resolve();
    });
    await Promise.all(promises);
    return operation;
  }

  private static async applyPhoneNormalizationUpdate(
    truck: FoodTruck,
    normalizedPhone: string,
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<void> {
    operation.affectedCount++;
    if (dryRun) {
      operation.successCount++;
    } else {
      try {
        await FoodTruckService.updateTruck(truck.id, {
          contact_info: {
            ...truck.contact_info,
            phone: normalizedPhone,
          },
        });
        operation.successCount++;
      } catch (error) {
        operation.errorCount++;
        operation.errors.push(
          `Failed to normalize phone for truck ${truck.id}: ${error instanceof Error ? error.message : String(error)}`,
        );
      }
    }
  }

  /**
   * Helper to determine if coordinates need fixing and provide updates
   */
  private static getFixedCoordinates(
    lat: number | undefined,
    lng: number | undefined,
    defaultLat: number,
    defaultLng: number,
  ): Partial<FoodTruck['current_location']> | undefined {
    // Fix invalid coordinates (0,0 or undefined)
    if (lat === undefined || lng === undefined || lat === 0 || lng === 0) {
      return { lat: defaultLat, lng: defaultLng };
    }
    // Fix coordinates outside reasonable bounds for Charleston area
    if (lat < 32 || lat > 34 || lng > -79 || lng < -81) {
      return { lat: defaultLat, lng: defaultLng };
    }
    return undefined;
  }

  /**
   * Fix invalid GPS coordinates
   */
  private static async fixCoordinates(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<CleanupOperation> {
    const context: Omit<CoordinateProcessContext, 'operation'> = {
      defaultLat: 32.7767,
      defaultLng: -79.9311,
      dryRun,
    };

    const promises = trucks.map((truck) =>
      this.processSingleTruckCoordinates(truck, { ...context, operation }),
    );
    await Promise.all(promises);
    return operation;
  }

  private static async processSingleTruckCoordinates(
    truck: FoodTruck,
    context: CoordinateProcessContext,
  ): Promise<void> {
    if (!truck.current_location) return;
    const { defaultLat, defaultLng, dryRun, operation } = context;
    const { lat, lng } = truck.current_location;
    const updates = this.getFixedCoordinates(lat, lng, defaultLat, defaultLng);
    if (updates) {
      await this.applyCoordinateFixUpdate(truck, updates, dryRun, operation);
    }
  }

  private static async applyCoordinateFixUpdate(
    truck: FoodTruck,
    updates: Partial<FoodTruck['current_location']>,
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<void> {
    operation.affectedCount++;
    if (dryRun) {
      operation.successCount++;
    } else {
      try {
        await FoodTruckService.updateTruck(truck.id, {
          current_location: {
            ...truck.current_location,
            ...updates,
          },
        });
        operation.successCount++;
      } catch (error) {
        operation.errorCount++;
        operation.errors.push(
          `Failed to fix coordinates for truck ${truck.id}: ${error instanceof Error ? error.message : String(error)}`,
        );
      }
    }
  }

  /**
   * Update quality scores for all trucks
   */
  private static async updateQualityScores(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<CleanupOperation> {
    const promises = trucks.map((truck) =>
      this.processSingleTruckForQualityScore(truck, dryRun, operation),
    );
    await Promise.all(promises);
    return operation;
  }

  private static async processSingleTruckForQualityScore(
    truck: FoodTruck,
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<void> {
    const qualityAssessment = DataQualityService.calculateQualityScore(truck);
    if (qualityAssessment) {
      const newScore = qualityAssessment.score;
      const currentScore = truck.data_quality_score ?? 0;
      // Only update if score changed significantly (>5% difference)
      if (typeof newScore === 'number' && Math.abs(newScore - currentScore) > 0.05) {
        await this.applyQualityScoreUpdate(truck, dryRun, operation);
      }
    }
  }

  private static async applyQualityScoreUpdate(
    truck: FoodTruck,
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<void> {
    operation.affectedCount++;
    if (dryRun) {
      operation.successCount++;
    } else {
      try {
        const updateResult = await DataQualityService.updateTruckQualityScore(truck.id);
        if ('error' in updateResult) {
          throw new Error(updateResult.error);
        }
        operation.successCount++;
      } catch (error) {
        operation.errorCount++;
        operation.errors.push(
          `Failed to update quality score for truck ${truck.id}: ${error instanceof Error ? error.message : String(error)}`,
        );
      }
    }
  }

  /**
   * Identify and merge duplicate trucks
   */
  private static async mergeDuplicates(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation,
  ): Promise<CleanupOperation> {
    const processedIds = new Set<string>();
    for (const truck of trucks) {
      await this.processSingleTruckForDuplicates(truck, dryRun, { operation, processedIds });
    }
    return operation;
  }

  private static async processSingleTruckForDuplicates(
    truck: FoodTruck,
    dryRun: boolean,
    context: MergeOperationContext,
  ): Promise<void> {
    const { processedIds } = context;
    if (processedIds.has(truck.id)) return;

    const duplicateCheck = await DuplicatePreventionService.checkForDuplicates(truck);
    if (
      duplicateCheck.isDuplicate &&
      duplicateCheck.bestMatch?.confidence === 'high' &&
      duplicateCheck.bestMatch.recommendation === 'merge'
    ) {
      await this.applyMergeOperation(
        truck,
        duplicateCheck.bestMatch.existingTruck,
        dryRun,
        context,
      );
    }
    processedIds.add(truck.id);
  }

  private static async applyMergeOperation(
    truck: FoodTruck,
    existingTruck: FoodTruck,
    dryRun: boolean,
    context: MergeOperationContext,
  ): Promise<void> {
    const { operation, processedIds } = context;
    operation.affectedCount++;
    if (dryRun) {
      operation.successCount++;
    } else {
      try {
        const mergeResult = await DuplicatePreventionService.mergeDuplicates(
          truck.id,
          existingTruck.id,
        );
        if ('error' in mergeResult) {
          throw new Error(mergeResult.error);
        }
        processedIds.add(existingTruck.id);
        operation.successCount++;
      } catch (error) {
        operation.errorCount++;
        operation.errors.push(
          `Failed to merge duplicates ${truck.id} and ${existingTruck.id}: ${error instanceof Error ? error.message : String(error)}`,
        );
      }
    }
  }

  /**
   * Normalize phone number format
   */
  private static normalizePhone(phone: string): string | undefined {
    if (!phone) return undefined;

    // Remove all non-digit characters
    const digits = phone.replaceAll(/\D/g, '');

    // Handle US phone numbers
    if (digits.length === 10) {
      return `(${digits.slice(0, 3)}) ${digits.slice(3, 6)}-${digits.slice(6)}`;
    }
    if (digits.length === 11 && digits.startsWith('1')) {
      return `+1 (${digits.slice(1, 4)}) ${digits.slice(4, 7)}-${digits.slice(7)}`;
    }

    // Return original if can't normalize
    return phone;
  }

  /**
   * Get operation description
   */
  private static getOperationDescription(type: CleanupOperation['type']): string {
    const descriptions = {
      remove_placeholders: 'Remove placeholder and mock data values',
      normalize_phone: 'Normalize phone numbers to consistent format',
      fix_coordinates: 'Fix invalid GPS coordinates',
      update_quality_scores: 'Recalculate data quality scores',
      merge_duplicates: 'Identify and merge duplicate truck entries',
    };

    return descriptions[type] ?? 'Unknown operation';
  }

  /**
   * Calculate cleanup summary
   */
  private static calculateSummary(operations: CleanupOperation[]): BatchCleanupResult['summary'] {
    return {
      trucksImproved: operations.reduce((sum, op) => sum + op.successCount, 0),
      duplicatesRemoved: operations.find((op) => op.type === 'merge_duplicates')?.successCount ?? 0,
      qualityScoreImprovement:
        operations.find((op) => op.type === 'update_quality_scores')?.successCount ?? 0,
      placeholdersRemoved:
        operations.find((op) => op.type === 'remove_placeholders')?.successCount ?? 0,
    };
  }
}

/**
 * SOTA Duplicate Prevention System
 * Implements intelligent duplicate detection and prevention for food truck data
 */

import { FoodTruckService, type FoodTruck } from '@/lib/supabase';

// Duplicate detection configuration
export const DUPLICATE_DETECTION_CONFIG = {
  // Similarity thresholds (0.0 = no match, 1.0 = exact match)
  thresholds: {
    name: 0.85, // High threshold for name matching
    location: 0.9, // Very high threshold for location matching
    phone: 1, // Exact match for phone numbers
    website: 1, // Exact match for websites
    overall: 0.8, // Overall similarity threshold
  },

  // Weight factors for different fields
  weights: {
    name: 0.4, // 40% weight for name similarity
    location: 0.3, // 30% weight for location similarity
    contact: 0.2, // 20% weight for contact info similarity
    menu: 0.1, // 10% weight for menu similarity
  },
} as const;

export interface DuplicateMatch {
  existingTruck: FoodTruck;
  similarity: number;
  matchedFields: string[];
  confidence: 'high' | 'medium' | 'low';
  recommendation: 'merge' | 'update' | 'skip' | 'manual_review';
}

export interface DuplicateDetectionResult {
  isDuplicate: boolean;
  matches: DuplicateMatch[];
  bestMatch?: DuplicateMatch;
  action: 'create' | 'update' | 'merge' | 'manual_review';
  reason: string;
}

/**
 * Advanced Duplicate Prevention Service
 */
export class DuplicatePreventionService {
  /**
   * Check if a food truck is a duplicate of existing trucks
   */
  static async checkForDuplicates(
    candidateTruck: Partial<FoodTruck>,
  ): Promise<DuplicateDetectionResult> {
    try {
      // Get all existing trucks for comparison
      const existingTrucksResult = await FoodTruckService.getAllTrucks();

      if ('error' in existingTrucksResult) {
        console.error('Error fetching existing trucks:', existingTrucksResult.error);
        return {
          isDuplicate: false,
          matches: [],
          action: 'create',
          reason: `Error fetching existing trucks: ${existingTrucksResult.error}`,
        };
      }

      const existingTrucks = existingTrucksResult.trucks;
      const matches: DuplicateMatch[] = [];

      for (const existingTruck of existingTrucks) {
        const similarity = this.calculateSimilarity(candidateTruck, existingTruck);

        if (similarity.overall >= DUPLICATE_DETECTION_CONFIG.thresholds.overall) {
          matches.push({
            existingTruck,
            similarity: similarity.overall,
            matchedFields: similarity.matchedFields,
            confidence: this.getConfidenceLevel(similarity.overall),
            recommendation: this.getRecommendation(similarity),
          });
        }
      }

      return this.processDuplicateMatches(matches, candidateTruck);
    } catch (error) {
      console.error('Error checking for duplicates:', error);
      return {
        isDuplicate: false,
        matches: [],
        action: 'create',
        reason:
          'An unexpected error occurred during duplicate detection - proceeding with creation',
      };
    }
  }

  /**
   * Processes the matches found during duplicate detection and returns the result.
   */
  private static processDuplicateMatches(
    matches: DuplicateMatch[],
    candidateTruck: Partial<FoodTruck>,
  ): DuplicateDetectionResult {
    // Sort matches by similarity (highest first)
    matches.sort((a, b) => b.similarity - a.similarity);

    const bestMatch = matches.length > 0 ? matches[0] : undefined;
    const isDuplicate = matches.length > 0;

    return {
      isDuplicate,
      matches,
      bestMatch,
      action: this.determineAction(matches, candidateTruck),
      reason: this.generateReason(matches, candidateTruck),
    };
  }

  /**
   * Calculate similarity between two food trucks
   */
  private static calculateSimilarity(
    candidate: Partial<FoodTruck>,
    existing: FoodTruck,
  ): { overall: number; matchedFields: string[]; breakdown: Record<string, number> } {
    const breakdown: Record<string, number> = {};
    const matchedFields: string[] = [];

    // Name similarity
    const nameSimilarity = this.calculateStringSimilarity(
      candidate.name ?? '',
      existing.name ?? '',
    );
    breakdown.name = nameSimilarity;
    if (nameSimilarity >= DUPLICATE_DETECTION_CONFIG.thresholds.name) {
      matchedFields.push('name');
    }

    // Location similarity
    const locationSimilarity = this.calculateLocationSimilarity(
      candidate.current_location,
      existing.current_location,
    );
    breakdown.location = locationSimilarity;
    if (locationSimilarity >= DUPLICATE_DETECTION_CONFIG.thresholds.location) {
      matchedFields.push('location');
    }

    // Contact similarity
    const contactSimilarity = this.calculateContactSimilarity(
      candidate.contact_info,
      existing.contact_info,
    );
    breakdown.contact = contactSimilarity;
    if (contactSimilarity >= DUPLICATE_DETECTION_CONFIG.thresholds.phone) {
      matchedFields.push('contact');
    }

    // Menu similarity (basic)
    const menuSimilarity = this.calculateMenuSimilarity(candidate.menu, existing.menu);
    breakdown.menu = menuSimilarity;
    if (menuSimilarity > 0.7) {
      matchedFields.push('menu');
    }

    // Calculate weighted overall similarity
    const overall =
      nameSimilarity * DUPLICATE_DETECTION_CONFIG.weights.name +
      locationSimilarity * DUPLICATE_DETECTION_CONFIG.weights.location +
      contactSimilarity * DUPLICATE_DETECTION_CONFIG.weights.contact +
      menuSimilarity * DUPLICATE_DETECTION_CONFIG.weights.menu;

    return { overall, matchedFields, breakdown };
  }

  /**
   * Calculate string similarity using Levenshtein distance
   */
  private static calculateStringSimilarity(str1: string, str2: string): number {
    if (!str1 || !str2) return 0;

    // Normalize strings
    const s1 = str1.toLowerCase().trim();
    const s2 = str2.toLowerCase().trim();

    if (s1 === s2) return 1;

    // Calculate Levenshtein distance
    const matrix: number[][] = [];
    const len1 = s1.length;
    const len2 = s2.length;

    for (let i = 0; i <= len1; i++) {
      matrix[i] = [i];
    }

    for (let j = 0; j <= len2; j++) {
      matrix[0][j] = j;
    }

    for (let i = 1; i <= len1; i++) {
      for (let j = 1; j <= len2; j++) {
        const cost = s1[i - 1] === s2[j - 1] ? 0 : 1;
        matrix[i][j] = Math.min(
          matrix[i - 1][j] + 1, // deletion
          matrix[i][j - 1] + 1, // insertion
          matrix[i - 1][j - 1] + cost, // substitution
        );
      }
    }

    const distance = matrix[len1][len2];
    const maxLength = Math.max(len1, len2);

    return maxLength === 0 ? 1 : 1 - distance / maxLength;
  }

  /**
   * Calculate location similarity
   */
  private static calculateLocationSimilarity(
    loc1: FoodTruck['current_location'] | undefined | null,
    loc2: FoodTruck['current_location'] | undefined | null,
  ): number {
    if (!loc1 || !loc2) return 0;

    let similarity = 0;
    let factors = 0;

    // Address similarity
    if (loc1.address && loc2.address) {
      similarity += this.calculateStringSimilarity(loc1.address, loc2.address);
      factors++;
    }

    // GPS coordinate similarity (within 100 meters = high similarity)
    if (loc1.lat && loc1.lng && loc2.lat && loc2.lng) {
      const distance = this.calculateGPSDistance(loc1.lat, loc1.lng, loc2.lat, loc2.lng);

      // Distance similarity (closer = higher similarity)
      const distanceSimilarity = distance <= 0.1 ? 1 : Math.max(0, 1 - distance / 1); // 1km max
      similarity += distanceSimilarity;
      factors++;
    }

    return factors > 0 ? similarity / factors : 0;
  }

  /**
   * Calculate GPS distance in kilometers
   */
  private static calculateGPSDistance(
    lat1: number,
    lng1: number,
    lat2: number,
    lng2: number,
  ): number {
    const R = 6371; // Earth's radius in kilometers
    const dLat = ((lat2 - lat1) * Math.PI) / 180;
    const dLng = ((lng2 - lng1) * Math.PI) / 180;
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos((lat1 * Math.PI) / 180) *
        Math.cos((lat2 * Math.PI) / 180) *
        Math.sin(dLng / 2) *
        Math.sin(dLng / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  }

  /**
   * Calculate contact similarity
   */
  private static calculateContactSimilarity(
    contact1: FoodTruck['contact_info'] | undefined | null,
    contact2: FoodTruck['contact_info'] | undefined | null,
  ): number {
    if (!contact1 || !contact2) return 0;

    let matches = 0;
    let total = 0;

    // Phone number exact match
    if (contact1.phone != undefined && contact2.phone != undefined) {
      const phone1 = contact1.phone.replaceAll(/\D/g, ''); // Remove non-digits
      const phone2 = contact2.phone.replaceAll(/\D/g, '');
      if (phone1 === phone2) matches++;
      total++;
    }

    // Website exact match
    if (contact1.website != undefined && contact2.website != undefined) {
      const url1 = contact1.website
        .toLowerCase()
        .replace(/^https?:\/\//, '')
        .replace(/\/$/, '');
      const url2 = contact2.website
        .toLowerCase()
        .replace(/^https?:\/\//, '')
        .replace(/\/$/, '');
      if (url1 === url2) matches++;
      total++;
    }

    // Email similarity
    if (contact1.email != undefined && contact2.email != undefined) {
      if (contact1.email.toLowerCase() === contact2.email.toLowerCase()) matches++;
      total++;
    }

    return total > 0 ? matches / total : 0;
  }

  /**
   * Calculate menu similarity (basic implementation)
   */
  private static calculateMenuSimilarity(
    menu1: FoodTruck['menu'] | undefined | null,
    menu2: FoodTruck['menu'] | undefined | null,
  ): number {
    if (!menu1 || !menu2 || menu1.length === 0 || menu2.length === 0) return 0;

    // Simple category name matching
    const categories1 = menu1
      .map((cat) => (cat as { category?: string }).category?.toLowerCase() ?? '')
      .filter(Boolean);
    const categories2 = menu2
      .map((cat) => (cat as { category?: string }).category?.toLowerCase() ?? '')
      .filter(Boolean);

    const commonCategories = categories1.filter((cat) => categories2.includes(cat));
    const totalCategories = new Set([...categories1, ...categories2]).size;

    return totalCategories > 0 ? commonCategories.length / totalCategories : 0;
  }

  /**
   * Get confidence level based on similarity score
   */
  private static getConfidenceLevel(similarity: number): 'high' | 'medium' | 'low' {
    if (similarity >= 0.95) return 'high';
    if (similarity >= 0.85) return 'medium';
    return 'low';
  }

  /**
   * Get recommendation based on similarity analysis
   */
  private static getRecommendation(similarity: {
    overall: number;
    matchedFields: string[];
  }): 'merge' | 'update' | 'skip' | 'manual_review' {
    if (similarity.overall >= 0.95) return 'merge';
    if (similarity.overall >= 0.9) return 'update';
    if (similarity.overall >= 0.8) return 'manual_review';
    return 'skip';
  }

  /**
   * Determine action based on matches
   */
  private static determineAction(
    matches: DuplicateMatch[],
    _candidate: Partial<FoodTruck>,
  ): 'create' | 'update' | 'merge' | 'manual_review' {
    if (matches.length === 0) return 'create';

    const bestMatch = matches[0];

    if (bestMatch.confidence === 'high') {
      const recommendation = bestMatch.recommendation;
      if (recommendation === 'merge' || recommendation === 'update') {
        return recommendation;
      }
      return 'manual_review';
    }

    return 'manual_review';
  }

  /**
   * Generate human-readable reason
   */
  private static generateReason(matches: DuplicateMatch[], _candidate: Partial<FoodTruck>): string {
    if (matches.length === 0) {
      return 'No duplicates found - safe to create new truck entry';
    }

    const bestMatch = matches[0];
    const similarity = Math.round(bestMatch.similarity * 100);

    return `Found ${matches.length} potential duplicate(s). Best match: ${similarity}% similarity with "${bestMatch.existingTruck.name}" (matched: ${bestMatch.matchedFields.join(', ')})`;
  }

  /**
   * Merge duplicate truck data intelligently
   */
  static async mergeDuplicates(
    targetId: string,
    sourceId: string,
  ): Promise<FoodTruck | { error: string }> {
    const targetResult = await FoodTruckService.getTruckById(targetId);
    const sourceResult = await FoodTruckService.getTruckById(sourceId);

    if ('error' in targetResult) {
      return {
        error: `Failed to retrieve target truck with ID ${targetId}: ${targetResult.error}`,
      };
    }
    if ('error' in sourceResult) {
      return {
        error: `Failed to retrieve source truck with ID ${sourceId}: ${sourceResult.error}`,
      };
    }

    const target = targetResult;
    const source = sourceResult;

    // Merge logic: prefer non-null, more complete data
    const mergedData: Partial<FoodTruck> = {
      name: target.name ?? source.name,
      description: target.description ?? source.description,
      cuisine_type:
        (target.cuisine_type?.length ?? 0) > 0 ? target.cuisine_type : source.cuisine_type,
      price_range: target.price_range ?? source.price_range,
      current_location: target.current_location ?? source.current_location,
      contact_info: {
        ...source.contact_info,
        ...target.contact_info, // Target takes precedence
      },
      operating_hours: target.operating_hours ?? source.operating_hours,
      menu: (target.menu?.length ?? 0) > 0 ? target.menu : source.menu,
      social_media: {
        ...source.social_media,
        ...target.social_media,
      },
      source_urls: [...new Set([...(target.source_urls ?? []), ...(source.source_urls ?? [])])],
      last_scraped_at: new Date().toISOString(),
    };

    // Update target with merged data
    const updatedTruckResult = await FoodTruckService.updateTruck(targetId, mergedData);

    if ('error' in updatedTruckResult) {
      return {
        error: `Failed to update target truck with merged data: ${updatedTruckResult.error}`,
      };
    }

    console.info(`Merged truck ${sourceId} into ${targetId}`);

    return updatedTruckResult;
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { AuditLogger, SecurityEvent } from '@/lib/security/auditLogger';
import { createSupabaseMiddlewareClient } from '@/lib/supabaseMiddleware';

interface RequestMetadata {
  ip: string;
  userAgent: string;
  url: string;
  method: string;
}

interface SupabaseUser {
  id: string;
  email?: string;
}

interface SupabaseProfile {
  role?: string;
}

interface LogAndRedirectParams {
  req: NextRequest;
  res: NextResponse;
  requestMetadata: RequestMetadata;
  reason: string;
  userError?: { message?: string };
}

interface LogSecurityEventParams {
  event_type: SecurityEvent['event_type'];
  ip_address: string;
  user_agent: string;
  details: Record<string, any>;
  severity: SecurityEvent['severity'];
  user_id?: string;
  user_email?: string;
}

interface LogSecurityEventAndRedirectParams {
  req: NextRequest;
  res: NextResponse;
  logParams: LogSecurityEventParams;
  redirectPath: string;
  redirectFromPath?: string;
}

async function logSecurityEventAndRedirect({
  req,
  res: _res,
  logParams,
  redirectPath,
  redirectFromPath,
}: LogSecurityEventAndRedirectParams) {
  await AuditLogger.logSecurityEvent(logParams);
  const redirectUrl = req.nextUrl.clone();
  redirectUrl.pathname = redirectPath;
  if (redirectFromPath) {
    redirectUrl.searchParams.set(`redirectedFrom`, redirectFromPath);
  }
  return NextResponse.redirect(redirectUrl);
}

async function logAndRedirect({
  req,
  res,
  requestMetadata,
  reason,
  userError,
}: LogAndRedirectParams) {
  return logSecurityEventAndRedirect({
    req,
    res,
    logParams: {
      event_type: 'permission_denied',
      ip_address: requestMetadata.ip,
      user_agent: requestMetadata.userAgent,
      details: {
        attempted_url: requestMetadata.url,
        reason,
        error: userError?.message,
      },
      severity: 'warning',
    },
    redirectPath: '/login',
    redirectFromPath: req.nextUrl.pathname,
  });
}

interface LogAndRedirectDeniedParams {
  req: NextRequest;
  res: NextResponse;
  requestMetadata: RequestMetadata;
  user: SupabaseUser;
  profile: SupabaseProfile | null;
  profileQueryError?: { message?: string } | null; // Changed to allow null
}

async function logAndRedirectDenied({
  req,
  res,
  requestMetadata,
  user,
  profile,
  profileQueryError,
}: LogAndRedirectDeniedParams) {
  return logSecurityEventAndRedirect({
    req,
    res,
    logParams: {
      event_type: 'permission_denied',
      user_id: user.id,
      user_email: user.email ?? undefined,
      ip_address: requestMetadata.ip,
      user_agent: requestMetadata.userAgent,
      details: {
        attempted_url: requestMetadata.url,
        user_role: profile?.role ?? 'none',
        reason: 'insufficient_privileges',
        error: profileQueryError?.message,
      },
      severity: 'error',
    },
    redirectPath: '/access-denied',
  });
}

export async function protectAdminRoutes(
  req: NextRequest,
  res: NextResponse,
  requestMetadata: RequestMetadata,
) {
  const supabase = createSupabaseMiddlewareClient(req, res);
  const { data, error: userError } = await supabase.auth.getUser();
  const user = data?.user;

  if (userError || !user) {
    return logAndRedirect({
      req,
      res,
      requestMetadata,
      reason: 'no_session',
      userError: userError ?? undefined,
    });
  }
  // Explicitly type the result of the Supabase query
  const { data: profile, error: profileQueryError } = (await supabase
    .from('profiles')
    .select('role')
    .eq('id', user.id)
    .single()) as { data: SupabaseProfile | null; error: { message?: string } | null };
  if (profileQueryError || (profile && profile.role !== 'admin')) {
    return logAndRedirectDenied({
      req,
      res,
      requestMetadata,
      user,
      profile: profile ?? undefined,
      profileQueryError: profileQueryError ?? undefined,
    });
  }
  if (req.method !== 'GET' || req.nextUrl.pathname.includes('/api/')) {
    await AuditLogger.logDataAccess({
      userId: user.id,
      userEmail: user.email ?? 'unknown',
      resourceType: 'admin_panel',
      resourceId: req.nextUrl.pathname,
      action: req.method === 'GET' ? 'read' : 'admin_access',
      request: {
        ip: requestMetadata.ip,
        userAgent: requestMetadata.userAgent,
      },
    });
  }
  return res;
}

import { firecrawl } from '@/lib/firecrawl';
import { gemini } from '@/lib/gemini';
import { ScrapingJobService } from '@/lib/supabase';
import { ExtractedFoodTruckDetails } from '../types';
import {
  validateInputAndPrepare,
  buildTruckDataSchema,
  handleDuplicateCheck,
  finalizeJobStatus,
} from './pipelineHelpers';

async function handleScraping(targetUrl: string, jobId: string) {
  console.info(`Starting scrape for ${targetUrl}`);
  const scrapeResult = await firecrawl.scrapeFoodTruckWebsite(targetUrl);

  if (!scrapeResult.success || !scrapeResult.data?.markdown) {
    const error = scrapeResult.error ?? 'Scraping failed or markdown content not found';
    await handleJobFailure(jobId, error);
    throw new Error(error);
  }

  console.info(`Scraping successful for ${targetUrl}, proceeding to Gemini extraction.`);
  return scrapeResult.data;
}

async function handleGeminiExtraction(markdown: string, sourceUrl: string, jobId: string) {
  const geminiResult = await gemini.extractFoodTruckDetailsFromMarkdown(markdown, sourceUrl);

  if (!geminiResult.success || !geminiResult.data) {
    const error = geminiResult.error ?? 'Gemini data extraction failed';
    await handleJobFailure(jobId, error);
    throw new Error(error);
  }

  console.info(`Gemini extraction successful for ${sourceUrl}.`);
  return geminiResult.data;
}

async function handleJobFailure(jobId: string, error: string) {
  console.error(`Job ${jobId} failed:`, error);
  try {
    const currentJob = await ScrapingJobService.getJobsByStatus('all').then((jobs) =>
      jobs.find((j) => j.id === jobId),
    );
    if (currentJob && currentJob.status !== 'failed') {
      await ScrapingJobService.updateJobStatus(jobId, 'failed', { errors: [error] });
    }
  } catch (statusUpdateError) {
    console.error(`Error updating job ${jobId} status to failed:`, statusUpdateError);
  }
}

async function handleRetryLogic(jobId: string) {
  try {
    const job = await ScrapingJobService.incrementRetryCount(jobId);
    if (job && typeof job.retry_count === 'number' && typeof job.max_retries === 'number') {
      if (job.retry_count < job.max_retries) {
        console.info(`Retrying job ${jobId} (attempt ${job.retry_count}/${job.max_retries})`);
        setTimeout(() => {
          void processScrapingJob(jobId);
        }, 5000);
      } else {
        console.warn(`Job ${jobId} reached max retries (${job.max_retries}).`);
      }
    } else {
      console.error(
        `Job ${jobId}: Could not get valid retry_count or max_retries. Won't attempt retry.`,
      );
    }
  } catch (retryError) {
    console.error(`Error during retry logic for job ${jobId}:`, retryError);
  }
}

export async function processScrapingJob(jobId: string): Promise<void> {
  try {
    const job = await ScrapingJobService.updateJobStatus(jobId, 'running');
    if (!job.target_url) {
      throw new Error('No target URL specified');
    }

    const scrapeData = await handleScraping(job.target_url, jobId);
    const extractedData = await handleGeminiExtraction(
      scrapeData.markdown,
      scrapeData.source_url ?? job.target_url,
      jobId,
    );

    await ScrapingJobService.updateJobStatus(jobId, 'completed', {
      data_collected: extractedData as unknown as Record<string, unknown>,
      completed_at: new Date().toISOString(),
    });

    await createOrUpdateFoodTruck(jobId, extractedData, scrapeData.source_url ?? job.target_url);

    console.info(`Scraping job ${jobId} completed successfully and data processed.`);
  } catch {
    await handleRetryLogic(jobId);
  }
}

export async function createOrUpdateFoodTruck(
  jobId: string,
  extractedTruckData: ExtractedFoodTruckDetails,
  sourceUrl: string,
) {
  try {
    const validation = await validateInputAndPrepare(jobId, extractedTruckData, sourceUrl);
    if (!validation.isValid) {
      return;
    }

    const truckData = buildTruckDataSchema(extractedTruckData, sourceUrl, validation.name);
    const truck = await handleDuplicateCheck(jobId, truckData, validation.name);
    await finalizeJobStatus(jobId, truck, sourceUrl);
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : 'Unknown error';
    console.error(
      `Job ${jobId}: Error in createOrUpdateFoodTruck from ${sourceUrl ?? 'Unknown Source'}:`,
      error,
    );
    await handleJobFailure(jobId, `Food truck data processing/saving failed: ${errorMessage}`);
  }
}

/**
 * SOTA Rate Limiting Implementation
 * Prevents brute force attacks and API abuse with intelligent rate limiting
 */

import { NextResponse } from 'next/server'; // Added import for NextResponse

interface RateLimitEntry {
  count: number;
  resetTime: number;
  blocked: boolean;
  blockUntil?: number;
}

// In-memory rate limit store (in production, use Redis)
const rateLimitStore = new Map<string, RateLimitEntry>();

// Rate limit configurations for different endpoints
export const RATE_LIMIT_CONFIGS = {
  // Authentication endpoints - strict limits
  auth: {
    windowMs: 15 * 60 * 1000, // 15 minutes
    maxRequests: 5, // 5 attempts per 15 minutes
    blockDurationMs: 30 * 60 * 1000, // Block for 30 minutes
    skipSuccessfulRequests: true,
  },
  // API endpoints - moderate limits
  api: {
    windowMs: 60 * 1000, // 1 minute
    maxRequests: 60, // 60 requests per minute
    blockDurationMs: 5 * 60 * 1000, // Block for 5 minutes
    skipSuccessfulRequests: false,
  },
  // Admin endpoints - very strict limits
  admin: {
    windowMs: 60 * 1000, // 1 minute
    maxRequests: 20, // 20 requests per minute
    blockDurationMs: 15 * 60 * 1000, // Block for 15 minutes
    skipSuccessfulRequests: false,
  },
} as const;

export type RateLimitType = keyof typeof RATE_LIMIT_CONFIGS;

/**
 * Rate Limiter Service
 */
export class RateLimiter {
  // Extract logic from checkRateLimit to reduce function size
  private static isBlocked(entry: RateLimitEntry | undefined, now: number): boolean {
    return Boolean(
      entry && entry.blocked && entry.blockUntil != undefined && now < entry.blockUntil,
    );
  }

  private static resetEntry(
    entry: RateLimitEntry,
    now: number,
    config: (typeof RATE_LIMIT_CONFIGS)[RateLimitType],
  ): void {
    entry.count = 0;
    entry.resetTime = now + config.windowMs;
    entry.blocked = false;
    entry.blockUntil = undefined;
  }

  /**
   * Check if request should be rate limited
   */
  static checkRateLimit(
    identifier: string,
    type: RateLimitType = 'api',
  ): {
    allowed: boolean;
    remaining: number;
    resetTime: number;
    retryAfter?: number;
  } {
    const config = RATE_LIMIT_CONFIGS[type];
    const now = Date.now();
    const key = `${type}:${identifier}`;
    this.cleanupExpiredEntries();
    let entry = rateLimitStore.get(key);
    if (!entry) {
      entry = { count: 0, resetTime: now + config.windowMs, blocked: false };
      rateLimitStore.set(key, entry);
    }
    if (this.isBlocked(entry, now)) {
      return {
        allowed: false,
        remaining: 0,
        resetTime: entry.resetTime,
        retryAfter: Math.ceil((entry.blockUntil! - now) / 1000),
      };
    }
    if (now >= entry.resetTime) {
      this.resetEntry(entry, now, config);
    }
    if (entry.count >= config.maxRequests) {
      entry.blocked = true;
      entry.blockUntil = now + config.blockDurationMs;
      return {
        allowed: false,
        remaining: 0,
        resetTime: entry.resetTime,
        retryAfter: Math.ceil(config.blockDurationMs / 1000),
      };
    }
    entry.count++;
    rateLimitStore.set(key, entry);
    return {
      allowed: true,
      remaining: config.maxRequests - entry.count,
      resetTime: entry.resetTime,
    };
  }

  /**
   * Record successful request (for auth endpoints)
   */
  static recordSuccess(identifier: string, type: RateLimitType = 'api'): void {
    const config = RATE_LIMIT_CONFIGS[type];
    if (!config.skipSuccessfulRequests) return;

    const key = `${type}:${identifier}`;
    const entry = rateLimitStore.get(key);

    if (entry) {
      // Reset counter on successful auth
      entry.count = 0;
      entry.blocked = false;
      entry.blockUntil = undefined;
      rateLimitStore.set(key, entry);
    }
  }

  /**
   * Get rate limit status without incrementing
   */
  static getStatus(
    identifier: string,
    type: RateLimitType = 'api',
  ): {
    remaining: number;
    resetTime: number;
    blocked: boolean;
    retryAfter?: number;
  } {
    const config = RATE_LIMIT_CONFIGS[type];
    const now = Date.now();
    const key = `${type}:${identifier}`;
    const entry = rateLimitStore.get(key);

    if (!entry) {
      return {
        remaining: config.maxRequests,
        resetTime: now + config.windowMs,
        blocked: false,
      };
    }

    // Check if blocked
    if (entry.blocked && entry.blockUntil != undefined && now < entry.blockUntil) {
      // Changed != undefined to != null
      return {
        remaining: 0,
        resetTime: entry.resetTime,
        blocked: true,
        retryAfter: Math.ceil((entry.blockUntil - now) / 1000),
      };
    }

    // Check if window expired
    if (now >= entry.resetTime) {
      return {
        remaining: config.maxRequests,
        resetTime: now + config.windowMs,
        blocked: false,
      };
    }

    return {
      remaining: Math.max(0, config.maxRequests - entry.count),
      resetTime: entry.resetTime,
      blocked: false,
    };
  }

  /**
   * Clean up expired entries to prevent memory leaks
   */
  private static cleanupExpiredEntries(): void {
    const now = Date.now();

    for (const [key, entry] of rateLimitStore.entries()) {
      // Remove entries that are expired and not blocked
      if (
        now >= entry.resetTime &&
        (!entry.blocked || entry.blockUntil == undefined || now >= entry.blockUntil)
      ) {
        // Changed == undefined to == null
        rateLimitStore.delete(key);
      }
    }
  }

  /**
   * Clear all rate limit data for an identifier
   */
  static clearLimits(identifier: string, type?: RateLimitType): void {
    if (type) {
      const key = `${type}:${identifier}`;
      rateLimitStore.delete(key);
    } else {
      // Clear all types for this identifier
      for (const limitType of Object.keys(RATE_LIMIT_CONFIGS) as RateLimitType[]) {
        // Added type assertion
        const key = `${limitType}:${identifier}`;
        rateLimitStore.delete(key);
      }
    }
  }

  /**
   * Get rate limit statistics
   */
  static getStats(): {
    totalEntries: number;
    blockedEntries: number;
    entriesByType: Record<string, number>;
  } {
    const stats = {
      totalEntries: rateLimitStore.size,
      blockedEntries: 0,
      entriesByType: {} as Record<string, number>,
    };

    for (const [key, entry] of rateLimitStore.entries()) {
      const type = key.split(':')[0];
      stats.entriesByType[type] = (stats.entriesByType[type] ?? 0) + 1;

      if (entry.blocked) {
        stats.blockedEntries++;
      }
    }

    return stats;
  }
}

/**
 * Utility function to get client identifier from request
 */
export function getClientIdentifier(request: Request): string {
  // Try to get IP address from various headers
  const forwarded = request.headers.get('x-forwarded-for');
  const realIp = request.headers.get('x-real-ip');
  const cfConnectingIp = request.headers.get('cf-connecting-ip');

  // Use the first available IP
  const ip = forwarded?.split(',')[0]?.trim() ?? realIp ?? cfConnectingIp ?? 'unknown';

  // Include user agent for more specific identification
  const userAgent = request.headers.get('user-agent') ?? 'unknown';

  // Create a hash of IP + User Agent for better identification
  return `${ip}:${userAgent.slice(0, 50)}`;
}

/**
 * Rate limiting middleware for API routes
 */
export function withRateLimit(
  handler: (request: Request) => Promise<Response>,
  type: RateLimitType = 'api',
) {
  return async (request: Request): Promise<Response> => {
    const identifier = getClientIdentifier(request);
    const result = RateLimiter.checkRateLimit(identifier, type);

    if (result.allowed === false) {
      const headers = new Headers({
        'X-RateLimit-Limit': RATE_LIMIT_CONFIGS[type].maxRequests.toString(),
        'X-RateLimit-Remaining': '0',
        'X-RateLimit-Reset': new Date(result.resetTime).toISOString(),
      });

      if (result.retryAfter) {
        headers.set('Retry-After', result.retryAfter.toString());
      }

      return new NextResponse('Too Many Requests', { status: 429, headers });
    }

    const response = await handler(request);

    // Update headers on successful requests if not skipped
    if (RATE_LIMIT_CONFIGS[type].skipSuccessfulRequests === false) {
      const status = RateLimiter.getStatus(identifier, type);
      response.headers.set('X-RateLimit-Limit', RATE_LIMIT_CONFIGS[type].maxRequests.toString());
      response.headers.set('X-RateLimit-Remaining', status.remaining.toString());
      response.headers.set('X-RateLimit-Reset', new Date(status.resetTime).toISOString());
    }

    return response;
  };
}

