import React from 'react';
// Removed Mail, Phone, Globe imports as they are not directly used here

interface ContactFieldProps {
  readonly icon: React.ComponentType<{ className?: string }>;
  readonly label: string;
  readonly value?: string;
  readonly href?: string;
  readonly unavailableText: string;
}

export function ContactField({
  icon: Icon,
  label,
  value,
  href,
  unavailableText
}: Readonly<ContactFieldProps>) {
  if (value == undefined || value === '') {
    return (
      <div className="flex items-center gap-3 text-gray-400">
        <Icon className="h-4 w-4" />
        <span className="text-sm">{unavailableText}</span>
      </div>
    );
  }

  return (
    <div className="flex items-center gap-3">
      <Icon className="h-4 w-4 text-gray-500" />
      <div>
        <label className="text-sm font-medium text-gray-500">{label}</label>
        {href ? (
          <a
            href={href}
            target="_blank"
            rel="noopener noreferrer"
            className="text-blue-600 hover:text-blue-800 underline"
          >
            {value}
          </a>
        ) : (
          <p className="text-gray-900">{value}</p>
        )}
      </div>
    </div>
  );
}

import React from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { ConnectionStatusHeader } from './ConnectionStatusHeader';
import { SystemMetricsGrid } from './SystemMetricsGrid';
import { ScrapingJobsStatus } from './ScrapingJobsStatus';
import { SystemAlerts } from './SystemAlerts';
import { EventControls } from './EventControls';
import { type SystemAlert } from './StatusHelpers';
import { type StatusMetric } from './useSystemMetrics';
import { type RealtimeEvent } from '@/hooks/useRealtimeAdminEvents.types';

interface RealtimeStatusDisplayProps {
  readonly isConnected: boolean;
  readonly isConnecting: boolean;
  readonly connectionError: string | undefined;
  readonly lastEventTime: Date | undefined;
  readonly connect: () => void;
  readonly disconnect: () => void;
  readonly systemMetrics: StatusMetric[];
  readonly scrapingJobs: {
    active: number;
    completed: number;
    failed: number;
    pending: number;
  } | undefined;
  readonly alerts: SystemAlert[];
  readonly showDetails: boolean;
  readonly onToggleDetails: () => void;
  readonly onAcknowledgeAlert: (alertId: string) => void;
  readonly recentEventsCount: number;
  readonly onClearEvents: () => void;
}

export function RealtimeStatusDisplay({
  isConnected,
  isConnecting,
  connectionError,
  lastEventTime,
  connect,
  disconnect,
  systemMetrics,
  scrapingJobs,
  alerts,
  showDetails,
  onToggleDetails,
  onAcknowledgeAlert,
  recentEventsCount,
  onClearEvents,
}: RealtimeStatusDisplayProps) {
  return (
    <div className="space-y-4">
      <Card className={`border-l-4 ${isConnected ? 'border-l-green-500' : 'border-l-red-500'}`}>
        <ConnectionStatusHeader
          isConnected={isConnected}
          isConnecting={isConnecting}
          lastEventTime={lastEventTime ? new Date(lastEventTime) : undefined}
          connect={connect}
          disconnect={disconnect}
        />
        <CardContent>
          {connectionError !== undefined && (
            <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-md">
              <p className="text-sm text-red-600">{connectionError}</p>
            </div>
          )}
          <SystemMetricsGrid
            metrics={systemMetrics}
          />
          <ScrapingJobsStatus scrapingJobs={scrapingJobs} />
          <SystemAlerts
            alerts={alerts}
            showDetails={showDetails}
            onToggleDetails={onToggleDetails}
            onAcknowledgeAlert={onAcknowledgeAlert}
          />
          <EventControls
            recentEventsCount={recentEventsCount}
            onClearEvents={onClearEvents}
          />
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import React from 'react';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { type SystemAlert } from './StatusHelpers';

interface SystemAlertItemProps {
  readonly alert: SystemAlert;
  readonly onAcknowledgeAlert: (id: string) => void;
}

export function SystemAlertItem({ alert, onAcknowledgeAlert }: Readonly<SystemAlertItemProps>) {
  const getAlertClasses = (type: 'warning' | 'error' | 'critical', acknowledged: boolean) => {
    if (acknowledged) {
      return 'border-gray-300 bg-gray-50 text-gray-500';
    }
    switch (type) {
      case 'warning': {
        return 'border-yellow-500 bg-yellow-50 text-yellow-800';
      }
      case 'error': {
        return 'border-red-500 bg-red-50 text-red-800';
      }
      case 'critical': {
        return 'border-red-700 bg-red-100 text-red-900 font-bold';
      }
      default: {
        return 'border-gray-300 bg-gray-50 text-gray-800';
      }
    }
  };

  return (
    <div className={`p-2 rounded border-l-4 ${getAlertClasses(alert.type, alert.acknowledged)}`}>
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <Badge variant={alert.type === 'critical' ? 'destructive' : 'secondary'}>
            {alert.type}
          </Badge>
          <span className="text-sm">{alert.message}</span>
        </div>
        {alert.acknowledged !== true && (
          <Button
            variant="ghost"
            size="sm"
            onClick={() => onAcknowledgeAlert(alert.id)}
          >
            Acknowledge
          </Button>
        )}
      </div>
      <p className="text-xs text-gray-500 mt-1">
        {new Date(alert.timestamp).toLocaleString()}
      </p>
    </div>
  );
}

'use client';

import React from 'react';
import { type StatusMetric } from './useSystemMetrics'; // Import StatusMetric type
import { getStatusColor, getStatusIcon, getTrendIcon } from './status-helpers'; // Import helper functions

interface SystemMetricsGridProps {
  readonly metrics: StatusMetric[];
}

export function SystemMetricsGrid({ metrics }: SystemMetricsGridProps) {
  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
      {metrics.map((metric, index) => (
        <div
          key={index}
          className={`p-3 rounded-lg border ${getStatusColor(metric.status)}`}
        >
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center gap-2">
              {metric.icon}
              <span className="text-sm font-medium">{metric.label}</span>
            </div>
            {getStatusIcon(metric.status)}
          </div>
          <div className="flex items-center gap-2">
            <span className="text-lg font-bold">
              {metric.value}{metric.unit}
            </span>
            {getTrendIcon(metric.trend)}
          </div>
        </div>
      ))}
    </div>
  );
}

import React, { useEffect, useState } from 'react';

interface APIUsageData {
  success: boolean;
  data: Record<string, unknown>; // Changed from any to Record<string, unknown>
  timestamp: string;
}

export function ApiMonitoringDashboard() {
  const [usage, setUsage] = useState<APIUsageData | undefined>(); // Changed null to undefined
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | undefined>(); // Changed null to undefined

  useEffect(() => {
    async function fetchUsage() {
      setLoading(true);
      setError(undefined); // Changed null to undefined
      try {
        const res = await fetch('/api/monitoring/api-usage');
        if (!res.ok) throw new Error('Failed to fetch API usage');
        const data = (await res.json()) as APIUsageData;
        setUsage(data);
      } catch (error_: unknown) {
        const errorMessage = error_ instanceof Error ? error_.message : String(error_);
        setError(errorMessage);
      } finally {
        setLoading(false);
      }
    }
    void fetchUsage();
  }, []);

  if (loading) return <div>Loading API usage...</div>;
  if (error !== undefined) return <div className="text-red-600">Error: {error}</div>;
  if (usage === undefined) return <div>No data available.</div>;

  return (
    <div className="mt-4 p-4 border rounded bg-muted">
      <h2 className="text-lg font-semibold mb-2">API Usage Monitoring</h2>
      <pre className="text-xs bg-background p-2 rounded overflow-x-auto">
        {JSON.stringify(usage.data, null, 2)}
      </pre>
      <div className="text-xs text-muted-foreground mt-2">Last updated: {usage.timestamp}</div>
    </div>
  );
}

import * as React from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Textarea } from '@/components/ui/textarea';
import { StageResult } from '@/lib/types';

interface StageResultCardProps {
  readonly stageName: string;
  readonly result?: StageResult;
}

export function StageResultCard({ stageName, result }: Readonly<StageResultCardProps>) {
  if (!result) return;

  return (
    <Card className="border-red-500">
      <CardHeader>
        <CardTitle>{stageName}</CardTitle>
        <CardDescription>
          Status:{' '}
          <span className={result.status === 'Success' ? 'text-green-500' : 'text-red-500'}>
            {result.status}
          </span>
        </CardDescription>
      </CardHeader>
      <CardContent>
        {result.error !== undefined && (
          <p className="text-red-500">
            <strong>Error:</strong> {result.error}
          </p>
        )}
        {result.details !== undefined && (
          <p>
            <strong>Details:</strong> {result.details}
          </p>
        )}
        {result.prompt !== undefined && (
          <div>
            <strong>Prompt:</strong>
            <Textarea
              readOnly
              value={result.prompt}
              className="mt-1 h-32 bg-gray-50 dark:bg-slate-700"
            />
          </div>
        )}
        {result.rawContent !== undefined && (
          <div>
            <strong>Raw Content (Firecrawl):</strong>
            <Textarea
              readOnly
              value={result.rawContent}
              className="mt-1 h-48 bg-gray-50 dark:bg-slate-700"
            />
          </div>
        )}
        {result.data !== undefined && (
          <div className="mt-2">
            <strong>Data Output:</strong>{' '}
            <pre className="mt-1 p-2 bg-gray-100 dark:bg-slate-700 rounded-md overflow-x-auto text-sm">
              {JSON.stringify(result.data, null, 2)}
            </pre>
          </div>
        )}
        {result.preparedData !== undefined && (
          <div className="mt-2">
            <strong>Data Prepared for Supabase:</strong>{' '}
            <pre className="mt-1 p-2 bg-gray-100 dark:bg-slate-700 rounded-md overflow-x-auto text-sm">
              {JSON.stringify(result.preparedData, null, 2)}
            </pre>
          </div>
        )}
        {result.recordId !== undefined && (
          <p>
            <strong>Supabase Record id:</strong> {result.recordId}
          </p>
        )}
        {result.tokensUsed !== undefined && (
          <p>
            <strong>Gemini Tokens Used:</strong> {result.tokensUsed}
          </p>
        )}
        {result.metadata !== undefined && (
          <div>
            <strong>Metadata (Firecrawl):</strong>{' '}
            <pre className="mt-1 p-2 bg-gray-100 dark:bg-slate-700 rounded-md overflow-x-auto text-sm">
              {JSON.stringify(result.metadata, null, 2)}
            </pre>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

import * as React from 'react';
import { FormEvent } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Checkbox } from '@/components/ui/checkbox';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';

interface TestPipelineFormProps {
  readonly url: string;
  readonly setUrl: (url: string) => void;
  readonly rawText: string;
  readonly setRawText: (text: string) => void;
  readonly useRawText: boolean;
  readonly setUseRawText: (use: boolean) => void;
  readonly isDryRun: boolean;
  readonly setIsDryRun: (isDry: boolean) => void;
  readonly isLoading: boolean;
  readonly onSubmit: (event: FormEvent<HTMLFormElement>) => void;
}

export function TestPipelineForm({
  url, setUrl, rawText, setRawText, useRawText, setUseRawText,
  isDryRun, setIsDryRun, isLoading, onSubmit
}: Readonly<TestPipelineFormProps>) {
  return (
    <Card className="mb-6">
      <CardHeader>
        <CardTitle>Test Data Pipeline</CardTitle>
        <CardDescription>
          Use this page to test the data scraping and processing pipeline with a specific url or
          raw text.
        </CardDescription>
      </CardHeader>
      <CardContent>
        <form
          onSubmit={(e) => {
            onSubmit(e);
          }}
          className="space-y-6"
        >
          <div>
            <Label htmlFor="url-input">url to Scrape</Label>
            <Input
              id="url-input"
              type="url"
              placeholder="https://example.com"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              disabled={useRawText || isLoading}
              className="mt-1"
            />
          </div>

          <div className="flex items-center space-x-2">
            <Checkbox
              id="use-raw-text-checkbox"
              checked={useRawText}
              onCheckedChange={(checked: boolean) => setUseRawText(Boolean(checked))}
              disabled={isLoading}
            />
            <Label htmlFor="use-raw-text-checkbox">Use Raw Text Input Instead</Label>
          </div>

          <div>
            <Label htmlFor="raw-text-input">Raw Text (Markdown/html)</Label>
            <Textarea
              id="raw-text-input"
              placeholder="Paste Markdown or html content here..."
              value={rawText}
              onChange={(e) => setRawText(e.target.value)}
              disabled={!useRawText || isLoading}
              className="mt-1 h-40"
            />
          </div>

          <div className="flex items-center space-x-2">
            <Checkbox
              id="dry-run-checkbox"
              checked={isDryRun}
              onCheckedChange={(checked: boolean) => setIsDryRun(Boolean(checked))}
              disabled={isLoading}
            />
            <Label htmlFor="dry-run-checkbox">Dry Run (Do not save to Supabase)</Label>
          </div>

          <Button type="submit" disabled={isLoading || (useRawText ? !rawText : !url)}>
            {isLoading ? 'Testing...' : 'Run Test'}
          </Button>
        </form>
      </CardContent>
    </Card>
  );
}

import React from 'react';
import {
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from '@/components/ui/accordion';
import { Badge } from '@/components/ui/badge';
import { TruckCard } from '@/components/TruckCard';
import { FoodTruck } from '@/lib/types/foodTruck';

interface TruckAccordionItemProps {
  readonly truck: FoodTruck;
  readonly selectedTruckId: string | undefined;
  readonly setSelectedTruckId: (id: string | undefined) => void;
  readonly isOpen: (truck: FoodTruck) => boolean;
  readonly userLocation: { lat: number; lng: number } | undefined;
}

export function TruckAccordionItem({
  truck,
  selectedTruckId,
  setSelectedTruckId,
  isOpen,
  userLocation,
}: TruckAccordionItemProps) {
  return (
    <AccordionItem value={truck.id} key={truck.id}>
      <AccordionTrigger className="flex items-center justify-between p-4 hover:bg-gray-50 dark:hover:bg-slate-700 rounded-md">
        <div className="flex-1 text-left">
          <h4 className="font-medium dark:text-gray-100">{truck.name}</h4>
          {truck.current_location?.address && (
            <p className="text-sm text-gray-600 dark:text-gray-400 line-clamp-1">
              {truck.current_location.address}
            </p>
          )}
        </div>
        <Badge variant={isOpen(truck) ? 'default' : 'secondary'}>
          {isOpen(truck) ? 'Open' : 'Closed'}
        </Badge>
      </AccordionTrigger>
      <AccordionContent>
        <TruckCard
          truck={truck}
          isOpen={isOpen(truck)}
          onSelectTruck={() => setSelectedTruckId(truck.id)}
          userLocation={userLocation}
          hideHeader={true}
        />
      </AccordionContent>
    </AccordionItem>
  );
}

import React from 'react';
import { Badge } from '@/components/ui/badge';
import { FoodTruck } from '@/lib/types/foodTruck';
import { RatingSection } from './RatingSection';
import { MenuSection } from './MenuSection';
import { ContactSection } from './ContactSection';
import { SocialMediaSection } from './SocialMediaSection';
import { OperatingHoursSection } from './OperatingHoursSection';
import { formatHours, formatPrice } from '@/lib/utils/foodTruckHelpers';

interface TruckCardContentProps {
  readonly truck: FoodTruck;
  readonly todayHours?: { open: string; close: string; closed: boolean };
  readonly popularItems: { name: string; price: number }[];
}

export function TruckCardContent({
  truck,
  todayHours,
  popularItems,
}: TruckCardContentProps) {
  return (
    <>
      {truck.description && (
        <p className="text-gray-600 dark:text-gray-400 mb-4 line-clamp-2">{truck.description}</p>
      )}
      <div className="space-y-4">
        {/* Ratings & Hours Row */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <RatingSection averageRating={truck.average_rating} reviewCount={truck.review_count} />
          <OperatingHoursSection todayHours={todayHours} formatHours={formatHours} />
        </div>

        {/* Menu & Contact Row */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <MenuSection popularItems={popularItems} />
          <ContactSection contactInfo={truck.contact_info} verificationStatus={truck.verification_status} />
        </div>

        <SocialMediaSection socialMedia={truck.social_media} />
      </div>
      {truck.verification_status && (
        <div className="mt-2">
          <Badge variant={truck.verification_status === 'verified' ? 'default' : 'secondary'}>
            <span className="capitalize">{truck.verification_status}</span>
          </Badge>
        </div>
      )}
    </>
  );
}

import React from 'react';
import { CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { MapPin } from 'lucide-react';
import { FoodTruck } from '@/lib/types/foodTruck';

interface TruckCardHeaderProps {
  readonly truck: FoodTruck;
  readonly isOpen: boolean;
  readonly popularItems: Array<{ name: string; price?: number }>;
  readonly priceRange: string | undefined;
}

export function TruckCardHeader({
  truck,
  isOpen,
  popularItems,
  priceRange
}: TruckCardHeaderProps) {
  return (
    <CardHeader>
      <div className="flex justify-between items-start">
        <div className="flex-1">
          <CardTitle className="text-lg dark:text-gray-100">{truck.name}</CardTitle>
          {(truck.current_location?.address != undefined) && (
            <CardDescription className="flex items-center mt-1 dark:text-gray-400">
              <MapPin className="h-4 w-4 mr-1" />
              {truck.current_location.address}
            </CardDescription>
          )}
        </div>
        <div className="flex flex-col items-end space-y-1">
          <Badge variant={isOpen ? 'default' : 'secondary'}>{isOpen ? 'Open' : 'Closed'}</Badge>
          {/* Show price range fallback if no explicit prices */}
          {popularItems.every((item) => !item.price) && priceRange && (
            <Badge variant="outline" className="mt-1">
              {priceRange}
            </Badge>
          )}
        </div>
      </div>
    </CardHeader>
  );
}

