export class GeminiDataProcessor {
  private _apiKey: string;
  private _baseUrl: string;
  private requestCount: number;
  private tokenCount: number;
  private dailyLimit: { requests: number; tokens: number };

  constructor(apiKey: string) {
    this._apiKey = apiKey;
    this._baseUrl = 'https://generativelanguage.googleapis.com/v1beta';
    this.requestCount = 0;
    this.tokenCount = 0;
    this.dailyLimit = { requests: 1500, tokens: 32_000 };
  }
  async processMenuData(rawMenuText: string): Promise<{ categories: MenuCategory[] }> {
    if (!this.canMakeRequest()) {
      throw new Error('Gemini API rate limit exceeded');
    }

    try {
      const prompt = `
        Parse the following food truck menu text and return a structured JSON format:
        
        ${rawMenuText}
        
        Return format:
        {
          "categories": [
            {
              "name": "category_name",
              "items": [
                {
                  "name": "item_name",
                  "description": "item_description",
                  "price": 0.00,
                  "dietary_tags": ["vegetarian", "vegan", "gluten-free", etc.]
                }
              ]
            }
          ]
        }
        
        Only return valid JSON, no additional text.
      `;

      const response = await this.makeGeminiRequest(prompt);
      this.updateUsageCounters(1, prompt.length + response.length);

      // Ensure type safety for parsed response
      const parsed = JSON.parse(response);
      if (!parsed || typeof parsed !== 'object' || !('categories' in parsed)) {
        throw new Error('Invalid Gemini menu response');
      }
      return parsed as { categories: MenuCategory[] };
    } catch (error) {
      console.error('Error processing menu data with Gemini:', error);
      throw error;
    }
  }

  async extractLocationFromText(text: string): Promise<GeminiLocationData> {
    if (!this.canMakeRequest()) {
      throw new Error('Gemini API rate limit exceeded');
    }

    try {
      const prompt = `
        Extract location information from the following text and return structured data:
        
        "${text}"
        
        Return format:
        {
          "address": "full_address",
          "city": "city_name",
          "state": "state",
          "coordinates": {
            "lat": 0.0,
            "lng": 0.0
          },
          "confidence": 0.95
        }
        
        If coordinates cannot be determined, set them to undefined. Only return valid JSON.
      `;
      const response = await this.makeGeminiRequest(prompt);
      this.updateUsageCounters(1, prompt.length + response.length);
      const parsedResponse: unknown = JSON.parse(response);
      const validatedResponse = this.validateGeminiLocationResponse(parsedResponse);
      return validatedResponse;
    } catch (error) {
      console.error('Error extracting location with Gemini:', error);
      throw error;
    }
  }

  private validateGeminiLocationResponse(parsedResponse: unknown): GeminiLocationData {
    if (
      parsedResponse == undefined ||
      typeof parsedResponse !== 'object' ||
      !('coordinates' in parsedResponse) ||
      typeof (parsedResponse as Record<string, unknown>).coordinates !== 'object'
    ) {
      throw new Error('Invalid Gemini location response');
    }
    const coordinates = (parsedResponse as { coordinates?: { lat?: unknown; lng?: unknown } }).coordinates;
    if (coordinates && typeof coordinates === 'object') {
      if (typeof coordinates.lat !== 'number') {
        (coordinates as { lat?: number | undefined }).lat = undefined;
      }
      if (typeof coordinates.lng !== 'number') {
        (coordinates as { lng?: number | undefined }).lng = undefined;
      }
    }
    return parsedResponse as GeminiLocationData;
  }

  async standardizeOperatingHours(hoursText: string): Promise<GeminiOperatingHours> {
    if (!this.canMakeRequest()) {
      throw new Error('Gemini API rate limit exceeded');
    }

    try {
      const prompt = `
        Parse the following operating hours text and return standardized format:
        
        "${hoursText}"
        
        Return format:
        {
          "monday": {"open": "HH:MM", "close": "HH:MM", "closed": false},
          "tuesday": {"open": "HH:MM", "close": "HH:MM", "closed": false},
          "wednesday": {"open": "HH:MM", "close": "HH:MM", "closed": false},
          "thursday": {"open": "HH:MM", "close": "HH:MM", "closed": false},
          "friday": {"open": "HH:MM", "close": "HH:MM", "closed": false},
          "saturday": {"open": "HH:MM", "close": "HH:MM", "closed": false},
          "sunday": {"open": "HH:MM", "close": "HH:MM", "closed": false}
        }
          Use 24-hour format. If closed on a day, set "closed": true and omit open/close times.
        Only return valid JSON.
      `;

      const response = await this.makeGeminiRequest(prompt);
      this.updateUsageCounters(1, prompt.length + response.length);
      const parsed = JSON.parse(response);
      if (parsed == undefined || typeof parsed !== 'object') {
        throw new Error('Invalid Gemini hours response');
      }
      return parsed as GeminiOperatingHours;
    } catch (error) {
      console.error('Error standardizing hours with Gemini:', error);
      throw error;
    }
  }

  async analyzeSentiment(reviewText: string): Promise<GeminiSentimentAnalysis> {
    if (!this.canMakeRequest()) {
      throw new Error('Gemini API rate limit exceeded');
    }

    try {
      const prompt = `
        Analyze the sentiment of this food truck review and extract key insights:
        
        "${reviewText}"
        
        Return format:
        {
          "sentiment": "positive|negative|neutral",
          "score": 0.85,
          "key_topics": ["food_quality", "service", "price", "location"],
          "summary": "brief_summary_of_review"
        }
          Score should be between 0 (very negative) and 1 (very positive).
        Only return valid JSON.
      `;

      const response = await this.makeGeminiRequest(prompt);
      this.updateUsageCounters(1, prompt.length + response.length);
      const parsed = JSON.parse(response);
      if (parsed == undefined || typeof parsed !== 'object') {
        throw new Error('Invalid Gemini sentiment response');
      }
      return parsed as GeminiSentimentAnalysis;
    } catch (error) {
      console.error('Error analyzing sentiment with Gemini:', error);
      throw error;
    }
  }

  private async makeGeminiRequest(prompt: string): Promise<string> {
    await new Promise((resolve) => setTimeout(resolve, 1000)); // Simulate API delay
    return this.getMockGeminiResponse(prompt);
  }

  private getMockGeminiResponse(prompt: string): string {
    if (prompt.includes('menu text')) {
      return JSON.stringify({
        categories: [
          {
            name: 'Burgers',
            items: [
              {
                name: 'Classic Cheeseburger',
                description: 'Beef patty with cheese, lettuce, tomato',
                price: 12.99,
                dietary_tags: [],
              },
            ],
          },
        ],
      });
    } else if (prompt.includes('location information')) {
      return JSON.stringify({
        address: '123 Market St, San Francisco, CA 94105',
        city: 'San Francisco',
        state: 'CA',
        coordinates: {
          lat: 37.7749,
          lng: -122.4194,
        },
        confidence: 0.95,
      });
    } else if (prompt.includes('operating hours')) {
      return JSON.stringify({
        monday: { open: '11:00', close: '15:00', closed: false },
        tuesday: { open: '11:00', close: '15:00', closed: false },
        wednesday: { open: '11:00', close: '15:00', closed: false },
        thursday: { open: '11:00', close: '15:00', closed: false },
        friday: { open: '11:00', close: '20:00', closed: false },
        saturday: { open: '12:00', close: '20:00', closed: false },
        sunday: { open: '12:00', close: '16:00', closed: false },
      });
    } else if (prompt.includes('sentiment')) {
      return JSON.stringify({
        sentiment: 'positive',
        score: 0.85,
        key_topics: ['food_quality', 'service'],
        summary: 'Customer enjoyed the food and service',
      });
    }
    return '{"processed": true}';
  }

  private canMakeRequest(): boolean {
    return this.requestCount < this.dailyLimit.requests && this.tokenCount < this.dailyLimit.tokens;
  }

  private updateUsageCounters(requests: number, tokens: number): void {
    this.requestCount += requests;
    this.tokenCount += tokens;
  }

  getUsageStats(): {
    requests: { used: number; limit: number; remaining: number };
    tokens: { used: number; limit: number; remaining: number };
  } {
    return {
      requests: {
        used: this.requestCount,
        limit: this.dailyLimit.requests,
        remaining: this.dailyLimit.requests - this.requestCount,
      },
      tokens: {
        used: this.tokenCount,
        limit: this.dailyLimit.tokens,
        remaining: this.dailyLimit.tokens - this.tokenCount,
      },
    };
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { BatchCleanupService } from '@/lib/data-quality/batchCleanup';
import { DuplicatePreventionService } from '@/lib/data-quality/duplicatePrevention';

export interface DataCleanupRequestBody {
  action: string;
  options?: {
    batchSize?: number;
    dryRun?: boolean;
    operations?: string[];
    truckData?: Record<string, unknown>;
    targetId?: string;
    sourceId?: string;
  };
}

export async function handlePostRequest(body: DataCleanupRequestBody): Promise<NextResponse> {
  const { action, options = {} } = body;

  switch (action) {
    case 'full-cleanup': {
      return await handleFullCleanup(options);
    }
    case 'check-duplicates': {
      return await handleCheckDuplicates(options);
    }
    case 'merge-duplicates': {
      return await handleMergeDuplicates(options);
    }
    case 'dry-run': {
      return await handleDryRun(options);
    }
    default: {
      return NextResponse.json(
        { success: false, error: `Unknown action: ${action}` },
        { status: 400 }
      );
    }
  }
}

export async function handleGetRequest(request: NextRequest): Promise<NextResponse> {
  const { searchParams } = new URL(request.url);
  const action = searchParams.get('action');

  switch (action) {
    case 'status': {
      return await handleGetStatus();
    }
    case 'preview': {
      return await handleGetPreview();
    }
    default: {
      return await handleGetDefault();
    }
  }
}

export async function handleFullCleanup(options: DataCleanupRequestBody['options']): Promise<NextResponse> {
  const result = await BatchCleanupService.runFullCleanup({
    batchSize: options?.batchSize ?? 50,
    dryRun: options?.dryRun ?? false,
    operations: options?.operations as any
  });
  
  return NextResponse.json({
    success: true,
    action: 'full-cleanup',
    result,
    message: `Cleanup completed: ${result.summary.trucksImproved} trucks improved, ${result.summary.duplicatesRemoved} duplicates removed`
  });
}

export async function handleCheckDuplicates(options: DataCleanupRequestBody['options']): Promise<NextResponse> {
  const { truckData } = options ?? {};
  if (!truckData) {
    return NextResponse.json(
      { success: false, error: 'Missing truckData for duplicate check' },
      { status: 400 }
    );
  }

  const duplicateCheck = await DuplicatePreventionService.checkForDuplicates(truckData);
  
  return NextResponse.json({
    success: true,
    action: 'check-duplicates',
    result: duplicateCheck
  });
}

export async function handleMergeDuplicates(options: DataCleanupRequestBody['options']): Promise<NextResponse> {
  const { targetId, sourceId } = options ?? {};
  if (targetId === undefined || sourceId === undefined) {
    return NextResponse.json(
      { success: false, error: 'Missing targetId or sourceId for merge operation' },
      { status: 400 }
    );
  }

  const mergedTruck = await DuplicatePreventionService.mergeDuplicates(targetId, sourceId);
  
  return NextResponse.json({
    success: true,
    action: 'merge-duplicates',
    result: mergedTruck,
    message: `Successfully merged truck ${sourceId} into ${targetId}`
  });
}

export async function handleDryRun(options: DataCleanupRequestBody['options']): Promise<NextResponse> {
  const result = await BatchCleanupService.runFullCleanup({
    ...options,
    operations: options?.operations as any,
    dryRun: true
  });
  
  return NextResponse.json({
    success: true,
    action: 'dry-run',
    result,
    message: 'Dry run completed - no changes made to database'
  });
}

export  function handleGetStatus(): Promise<NextResponse> {
  return NextResponse.json({
    success: true,
    status: {
      available_operations: [
        'remove_placeholders',
        'normalize_phone',
        'fix_coordinates',
        'update_quality_scores',
        'merge_duplicates'
      ],
      default_batch_size: 50,
      supports_dry_run: true
    }
  });
}

export async function handleGetPreview(): Promise<NextResponse> {
  const result = await BatchCleanupService.runFullCleanup({
    batchSize: 10,
    dryRun: true
  });
  
  return NextResponse.json({
    success: true,
    preview: {
      estimated_improvements: result.summary.trucksImproved,
      estimated_duplicates: result.summary.duplicatesRemoved,
      operations: result.operations.map(op => ({
        type: op.type,
        description: op.description,
        affected_count: op.affectedCount
      }))
    }
  });
}

export  function handleGetDefault(): Promise<NextResponse> {
  return NextResponse.json({
    success: true,
    endpoints: [
      'GET /api/admin/data-cleanup?action=status - Get cleanup system status',
      'GET /api/admin/data-cleanup?action=preview - Preview cleanup changes',
      'POST /api/admin/data-cleanup - Run cleanup operations'
    ],
    actions: [
      'full-cleanup - Run all cleanup operations',
      'check-duplicates - Check if truck data is duplicate',
      'merge-duplicates - Merge two duplicate trucks',
      'dry-run - Preview changes without making them'
    ]
  });
}

