async function getOAuthStatus(): Promise<OAuthStatus> {
  const status: OAuthStatus = {
    timestamp: new Date().toISOString(),
    environment: process.env.NODE_ENV === 'production' ? 'production' : 'development',
    supabase: {
      connected: false,
      projectId: 'zkwliyjjkdnigizidlln'
    },
    environment_variables: {
      supabaseUrl: process.env.NEXT_PUBLIC_SUPABASE_URL != undefined,
      supabaseAnonKey: process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY != undefined,
      supabaseServiceKey: process.env.SUPABASE_SERVICE_ROLE_KEY != undefined
    },
    oauth_flow: {
      loginPageExists: true,
      callbackRouteExists: true,
      authProviderConfigured: false
    },
    recommendations: [],
    overall_status: 'not_configured'
  };

  await checkSupabaseConnection(status, supabase);
  await checkSupabaseAuthSettings(status);
  await testOAuthProvider(status, supabase);

  status.recommendations = generateRecommendations(status);
  status.overall_status = determineOverallStatus(status);

  return status;
}

async function checkSupabaseConnection(status: OAuthStatus, supabase: any) {
  try {
    const { error } = await supabase.from('profiles').select('count').limit(1);
    if (error == undefined) {
      status.supabase.connected = true;
    } else {
      status.supabase.error = error.message;
    }
  } catch (error) {
    status.supabase.error = error instanceof Error ? error.message : 'Unknown connection error';
  }
}

async function checkSupabaseAuthSettings(status: OAuthStatus) {
  try {
    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
    if (supabaseUrl != undefined && supabaseUrl !== '') {
      const settingsResponse = await fetch(`${supabaseUrl}/auth/v1/settings`);
      if (settingsResponse.ok === true) {
        const settings = await settingsResponse.json() as {
          external?: { google?: boolean };
          disable_signup?: boolean;
          autoconfirm?: boolean;
        };
        status.supabase.authSettings = {
          googleEnabled: settings.external?.google ?? false,
          signupEnabled: settings.disable_signup !== true,
          autoconfirm: settings.autoconfirm ?? false
        };
        if (settings.external?.google != undefined) {
          status.oauth_flow.authProviderConfigured = true;
        }
      }
    }
  } catch {
    console.info('Auth settings endpoint requires authentication (normal)');
  }
}

async function testOAuthProvider(status: OAuthStatus, supabase: any) {
  try {
    const { error: oauthError } = await supabase.auth.signInWithOAuth({
      provider: 'google',
      options: {
        redirectTo: 'http://localhost:3000/auth/callback',
        skipBrowserRedirect: true
      }
    });
    if (!oauthError || oauthError.message !== 'Provider not found') {
      status.oauth_flow.authProviderConfigured = true;
    }
  } catch {
    console.info('OAuth provider test failed (may be normal)');
  }
}

function generateRecommendations(status: OAuthStatus): string[] {
  const recommendations: string[] = [];

  if (!status.environment_variables.supabaseUrl) {
    recommendations.push(' Configure NEXT_PUBLIC_SUPABASE_URL environment variable');
  }
  if (!status.environment_variables.supabaseAnonKey) {
    recommendations.push(' Configure NEXT_PUBLIC_SUPABASE_ANON_KEY environment variable');
  }
  if (!status.environment_variables.supabaseServiceKey) {
    recommendations.push(' Configure SUPABASE_SERVICE_ROLE_KEY environment variable');
  }

  if (!status.supabase.connected) {
    recommendations.push(' Fix Supabase connection issue');
    if (status.supabase.error != undefined && status.supabase.error !== '') {
      recommendations.push(`   Error: ${status.supabase.error}`);
    }
  }

  if (status.supabase.authSettings == undefined) {
    recommendations.push(' Configure Google OAuth in Supabase Dashboard', '   1. Create Google Cloud Console OAuth credentials', '   2. Add credentials to Supabase Auth settings');
  } else {
    if (status.supabase.authSettings.googleEnabled === true) {
      recommendations.push(' Google OAuth provider is enabled');
    } else {
      recommendations.push(' Enable Google OAuth provider in Supabase Dashboard', '   Go to: Authentication > Providers > Google');
    }
  }

  if (status.overall_status === 'ready') {
    recommendations.push(' OAuth configuration is complete!', ' Test the login flow at /login');
  }

  if (recommendations.length > 1) {
    recommendations.push(' See docs/GOOGLE_OAUTH_SETUP_GUIDE.md for detailed instructions', ' Run: npm run oauth:verify for automated checks');
  }

  return recommendations;
}

function determineOverallStatus(status: OAuthStatus): 'ready' | 'partial' | 'not_configured' | 'error' {
  if (status.supabase.connected !== true || (status.supabase.error != undefined && status.supabase.error !== '')) {
    return 'error';
  }

  const envVarsComplete = Object.values(status.environment_variables).every(Boolean);
  if (!envVarsComplete) {
    return 'not_configured';
  }

  if (status.supabase.authSettings?.googleEnabled === true && status.oauth_flow.authProviderConfigured === true) {
    return 'ready';
  }

  if (status.supabase.connected === true && envVarsComplete) {
    return 'partial';
  }

  return 'not_configured';
}

function getStatusMessage(status: string): string {
  switch (status) {
    case 'ready': {
      return 'Google OAuth is fully configured and ready to use';
    }
    case 'partial': {
      return 'Basic configuration complete, OAuth provider needs setup';
    }
    case 'not_configured': {
      return 'Google OAuth is not configured';
    }
    case 'error': {
      return 'Configuration error detected';
    }
    default: {
      return 'Unknown configuration status';
    }
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { supabase, supabaseAdmin, ScrapingJobService, FoodTruckService } from '@/lib/supabase';
import { AdminEvent } from './types';

interface RealtimeMetrics {
  scrapingJobs: {
    active: number;
    completed: number;
    failed: number;
    pending: number;
  };
  dataQuality: {
    averageScore: number;
    totalTrucks: number;
    recentChanges: number;
  };
  systemHealth: {
    status: 'healthy' | 'warning' | 'error';
    uptime: number;
    lastUpdate: string;
  };
}

export async function verifyAdminAccess(request: NextRequest): Promise<boolean> {
  try {
    const authHeader = request.headers.get('authorization');
    if (authHeader?.startsWith('Bearer ') !== true) {
      return false;
    }

    const token = authHeader.slice(7);
    const { data: { user }, error } = await supabase.auth.getUser(token);

    if (error || !user) {
      return false;
    }

    if (!supabaseAdmin) {
      return false;
    }

    const { data: profile } = await supabaseAdmin
      .from('profiles')
      .select('role')
      .eq('id', user.id)
      .single();

    return profile?.role === 'admin';
  } catch {
    return false;
  }
}

export async function handleGetRequest(request: NextRequest): Promise<Response> {
  const stream = new ReadableStream({
    start(controller) {
      const encoder = new TextEncoder();

      const connectionEvent: AdminEvent = {
        id: generateEventId(),
        type: 'heartbeat',
        timestamp: new Date().toISOString(),
        data: {
          message: 'Real-time admin dashboard connected',
          connectionId: generateEventId()
        }
      };
      
      controller.enqueue(encoder.encode(formatSSEMessage(connectionEvent)));

      const handleHeartbeatEvent = async () => {
        try {
          const metrics = await fetchRealtimeMetrics();
          const event: AdminEvent = {
            id: generateEventId(),
            type: 'heartbeat',
            timestamp: new Date().toISOString(),
            data: { ...metrics }
          };

          controller.enqueue(encoder.encode(formatSSEMessage(event)));
        } catch (error) {
          console.error('Error fetching realtime metrics:', error);

          const errorEvent: AdminEvent = {
            id: generateEventId(),
            type: 'system_alert',
            timestamp: new Date().toISOString(),
            data: {
              error: 'Failed to fetch metrics',
              details: error instanceof Error ? error.message : 'Unknown error'
            },
            severity: 'error'
          };

          controller.enqueue(encoder.encode(formatSSEMessage(errorEvent)));
        }
      };

      const intervalId = setInterval(() => {
        void handleHeartbeatEvent();
      }, 5000);

      const changeMonitorId = setInterval(async () => {
        try {
          await monitorDataChanges(controller, encoder);
        } catch (error) {
          console.error('Error monitoring data changes:', error);
        }
      }, 10_000);

      request.signal.addEventListener('abort', () => {
        clearInterval(intervalId);
        clearInterval(changeMonitorId);
        controller.close();
      });
    }
  });

  return new Response(stream, {
    headers: {
      'Content-Type': 'text/event-stream',
      'Cache-Control': 'no-cache',
      'Connection': 'keep-alive',
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Headers': 'Cache-Control'
    }
  });
}

export async function handlePostRequest(request: NextRequest): Promise<Response> {
  try {
    const body = await request.json();
    const { action } = body as { action: string };

    switch (action) {
      case 'health_check': {
        return await handleHealthCheck();
      }

      case 'trigger_test_event': {
        return handleTriggerTestEvent();
      }

      default: {
        return new Response(JSON.stringify({
          success: false,
          error: 'Unknown action',
          available_actions: ['health_check', 'trigger_test_event']
        }), {
          status: 400,
          headers: { 'Content-Type': 'application/json' }
        });
      }
    }
  } catch (error) {
    console.error('Realtime events POST error:', error);
    return new Response(JSON.stringify({
      success: false,
      error: 'Internal server error',
      details: error instanceof Error ? error.message : 'Unknown error'
    }), {
      status: 500,
      headers: { 'Content-Type': 'application/json' }
    });
  }
}

async function fetchRealtimeMetrics(): Promise<RealtimeMetrics> {
  try {
    const recentJobs = await ScrapingJobService.getJobsByStatus('all');
    const typedJobs = recentJobs as Array<{ status?: string }>;
    const scrapingMetrics = {
      active: typedJobs.filter(job => job.status === 'running').length,
      completed: typedJobs.filter(job => job.status === 'completed').length,
      failed: typedJobs.filter(job => job.status === 'failed').length,
      pending: typedJobs.filter(job => job.status === 'pending').length
    };

    const qualityStats = await FoodTruckService.getDataQualityStats();
    const dataQualityMetrics = {
      averageScore: qualityStats.avg_quality_score ?? 0,
      totalTrucks: qualityStats.total_trucks ?? 0,
      recentChanges: 0
    };

    const systemHealth = {
      status: 'healthy' as const,
      uptime: process.uptime(),
      lastUpdate: new Date().toISOString()
    };

    return {
      scrapingJobs: scrapingMetrics,
      dataQuality: dataQualityMetrics,
      systemHealth
    };
  } catch (error) {
    console.error('Error fetching realtime metrics:', error);
    return {
      scrapingJobs: { active: 0, completed: 0, failed: 0, pending: 0 },
      dataQuality: { averageScore: 0, totalTrucks: 0, recentChanges: 0 },
      systemHealth: {
        status: 'error',
        uptime: 0,
        lastUpdate: new Date().toISOString()
      }
    };
  }
}

async function sendScrapingUpdateEvent(
  controller: ReadableStreamDefaultController<Uint8Array>,
  encoder: TextEncoder
): Promise<void> {
  const recentJobs = await ScrapingJobService.getJobsByStatus('all');

  if (recentJobs.length > 0) {
    const event: AdminEvent = {
      id: generateEventId(),
      type: 'scraping_update',
      timestamp: new Date().toISOString(),
      data: {
        recentJobs: recentJobs.map((job: unknown) => {
          const jobData = job as { id?: string; status?: string; started_at?: string; completed_at?: string };
          return {
            id: jobData.id,
            status: jobData.status,
            started_at: jobData.started_at,
            completed_at: jobData.completed_at
          };
        }),
        count: recentJobs.length
      },
      severity: 'info'
    };

    controller.enqueue(encoder.encode(formatSSEMessage(event)));
  }
}

async function sendDataQualityChangeEvent(
  controller: ReadableStreamDefaultController<Uint8Array>,
  encoder: TextEncoder
): Promise<void> {
  const recentTrucks = await FoodTruckService.getAllTrucks(10, 0);
  const recentlyUpdated = recentTrucks.trucks.filter(truck => {
    const updatedAt = new Date(truck.updated_at);
    const oneMinuteAgo = new Date(Date.now() - 60_000);
    return updatedAt > oneMinuteAgo;
  });

  if (recentlyUpdated.length > 0) {
    const event: AdminEvent = {
      id: generateEventId(),
      type: 'data_quality_change',
      timestamp: new Date().toISOString(),
      data: {
        updatedTrucks: recentlyUpdated.map(truck => ({
          id: truck.id,
          name: truck.name,
          data_quality_score: truck.data_quality_score,
          updated_at: truck.updated_at
        })),
        count: recentlyUpdated.length
      },
      severity: 'info'
    };

    controller.enqueue(encoder.encode(formatSSEMessage(event)));
  }
}

async function monitorDataChanges(
  controller: ReadableStreamDefaultController<Uint8Array>,
  encoder: TextEncoder
): Promise<void> {
  try {
    await sendScrapingUpdateEvent(controller, encoder);
    await sendDataQualityChangeEvent(controller, encoder);
  } catch (error) {
    console.error('Error monitoring data changes:', error);
  }
}

function formatSSEMessage(event: AdminEvent): string {
  return `id: ${event.id}\nevent: ${event.type}\ndata: ${JSON.stringify(event)}\n\n`;
}

function generateEventId(): string {
  return `${Date.now()}-${Math.random().toString(36).slice(2, 11)}`;
}

