async function getScrapingMetrics(): Promise<RealtimeMetrics> {
  // Fetch real scraping metrics from database
  const [allJobs, , recentTrucks] = await Promise.all([
    ScrapingJobService.getAllJobs(100, 0), // Get last 100 jobs for metrics
    ScrapingJobService.getJobsFromDate(new Date(Date.now() - 24 * 60 * 60 * 1000)), // Last 24 hours (unused but kept for potential future use)
    FoodTruckService.getAllTrucks(1000, 0), // Get trucks for processing count
  ]);

  const totalRuns = allJobs.length;
  const typedJobs = allJobs as Array<{ status?: string; started_at?: string; completed_at?: string }>;
  const successfulRuns = typedJobs.filter(job => job.status === 'completed').length;
  const failedRuns = typedJobs.filter(job => job.status === 'failed').length;

  // Calculate average run time from completed jobs
  const completedJobs = typedJobs.filter(job =>
    job.status === 'completed' && job.started_at !== undefined && job.completed_at !== undefined
  );

  let totalRunTime = 0;
  for (const job of completedJobs) {
    const start = new Date(job.started_at ?? '').getTime();
    const end = new Date(job.completed_at ?? '').getTime();
    totalRunTime += (end - start) / 1000; // Convert to seconds
  }

  const averageRunTime = completedJobs.length > 0
    ? Math.round(totalRunTime / completedJobs.length)
    : 0;

  // Count trucks processed today and new trucks
  const today = new Date();
  today.setHours(0, 0, 0, 0);

  const typedTrucks = recentTrucks.trucks as Array<{ created_at: string }>;
  const newTrucksToday = typedTrucks.filter(truck => {
    const createdAt = new Date(truck.created_at);
    return createdAt >= today;
  }).length;

  const metrics = {
    totalRuns,
    successfulRuns,
    failedRuns,
    averageRunTime,
    totalTrucksProcessed: recentTrucks.total,
    newTrucksToday,
  };

  return {
    scrapingJobs: {
      active: typedJobs.filter(job => job.status === 'running').length,
      completed: successfulRuns,
      failed: failedRuns,
      pending: typedJobs.filter(job => job.status === 'pending').length,
    },
    dataQuality: {
      averageScore: 0, // Placeholder, actual calculation might be complex
      totalTrucks: recentTrucks.total,
      recentChanges: 0, // Placeholder
    },
    systemHealth: {
      status: 'healthy',
      uptime: process.uptime(),
      lastUpdate: new Date().toISOString(),
    },
  };
}

import { NextRequest, NextResponse } from 'next/server';
import { supabaseAdmin } from '@/lib/supabase';
import { WebVitalMetric } from './types';

/**
 * Get Web Vitals Analytics Data
 */
export function getRequestParams(request: NextRequest) {
  const { searchParams } = new URL(request.url);
  const days = Number.parseInt(searchParams.get('days') ?? '7');
  const page = searchParams.get('page');
  return { days, page };
}

export async function fetchAndFilterMetrics(request: NextRequest) {
  const { days, page } = getRequestParams(request);

  if (!supabaseAdmin) {
    throw new Error('Database not available');
  }

  const startDate = new Date();
  startDate.setDate(startDate.getDate() - days);

  let query = supabaseAdmin
    .from('web_vitals_metrics')
    .select('*')
    .gte('recorded_at', startDate.toISOString())
    .order('recorded_at', { ascending: false });

  if (page != undefined && page !== '') {
    query = query.ilike('page_url', `%${page}%`);
  }

  const { data: metrics, error } = await query.limit(1000);

  if (error) {
    throw error;
  }

  return { metrics: metrics ?? [], days, startDate };
}

/**
 * Calculate summary statistics for metrics
 */
export function calculateMetricsSummary(metrics: { metric_name: string; metric_value: number; rating: string }[]) {
  const metricTypes: ('LCP' | 'FID' | 'CLS' | 'FCP' | 'TTFB')[] = ['LCP', 'FID', 'CLS', 'FCP', 'TTFB'];
  const summary: Record<string, {
    count: number;
    average: number | undefined;
    median: number | undefined;
    p75: number | undefined;
    p95: number | undefined;
    goodCount: number;
    needsImprovementCount: number;
    poorCount: number;
  }> = {};

  for (const metricName of metricTypes) {
    const metricData = metrics.filter(m => m.metric_name === metricName);
    
    if (metricData.length === 0) {
      summary[metricName] = {
        count: 0,
        average: undefined,
        median: undefined,
        p75: undefined,
        p95: undefined,
        goodCount: 0,
        needsImprovementCount: 0,
        poorCount: 0
      };
      continue;
    }

    const values = metricData.map(m => m.metric_value).sort((a, b) => a - b);
    const ratings = metricData.map(m => m.rating);

    summary[metricName] = {
      count: metricData.length,
      average: Math.round(values.reduce((sum: number, val: number) => sum + val, 0) / values.length),
      median: getPercentile(values, 50),
      p75: getPercentile(values, 75),
      p95: getPercentile(values, 95),
      goodCount: ratings.filter(r => r === 'good').length,
      needsImprovementCount: ratings.filter(r => r === 'needs-improvement').length,
      poorCount: ratings.filter(r => r === 'poor').length
    };
  }

  return summary;
}

/**
 * Calculate percentile value from sorted array
 */
export function getPercentile(sortedValues: number[], percentile: number): number {
  if (sortedValues.length === 0) return 0;
  
  const index = (percentile / 100) * (sortedValues.length - 1);
  const lower = Math.floor(index);
  const upper = Math.ceil(index);
  
  if (lower === upper) {
    return Math.round(sortedValues[lower]);
  }
  
  const weight = index - lower;
  return Math.round(sortedValues[lower] * (1 - weight) + sortedValues[upper] * weight);
}

export async function handlePostRequest(request: NextRequest) {
  try {
    const metric = await request.json() as WebVitalMetric;

    // Validate metric data
    if (!metric.name || typeof metric.value !== 'number' || !metric.url) {
      return NextResponse.json(
        { success: false, error: 'Invalid metric data' },
        { status: 400 }
      );
    }

    if (supabaseAdmin) {
      try {
        const { error } = await supabaseAdmin
          .from('web_vitals_metrics')
          .insert({
            metric_name: metric.name,
            metric_value: metric.value,
            rating: metric.rating,
            page_url: metric.url,
            user_agent: metric.userAgent,
            recorded_at: new Date(metric.timestamp).toISOString()
          });

        if (error) {
          console.warn('Failed to store web vital metric:', error);
          // Don't fail the request - metrics collection should be non-blocking
        }
      } catch (dbError) {
        console.warn('Database error storing web vital:', dbError);
      }
    }

    // Log performance issues for monitoring
    if (metric.rating === 'poor') {
      console.warn(`Poor ${metric.name} performance detected:`, {
        value: metric.value,
        url: metric.url,
        timestamp: new Date(metric.timestamp).toISOString()
      });
    }

    return NextResponse.json({ success: true });
  } catch (error) {
    console.error('Web vitals endpoint error:', error);
    return NextResponse.json(
      { success: false, error: 'Internal server error' },
      { status: 500 }
    );
  }
}

export async function handleGetRequest(request: NextRequest) {
  try {
    const { metrics, days, startDate } = await fetchAndFilterMetrics(request);

    const summary = calculateMetricsSummary(metrics);

    return NextResponse.json({
      success: true,
      data: {
        metrics,
        summary,
        period: {
          days,
          startDate: startDate.toISOString(),
          endDate: new Date().toISOString()
        }
      }
    });
  } catch (error) {
    console.error('Failed to fetch web vitals analytics:', error);
    return NextResponse.json(
      { success: false, error: 'Failed to fetch analytics data' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { autoScraper } from '@/lib/autoScraper';
import { scheduler } from '@/lib/scheduler';
import { logActivity } from '@/lib/activityLogger';
import { AutoScrapeResult } from './types';

function verifyCronSecret(request: NextRequest): NextResponse | null {
  const authHeader = request.headers.get('authorization');
  const cronSecret = process.env.CRON_SECRET;

  if (cronSecret === undefined || cronSecret === '') {
    console.error('CRON_SECRET not configured');
    return NextResponse.json({ error: 'Server configuration error' }, { status: 500 });
  }

  if (authHeader !== `Bearer ${cronSecret}`) {
    console.error('Unauthorized cron attempt:', authHeader);
    return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
  }
  return null;
}

function logAutoScrapeStart() {
  console.info('Starting automated scraping job...');
  logActivity({
    type: 'cron_job',
    action: 'auto_scrape_started',
    details: { timestamp: new Date().toISOString() },
  });
}

function logAutoScrapeCompletion(result: AutoScrapeResult) {
  logActivity({
    type: 'cron_job',
    action: 'auto_scrape_completed',
    details: {
      timestamp: new Date().toISOString(),
      trucksProcessed: result.trucksProcessed,
      newTrucksFound: result.newTrucksFound,
      errorsCount: result.errors?.length ?? 0,
    },
  });
  console.info('Automated scraping job completed successfully');
}

function logAutoScrapeFailure(error: unknown) {
  console.error('Auto-scraping cron job failed:', error);
  logActivity({
    type: 'cron_job',
    action: 'auto_scrape_failed',
    details: {
      timestamp: new Date().toISOString(),
      error: error instanceof Error ? error.message : 'Unknown error',
    },
  });
}

export async function handlePostRequest(request: NextRequest) {
  try {
    const authResponse = verifyCronSecret(request);
    if (authResponse) {
      return authResponse;
    }

    logAutoScrapeStart();

    const rawResult = await autoScraper.runAutoScraping();
    // Map errors to string[] for compatibility
    const result: AutoScrapeResult = {
      trucksProcessed: rawResult.trucksProcessed,
      newTrucksFound: rawResult.newTrucksFound,
      errors: rawResult.errors?.map(e => e.url + (e.details ? `: ${e.details}` : '')),
    };
    scheduler.scheduleFollowUpTasks(result);
    logAutoScrapeCompletion(result);

    return NextResponse.json({
      success: true,
      message: 'Auto-scraping completed successfully',
      data: {
        trucksProcessed: result.trucksProcessed,
        newTrucksFound: result.newTrucksFound,
        timestamp: new Date().toISOString(),
      },
    });
  } catch (error) {
    logAutoScrapeFailure(error);
    return NextResponse.json(
      {
        success: false,
        error: 'Auto-scraping failed',
        message: error instanceof Error ? error.message : 'Unknown error',
      },
      { status: 500 },
    );
  }
}

export function handleGetRequest() {
  return NextResponse.json(
    { error: 'Method not allowed. Use POST for cron jobs.' },
    { status: 405 },
  );
}

