  return NextResponse.json({
    trucks,
    total,
    limit,
    offset,
    hasMore: offset + limit < (total ?? 0),
    summary: {
      totalTrucks: total,
      averageQuality:
        trucks && trucks.length > 0
          ? trucks.reduce((acc, t) => acc + (t.data_quality_score ?? 0), 0) / trucks.length
