import { NextRequest, NextResponse } from 'next/server';
import { User } from '@supabase/supabase-js';
import { supabase } from '@/lib/supabase';
import { RateLimiter } from '@/lib/security/rateLimiter';
import { AuditLogger } from '@/lib/security/auditLogger';

interface RequestMetadata {
  ip: string;
  userAgent: string;
}

export async function handleSuccessfulAuth(
  user: User,
  redirectTo: string,
  origin: string,
  identifier: string,
  requestMetadata: RequestMetadata,
) {
  const { data: profile } = await supabase
    .from('profiles')
    .select('role')
    .eq('id', user.id)
    .single();

  await AuditLogger.logAuthEvent(
    'login_success',
    user.email,
    user.id,
    requestMetadata,
    { provider: 'google', role: profile?.role },
  );

  RateLimiter.recordSuccess(identifier, 'auth');

  if (profile?.role === 'admin') {
    return NextResponse.redirect(`${origin}${redirectTo}`);
  } else {
    await AuditLogger.logSecurityEvent({
      event_type: 'permission_denied',
      user_id: user.id,
      user_email: user.email,
      ip_address: requestMetadata.ip,
      user_agent: requestMetadata.userAgent,
      details: {
        reason: 'insufficient_role',
        user_role: profile?.role ?? 'none',
      },
      severity: 'warning',
    });
    return NextResponse.redirect(`${origin}/access-denied`);
  }
}

export async function handleAuthFailure(
  error: Error,
  identifier: string,
  requestMetadata: RequestMetadata,
) {
  await AuditLogger.logAuthEvent(
    'login_failure',
    undefined,
    undefined,
    requestMetadata,
    { provider: 'google', error: error.message },
  );
}

/**
 * SOTA Batch Data Cleanup System
 * Implements automated data quality improvements and cleanup operations
 */

import { FoodTruckService, type FoodTruck, DataQualityService } from '@/lib/supabase';
import { DuplicatePreventionService } from './duplicatePrevention';

export interface CleanupOperation {
  type: 'normalize_phone' | 'fix_coordinates' | 'remove_placeholders' | 'update_quality_scores' | 'merge_duplicates';
  description: string;
  affectedCount: number;
  successCount: number;
  errorCount: number;
  errors: string[];
}

export interface BatchCleanupResult {
  totalProcessed: number;
  operations: CleanupOperation[];
  summary: {
    trucksImproved: number;
    duplicatesRemoved: number;
    qualityScoreImprovement: number;
    placeholdersRemoved: number;
  };
  duration: number;
}

/**
 * Automated Data Quality Cleanup Service
 */
export class BatchCleanupService {
  
  /**
   * Run comprehensive data cleanup operations
   */
  static async runFullCleanup(options: {
    batchSize?: number;
    dryRun?: boolean;
    operations?: CleanupOperation['type'][];
  } = {}): Promise<BatchCleanupResult> {
    const startTime = Date.now();
    const { batchSize = 50, dryRun = false, operations = [
      'remove_placeholders',
      'normalize_phone',
      'fix_coordinates',
      'update_quality_scores',
      'merge_duplicates'
    ] } = options;
    
    console.info(`Starting batch cleanup (${dryRun ? 'DRY RUN' : 'LIVE'})...`);
    
    const result: BatchCleanupResult = {
      totalProcessed: 0,
      operations: [],
      summary: {
        trucksImproved: 0,
        duplicatesRemoved: 0,
        qualityScoreImprovement: 0,
        placeholdersRemoved: 0
      },
      duration: 0
    };
    
    try {
      // Get all trucks for processing
      const allTrucks = await FoodTruckService.getAllTrucks();
      result.totalProcessed = allTrucks.total;
      
      // Process trucks in batches
      for (let i = 0; i < allTrucks.trucks.length; i += batchSize) {
        const batch = allTrucks.trucks.slice(i, i + batchSize);
        
        for (const operation of operations) {
          const opResult = await this.runOperation(operation, batch, dryRun);
          result.operations.push(opResult);
        }
      }
      
      // Calculate summary
      result.summary = this.calculateSummary(result.operations);
      result.duration = Date.now() - startTime;
      
      console.info(`Batch cleanup completed in ${result.duration}ms`);
      return result;
      
    } catch (error) {
      console.error('Batch cleanup failed:', error);
      throw error;
    }
  }
  
  /**
   * Run a specific cleanup operation
   */
  private static async runOperation(
    type: CleanupOperation['type'],
    trucks: FoodTruck[],
    dryRun: boolean
  ): Promise<CleanupOperation> {
    const operation: CleanupOperation = {
      type,
      description: this.getOperationDescription(type),
      affectedCount: 0,
      successCount: 0,
      errorCount: 0,
      errors: []
    };
    
    try {
      switch (type) {
        case 'remove_placeholders': {
          return await this.removePlaceholders(trucks, dryRun, operation);
        }
        case 'normalize_phone': {
          return await this.normalizePhoneNumbers(trucks, dryRun, operation);
        }
        case 'fix_coordinates': {
          return await this.fixCoordinates(trucks, dryRun, operation);
        }
        case 'update_quality_scores': {
          return await this.updateQualityScores(trucks, dryRun, operation);
        }
        case 'merge_duplicates': {
          return await this.mergeDuplicates(trucks, dryRun, operation);
        }
        default: {
          operation.errors.push(`Unknown operation type: ${String(type)}`);
          return operation;
        }
      }
    } catch (error) {
      operation.errors.push(`Operation failed: ${error instanceof Error ? error.message : String(error)}`);
      return operation;
    }
  }
  
  /**
   * Get placeholder detection patterns
   */
  private static getPlaceholderPatterns(): RegExp[] {
    return [
      /undefined/i,
      /placeholder/i,
      /example\.com/i,
      /test\s*truck/i,
      /lorem\s*ipsum/i,
      /\bna\b/i,
      /\bn\/a\b/i,
      /^0+$/,
      /^null$/i
    ];
  }

  /**
   * Check if truck data needs placeholder cleanup
   */
  private static checkForPlaceholders(truck: FoodTruck, patterns: RegExp[]): { updates: Partial<FoodTruck>; needsUpdate: boolean } {
    const updates: Partial<FoodTruck> = {};
    let needsUpdate = false;

    // Check name
    if (truck.name && patterns.some(pattern => pattern.test(truck.name ?? ''))) {
      updates.name = undefined;
      needsUpdate = true;
    }

    // Check description
    if (truck.description !== undefined && typeof truck.description === 'string' && patterns.some(pattern => pattern.test(truck.description ?? ''))) {
      updates.description = undefined;
      needsUpdate = true;
    }

    // Check price range
    if (truck.price_range !== undefined && typeof truck.price_range === 'string' && patterns.some(pattern => pattern.test(truck.price_range ?? ''))) {
      updates.price_range = undefined;
      needsUpdate = true;
    }

    return { updates, needsUpdate };
  }

  /**
   * Remove placeholder and mock data
   */
  private static async removePlaceholders(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<CleanupOperation> {
    const placeholderPatterns = this.getPlaceholderPatterns();

    for (const truck of trucks) {
      const { updates, needsUpdate: initialNeedsUpdate } = this.checkForPlaceholders(truck, placeholderPatterns);
      let needsUpdate = initialNeedsUpdate;

      // Check contact info
      if (truck.contact_info) {
        const cleanContact = { ...truck.contact_info };
        let contactUpdated = false;

        if (cleanContact.phone !== undefined && typeof cleanContact.phone === 'string' && placeholderPatterns.some(pattern => pattern.test(cleanContact.phone ?? ''))) {
          cleanContact.phone = undefined;
          contactUpdated = true;
        }

        if (cleanContact.website !== undefined && typeof cleanContact.website === 'string' && placeholderPatterns.some(pattern => pattern.test(cleanContact.website ?? ''))) {
          cleanContact.website = undefined;
          contactUpdated = true;
        }

        if (cleanContact.email !== undefined && typeof cleanContact.email === 'string' && placeholderPatterns.some(pattern => pattern.test(cleanContact.email ?? ''))) {
          cleanContact.email = undefined;
          contactUpdated = true;
        }

        if (contactUpdated) {
          updates.contact_info = cleanContact;
          needsUpdate = true;
        }
      }

      // Check address
      if (truck.current_location?.address !== undefined && typeof truck.current_location.address === 'string' && placeholderPatterns.some(pattern => pattern.test(truck.current_location.address ?? ''))) {
        updates.current_location = {
          ...truck.current_location,
          address: undefined
        };
        needsUpdate = true;
      }
      
      if (needsUpdate) {
        operation.affectedCount++;
        
        if (dryRun) {
          operation.successCount++;
        } else {
          try {
            await FoodTruckService.updateTruck(truck.id, updates);
            operation.successCount++;
          } catch (error) {
            operation.errorCount++;
            operation.errors.push(`Failed to update truck ${truck.id}: ${error instanceof Error ? error.message : String(error)}`);
          }
        }
      }
    }
    
    return operation;
  }
  
  /**
   * Normalize phone numbers to consistent format
   */
  private static async normalizePhoneNumbers(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<CleanupOperation> {
    for (const truck of trucks) {
      if (truck.contact_info?.phone !== undefined) {
        const originalPhone = truck.contact_info.phone;
        const normalizedPhone = this.normalizePhone(originalPhone);

        if (normalizedPhone !== undefined && normalizedPhone !== originalPhone) {
          operation.affectedCount++;
          
          if (dryRun) {
            operation.successCount++;
          } else {
            try {
              await FoodTruckService.updateTruck(truck.id, {
                contact_info: {
                  ...truck.contact_info,
                  phone: normalizedPhone
                }
              });
              operation.successCount++;
            } catch (error) {
              operation.errorCount++;
              operation.errors.push(`Failed to normalize phone for truck ${truck.id}: ${error instanceof Error ? error.message : String(error)}`);
            }
          }
        }
      }
    }
    
    return operation;
  }
  
  /**
   * Fix invalid GPS coordinates
   */
  private static async fixCoordinates(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<CleanupOperation> {
    // Charleston, SC default coordinates
    const defaultLat = 32.7767;
    const defaultLng = -79.9311;
    
    for (const truck of trucks) {
      if (truck.current_location != undefined) {
        const { lat, lng } = truck.current_location;
        let needsUpdate = false;
        const updates: Partial<FoodTruck['current_location']> = {};

        // Fix invalid coordinates (0,0 or null)
        if (lat == undefined || lat === 0 || lng == undefined || lng === 0) {
          updates.lat = defaultLat;
          updates.lng = defaultLng;
          needsUpdate = true;
        }

        // Fix coordinates outside reasonable bounds for Charleston area
        if (lat != undefined && lng != undefined && (lat < 32 || lat > 34 || lng > -79 || lng < -81)) {
          updates.lat = defaultLat;
          updates.lng = defaultLng;
          needsUpdate = true;
        }
        
        if (needsUpdate) {
          operation.affectedCount++;
          
          if (dryRun) {
            operation.successCount++;
          } else {
            try {
              await FoodTruckService.updateTruck(truck.id, {
                current_location: {
                  ...truck.current_location,
                  ...updates
                }
              });
              operation.successCount++;
            } catch (error) {
              operation.errorCount++;
              operation.errors.push(`Failed to fix coordinates for truck ${truck.id}: ${error instanceof Error ? error.message : String(error)}`);
            }
          }
        }
      }
    }
    
    return operation;
  }
  
  /**
   * Update quality scores for all trucks
   */
  private static async updateQualityScores(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<CleanupOperation> {
    for (const truck of trucks) {
      const qualityAssessment: { score: number } = DataQualityService.calculateQualityScore(truck);
      const newScore: number = qualityAssessment.score;
      const currentScore = truck.data_quality_score ?? 0;
      
      // Only update if score changed significantly (>5% difference)
      if (Math.abs(newScore - currentScore) > 0.05) {
        operation.affectedCount++;
        
        if (dryRun) {
          operation.successCount++;
        } else {
          try {
             
            await DataQualityService.updateTruckQualityScore(truck.id);
            operation.successCount++;
          } catch (error) {
            operation.errorCount++;
            operation.errors.push(`Failed to update quality score for truck ${truck.id}: ${error instanceof Error ? error.message : String(error)}`);
          }
        }
      }
    }
    
    return operation;
  }
  
  /**
   * Identify and merge duplicate trucks
   */
  private static async mergeDuplicates(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<CleanupOperation> {
    const processedIds = new Set<string>();
    
    for (const truck of trucks) {
      if (processedIds.has(truck.id)) continue;
      
      const duplicateCheck = await DuplicatePreventionService.checkForDuplicates(truck);
      
      if (duplicateCheck.isDuplicate && duplicateCheck.bestMatch) {
        const { bestMatch } = duplicateCheck;
        
        if (bestMatch.confidence === 'high' && bestMatch.recommendation === 'merge') {
          operation.affectedCount++;
          
          if (dryRun) {
            operation.successCount++;
          } else {
            try {
              await DuplicatePreventionService.mergeDuplicates(truck.id, bestMatch.existingTruck.id);
              processedIds.add(bestMatch.existingTruck.id);
              operation.successCount++;
            } catch (error) {
              operation.errorCount++;
              operation.errors.push(`Failed to merge duplicates ${truck.id} and ${bestMatch.existingTruck.id}: ${error instanceof Error ? error.message : String(error)}`);
            }
          }
        }
      }
      
      processedIds.add(truck.id);
    }
    
    return operation;
  }
  
  /**
   * Normalize phone number format
   */
  private static normalizePhone(phone: string): string | undefined {
    if (!phone) return undefined;
    
    // Remove all non-digit characters
    const digits = phone.replaceAll(/\D/g, '');
    
    // Handle US phone numbers
    if (digits.length === 10) {
      return `(${digits.slice(0, 3)}) ${digits.slice(3, 6)}-${digits.slice(6)}`;
    } else if (digits.length === 11 && digits[0] === '1') {
      return `+1 (${digits.slice(1, 4)}) ${digits.slice(4, 7)}-${digits.slice(7)}`;
    }
    
    // Return original if can't normalize
    return phone;
  }
  
  /**
   * Get operation description
   */
  private static getOperationDescription(type: CleanupOperation['type']): string {
    const descriptions = {
      remove_placeholders: 'Remove placeholder and mock data values',
      normalize_phone: 'Normalize phone numbers to consistent format',
      fix_coordinates: 'Fix invalid GPS coordinates',
      update_quality_scores: 'Recalculate data quality scores',
      merge_duplicates: 'Identify and merge duplicate truck entries'
    };
    
    return descriptions[type] ?? 'Unknown operation';
  }
  
  /**
   * Calculate cleanup summary
   */
  private static calculateSummary(operations: CleanupOperation[]): BatchCleanupResult['summary'] {
    return {
      trucksImproved: operations.reduce((sum, op) => sum + op.successCount, 0),
      duplicatesRemoved: operations.find(op => op.type === 'merge_duplicates')?.successCount ?? 0,
      qualityScoreImprovement: operations.find(op => op.type === 'update_quality_scores')?.successCount ?? 0,
      placeholdersRemoved: operations.find(op => op.type === 'remove_placeholders')?.successCount ?? 0
    };
  }
}

/**
 * SOTA Duplicate Prevention System
 * Implements intelligent duplicate detection and prevention for food truck data
 */

import { FoodTruckService, type FoodTruck } from '@/lib/supabase';

// Duplicate detection configuration
export const DUPLICATE_DETECTION_CONFIG = {
  // Similarity thresholds (0.0 = no match, 1.0 = exact match)
  thresholds: {
    name: 0.85,        // High threshold for name matching
    location: 0.9,     // Very high threshold for location matching
    phone: 1,        // Exact match for phone numbers
    website: 1,      // Exact match for websites
    overall: 0.8       // Overall similarity threshold
  },
  
  // Weight factors for different fields
  weights: {
    name: 0.4,         // 40% weight for name similarity
    location: 0.3,     // 30% weight for location similarity
    contact: 0.2,      // 20% weight for contact info similarity
    menu: 0.1          // 10% weight for menu similarity
  }
} as const;

export interface DuplicateMatch {
  existingTruck: FoodTruck;
  similarity: number;
  matchedFields: string[];
  confidence: 'high' | 'medium' | 'low';
  recommendation: 'merge' | 'update' | 'skip' | 'manual_review';
}

export interface DuplicateDetectionResult {
  isDuplicate: boolean;
  matches: DuplicateMatch[];
  bestMatch?: DuplicateMatch;
  action: 'create' | 'update' | 'merge' | 'manual_review';
  reason: string;
}

/**
 * Advanced Duplicate Prevention Service
 */
export class DuplicatePreventionService {
  
  /**
   * Check if a food truck is a duplicate of existing trucks
   */
  static async checkForDuplicates(candidateTruck: Partial<FoodTruck>): Promise<DuplicateDetectionResult> {
    try {
      // Get all existing trucks for comparison
      const existingTrucks = await FoodTruckService.getAllTrucks();
      
      const matches: DuplicateMatch[] = [];
      
      for (const existingTruck of existingTrucks.trucks) {
        const similarity = this.calculateSimilarity(candidateTruck, existingTruck);
        
        if (similarity.overall >= DUPLICATE_DETECTION_CONFIG.thresholds.overall) {
          matches.push({
            existingTruck,
            similarity: similarity.overall,
            matchedFields: similarity.matchedFields,
            confidence: this.getConfidenceLevel(similarity.overall),
            recommendation: this.getRecommendation(similarity)
          });
        }
      }
      
      // Sort matches by similarity (highest first)
      matches.sort((a, b) => b.similarity - a.similarity);
      
      const bestMatch = matches[0];
      const isDuplicate = matches.length > 0;
      
      return {
        isDuplicate,
        matches,
        bestMatch,
        action: this.determineAction(matches, candidateTruck),
        reason: this.generateReason(matches, candidateTruck)
      };
      
    } catch (error) {
      console.error('Error checking for duplicates:', error);
      return {
        isDuplicate: false,
        matches: [],
        action: 'create',
        reason: 'Error during duplicate detection - proceeding with creation'
      };
    }
  }
  
  /**
   * Calculate similarity between two food trucks
   */
  private static calculateSimilarity(
    candidate: Partial<FoodTruck>, 
    existing: FoodTruck
  ): { overall: number; matchedFields: string[]; breakdown: Record<string, number> } {
    const breakdown: Record<string, number> = {};
    const matchedFields: string[] = [];
    
    // Name similarity
    const nameSimilarity = this.calculateStringSimilarity(
      candidate.name ?? '', 
      existing.name ?? ''
    );
    breakdown.name = nameSimilarity;
    if (nameSimilarity >= DUPLICATE_DETECTION_CONFIG.thresholds.name) {
      matchedFields.push('name');
    }
    
    // Location similarity
    const locationSimilarity = this.calculateLocationSimilarity(
      candidate.current_location,
      existing.current_location
    );
    breakdown.location = locationSimilarity;
    if (locationSimilarity >= DUPLICATE_DETECTION_CONFIG.thresholds.location) {
      matchedFields.push('location');
    }
    
    // Contact similarity
    const contactSimilarity = this.calculateContactSimilarity(
      candidate.contact_info,
      existing.contact_info
    );
    breakdown.contact = contactSimilarity;
    if (contactSimilarity >= DUPLICATE_DETECTION_CONFIG.thresholds.phone) {
      matchedFields.push('contact');
    }
    
    // Menu similarity (basic)
    const menuSimilarity = this.calculateMenuSimilarity(
      candidate.menu,
      existing.menu
    );
    breakdown.menu = menuSimilarity;
    if (menuSimilarity > 0.7) {
      matchedFields.push('menu');
    }
    
    // Calculate weighted overall similarity
    const overall = (
      nameSimilarity * DUPLICATE_DETECTION_CONFIG.weights.name +
      locationSimilarity * DUPLICATE_DETECTION_CONFIG.weights.location +
      contactSimilarity * DUPLICATE_DETECTION_CONFIG.weights.contact +
      menuSimilarity * DUPLICATE_DETECTION_CONFIG.weights.menu
    );
    
    return { overall, matchedFields, breakdown };
  }
  
  /**
   * Calculate string similarity using Levenshtein distance
   */
  private static calculateStringSimilarity(str1: string, str2: string): number {
    if (!str1 || !str2) return 0;
    
    // Normalize strings
    const s1 = str1.toLowerCase().trim();
    const s2 = str2.toLowerCase().trim();
    
    if (s1 === s2) return 1;
    
    // Calculate Levenshtein distance
    const matrix: number[][] = [];
    const len1 = s1.length;
    const len2 = s2.length;
    
    for (let i = 0; i <= len1; i++) {
      matrix[i] = [i];
    }
    
    for (let j = 0; j <= len2; j++) {
      matrix[0][j] = j;
    }
    
    for (let i = 1; i <= len1; i++) {
      for (let j = 1; j <= len2; j++) {
        const cost = s1[i - 1] === s2[j - 1] ? 0 : 1;
        matrix[i][j] = Math.min(
          matrix[i - 1][j] + 1,      // deletion
          matrix[i][j - 1] + 1,      // insertion
          matrix[i - 1][j - 1] + cost // substitution
        );
      }
    }
    
    const distance = matrix[len1][len2];
    const maxLength = Math.max(len1, len2);
    
    return maxLength === 0 ? 1 : 1 - (distance / maxLength);
  }
  
  /**
   * Calculate location similarity
   */
  private static calculateLocationSimilarity(
    loc1: FoodTruck['current_location'],
    loc2: FoodTruck['current_location']
  ): number {
    if (loc1 == undefined || loc2 == undefined) return 0;
    
    let similarity = 0;
    let factors = 0;
    
    // Address similarity
    if (loc1.address != undefined && loc2.address != undefined) {
      similarity += this.calculateStringSimilarity(loc1.address, loc2.address);
      factors++;
    }
    
    // GPS coordinate similarity (within 100 meters = high similarity)
    if (loc1.lat != undefined && loc1.lng != undefined && loc2.lat != undefined && loc2.lng != undefined) {
      const distance = this.calculateGPSDistance(
        loc1.lat, loc1.lng,
        loc2.lat, loc2.lng
      );
      
      // Distance similarity (closer = higher similarity)
      const distanceSimilarity = distance <= 0.1 ? 1 : Math.max(0, 1 - (distance / 1)); // 1km max
      similarity += distanceSimilarity;
      factors++;
    }
    
    return factors > 0 ? similarity / factors : 0;
  }
  
  /**
   * Calculate GPS distance in kilometers
   */
  private static calculateGPSDistance(lat1: number, lng1: number, lat2: number, lng2: number): number {
    const R = 6371; // Earth's radius in kilometers
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLng = (lng2 - lng1) * Math.PI / 180;
    const a = 
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLng / 2) * Math.sin(dLng / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  }
  
  /**
   * Calculate contact similarity
   */
  private static calculateContactSimilarity(
    contact1: FoodTruck['contact_info'],
    contact2: FoodTruck['contact_info']
  ): number {
    if (!contact1 || !contact2) return 0;
    
    let matches = 0;
    let total = 0;
    
    // Phone number exact match
    if (contact1.phone != undefined && contact2.phone != undefined) {
      const phone1 = contact1.phone.replaceAll(/\D/g, ''); // Remove non-digits
      const phone2 = contact2.phone.replaceAll(/\D/g, '');
      if (phone1 === phone2) matches++;
      total++;
    }
    
    // Website exact match
    if (contact1.website != undefined && contact2.website != undefined) {
      const url1 = contact1.website.toLowerCase().replace(/^https?:\/\//, '').replace(/\/$/, '');
      const url2 = contact2.website.toLowerCase().replace(/^https?:\/\//, '').replace(/\/$/, '');
      if (url1 === url2) matches++;
      total++;
    }
    
    // Email similarity
    if (contact1.email != undefined && contact2.email != undefined) {
      if (contact1.email.toLowerCase() === contact2.email.toLowerCase()) matches++;
      total++;
    }
    
    return total > 0 ? matches / total : 0;
  }
  
  /**
   * Calculate menu similarity (basic implementation)
   */
  private static calculateMenuSimilarity(
    menu1: FoodTruck['menu'],
    menu2: FoodTruck['menu']
  ): number {
    if (!menu1 || !menu2 || menu1.length === 0 || menu2.length === 0) return 0;
    
    // Simple category name matching
    const categories1 = menu1.map(cat => (cat as { category?: string }).category?.toLowerCase() ?? '').filter(Boolean);
    const categories2 = menu2.map(cat => (cat as { category?: string }).category?.toLowerCase() ?? '').filter(Boolean);
    
    const commonCategories = categories1.filter(cat => categories2.includes(cat));
    const totalCategories = new Set([...categories1, ...categories2]).size;
    
    return totalCategories > 0 ? commonCategories.length / totalCategories : 0;
  }
  
  /**
   * Get confidence level based on similarity score
   */
  private static getConfidenceLevel(similarity: number): 'high' | 'medium' | 'low' {
    if (similarity >= 0.95) return 'high';
    if (similarity >= 0.85) return 'medium';
    return 'low';
  }
  
  /**
   * Get recommendation based on similarity analysis
   */
  private static getRecommendation(similarity: { overall: number; matchedFields: string[] }): 'merge' | 'update' | 'skip' | 'manual_review' {
    if (similarity.overall >= 0.95) return 'merge';
    if (similarity.overall >= 0.9) return 'update';
    if (similarity.overall >= 0.8) return 'manual_review';
    return 'skip';
  }
  
  /**
   * Determine action based on matches
   */
  private static determineAction(matches: DuplicateMatch[], _candidate: Partial<FoodTruck>): 'create' | 'update' | 'merge' | 'manual_review' {
    if (matches.length === 0) return 'create';
    
    const bestMatch = matches[0];
    
    if (bestMatch.confidence === 'high') {
      const recommendation = bestMatch.recommendation;
      if (recommendation === 'merge' || recommendation === 'update') {
        return recommendation;
      }
      return 'manual_review';
    }
    
    return 'manual_review';
  }
  
  /**
   * Generate human-readable reason
   */
  private static generateReason(matches: DuplicateMatch[], _candidate: Partial<FoodTruck>): string {
    if (matches.length === 0) {
      return 'No duplicates found - safe to create new truck entry';
    }
    
    const bestMatch = matches[0];
    const similarity = Math.round(bestMatch.similarity * 100);
    
    return `Found ${matches.length} potential duplicate(s). Best match: ${similarity}% similarity with "${bestMatch.existingTruck.name}" (matched: ${bestMatch.matchedFields.join(', ')})`;
  }
  
  /**
   * Merge duplicate truck data intelligently
   */
  static async mergeDuplicates(targetId: string, sourceId: string): Promise<FoodTruck> {
    const target = await FoodTruckService.getTruckById(targetId);
    const source = await FoodTruckService.getTruckById(sourceId);
    
    // Merge logic: prefer non-null, more complete data
    const mergedData: Partial<FoodTruck> = {
      name: target.name ?? source.name,
      description: target.description ?? source.description,
      cuisine_type: (target.cuisine_type?.length ?? 0) > 0 ? target.cuisine_type : source.cuisine_type,
      price_range: target.price_range ?? source.price_range,
      current_location: target.current_location ?? source.current_location,
      contact_info: {
        ...source.contact_info,
        ...target.contact_info // Target takes precedence
      },
      operating_hours: target.operating_hours ?? source.operating_hours,
      menu: (target.menu?.length ?? 0) > 0 ? target.menu : source.menu,
      social_media: {
        ...source.social_media,
        ...target.social_media
      },
      source_urls: [...new Set([...(target.source_urls ?? []), ...(source.source_urls ?? [])])],
      last_scraped_at: new Date().toISOString()
    };

    // Update target with merged data
    const updatedTruck = await FoodTruckService.updateTruck(targetId, mergedData);

    // Note: Delete functionality would need to be implemented in FoodTruckService
    console.info(`Merged truck ${sourceId} into ${targetId}`);
    
    return updatedTruck;
  }
}

// lib/discoveryEngine.ts
import { supabaseAdmin } from './supabase';
import { SC_TARGET_CITIES, DISCOVERY_CONFIG } from './config';

export interface DiscoveredUrl {
  url: string;
  source_directory_url?: string;
  region?: string;
  status: 'new' | 'processing' | 'processed' | 'irrelevant';
  notes?: string;
}

export interface DiscoveryResult {
  urls_discovered: number;
  urls_stored: number;
  urls_duplicates: number;
  errors: string[];
}

/**
 * Autonomous Discovery Engine
 *
 * This engine discovers new food truck websites across South Carolina using Tavily for search
 * and Firecrawl for crawling. It operates autonomously without human intervention, finding and
 * validating new URLs to add to the discovered_urls table for subsequent scraping.
 *
 * Strategy:
 * 1. Search for food trucks in each SC city using Tavily search
 * 2. Find food truck directories and event listings using Firecrawl crawling
 * 3. Extract individual food truck website URLs from discovered content
 * 4. Validate and filter URLs to avoid false positives
 * 5. Store new URLs in discovered_urls table for processing
 *
 * NOTE: Uses Tavily for search operations and Firecrawl for crawling/scraping
 */

// Helper functions to call APIs
// Use Tavily for search, Firecrawl for crawling/scraping
interface TavilySearchResult {
  url: string;
  content?: string;
  raw_content?: string;
}

async function tavilySearch(
  query: string,
  options: Record<string, unknown> = {},
): Promise<TavilySearchResult[]> {
  const baseUrl = process.env.NEXT_PUBLIC_APP_URL ?? 'http://localhost:3003';
  const response = await fetch(`${baseUrl}/api/tavily`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
      operation: 'search',
      params: {
        query,
        limit: options.limit ?? 10,
        ...options,
      },
    }),
  });

  if (!response.ok) {
    throw new Error(`Tavily search failed: ${response.statusText}`);
  }

  const result = (await response.json()) as {
    data?: { results?: TavilySearchResult[] };
    results?: TavilySearchResult[];
  };
  return result.data?.results ?? result.results ?? [];
}

interface CrawlResult {
  url: string;
}

async function firecrawlCrawl(
  url: string,
  options: Record<string, unknown> = {},
): Promise<CrawlResult[]> {
  const baseUrl = process.env.NEXT_PUBLIC_APP_URL ?? 'http://localhost:3003';
  const response = await fetch(`${baseUrl}/api/firecrawl`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
      operation: 'crawl',
      url,
      options: {
        maxDepth: options.maxDepth ?? 2,
        limit: options.limit ?? 20,
        ...options,
      },
    }),
  });

  if (!response.ok) {
    throw new Error(`Firecrawl crawl failed: ${response.statusText}`);
  }

  const result = (await response.json()) as { data: CrawlResult[] };
  return result.data ?? [];
}

export class FoodTruckDiscoveryEngine {
  private readonly searchTerms = [
    'food truck South Carolina',
    'mobile food vendor SC',
    'food truck Charleston',
    'food truck Columbia SC',
    'food truck Greenville SC',
    'street food South Carolina',
    'food truck directory SC',
  ];

  private readonly directoryUrls = [
    'https://www.foodtrucksin.com/south-carolina',
    'https://www.roaminghunger.com/sc/',
    'https://southcarolinafoodtrucks.com',
    'https://www.yelp.com/sc/food-trucks',
  ];

  // Helper method to process search results
  private async processSearchResults(
    searchResults: TavilySearchResult[],
    discoveredUrls: Set<string>,
  ): Promise<void> {
    for (const result of searchResults) {
      if (result.url && (await this.isFoodTruckUrl(result.url))) {
        discoveredUrls.add(result.url);
      }

      // Extract URLs from content
      if (result.content != undefined || result.raw_content != undefined) {
        const content = result.content ?? result.raw_content ?? '';
        const extractedUrls = this.extractFoodTruckUrls(content);
        for (const url of extractedUrls) {
          if (await this.isFoodTruckUrl(url)) {
            discoveredUrls.add(url);
          }
        }
      }
    }
  }

  // Helper method to perform search term discovery
  private async performSearchTermDiscovery(
    discoveredUrls: Set<string>,
    results: DiscoveryResult,
  ): Promise<void> {
    for (const searchTerm of this.searchTerms) {
      try {
        console.info(` Searching for: ${searchTerm}`);

        const searchResults = await tavilySearch(searchTerm, {
          limit: DISCOVERY_CONFIG.searchResultsLimit,
        });

        if (searchResults != undefined && searchResults.length > 0) {
          await this.processSearchResults(searchResults, discoveredUrls);
        }
      } catch (error) {
        console.error(` Search failed for "${searchTerm}":`, error);
        results.errors.push(
          `Search failed for "${searchTerm}": ${error instanceof Error ? error.message : 'Unknown error'}`,
        );
      }

      await this.delay(DISCOVERY_CONFIG.rateLimitDelayMs);
    }
  }

  // Helper method to perform directory crawling
  private async performDirectoryCrawling(
    discoveredUrls: Set<string>,
    results: DiscoveryResult,
  ): Promise<void> {
    for (const directoryUrl of this.directoryUrls) {
      await this.crawlSingleDirectory(directoryUrl, discoveredUrls, results);
      await this.delay(DISCOVERY_CONFIG.rateLimitDelayMs);
    }
  }

  // Helper method to crawl a single directory
  private async crawlSingleDirectory(
    directoryUrl: string,
    discoveredUrls: Set<string>,
    results: DiscoveryResult,
  ): Promise<void> {
    try {
      console.info(` Crawling directory: ${directoryUrl}`);

      const crawlResults = await firecrawlCrawl(directoryUrl, {
        maxDepth: DISCOVERY_CONFIG.maxDepthCrawl,
        limit: DISCOVERY_CONFIG.maxUrlsPerRun,
      });

      await this.processCrawlResults(crawlResults, discoveredUrls);
    } catch (error) {
      console.error(` Crawl failed for ${directoryUrl}:`, error);
      results.errors.push(
        `Crawl failed for ${directoryUrl}: ${error instanceof Error ? error.message : 'Unknown error'}`,
      );
    }
  }

  // Helper method to process crawl results
  private async processCrawlResults(
    crawlResults: unknown,
    discoveredUrls: Set<string>,
  ): Promise<void> {
    if (crawlResults != undefined && Array.isArray(crawlResults) && crawlResults.length > 0) {
      for (const result of crawlResults) {
        if (typeof result === 'object' && result !== null && 'url' in result) {
          const resultUrl = (result as { url?: string }).url;
          if (
            resultUrl != undefined &&
            typeof resultUrl === 'string' &&
            (await this.isFoodTruckUrl(resultUrl))
          ) {
            discoveredUrls.add(resultUrl);
          }
        }
      }
    }
  }

  // Helper method to perform location-specific discovery
  private async performLocationDiscovery(
    discoveredUrls: Set<string>,
    results: DiscoveryResult,
  ): Promise<void> {
    for (const city of SC_TARGET_CITIES) {
      await this.searchSingleCity(city, discoveredUrls, results);
      await this.delay(DISCOVERY_CONFIG.rateLimitDelayMs);
    }
  }

  // Helper method to search a single city
  private async searchSingleCity(
    city: string,
    discoveredUrls: Set<string>,
    results: DiscoveryResult,
  ): Promise<void> {
    try {
      console.info(` Searching for food trucks in ${city}, SC`);
      const locationQuery = `food trucks in ${city} South Carolina`;
      const searchResults = await tavilySearch(locationQuery, {
        limit: 5,
      });

      await this.processLocationSearchResults(searchResults, discoveredUrls);
    } catch (error) {
      console.error(` Location search failed for ${city}:`, error);
      results.errors.push(
        `Location search failed for ${city}: ${error instanceof Error ? error.message : 'Unknown error'}`,
      );
    }
  }

  // Helper method to process location search results
  private async processLocationSearchResults(
    searchResults: unknown,
    discoveredUrls: Set<string>,
  ): Promise<void> {
    if (searchResults != undefined && Array.isArray(searchResults) && searchResults.length > 0) {
      for (const result of searchResults) {
        if (typeof result === 'object' && result !== null && 'url' in result) {
          const resultUrl = (result as { url?: string }).url;
          if (
            resultUrl != undefined &&
            typeof resultUrl === 'string' &&
            (await this.isFoodTruckUrl(resultUrl))
          ) {
            discoveredUrls.add(resultUrl);
          }
        }
      }
    }
  }

  // Helper method to store discovered URLs from discovery process
  private async storeDiscoveredUrlsFromDiscovery(
    discoveredUrls: Set<string>,
    results: DiscoveryResult,
  ): Promise<void> {
    for (const url of discoveredUrls) {
      try {
        const stored = await this.storeDiscoveredUrl(url, 'autonomous_search', {
          search_context: 'full_discovery',
          discovery_timestamp: new Date().toISOString(),
        });
        if (stored.isNew === true) {
          results.urls_stored++;
        } else {
          results.urls_duplicates++;
        }
      } catch (error) {
        console.error(` Failed to store URL ${url}:`, error);
        results.errors.push(
          `Failed to store URL ${url}: ${error instanceof Error ? error.message : 'Unknown error'}`,
        );
      }
    }
  }

  async discoverNewFoodTrucks(): Promise<DiscoveryResult> {
    console.info(' Starting autonomous food truck discovery...');

    const results: DiscoveryResult = {
      urls_discovered: 0,
      urls_stored: 0,
      urls_duplicates: 0,
      errors: [],
    };

    const discoveredUrls = new Set<string>();

    // 1. Search for food trucks using Tavily search
    await this.performSearchTermDiscovery(discoveredUrls, results);

    // 2. Crawl known food truck directory sites
    await this.performDirectoryCrawling(discoveredUrls, results);

    // 3. Location-specific discovery for SC cities
    await this.performLocationDiscovery(discoveredUrls, results);

    results.urls_discovered = discoveredUrls.size;
    console.info(` Discovered ${results.urls_discovered} potential food truck URLs`);

    // 4. Store new URLs in database
    await this.storeDiscoveredUrlsFromDiscovery(discoveredUrls, results);

    console.info(
      ` Discovery complete: ${results.urls_stored} new URLs stored, ${results.urls_duplicates} duplicates skipped`,
    );

    return results;
  }
  private extractFoodTruckUrls(content: string): string[] {
    const urls: string[] = [];

    // Look for URL patterns in content - using a safer regex to avoid backtracking
    const urlRegex = /https?:\/\/[^\s<>"']{1,200}/g;
    const foundUrls = content.match(urlRegex) ?? [];

    for (const url of foundUrls) {
      try {
        // Basic URL validation
        new URL(url);
        // Remove trailing punctuation - using safer regex to avoid backtracking
        const cleanUrl = url.replace(/[.,;!?]$/, '');
        urls.push(cleanUrl);
      } catch {
        // Invalid URL, skip
        continue;
      }
    }

    return urls;
  }

  private async isFoodTruckUrl(url: string): Promise<boolean> {
    try {
      // Basic URL validation
      new URL(url);

      // Food truck indicators in URL
      const foodTruckKeywords = [
        'food-truck',
        'foodtruck',
        'mobile-food',
        'street-food',
        'truck',
        'kitchen',
        'eats',
        'bbq',
        'burger',
        'taco',
        'catering',
        'mobile',
        'chef',
        'bistro',
        'cafe',
      ];

      // Blacklist - skip these domains
      const blacklistKeywords = [
        'facebook.com',
        'instagram.com',
        'twitter.com',
        'linkedin.com',
        'youtube.com',
        'yelp.com',
        'google.com',
        'maps.google.com',
        'foursquare.com',
        'tripadvisor.com',
        'zomato.com',
        'eventbrite.com',
        'meetup.com',
      ];

      const urlLower = url.toLowerCase();

      // Skip social media and review sites
      if (blacklistKeywords.some((keyword) => urlLower.includes(keyword))) {
        return false;
      }

      // Check if we already have this URL in discovered_urls
      if (supabaseAdmin == undefined) {
        return false;
      }

      const { data: existingDiscovered } = await supabaseAdmin
        .from('discovered_urls')
        .select('id')
        .eq('url', url)
        .limit(1);

      if (existingDiscovered != undefined && existingDiscovered.length > 0) {
        return false; // Already discovered
      }

      // Check if we already have this URL in food_trucks
      const { data: existingTrucks } = await supabaseAdmin
        .from('food_trucks')
        .select('id')
        .contains('source_urls', [url])
        .limit(1);

      if (existingTrucks != undefined && existingTrucks.length > 0) {
        return false; // Already have this URL
      }

      // Accept if has food truck keywords or if it's a business domain
      return (
        foodTruckKeywords.some((keyword) => urlLower.includes(keyword)) ||
        (/\.(com|net|org|biz|info)/.test(urlLower) &&
          !urlLower.includes('blog') &&
          !urlLower.includes('news'))
      );
    } catch (error) {
      console.error('Error validating food truck URL:', error);
      return false;
    }
  }

  async getLocationSpecificDiscovery(city: string, state: string = 'SC'): Promise<DiscoveryResult> {
    console.info(` Starting location-specific discovery for ${city}, ${state}`);

    const locationQuery = `food trucks in ${city} ${state}`;
    const results: DiscoveryResult = {
      urls_discovered: 0,
      urls_stored: 0,
      urls_duplicates: 0,
      errors: [],
    };

    const discoveredUrls = new Set<string>();

    try {
      await this.performLocationSpecificSearch(locationQuery, discoveredUrls);
      results.urls_discovered = discoveredUrls.size;
      await this.storeLocationDiscoveryResults({ discoveredUrls, locationQuery, city, state, results });
    } catch (error) {
      console.error(` Location search failed for ${city}:`, error);
      results.errors.push(
        `Location search failed for ${city}: ${error instanceof Error ? error.message : 'Unknown error'}`,
      );
    }

    console.info(
      ` Location discovery complete for ${city}: ${results.urls_stored} new URLs stored`,
    );
    return results;
  }

  // Helper method to perform location-specific search
  private async performLocationSpecificSearch(
    locationQuery: string,
    discoveredUrls: Set<string>,
  ): Promise<void> {
    const searchResults = await tavilySearch(locationQuery, { limit: 15 });

    if (searchResults != undefined && searchResults.length > 0) {
      for (const result of searchResults) {
        await this.processSearchResult(result, discoveredUrls);
      }
    }
  }

  // Helper method to process a single search result
  private async processSearchResult(result: unknown, discoveredUrls: Set<string>): Promise<void> {
    if (typeof result === 'object' && result !== null && 'url' in result) {
      const resultUrl = (result as { url?: string }).url;
      if (resultUrl != undefined && resultUrl !== '' && (await this.isFoodTruckUrl(resultUrl))) {
        discoveredUrls.add(resultUrl);
      }

      // Extract URLs from content
      await this.extractUrlsFromContent(result, discoveredUrls);
    }
  }

  // Helper method to extract URLs from content
  private async extractUrlsFromContent(
    result: unknown,
    discoveredUrls: Set<string>,
  ): Promise<void> {
    if (typeof result === 'object' && result != undefined) {
      const resultObj = result as { content?: string; raw_content?: string };
      if (resultObj.content != undefined || resultObj.raw_content != undefined) {
        const content = resultObj.content ?? resultObj.raw_content ?? '';
        const extractedUrls = this.extractFoodTruckUrls(content);
        for (const url of extractedUrls) {
          if (await this.isFoodTruckUrl(url)) {
            discoveredUrls.add(url);
          }
        }
      }
    }
  }

  // Helper method to store location discovery results
  private async storeLocationDiscoveryResults(
    params: {
      discoveredUrls: Set<string>;
      locationQuery: string;
      city: string;
      state: string;
      results: DiscoveryResult;
    }
  ): Promise<void> {
    const { discoveredUrls, locationQuery, city, state, results } = params;
    for (const url of discoveredUrls) {
      try {
        const stored = await this.storeDiscoveredUrl(url, 'tavily_search', {
          search_query: locationQuery,
          target_city: city,
          target_state: state,
        });
        if (stored.isNew === true) {
          results.urls_stored++;
        } else {
          results.urls_duplicates++;
        }
      } catch (error) {
        console.error(` Failed to store URL ${url}:`, error);
        results.errors.push(
          `Failed to store URL ${url}: ${error instanceof Error ? error.message : 'Unknown error'}`,
        );
      }
    }
  }

  private delay(ms: number): Promise<void> {
    return new Promise((resolve) => setTimeout(resolve, ms));
  }

  /**
   * Store multiple discovered URLs with metadata
   */
  async storeDiscoveredUrls(
    urls: string[],
    discoveryMethod: string = 'manual',
    metadata: Record<string, unknown> = {},
  ): Promise<{
    urls_stored: number;
    urls_duplicates: number;
    errors: string[];
  }> {
    const result: {
      urls_stored: number;
      urls_duplicates: number;
      errors: string[];
    } = {
      urls_stored: 0,
      urls_duplicates: 0,
      errors: [],
    };

    for (const url of urls) {
      try {
        const stored = await this.storeDiscoveredUrl(url, discoveryMethod, metadata);
        if (stored.isNew === true) {
          result.urls_stored++;
        } else {
          result.urls_duplicates++;
        }
      } catch (error) {
        const errorMsg = `Failed to store URL ${url}: ${error instanceof Error ? error.message : 'Unknown error'}`;
        result.errors.push(errorMsg);
        console.error(errorMsg);
      }
    }

    return result;
  }

  /**
   * Enhanced store method with discovery method and metadata
   */
  private async storeDiscoveredUrl(
    url: string,
    discoveryMethod: string = 'manual',
    metadata: Record<string, unknown> = {},
  ): Promise<{ isNew: boolean }> {
    try {
      if (!supabaseAdmin) {
        throw new Error('Supabase admin client not available');
      }

      // Check if URL already exists
      const { data: existing } = await supabaseAdmin
        .from('discovered_urls')
        .select('id')
        .eq('url', url)
        .limit(1);

      if (existing && existing.length > 0) {
        return { isNew: false };
      }

      // Store new URL - handle missing columns gracefully
      const insertData: Record<string, unknown> = {
        url,
        status: 'new',
        notes: `Discovered via ${discoveryMethod}`,
      };

      // Add optional columns only if they exist in the table
      try {
        // Check if discovery_method column exists
        const { error: testError } = await supabaseAdmin
          .from('discovered_urls')
          .select('discovery_method')
          .limit(1);

        if (!testError) {
          insertData.discovery_method = discoveryMethod;
          insertData.region = 'SC';
          insertData.metadata = metadata;
        }
      } catch {
        // Column doesn't exist, continue without it
        console.info('Some columns missing in discovered_urls table, using basic structure');
      }

      const { error } = await supabaseAdmin.from('discovered_urls').insert(insertData);

      if (error) {
        throw error;
      }

      return { isNew: true };
    } catch (error) {
      console.error('Error storing discovered URL:', error);
      throw error;
    }
  }

  /**
   * Search for food truck directories
   */
  async searchFoodTruckDirectories(
    query: string = 'food truck directory South Carolina',
  ): Promise<TavilySearchResult[]> {
    try {
      return await tavilySearch(query, { limit: 10 });
    } catch (error) {
      console.error('Error searching food truck directories:', error);
      return [];
    }
  }

  /**
   * Search for food truck websites
   */
  async searchFoodTruckWebsites(query: string): Promise<TavilySearchResult[]> {
    try {
      return await tavilySearch(query, { limit: 15 });
    } catch (error) {
      console.error('Error searching food truck websites:', error);
      return [];
    }
  }
}

export const discoveryEngine = new FoodTruckDiscoveryEngine();

