export const gemini = new GeminiService();

// Centralized Gemini operation dispatcher to eliminate duplication
export async function dispatchGeminiOperation(
  type: 'menu' | 'location' | 'hours' | 'sentiment' | 'enhance',
  data: unknown
): Promise<unknown> {
  switch (type) {
    case 'menu': {
      return await gemini.processMenuData(data as string);
    }
    case 'location': {
      return await gemini.extractLocationFromText(data as string);
    }
    case 'hours': {
      return await gemini.standardizeOperatingHours(data as string);
    }
    case 'sentiment': {
      return await gemini.analyzeSentiment(data as string);
    }
    case 'enhance': {
      return await gemini.enhanceFoodTruckData(data);
    }
    default: {
      throw new Error(`Unknown Gemini operation type: ${String(type)}`);
    }
  }
}

import { APIUsageService } from '../supabase';
import { APIMonitor } from '../monitoring/apiMonitor';

export interface UsageLimits {
  dailyRequestLimit: number;
  dailyTokenLimit: number;
}

export interface UsageCheck {
  canMakeRequest: boolean;
  usage?: {
    requests: { used: number; limit: number; remaining: number };
    tokens: { used: number; limit: number; remaining: number };
  };
}

export class GeminiUsageLimits {
  private static readonly DEFAULT_LIMITS: UsageLimits = {
    dailyRequestLimit: 1500,
    dailyTokenLimit: 32_000,
  };

  static async checkUsageLimits(limits: UsageLimits = this.DEFAULT_LIMITS): Promise<UsageCheck> {
    try {
      const usage = await APIUsageService.getTodayUsage('gemini');

      if (!usage) {
        return { canMakeRequest: true };
      }

      const requestsUsed = usage.requests_count ?? 0;
      const tokensUsed = usage.tokens_used ?? 0;

      const requestsRemaining = limits.dailyRequestLimit - requestsUsed;
      const tokensRemaining = limits.dailyTokenLimit - tokensUsed;

      return {
        canMakeRequest: requestsRemaining > 0 && tokensRemaining > 100, // Keep 100 token buffer
        usage: {
          requests: {
            used: requestsUsed,
            limit: limits.dailyRequestLimit,
            remaining: requestsRemaining,
          },
          tokens: {
            used: tokensUsed,
            limit: limits.dailyTokenLimit,
            remaining: tokensRemaining,
          },
        },
      };
    } catch (error: unknown) {
      console.warn('Error checking Gemini usage limits:', error);
      return { canMakeRequest: false };
    }
  }

  static async checkWithMonitoring(
    estimatedTokens: number,
    limits: UsageLimits = this.DEFAULT_LIMITS
  ): Promise<{ allowed: boolean; reason?: string; waitTime?: number }> {
    const canMakeRequest = await APIMonitor.canMakeRequest('gemini', 1, estimatedTokens);

    if (!canMakeRequest.allowed) {
      return {
        allowed: false,
        reason: `API limit reached: ${canMakeRequest.reason}. Wait time: ${Math.ceil((canMakeRequest.waitTime ?? 0) / 1000 / 60)} minutes`,
      };
    }

    return { allowed: true };
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { AuditLogger } from '@/lib/security/auditLogger';
import { createSupabaseMiddlewareClient } from '@/lib/supabaseMiddleware';

interface RequestMetadata {
  ip: string;
  userAgent: string;
  url: string;
  method: string;
}

async function logAndRedirect(req: NextRequest, res: NextResponse, requestMetadata: RequestMetadata, reason: string, userError?: { message?: string }) {
  await AuditLogger.logSecurityEvent({
    event_type: 'permission_denied',
    ip_address: requestMetadata.ip,
    user_agent: requestMetadata.userAgent,
    details: {
      attempted_url: requestMetadata.url,
      reason,
      error: userError?.message,
    },
    severity: 'warning',
  });
  const redirectUrl = req.nextUrl.clone();
  redirectUrl.pathname = '/login';
  redirectUrl.searchParams.set(`redirectedFrom`, req.nextUrl.pathname);
  return NextResponse.redirect(redirectUrl);
}

async function logAndRedirectDenied(req: NextRequest, res: NextResponse, requestMetadata: RequestMetadata, user: any, profile: any, profileQueryError?: { message?: string }) {
  await AuditLogger.logSecurityEvent({
    event_type: 'permission_denied',
    user_id: user.id,
    user_email: user.email,
    ip_address: requestMetadata.ip,
    user_agent: requestMetadata.userAgent,
    details: {
      attempted_url: requestMetadata.url,
      user_role: profile?.role ?? 'none',
      reason: 'insufficient_privileges',
      error: profileQueryError?.message,
    },
    severity: 'error',
  });
  const redirectUrl = req.nextUrl.clone();
  redirectUrl.pathname = '/access-denied';
  return NextResponse.redirect(redirectUrl);
}

export async function protectAdminRoutes(req: NextRequest, res: NextResponse, requestMetadata: RequestMetadata) {
  const supabase = createSupabaseMiddlewareClient(req, res);
  const {
    data: { user },
    error: userError,
  } = await supabase.auth.getUser();
  if (userError || !user) {
    return logAndRedirect(req, res, requestMetadata, 'no_session', userError ?? undefined);
  }
  const { data: profile, error: profileQueryError } = await supabase
    .from('profiles')
    .select('role')
    .eq('id', user.id)
    .single();
  if (profileQueryError || profile?.role !== 'admin') {
    return logAndRedirectDenied(req, res, requestMetadata, user, profile, profileQueryError ?? undefined);
  }
  if (req.method !== 'GET' || req.nextUrl.pathname.includes('/api/')) {
    await AuditLogger.logDataAccess(
      user.id,
      user.email ?? 'unknown',
      'admin_panel',
      req.nextUrl.pathname,
      req.method === 'GET' ? 'read' : 'admin_access',
      {
        ip: requestMetadata.ip,
        userAgent: requestMetadata.userAgent,
      },
    );
  }
  return res;
}

/**
 * SOTA API Usage Monitoring and Alerting System
 * Implements proactive monitoring, alerting, and throttling for all external APIs
 */

import { APIUsageService } from '@/lib/supabase';

// API Rate Limits Configuration
export const API_LIMITS = {
  gemini: {
    requests: { daily: 1500, hourly: 100 },
    tokens: { daily: 32_000, hourly: 2000 },
    alertThresholds: { warning: 0.8, critical: 0.95 }
  },
  firecrawl: {
    requests: { daily: 500, hourly: 50 },
    tokens: { daily: 0, hourly: 0 },
    alertThresholds: { warning: 0.8, critical: 0.95 }
  },
  tavily: {
    requests: { daily: 1000, hourly: 100 },
    tokens: { daily: 0, hourly: 0 },
    alertThresholds: { warning: 0.8, critical: 0.95 }
  },
  supabase: {
    requests: { daily: 50_000, hourly: 5000 },
    tokens: { daily: 0, hourly: 0 },
    alertThresholds: { warning: 0.9, critical: 0.98 }
  }
} as const;

export type APIService = keyof typeof API_LIMITS;
export type AlertLevel = 'info' | 'warning' | 'critical';

export interface APIUsageAlert {
  service: APIService;
  level: AlertLevel;
  message: string;
  usage: {
    current: number;
    limit: number;
    percentage: number;
  };
  timestamp: string;
  recommendations: string[];
}

export interface APIUsageData {
  requests: { used: number; limit: number; percentage: number };
  tokens?: { used: number; limit: number; percentage: number };
}

export interface APIMonitoringResult {
  canMakeRequest: boolean;
  alerts: APIUsageAlert[];
  usage: Record<APIService, APIUsageData>;
  recommendations: string[];
}

/**
 * Comprehensive API Monitoring Service
 */
export class APIMonitor {
  private static alertHistory: APIUsageAlert[] = [];

  /**
   * Check if API request can be made safely
   */
  static async canMakeRequest(
    service: APIService, 
    requestCount: number = 1, 
    tokenCount: number = 0
  ): Promise<{ allowed: boolean; reason?: string; waitTime?: number }> {
    try {
      const usage = await this.getCurrentUsage(service);
      const limits = API_LIMITS[service];

      // Check daily limits
      const newRequestCount = usage.requests.used + requestCount;
      const newTokenCount = (usage.tokens?.used ?? 0) + tokenCount;

      if (newRequestCount > limits.requests.daily) {
        return {
          allowed: false,
          reason: `Daily request limit exceeded (${newRequestCount}/${limits.requests.daily})`,
          waitTime: this.getTimeUntilReset('daily')
        };
      }

      if (limits.tokens != undefined && newTokenCount > limits.tokens.daily) {
        return {
          allowed: false,
          reason: `Daily token limit exceeded (${newTokenCount}/${limits.tokens.daily})`,
          waitTime: this.getTimeUntilReset('daily')
        };
      }

      // Check if approaching critical threshold
      const requestPercentage = newRequestCount / limits.requests.daily;
      if (requestPercentage > limits.alertThresholds.critical) {
        return {
          allowed: false,
          reason: `Approaching critical usage threshold (${(requestPercentage * 100).toFixed(1)}%)`,
          waitTime: this.getTimeUntilReset('daily')
        };
      }

      return { allowed: true };
    } catch (error) {
      console.error(`Error checking API limits for ${service}:`, error);
      // Fail safe - allow request but log error
      return { allowed: true };
    }
  }

  /**
   * Get current usage for a service
   */
  static async getCurrentUsage(service: APIService): Promise<APIUsageData> {
    const todayUsage = await APIUsageService.getTodayUsage(service);
    const limits = API_LIMITS[service];

    const usage: APIUsageData = {
      requests: {
        used: todayUsage?.requests_count ?? 0,
        limit: limits.requests.daily,
        percentage: ((todayUsage?.requests_count ?? 0) / limits.requests.daily) * 100
      }
    };

    if (limits.tokens.daily > 0) {
      usage.tokens = {
        used: todayUsage?.tokens_used ?? 0,
        limit: limits.tokens.daily,
        percentage: ((todayUsage?.tokens_used ?? 0) / limits.tokens.daily) * 100
      };
    }

    return usage;
  }

  /**
   * Comprehensive monitoring check for all APIs
   */
  static async checkAllAPIs(): Promise<APIMonitoringResult> {
    const alerts: APIUsageAlert[] = [];
    const usage: Record<APIService, APIUsageData> = {} as Record<APIService, APIUsageData>;
    const recommendations: string[] = [];
    let canMakeRequest = true;

    for (const service of Object.keys(API_LIMITS) as APIService[]) {
      try {
        const serviceUsage = await this.getCurrentUsage(service);
        usage[service] = serviceUsage;

        // Check for alerts
        const serviceAlerts = this.generateAlerts(service, serviceUsage);
        alerts.push(...serviceAlerts);

        // Check if any service is at critical level
        if (serviceUsage.requests.percentage > API_LIMITS[service].alertThresholds.critical * 100) {
          canMakeRequest = false;
        }
      } catch (error) {
        console.error(`Error monitoring ${service}:`, error);
        alerts.push({
          service,
          level: 'warning',
          message: `Failed to check usage for ${service}`,
          usage: { current: 0, limit: 0, percentage: 0 },
          timestamp: new Date().toISOString(),
          recommendations: ['Check API connectivity', 'Verify credentials']
        });
      }
    }

    // Generate global recommendations
    recommendations.push(...this.generateRecommendations(usage, alerts));

    return {
      canMakeRequest,
      alerts,
      usage,
      recommendations
    };
  }

  // Helper for token alerts
  private static generateTokenAlerts(service: APIService, usage: APIUsageData, limits: typeof API_LIMITS[APIService], timestamp: string): APIUsageAlert[] {
    const alerts: APIUsageAlert[] = [];
    if (usage.tokens && limits.tokens.daily > 0) {
      const tokenPercentage = usage.tokens.percentage;
      if (tokenPercentage > limits.alertThresholds.critical * 100) {
        alerts.push({
          service,
          level: 'critical',
          message: `Critical: ${service} token usage at ${tokenPercentage.toFixed(1)}%`,
          usage: {
            current: usage.tokens.used,
            limit: usage.tokens.limit,
            percentage: tokenPercentage
          },
          timestamp,
          recommendations: [
            'Reduce prompt complexity',
            'Implement response caching',
            'Optimize token usage patterns'
          ]
        });
      } else if (tokenPercentage > limits.alertThresholds.warning * 100) {
        alerts.push({
          service,
          level: 'warning',
          message: `Warning: ${service} token usage at ${tokenPercentage.toFixed(1)}%`,
          usage: {
            current: usage.tokens.used,
            limit: usage.tokens.limit,
            percentage: tokenPercentage
          },
          timestamp,
          recommendations: [
            'Monitor token consumption',
            'Optimize prompt efficiency',
            'Consider response caching'
          ]
        });
      }
    }
    return alerts;
  }

  private static generateAlerts(service: APIService, usage: APIUsageData): APIUsageAlert[] {
    const alerts: APIUsageAlert[] = [];
    const limits = API_LIMITS[service];
    const timestamp = new Date().toISOString();

    // Check request usage
    const requestPercentage = usage.requests.percentage;
    if (requestPercentage > limits.alertThresholds.critical * 100) {
      alerts.push({
        service,
        level: 'critical',
        message: `Critical: ${service} request usage at ${requestPercentage.toFixed(1)}%`,
        usage: {
          current: usage.requests.used,
          limit: usage.requests.limit,
          percentage: requestPercentage
        },
        timestamp,
        recommendations: [
          'Immediately reduce API calls',
          'Implement request queuing',
          'Consider upgrading API plan'
        ]
      });
    } else if (requestPercentage > limits.alertThresholds.warning * 100) {
      alerts.push({
        service,
        level: 'warning',
        message: `Warning: ${service} request usage at ${requestPercentage.toFixed(1)}%`,
        usage: {
          current: usage.requests.used,
          limit: usage.requests.limit,
          percentage: requestPercentage
        },
        timestamp,
        recommendations: [
          'Monitor usage closely',
          'Optimize request patterns',
          'Enable request caching'
        ]
      });
    }

    // Token alerts
    alerts.push(...this.generateTokenAlerts(service, usage, limits, timestamp));

    // Store alerts in history
    this.alertHistory.push(...alerts);
    
    // Keep only last 100 alerts
    if (this.alertHistory.length > 100) {
      this.alertHistory = this.alertHistory.slice(-100);
    }

    return alerts;
  }

  /**
   * Generate optimization recommendations
   */
  private static generateRecommendations(usage: Record<APIService, APIUsageData>, alerts: APIUsageAlert[]): string[] {
    const recommendations: string[] = [];

    // High-level optimization recommendations
    const criticalAlerts = alerts.filter(a => a.level === 'critical');
    const warningAlerts = alerts.filter(a => a.level === 'warning');

    if (criticalAlerts.length > 0) {
      recommendations.push('URGENT: Implement immediate API throttling', 'Enable aggressive caching for all API responses', 'Consider upgrading API plans for critical services');
    }

    if (warningAlerts.length > 0) {
      recommendations.push('Implement request queuing and batching', 'Optimize API call patterns and frequency', 'Enable response caching where possible');
    }

    // Service-specific recommendations
    if (usage.gemini?.requests?.percentage > 70) {
      recommendations.push('Gemini: Optimize prompt length and complexity', 'Gemini: Implement response caching for similar queries');
    }

    if (usage.firecrawl?.requests?.percentage > 70) {
      recommendations.push('Firecrawl: Implement URL deduplication', 'Firecrawl: Cache crawl results for repeated URLs');
    }

    return [...new Set(recommendations)]; // Remove duplicates
  }

  /**
   * Get time until rate limit reset
   */
  private static getTimeUntilReset(period: 'hourly' | 'daily'): number {
    const now = new Date();
    
    if (period === 'hourly') {
      const nextHour = new Date(now);
      nextHour.setHours(now.getHours() + 1, 0, 0, 0);
      return nextHour.getTime() - now.getTime();
    } else {
      const nextDay = new Date(now);
      nextDay.setDate(now.getDate() + 1);
      nextDay.setHours(0, 0, 0, 0);
      return nextDay.getTime() - now.getTime();
    }
  }

  /**
   * Get alert history
   */
  static getAlertHistory(): APIUsageAlert[] {
    return [...this.alertHistory];
  }

  /**
   * Clear alert history
   */
  static clearAlertHistory(): void {
    this.alertHistory = [];
  }
}

/**
 * SOTA Database Query Optimization and Caching
 * Implements Next.js unstable_cache for optimal performance
 */

import { unstable_cache } from 'next/cache';
import { supabaseAdmin, FoodTruckService } from '@/lib/supabase';
import type { FoodTruck } from '@/lib/types';

// Cache configuration
const CACHE_CONFIG = {
  // Short-term cache for frequently changing data
  SHORT_TTL: 60 * 5, // 5 minutes
  // Medium-term cache for semi-static data
  MEDIUM_TTL: 60 * 30, // 30 minutes
  // Long-term cache for static data
  LONG_TTL: 60 * 60 * 24, // 24 hours
} as const;

/**
 * Cached food truck queries with optimized database access
 */
export const CachedFoodTruckService = {
  /**
   * Get all food trucks with caching
   * Cache for 30 minutes since truck data changes moderately
   */
  getAllTrucksCached : unstable_cache(
    async (): Promise<{ trucks: FoodTruck[]; count: number }> => {
      console.info('CachedFoodTruckService: Cache miss - fetching all trucks from database');
      const result = await FoodTruckService.getAllTrucks();
      return { trucks: result.trucks, count: result.total };
    },
    ['all-trucks'],
    {
      revalidate: CACHE_CONFIG.MEDIUM_TTL,
      tags: ['food-trucks', 'all-trucks']
    }
  ),

  /**
   * Get trucks by location with caching
   * Cache for 5 minutes since location-based queries are time-sensitive
   */
  getTrucksByLocationCached : unstable_cache(
    async (lat: number, lng: number, radiusKm: number): Promise<FoodTruck[]> => {
      console.info(`CachedFoodTruckService: Cache miss - fetching trucks near ${lat},${lng} (${radiusKm}km)`);
      return await FoodTruckService.getTrucksByLocation(lat, lng, radiusKm);
    },
    ['trucks-by-location'],
    {
      revalidate: CACHE_CONFIG.SHORT_TTL,
      tags: ['food-trucks', 'location-search']
    }
  ),

  /**
   * Get truck by ID with caching
   * Cache for 30 minutes since individual truck data is relatively stable
   */
  getTruckByIdCached : unstable_cache(
    async (id: string): Promise<FoodTruck | null> => {
      console.info(`CachedFoodTruckService: Cache miss - fetching truck ${id} from database`);
      return await FoodTruckService.getTruckById(id);
    },
    ['truck-by-id'],
    {
      revalidate: CACHE_CONFIG.MEDIUM_TTL,
      tags: ['food-trucks', 'truck-details']
    }
  ),

  /**
   * Search trucks with caching
   * Cache for 5 minutes since search results should be relatively fresh
   */
  searchTrucksCached : unstable_cache(
    async (query: string, filters?: {
      cuisine?: string;
      openNow?: boolean;
      lat?: number;
      lng?: number;
      radius?: number;
    }): Promise<FoodTruck[]> => {
      console.info(`CachedFoodTruckService: Cache miss - searching trucks for "${query}"`);
      
      if (supabaseAdmin == undefined) {
        throw new Error('Supabase admin client not available');
      }

      let dbQuery = supabaseAdmin
        .from('food_trucks')
        .select('*')
        .or(`name.ilike.%${query}%,description.ilike.%${query}%,cuisine_type.cs.{${query}}`);

      // Apply filters
      if (filters?.cuisine != undefined && filters.cuisine !== '') {
        dbQuery = dbQuery.contains('cuisine_type', [filters.cuisine]);
      }

      const { data: trucks, error } = await dbQuery.limit(50);

      if (error != undefined) {
        throw new Error(`Search query failed: ${error.message}`);
      }

      let results = trucks ?? [];

      // Apply location filter if provided
      if (filters?.lat != undefined && filters?.lng != undefined && filters?.radius != undefined) {
        results = results.filter((truck: FoodTruck) => {
          if (truck.current_location?.lat == undefined || truck.current_location?.lng == undefined) {
            return false;
          }
          const distance = calculateDistance(
            filters.lat,
            filters.lng,
            truck.current_location.lat,
            truck.current_location.lng
          );
          return distance <= (filters.radius ?? 10);
        });
      }

      // Apply openNow filter if provided
      if (filters?.openNow === true) {
        const now = new Date();
        const currentDay = now.toLocaleDateString('en-US', { weekday: 'short' }).toLowerCase(); // 'mon', 'tue', etc.
        const currentTime = now.getHours() * 100 + now.getMinutes(); // HHMM format

        results = results.filter((truck: FoodTruck) => {
          const hours = truck.operating_hours?.[currentDay as keyof typeof truck.operating_hours] as { closed?: boolean; open?: string; close?: string } | undefined;
          if (hours == undefined || hours.closed === true) return false;

          const openTime = parseTimeString(hours.open ?? '');
          const closeTime = parseTimeString(hours.close ?? '');

          return currentTime >= openTime && currentTime <= closeTime;
        });
      }

      return results;
    },
    ['search-trucks'],
    {
      revalidate: CACHE_CONFIG.SHORT_TTL,
      tags: ['food-trucks', 'search']
    }
  ),

  /**
   * Get data quality statistics with caching
   * Cache for 24 hours since quality stats change slowly
   */
  getDataQualityStatsCached : unstable_cache(
    async (): Promise<{
      averageScore: number;
      distribution: Record<string, number>;
      totalTrucks: number;
    }> => {
      console.info('CachedFoodTruckService: Cache miss - calculating data quality stats');
      
      if (supabaseAdmin == undefined) {
        throw new Error('Supabase admin client not available');
      }

      const { data: trucks, error } = await supabaseAdmin
        .from('food_trucks')
        .select('data_quality_score');

      if (error) {
        throw new Error(`Quality stats query failed: ${error.message}`);
      }

      const scores = trucks == undefined ? [] : trucks.map((t: { data_quality_score?: number }) => t.data_quality_score ?? 0);
      const averageScore = scores.length > 0
        ? scores.reduce((sum: number, score: number) => sum + score, 0) / scores.length
        : 0;

      const distribution = {
        high: scores.filter((s: number) => s >= 0.8).length,
        medium: scores.filter((s: number) => s >= 0.6 && s < 0.8).length,
        low: scores.filter((s: number) => s < 0.6).length
      };

      return {
        averageScore: Math.round(averageScore * 100) / 100,
        distribution,
        totalTrucks: scores.length
      };
    },
    ['data-quality-stats'],
    {
      revalidate: CACHE_CONFIG.LONG_TTL,
      tags: ['food-trucks', 'data-quality']
    }
  ),
};

