/**
 * Utility functions
 */
function calculateDistance(lat1: number, lng1: number, lat2: number, lng2: number): number {
  const R = 6371; // Earth's radius in kilometers
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLng = (lng2 - lng1) * Math.PI / 180;
  const a = 
    Math.sin(dLat/2) * Math.sin(dLat/2) +
    Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
    Math.sin(dLng/2) * Math.sin(dLng/2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
  return R * c;
}

function parseTimeString(timeStr: string): number {
  if (timeStr == undefined || timeStr === '') return 0;
  const [hours, minutes] = timeStr.split(':').map(Number);
  return hours * 100 + (minutes ?? 0);
}

/**
 * SOTA Core Web Vitals Monitoring Implementation
 * Tracks LCP, FID, CLS, FCP, and TTFB metrics for performance optimization
 */

// @ts-expect-error TS(2792): Cannot find module 'web-vitals'. Did you mean to s... Remove this comment to see the full error message
import { getCLS, getFCP, getFID, getLCP, getTTFB, Metric } from 'web-vitals';

// Performance thresholds based on Google's Core Web Vitals standards
export const PERFORMANCE_THRESHOLDS = {
  LCP: { good: 2500, needsImprovement: 4000 }, // Largest Contentful Paint
  FID: { good: 100, needsImprovement: 300 },   // First Input Delay
  CLS: { good: 0.1, needsImprovement: 0.25 },  // Cumulative Layout Shift
  FCP: { good: 1800, needsImprovement: 3000 }, // First Contentful Paint
  TTFB: { good: 800, needsImprovement: 1800 }  // Time to First Byte
} as const;

export type MetricName = keyof typeof PERFORMANCE_THRESHOLDS;

export interface PerformanceMetric {
  name: MetricName;
  value: number;
  rating: 'good' | 'needs-improvement' | 'poor';
  timestamp: number;
  url: string;
  userAgent: string;
}

// In-memory storage for metrics (in production, send to analytics service)
const metricsStore: PerformanceMetric[] = [];

/**
 * Categorizes metric value based on thresholds
 */
function getRating(name: MetricName, value: number): 'good' | 'needs-improvement' | 'poor' {
  const thresholds = PERFORMANCE_THRESHOLDS[name];
  if (value <= thresholds.good) return 'good';
  if (value <= thresholds.needsImprovement) return 'needs-improvement';
  return 'poor';
}

/**
 * Processes and stores a web vital metric
 */
function handleMetric(metric: Metric) {
  // Type-safe casting with validation
  const metricName = String((metric as { name: string }).name) as MetricName;
  const metricValue = Number((metric as { value: number }).value);

  const performanceMetric: PerformanceMetric = {
    name: metricName,
    value: metricValue,
    rating: getRating(metricName, metricValue),
    timestamp: Date.now(),
    url: globalThis.location.href,
    userAgent: navigator.userAgent
  };

  // Store metric locally
  metricsStore.push(performanceMetric);

  // Send to analytics endpoint (non-blocking)
  sendMetricToAnalytics(performanceMetric).catch(error => {
    console.warn('Failed to send metric to analytics:', error);
  });

  // Log performance issues
  if (performanceMetric.rating === 'poor') {
    console.warn(`Poor ${metricName} performance:`, {
      value: metricValue,
      threshold: PERFORMANCE_THRESHOLDS[metricName],
      url: performanceMetric.url
    });
  }
}

/**
 * Sends metric data to analytics endpoint
 */
async function sendMetricToAnalytics(metric: PerformanceMetric): Promise<void> {
  try {
    await fetch('/api/analytics/web-vitals', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(metric),
    });
  } catch (error) {
    // Silently fail - don't impact user experience
    console.info('Analytics endpoint unavailable:', error);
  }
}

/**
 * Initializes Core Web Vitals monitoring
 * Call this in your app's root component or _app.tsx
 */
export function initWebVitalsMonitoring(): void {
  try {
    // Type-safe metric handlers with explicit casting
    const safeHandleMetric = (metric: Metric) => handleMetric(metric);

    // eslint-disable-next-line @typescript-eslint/no-unsafe-call
    getCLS(safeHandleMetric);
    // eslint-disable-next-line @typescript-eslint/no-unsafe-call
    getFCP(safeHandleMetric);
    // eslint-disable-next-line @typescript-eslint/no-unsafe-call
    getFID(safeHandleMetric);
    // eslint-disable-next-line @typescript-eslint/no-unsafe-call
    getLCP(safeHandleMetric);
    // eslint-disable-next-line @typescript-eslint/no-unsafe-call
    getTTFB(safeHandleMetric);
  } catch (error) {
    console.warn('Failed to initialize web vitals monitoring:', error);
  }
}

/**
 * Gets current performance metrics summary
 */
export function getPerformanceMetrics(): {
  metrics: PerformanceMetric[];
  summary: Record<MetricName, {
    latest: number | null;
    average: number;
    rating: 'good' | 'needs-improvement' | 'poor' | 'no-data';
    count: number;
  }>;
} {
  const summary = {} as Record<MetricName, {
    latest: number | null;
    average: number;
    rating: 'good' | 'needs-improvement' | 'poor' | 'no-data';
    count: number;
  }>;

  // Initialize summary for all metrics
  for (const metricName of Object.keys(PERFORMANCE_THRESHOLDS)) {
    const name = metricName as MetricName;
    const metricData = metricsStore.filter(m => m.name === name);
    
    if (metricData.length === 0) {
      summary[name] = {
        latest: undefined,
        average: 0,
        rating: 'no-data',
        count: 0
      };
    } else {
      const latest = metricData.at(-1);
      const average = metricData.reduce((sum, m) => sum + m.value, 0) / metricData.length;
      
      summary[name] = {
        latest: latest?.value ?? undefined,
        average: Math.round(average),
        rating: getRating(name, average),
        count: metricData.length
      };
    }
  }

  return {
    metrics: [...metricsStore],
    summary
  };
}

/**
 * Performance budget checker
 */
export function checkPerformanceBudget(): {
  passed: boolean;
  violations: Array<{
    metric: MetricName;
    value: number;
    threshold: number;
    severity: 'warning' | 'critical';
  }>;
} {
  const { summary } = getPerformanceMetrics();
  const violations: Array<{
    metric: MetricName;
    value: number;
    threshold: number;
    severity: 'warning' | 'critical';
  }> = [];

  for (const [metricName, data] of Object.entries(summary)) {
    const name = metricName as MetricName;
    if (data.latest == undefined) continue;

    const thresholds = PERFORMANCE_THRESHOLDS[name];
    
    if (data.latest > thresholds.needsImprovement) {
      violations.push({
        metric: name,
        value: data.latest,
        threshold: thresholds.needsImprovement,
        severity: 'critical'
      });
    } else if (data.latest > thresholds.good) {
      violations.push({
        metric: name,
        value: data.latest,
        threshold: thresholds.good,
        severity: 'warning'
      });
    }
  }

  return {
    passed: violations.length === 0,
    violations
  };
}

/**
 * Performance optimization suggestions based on metrics
 */
export function getPerformanceOptimizationSuggestions(): Array<{
  metric: MetricName;
  issue: string;
  suggestions: string[];
  priority: 'high' | 'medium' | 'low';
}> {
  const { summary } = getPerformanceMetrics();
  const suggestions: Array<{
    metric: MetricName;
    issue: string;
    suggestions: string[];
    priority: 'high' | 'medium' | 'low';
  }> = [];

  for (const [metricName, data] of Object.entries(summary)) {
    const name = metricName as MetricName;
    if (data.rating === 'poor' || data.rating === 'needs-improvement') {
      switch (name) {
        case 'LCP': {
          suggestions.push({
            metric: name,
            issue: `Largest Contentful Paint is ${data.latest}ms (target: <2500ms)`,
            suggestions: [
              'Optimize images with Next.js Image component',
              'Implement lazy loading for non-critical content',
              'Use CDN for static assets',
              'Optimize server response times',
              'Preload critical resources'
            ],
            priority: 'high'
          });
          break;
        }
        case 'FID': {
          suggestions.push({
            metric: name,
            issue: `First Input Delay is ${data.latest}ms (target: <100ms)`,
            suggestions: [
              'Reduce JavaScript bundle size',
              'Implement code splitting',
              'Use web workers for heavy computations',
              'Optimize third-party scripts',
              'Defer non-critical JavaScript'
            ],
            priority: 'high'
          });
          break;
        }
        case 'CLS': {
          suggestions.push({
            metric: name,
            issue: `Cumulative Layout Shift is ${data.latest} (target: <0.1)`,
            suggestions: [
              'Set explicit dimensions for images and videos',
              'Reserve space for dynamic content',
              'Use CSS aspect-ratio for responsive images',
              'Avoid inserting content above existing content',
              'Use transform animations instead of layout changes'
            ],
            priority: 'medium'
          });
          break;
        }
        case 'FCP': {
          suggestions.push({
            metric: name,
            issue: `First Contentful Paint is ${data.latest}ms (target: <1800ms)`,
            suggestions: [
              'Optimize critical rendering path',
              'Inline critical CSS',
              'Minimize render-blocking resources',
              'Use resource hints (preload, prefetch)',
              'Optimize web fonts loading'
            ],
            priority: 'medium'
          });
          break;
        }
        case 'TTFB': {
          suggestions.push({
            metric: name,
            issue: `Time to First Byte is ${data.latest}ms (target: <800ms)`,
            suggestions: [
              'Optimize server response times',
              'Use CDN for global distribution',
              'Implement server-side caching',
              'Optimize database queries',
              'Use edge computing for dynamic content'
            ],
            priority: 'high'
          });
          break;
        }
      }
    }
  }

  return suggestions.sort((a, b) => {
    const priorityOrder = { high: 3, medium: 2, low: 1 };
    return priorityOrder[b.priority] - priorityOrder[a.priority];
  });
}

import { ExtractedFoodTruckDetails, FoodTruckSchema, MenuCategory, MenuItem } from '../types';
import { ScrapingJobService, FoodTruckService } from '../supabase';
import { DuplicatePreventionService } from '../data-quality/duplicatePrevention';
import { PostgrestError } from '@supabase/supabase-js';

// Helper function to validate input and prepare basic data
export async function validateInputAndPrepare(
  jobId: string,
  extractedTruckData: ExtractedFoodTruckDetails,
  sourceUrl: string
): Promise<{ isValid: boolean; name: string }> {
  // Basic input validation
  if (!validateTruckData(jobId, extractedTruckData)) {
    await ScrapingJobService.updateJobStatus(jobId, 'failed', {
      errors: ['Invalid extracted data received from AI processing step.'],
    });
    return { isValid: false, name: '' };
  }

  if (!sourceUrl) {
    // Log a warning but proceed if sourceUrl is missing, as it might not be critical for all data.
    console.warn(`Job ${jobId}: Missing sourceUrl for food truck data, proceeding without it.`);
  }

  const name = extractedTruckData.name ?? 'Unknown Food Truck'; // Ensure name has a fallback
  console.info(
    `Job ${jobId}: Preparing to create/update food truck: ${name} from ${sourceUrl ?? 'Unknown Source'}`,
  );

  return { isValid: true, name };
}

// Helper function to build truck data schema
export function buildTruckDataSchema(
  extractedTruckData: ExtractedFoodTruckDetails,
  sourceUrl: string,
  name: string
): FoodTruckSchema {
  const currentLocation = buildLocationData(extractedTruckData);

  return {
    name: name,
    description: extractedTruckData.description ?? undefined, // Keep as undefined if null/missing
    current_location: currentLocation,
    scheduled_locations: Array.isArray(extractedTruckData.scheduled_locations)
      ? extractedTruckData.scheduled_locations.map((loc) => ({
          lat: typeof loc.lat === 'number' ? loc.lat : 0,
          lng: typeof loc.lng === 'number' ? loc.lng : 0,
          address: loc.address ?? undefined,
          start_time: loc.start_time ?? undefined,
          end_time: loc.end_time ?? undefined,
          timestamp: new Date().toISOString(),
        }))
      : undefined,
    operating_hours: extractedTruckData.operating_hours == undefined
      ? {
          monday: { closed: true },
          tuesday: { closed: true },
          wednesday: { closed: true },
          thursday: { closed: true },
          friday: { closed: true },
          saturday: { closed: true },
          sunday: { closed: true },
        }
      : {
          monday: extractedTruckData.operating_hours.monday ?? { closed: true },
          tuesday: extractedTruckData.operating_hours.tuesday ?? { closed: true },
          wednesday: extractedTruckData.operating_hours.wednesday ?? { closed: true },
          thursday: extractedTruckData.operating_hours.thursday ?? { closed: true },
          friday: extractedTruckData.operating_hours.friday ?? { closed: true },
          saturday: extractedTruckData.operating_hours.saturday ?? { closed: true },
          sunday: extractedTruckData.operating_hours.sunday ?? { closed: true },
        },
    menu: processMenuData(extractedTruckData),
    contact_info: {
      phone: extractedTruckData.contact_info?.phone ?? undefined,
      email: extractedTruckData.contact_info?.email ?? undefined,
      website: extractedTruckData.contact_info?.website ?? undefined,
    },
    social_media: {
      instagram: extractedTruckData.social_media?.instagram ?? undefined,
      facebook: extractedTruckData.social_media?.facebook ?? undefined,
      twitter: extractedTruckData.social_media?.twitter ?? undefined,
      tiktok: extractedTruckData.social_media?.tiktok ?? undefined,
      yelp: extractedTruckData.social_media?.yelp ?? undefined,
    },
    cuisine_type: Array.isArray(extractedTruckData.cuisine_type)
      ? extractedTruckData.cuisine_type
      : [],
    price_range: extractedTruckData.price_range ?? undefined, // Ensure it's one of the allowed enum values or undefined
    specialties: Array.isArray(extractedTruckData.specialties)
      ? extractedTruckData.specialties
      : [],
    data_quality_score: 0.5, // Default score - confidence_score not available in type
    verification_status: 'pending',
    source_urls: sourceUrl != undefined && sourceUrl !== '' ? [sourceUrl] : [], // Ensure source_urls is always an array
    last_scraped_at: new Date().toISOString(),
  };
}

// Helper function to handle duplicate checking and resolution
export async function handleDuplicateCheck(
  jobId: string,
  truckData: FoodTruckSchema,
  name: string
): Promise<any> {
  // Check for duplicates before creating
  console.info(`Job ${jobId}: Checking for duplicates before creating truck: ${name}`);
  const duplicateCheck = await DuplicatePreventionService.checkForDuplicates(truckData);

  let truck;
  if (duplicateCheck.isDuplicate && duplicateCheck.bestMatch) {
    const { bestMatch } = duplicateCheck;
    console.info(`Job ${jobId}: Found potential duplicate (${Math.round(bestMatch.similarity * 100)}% similarity) with truck: ${bestMatch.existingTruck.name}`);

    if (bestMatch.confidence === 'high' && bestMatch.recommendation === 'merge') {
      // Merge with existing truck
      truck = await DuplicatePreventionService.mergeDuplicates(bestMatch.existingTruck.id, bestMatch.existingTruck.id);
      console.info(`Job ${jobId}: Merged data with existing truck: ${truck.name} (ID: ${truck.id})`);
    } else if (bestMatch.recommendation === 'update') {
      // Update existing truck with new data
      truck = await FoodTruckService.updateTruck(bestMatch.existingTruck.id, truckData);
      console.info(`Job ${jobId}: Updated existing truck: ${truck.name} (ID: ${truck.id})`);
    } else {
      // Create new truck but log the potential duplicate
      truck = await FoodTruckService.createTruck(truckData);
      console.warn(`Job ${jobId}: Created new truck despite potential duplicate (${duplicateCheck.reason})`);
    }
  } else {
    // No duplicates found, create new truck
    truck = await FoodTruckService.createTruck(truckData);
  }

  return truck;
}

// Helper function to finalize job status
export async function finalizeJobStatus(
  jobId: string,
  truck: any,
  sourceUrl: string
): Promise<void> {
  console.info(
    `Job ${jobId}: Successfully created food truck: ${truck.name} (ID: ${truck.id}) from ${sourceUrl ?? 'Unknown Source'}`,
  );

  // Link truck_id back to the scraping job
  await ScrapingJobService.updateJobStatus(jobId, 'completed', {
    completed_at: new Date().toISOString(),
  });
}

// Helper function to validate input data
function validateTruckData(jobId: string, extractedTruckData: ExtractedFoodTruckDetails): boolean {
  if (extractedTruckData == undefined || typeof extractedTruckData !== 'object') {
    console.error(`Job ${jobId}: Invalid extractedTruckData, cannot create/update food truck.`);
    return false;
  }
  return true;
}

// Helper function to build location data
function buildLocationData(extractedTruckData: ExtractedFoodTruckDetails) {
  const locationData = extractedTruckData.current_location ?? {};
  const fullAddress = [
    locationData.address,
    locationData.city,
    locationData.state,
    locationData.zip_code,
  ]
    .filter(Boolean)
    .join(', ');

  return {
    lat: typeof locationData.lat === 'number' ? locationData.lat : 0,
    lng: typeof locationData.lng === 'number' ? locationData.lng : 0,
    address: fullAddress || (locationData.raw_text ?? undefined),
    timestamp: new Date().toISOString(),
  };
}

// Helper function to process menu data
function processMenuData(extractedTruckData: ExtractedFoodTruckDetails): MenuCategory[] {
  if (!Array.isArray(extractedTruckData.menu)) {
    return [];
  }

  return extractedTruckData.menu.map((category: unknown): MenuCategory => {
    const categoryData = category as { items?: unknown[]; category?: string; name?: string };
    const items = (Array.isArray(categoryData.items) ? categoryData.items : []).map(
      (item: unknown): MenuItem => {
        const itemData = item as {
          name?: string;
          description?: string;
          price?: string | number;
          dietary_tags?: string[];
        };
        let price: number | undefined = undefined;
        if (typeof itemData.price === 'number') {
          price = itemData.price;
        } else if (typeof itemData.price === 'string') {
          const parsedPrice = Number.parseFloat(itemData.price.replaceAll(/[^\d.-]/g, ''));
          if (!Number.isNaN(parsedPrice)) {
            price = parsedPrice;
          }
        }
        return {
          name: itemData.name ?? 'Unknown Item',
          description: itemData.description ?? undefined,
          price: price,
          dietary_tags: Array.isArray(itemData.dietary_tags) ? itemData.dietary_tags : [],
        };
      },
    );
    return {
      name: categoryData.category ?? categoryData.name ?? 'Uncategorized',
      items: items,
    };
  });
}

import { firecrawl } from '@/lib/firecrawl';
import { gemini } from '@/lib/gemini';
import { ScrapingJobService } from '@/lib/supabase';
import { ExtractedFoodTruckDetails } from '../types';
import {
  validateInputAndPrepare,
  buildTruckDataSchema,
  handleDuplicateCheck,
  finalizeJobStatus,
} from './pipelineHelpers';

async function handleScraping(targetUrl: string, jobId: string) {
  console.info(`Starting scrape for ${targetUrl}`);
  const scrapeResult = await firecrawl.scrapeFoodTruckWebsite(targetUrl);

  if (!scrapeResult.success || !scrapeResult.data?.markdown) {
    const error = scrapeResult.error ?? 'Scraping failed or markdown content not found';
    await handleJobFailure(jobId, error);
    throw new Error(error);
  }

  console.info(`Scraping successful for ${targetUrl}, proceeding to Gemini extraction.`);
  return scrapeResult.data;
}

async function handleGeminiExtraction(markdown: string, sourceUrl: string, jobId: string) {
  const geminiResult = await gemini.extractFoodTruckDetailsFromMarkdown(markdown, sourceUrl);

  if (!geminiResult.success || !geminiResult.data) {
    const error = geminiResult.error ?? 'Gemini data extraction failed';
    await handleJobFailure(jobId, error);
    throw new Error(error);
  }

  console.info(`Gemini extraction successful for ${sourceUrl}.`);
  return geminiResult.data;
}

async function handleJobFailure(jobId: string, error: string) {
  console.error(`Job ${jobId} failed:`, error);
  try {
    const currentJob = await ScrapingJobService.getJobsByStatus('all').then(jobs =>
      jobs.find(j => j.id === jobId)
    );
    if (currentJob && currentJob.status !== 'failed') {
      await ScrapingJobService.updateJobStatus(jobId, 'failed', { errors: [error] });
    }
  } catch (statusUpdateError) {
    console.error(`Error updating job ${jobId} status to failed:`, statusUpdateError);
  }
}

async function handleRetryLogic(jobId: string) {
  try {
    const job = await ScrapingJobService.incrementRetryCount(jobId);
    if (job && typeof job.retry_count === 'number' && typeof job.max_retries === 'number') {
      if (job.retry_count < job.max_retries) {
        console.info(`Retrying job ${jobId} (attempt ${job.retry_count}/${job.max_retries})`);
        setTimeout(() => {
          void processScrapingJob(jobId);
        }, 5000);
      } else {
        console.warn(`Job ${jobId} reached max retries (${job.max_retries}).`);
      }
    } else {
      console.error(`Job ${jobId}: Could not get valid retry_count or max_retries. Won't attempt retry.`);
    }
  } catch (retryError) {
    console.error(`Error during retry logic for job ${jobId}:`, retryError);
  }
}

export async function processScrapingJob(jobId: string): Promise<void> {
  try {
    const job = await ScrapingJobService.updateJobStatus(jobId, 'running');
    if (!job.target_url) {
      throw new Error('No target URL specified');
    }

    const scrapeData = await handleScraping(job.target_url, jobId);
    const extractedData = await handleGeminiExtraction(scrapeData.markdown, scrapeData.source_url ?? job.target_url, jobId);

    await ScrapingJobService.updateJobStatus(jobId, 'completed', {
      data_collected: extractedData as unknown as Record<string, unknown>,
      completed_at: new Date().toISOString(),
    });

    await createOrUpdateFoodTruck(jobId, extractedData, scrapeData.source_url ?? job.target_url);

    console.info(`Scraping job ${jobId} completed successfully and data processed.`);
  } catch {
    await handleRetryLogic(jobId);
  }
}

export async function createOrUpdateFoodTruck(
  jobId: string,
  extractedTruckData: ExtractedFoodTruckDetails,
  sourceUrl: string,
) {
  try {
    const validation = await validateInputAndPrepare(jobId, extractedTruckData, sourceUrl);
    if (!validation.isValid) {
      return;
    }

    const truckData = buildTruckDataSchema(extractedTruckData, sourceUrl, validation.name);
    const truck = await handleDuplicateCheck(jobId, truckData, validation.name);
    await finalizeJobStatus(jobId, truck, sourceUrl);
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : 'Unknown error';
    console.error(`Job ${jobId}: Error in createOrUpdateFoodTruck from ${sourceUrl ?? 'Unknown Source'}:`, error);
    await handleJobFailure(jobId, `Food truck data processing/saving failed: ${errorMessage}`);
  }
}

/**
 * SOTA Security Audit Logging System
 * Implements comprehensive audit logging for admin actions and security events
 */

import { supabaseAdmin } from '@/lib/supabase';

// Type alias for severity levels to comply with sonarjs/use-type-alias
type SeverityLevel = 'info' | 'warning' | 'error' | 'critical';

export interface AuditLogEntry {
  user_id: string;
  user_email: string;
  action: string;
  resource_type: string;
  resource_id?: string;
  details?: Record<string, unknown>;
  ip_address?: string;
  user_agent?: string;
  session_id?: string;
  timestamp: string;
  severity: SeverityLevel;
}

export interface SecurityEvent {
  event_type: 'login_attempt' | 'login_success' | 'login_failure' | 'logout' | 'permission_denied' | 'data_access' | 'data_modification' | 'admin_action';
  user_id?: string;
  user_email?: string;
  ip_address?: string;
  user_agent?: string;
  details?: Record<string, unknown>;
  severity: SeverityLevel;
}

/**
 * Audit Logger Service
 */
export class AuditLogger {
  /**
   * Log admin action with full audit trail
   */
  static async logAdminAction(
    userId: string,
    userEmail: string,
    action: string,
    resourceType: string,
    resourceId?: string,
    details?: Record<string, unknown>,
    request?: {
      ip?: string;
      userAgent?: string;
      sessionId?: string;
    }
  ): Promise<void> {
    const auditEntry: AuditLogEntry = {
      user_id: userId,
      user_email: userEmail,
      action,
      resource_type: resourceType,
      resource_id: resourceId,
      details,
      ip_address: request?.ip,
      user_agent: request?.userAgent,
      session_id: request?.sessionId,
      timestamp: new Date().toISOString(),
      severity: this.determineSeverity(action, resourceType)
    };

    await this.writeAuditLog(auditEntry);
    
    // Log to console for immediate monitoring
    console.info('Admin Action Audit:', {
      user: userEmail,
      action,
      resource: (resourceId === undefined) ? resourceType : `${resourceType}:${resourceId}`,
      timestamp: auditEntry.timestamp
    });
  }

  /**
   * Log security event
   */
  static async logSecurityEvent(event: SecurityEvent): Promise<void> {
    const logEntry = {
      event_type: event.event_type,
      user_id: event.user_id,
      user_email: event.user_email,
      ip_address: event.ip_address,
      user_agent: event.user_agent,
      details: event.details,
      severity: event.severity,
      timestamp: new Date().toISOString()
    };

    try {
      if (supabaseAdmin) {
        await supabaseAdmin
          .from('security_events')
          .insert(logEntry);
      }
    } catch (error) {
      console.error('Failed to log security event:', error);
    }

    // Always log security events to console
    console.info('Security Event:', logEntry);

    // Alert on critical security events
    if (event.severity === 'critical' || event.severity === 'error') {
      console.warn('SECURITY ALERT:', logEntry);
    }
  }

  /**
   * Log authentication events
   */
  static async logAuthEvent(
    eventType: 'login_attempt' | 'login_success' | 'login_failure' | 'logout',
    userEmail?: string,
    userId?: string,
    request?: {
      ip?: string;
      userAgent?: string;
    },
    details?: Record<string, unknown>
  ): Promise<void> {
    const severity = eventType === 'login_failure' ? 'warning' : 'info';
    
    await this.logSecurityEvent({
      event_type: eventType,
      user_id: userId,
      user_email: userEmail,
      ip_address: request?.ip,
      user_agent: request?.userAgent,
      details,
      severity
    });
  }

  /**
   * Log data access events
   */
  static async logDataAccess(
    userId: string,
    userEmail: string,
    resourceType: string,
    resourceId?: string,
    action: 'read' | 'search' | 'export' | 'admin_access' = 'read',
    request?: {
      ip?: string;
      userAgent?: string;
    }
  ): Promise<void> {
    await this.logSecurityEvent({
      event_type: 'data_access',
      user_id: userId,
      user_email: userEmail,
      ip_address: request?.ip,
      user_agent: request?.userAgent,
      details: {
        resource_type: resourceType,
        resource_id: resourceId,
        action
      },
      severity: 'info'
    });
  }

  /**
   * Write audit log to database
   */
  private static async writeAuditLog(entry: AuditLogEntry): Promise<void> {
    try {
      if (supabaseAdmin) {
        await supabaseAdmin
          .from('audit_logs')
          .insert(entry);
      }
    } catch (error) {
      console.error('Failed to write audit log:', error);
      // Don't throw - audit logging should not break application flow
    }
  }

  /**
   * Determine severity based on action and resource type
   */
  private static determineSeverity(action: string, resourceType: string): SeverityLevel {
    // Critical actions
    if (action.includes('delete') || action.includes('remove')) {
      return 'critical';
    }
    
    // Warning actions
    if (action.includes('update') || action.includes('modify') || action.includes('change')) {
      return 'warning';
    }
    
    // Admin-specific actions
    if (resourceType === 'user' || resourceType === 'admin' || resourceType === 'system') {
      return 'warning';
    }
    
    // Default to info
    return 'info';
  }

  /**
   * Get audit logs for a specific user
   */
  static async getUserAuditLogs(
    userId: string,
    limit: number = 100,
    offset: number = 0
  ): Promise<AuditLogEntry[]> {
    try {
      if (!supabaseAdmin) {
        return [];
      }

      const { data, error } = await supabaseAdmin
        .from('audit_logs')
        .select('*')
        .eq('user_id', userId)
        .order('timestamp', { ascending: false })
        .range(offset, offset + limit - 1);

      if (error) {
        console.error('Failed to fetch user audit logs:', error);
        return [];
      }

      // eslint-disable-next-line @typescript-eslint/no-unsafe-return
      return data ?? [];
    } catch (error) {
      console.error('Error fetching user audit logs:', error);
      return [];
    }
  }

  /**
   * Get recent security events
   */
  static async getRecentSecurityEvents(
    hours: number = 24,
    severity?: SeverityLevel
  ): Promise<Record<string, unknown>[]> {
    try {
      if (!supabaseAdmin) {
        return [];
      }

      const startTime = new Date();
      startTime.setHours(startTime.getHours() - hours);

      let query = supabaseAdmin
        .from('security_events')
        .select('*')
        .gte('timestamp', startTime.toISOString())
        .order('timestamp', { ascending: false });

      if (severity) {
        query = query.eq('severity', severity);
      }

      const { data, error } = await query.limit(500);

      if (error) {
        console.error('Failed to fetch security events:', error);
        return [];
      }

      // eslint-disable-next-line @typescript-eslint/no-unsafe-return
      return data ?? [];
    } catch (error) {
      console.error('Error fetching security events:', error);
      return [];
    }
  }
}

/**
 * Security monitoring utilities
 */
export const SecurityMonitor = {
  /**
   * Check for suspicious activity patterns
   */
  async checkSuspiciousActivity(userId: string): Promise<{
    suspicious: boolean;
    reasons: string[];
    riskLevel: 'low' | 'medium' | 'high';
  }> {
    const reasons: string[] = [];
    let riskLevel: 'low' | 'medium' | 'high' = 'low';

    try {
      // Check recent failed login attempts
      const recentEvents = await AuditLogger.getRecentSecurityEvents(1, 'warning');
      const failedLogins = recentEvents.filter(
         
        event => event.event_type === 'login_failure' && event.user_id === userId
      );

      if (failedLogins.length > 5) {
        reasons.push('Multiple failed login attempts');
        riskLevel = 'high';
      } else if (failedLogins.length > 2) {
        reasons.push('Recent failed login attempts');
        riskLevel = 'medium';
      }

      // Check for unusual access patterns
      const auditLogs = await AuditLogger.getUserAuditLogs(userId, 50);
      const recentActions = auditLogs.filter(
        log => new Date(log.timestamp) > new Date(Date.now() - 60 * 60 * 1000) // Last hour
      );

      if (recentActions.length > 20) {
        reasons.push('High activity volume');
        riskLevel = riskLevel === 'high' ? 'high' : 'medium';
      }

      return {
        suspicious: reasons.length > 0,
        reasons,
        riskLevel
      };
    } catch (error) {
      console.error('Error checking suspicious activity:', error);
      return {
        suspicious: false,
        reasons: [],
        riskLevel: 'low'
      };
    }
  },
};

/**
 * SOTA Rate Limiting Implementation
 * Prevents brute force attacks and API abuse with intelligent rate limiting
 */

import { NextResponse } from 'next/server'; // Added import for NextResponse

interface RateLimitEntry {
  count: number;
  resetTime: number;
  blocked: boolean;
  blockUntil?: number;
}

// In-memory rate limit store (in production, use Redis)
const rateLimitStore = new Map<string, RateLimitEntry>();

// Rate limit configurations for different endpoints
export const RATE_LIMIT_CONFIGS = {
  // Authentication endpoints - strict limits
  auth: {
    windowMs: 15 * 60 * 1000, // 15 minutes
    maxRequests: 5, // 5 attempts per 15 minutes
    blockDurationMs: 30 * 60 * 1000, // Block for 30 minutes
    skipSuccessfulRequests: true
  },
  // API endpoints - moderate limits
  api: {
    windowMs: 60 * 1000, // 1 minute
    maxRequests: 60, // 60 requests per minute
    blockDurationMs: 5 * 60 * 1000, // Block for 5 minutes
    skipSuccessfulRequests: false
  },
  // Admin endpoints - very strict limits
  admin: {
    windowMs: 60 * 1000, // 1 minute
    maxRequests: 20, // 20 requests per minute
    blockDurationMs: 15 * 60 * 1000, // Block for 15 minutes
    skipSuccessfulRequests: false
  }
} as const;

export type RateLimitType = keyof typeof RATE_LIMIT_CONFIGS;

/**
 * Rate Limiter Service
 */
export class RateLimiter {
  // Extract logic from checkRateLimit to reduce function size
  private static isBlocked(entry: RateLimitEntry | undefined, now: number): boolean {
    return Boolean(entry && entry.blocked && entry.blockUntil != undefined && now < entry.blockUntil);
  }

  private static resetEntry(entry: RateLimitEntry, now: number, config: typeof RATE_LIMIT_CONFIGS[RateLimitType]): void {
    entry.count = 0;
    entry.resetTime = now + config.windowMs;
    entry.blocked = false;
    entry.blockUntil = undefined;
  }

  /**
   * Check if request should be rate limited
   */
  static checkRateLimit(
    identifier: string,
    type: RateLimitType = 'api'
  ): {
    allowed: boolean;
    remaining: number;
    resetTime: number;
    retryAfter?: number;
  } {
    const config = RATE_LIMIT_CONFIGS[type];
    const now = Date.now();
    const key = `${type}:${identifier}`;
    this.cleanupExpiredEntries();
    let entry = rateLimitStore.get(key);
    if (!entry) {
      entry = { count: 0, resetTime: now + config.windowMs, blocked: false };
      rateLimitStore.set(key, entry);
    }
    if (this.isBlocked(entry, now)) {
      return {
        allowed: false,
        remaining: 0,
        resetTime: entry.resetTime,
        retryAfter: Math.ceil((entry.blockUntil! - now) / 1000)
      };
    }
    if (now >= entry.resetTime) {
      this.resetEntry(entry, now, config);
    }
    if (entry.count >= config.maxRequests) {
      entry.blocked = true;
      entry.blockUntil = now + config.blockDurationMs;
      return {
        allowed: false,
        remaining: 0,
        resetTime: entry.resetTime,
        retryAfter: Math.ceil(config.blockDurationMs / 1000)
      };
    }
    entry.count++;
    rateLimitStore.set(key, entry);
    return {
      allowed: true,
      remaining: config.maxRequests - entry.count,
      resetTime: entry.resetTime
    };
  }
  
  /**
   * Record successful request (for auth endpoints)
   */
  static recordSuccess(identifier: string, type: RateLimitType = 'api'): void {
    const config = RATE_LIMIT_CONFIGS[type];
    if (!config.skipSuccessfulRequests) return;
    
    const key = `${type}:${identifier}`;
    const entry = rateLimitStore.get(key);
    
    if (entry) {
      // Reset counter on successful auth
      entry.count = 0;
      entry.blocked = false;
      entry.blockUntil = undefined;
      rateLimitStore.set(key, entry);
    }
  }
  
  /**
   * Get rate limit status without incrementing
   */
  static getStatus(
    identifier: string,
    type: RateLimitType = 'api'
  ): {
    remaining: number;
    resetTime: number;
    blocked: boolean;
    retryAfter?: number;
  } {
    const config = RATE_LIMIT_CONFIGS[type];
    const now = Date.now();
    const key = `${type}:${identifier}`;
    const entry = rateLimitStore.get(key);
    
    if (!entry) {
      return {
        remaining: config.maxRequests,
        resetTime: now + config.windowMs,
        blocked: false
      };
    }
    
    // Check if blocked
    if (entry.blocked && (entry.blockUntil != undefined) && now < entry.blockUntil) { // Changed != undefined to != null
      return {
        remaining: 0,
        resetTime: entry.resetTime,
        blocked: true,
        retryAfter: Math.ceil((entry.blockUntil - now) / 1000)
      };
    }
    
    // Check if window expired
    if (now >= entry.resetTime) {
      return {
        remaining: config.maxRequests,
        resetTime: now + config.windowMs,
        blocked: false
      };
    }
    
    return {
      remaining: Math.max(0, config.maxRequests - entry.count),
      resetTime: entry.resetTime,
      blocked: false
    };
  }
  
  /**
   * Clean up expired entries to prevent memory leaks
   */
  private static cleanupExpiredEntries(): void {
    const now = Date.now();
    
    for (const [key, entry] of rateLimitStore.entries()) {
      // Remove entries that are expired and not blocked
      if (now >= entry.resetTime && (!entry.blocked || (entry.blockUntil == undefined) || now >= entry.blockUntil)) { // Changed == undefined to == null
        rateLimitStore.delete(key);
      }
    }
  }
  
  /**
   * Clear all rate limit data for an identifier
   */
  static clearLimits(identifier: string, type?: RateLimitType): void {
    if (type) {
      const key = `${type}:${identifier}`;
      rateLimitStore.delete(key);
    } else {
      // Clear all types for this identifier
      for (const limitType of Object.keys(RATE_LIMIT_CONFIGS) as RateLimitType[]) { // Added type assertion
        const key = `${limitType}:${identifier}`;
        rateLimitStore.delete(key);
      }
    }
  }
  
  /**
   * Get rate limit statistics
   */
  static getStats(): {
    totalEntries: number;
    blockedEntries: number;
    entriesByType: Record<string, number>;
  } {
    const stats = {
      totalEntries: rateLimitStore.size,
      blockedEntries: 0,
      entriesByType: {} as Record<string, number>
    };
    
    for (const [key, entry] of rateLimitStore.entries()) {
      const type = key.split(':')[0];
      stats.entriesByType[type] = (stats.entriesByType[type] ?? 0) + 1;
      
      if (entry.blocked) {
        stats.blockedEntries++;
      }
    }
    
    return stats;
  }
}

/**
 * Utility function to get client identifier from request
 */
export function getClientIdentifier(request: Request): string {
  // Try to get IP address from various headers
  const forwarded = request.headers.get('x-forwarded-for');
  const realIp = request.headers.get('x-real-ip');
  const cfConnectingIp = request.headers.get('cf-connecting-ip');
  
  // Use the first available IP
  const ip = (forwarded?.split(',')[0]?.trim() ?? realIp ?? cfConnectingIp) ?? 'unknown';
  
  // Include user agent for more specific identification
  const userAgent = request.headers.get('user-agent') ?? 'unknown';
  
  // Create a hash of IP + User Agent for better identification
  return `${ip}:${userAgent.slice(0, 50)}`;
}

/**
 * Rate limiting middleware for API routes
 */
export function withRateLimit(
  handler: (request: Request) => Promise<Response>,
  type: RateLimitType = 'api'
) {
  return async (request: Request): Promise<Response> => {
    const identifier = getClientIdentifier(request);
    const result = RateLimiter.checkRateLimit(identifier, type);
    
    if (result.allowed === false) {
      const headers = new Headers({
        'X-RateLimit-Limit': RATE_LIMIT_CONFIGS[type].maxRequests.toString(),
        'X-RateLimit-Remaining': '0',
        'X-RateLimit-Reset': new Date(result.resetTime).toISOString(),
      });
      
      if (result.retryAfter) {
        headers.set('Retry-After', result.retryAfter.toString());
      }
      
      return new NextResponse('Too Many Requests', { status: 429, headers });
    }
    
    const response = await handler(request);
    
    // Update headers on successful requests if not skipped
    if (RATE_LIMIT_CONFIGS[type].skipSuccessfulRequests === false) {
      const status = RateLimiter.getStatus(identifier, type);
      response.headers.set('X-RateLimit-Limit', RATE_LIMIT_CONFIGS[type].maxRequests.toString());
      response.headers.set('X-RateLimit-Remaining', status.remaining.toString());
      response.headers.set('X-RateLimit-Reset', new Date(status.resetTime).toISOString());
    }
    
    return response;
  };
}

