import React from 'react';
import { FoodTruckService, type FoodTruck, type OperatingHours } from '@/lib/supabase';
import {
  formatQualityScore,
  categorizeQualityScore,
  getQualityBadgeClasses,
  type QualityCategory
} from '@/lib/utils/dataQualityFormatters';
import { BasicInfoCard } from '@/components/admin/food-trucks/detail/BasicInfoCard';
import { ContactInfoCard } from '@/components/admin/food-trucks/detail/ContactInfoCard';
import { DataQualityCard } from '@/components/admin/food-trucks/detail/DataQualityCard';
import { LocationInfoCard } from '@/components/admin/food-trucks/detail/LocationInfoCard';
import { OperatingHoursCard } from '@/components/admin/food-trucks/detail/OperatingHoursCard';
import { RatingsReviewsCard } from '@/components/admin/food-trucks/detail/RatingsReviewsCard';
import { TruckDetailHeader } from '@/components/admin/food-trucks/detail/TruckDetailHeader';
import { TruckNotFound } from '@/components/admin/food-trucks/detail/TruckNotFound';

interface FoodTruckDetailPageProps {
  readonly params: {
    readonly id: string;
  };
}

interface TruckProps {
  truck: FoodTruck;
}

export default async function FoodTruckDetailPage({ params }: FoodTruckDetailPageProps) {
  const truck = await FoodTruckService.getTruckById(params.id);

  if (truck == undefined) {
    return <TruckNotFound />;
  }

  const qualityCategory: QualityCategory = categorizeQualityScore(truck.data_quality_score);
  const badgeClasses: string = getQualityBadgeClasses(truck.data_quality_score);

  return (
    <div className="flex flex-col gap-6">
      <TruckDetailHeader truck={truck} badgeClasses={badgeClasses} qualityCategory={qualityCategory} />

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <BasicInfoCard truck={truck} />
        <ContactInfoCard truck={truck} />
        <LocationInfoCard truck={truck} />
        <OperatingHoursCard truck={truck as { operating_hours?: Record<string, { closed?: boolean; open?: string; close?: string }> }} />
        <RatingsReviewsCard truck={truck as { average_rating?: number; review_count?: number }} />
      </div>

      <DataQualityCard truck={truck} qualityCategory={qualityCategory} />
    </div>
  );
}

import React from 'react';
import {
  ScrapingJobService,
  DataProcessingService,
  ScrapingJob,
  DataProcessingQueue,
} from '@/lib/supabase';
import { PipelineStatisticsCards } from '@/components/admin/pipeline/PipelineStatisticsCards';
import { RecentScrapingJobsTable } from '@/components/admin/pipeline/RecentScrapingJobsTable';
import { FailedProcessingQueueTable } from '@/components/admin/pipeline/FailedProcessingQueueTable';

async function getPipelineData() {
  const pendingScrapingJobs = await ScrapingJobService.getJobsByStatus('pending');
  const runningScrapingJobs = await ScrapingJobService.getJobsByStatus('running');
  const completedScrapingJobs = await ScrapingJobService.getJobsByStatus('completed');
  const failedScrapingJobs = await ScrapingJobService.getJobsByStatus('failed');

  const pendingProcessingQueue = await DataProcessingService.getQueueByStatus('pending');
  const processingProcessingQueue = await DataProcessingService.getQueueByStatus('processing');
  const completedProcessingQueue = await DataProcessingService.getQueueByStatus('completed');
  const failedProcessingQueue = await DataProcessingService.getQueueByStatus('failed');

  return {
    scrapingJobs: {
      pending: pendingScrapingJobs,
      running: runningScrapingJobs,
      completed: completedScrapingJobs,
      failed: failedScrapingJobs,
    },
    processingQueue: {
      pending: pendingProcessingQueue,
      processing: processingProcessingQueue,
      completed: completedProcessingQueue,
      failed: failedProcessingQueue,
    },
  };
}

export default async function PipelineMonitoringPage() {
  const { scrapingJobs, processingQueue } = await getPipelineData();

  return (
    <div className="flex flex-col gap-4">
      <h1 className="text-2xl font-bold">Pipeline Monitoring</h1>

      <PipelineStatisticsCards scrapingJobs={scrapingJobs} processingQueue={processingQueue} />

      <RecentScrapingJobsTable scrapingJobs={scrapingJobs} />

      <FailedProcessingQueueTable processingQueue={processingQueue} />
    </div>
  );
}

/*
  Test Pipeline Admin Page

  Purpose:
  This page provides an interface for manually testing the entire data pipeline,
  from web scraping with Firecrawl, through data extraction with Gemini,
  to data preparation and (optional) saving to Supabase. It allows for step-by-step
  inspection of outputs from each stage, aiding in debugging, prompt engineering,
  and validation of the pipeline's components.

  How to Use:
  1. Input a url to test the full scraping and processing flow.
  2. Alternatively, select "Use Raw Text Input Instead" and paste Markdown/html
     directly into the textarea to test only the Gemini processing stage onwards.
  3. Use the "Dry Run" checkbox to control whether data is actually saved to
     Supabase or if the save operation is just simulated. By default, it's a dry run.
  4. Click "Run Test" to execute the pipeline with the provided inputs.
  5. Results from each stage (Firecrawl, Gemini, Supabase) will be displayed in tabs,
     including status, raw outputs, processed data, errors, and relevant metadata
     (like prompts sent to Gemini or tokens used).

  Automated Testing:
  As part of a comprehensive testing strategy, placeholder files for automated unit tests
  (e.g., `firecrawl.test.ts`, `gemini.test.ts`, `pipelineProcessor.test.ts`) have been
  added to the `lib/` directory. These outline future tests to be implemented using a
  framework like Jest or Vitest.
*/
'use client';

/*
Admin Dashboard Architecture Proposal (wbs 3.1.2)
--------------------------------------------------
Recommended Approach: Sub-domain or separate route within the existing Next.js app, with robust access control.

- Security: Use Supabase rls and custom roles to restrict access. Consider deploying the admin dashboard at /admin or on a sub-domain (admin.yoursite.com) for separation.
- Separation of Concerns: Keep admin components, logic, and styles in a dedicated /admin directory. Avoid mixing admin and user-facing code.
- Maintainability: Modularize admin features (data editing, pipeline monitoring, quality management) as separate components/pages.
- Authentication: Require login with admin role for all admin routes. Use Supabase Auth or a similar provider.
- Deployment: Can be deployed as part of the main app or as a separate Next.js app if stricter separation is needed in the future.
*/

import * as React from 'react';
import { useState, FormEvent } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Checkbox } from '@/components/ui/checkbox';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { StageResult, TestPipelineResults } from '@/lib/types';
import { StageResultCard } from '@/components/test-pipeline/StageResultCard';
import { TestPipelineForm } from '@/components/test-pipeline/TestPipelineForm';
import { ErrorDisplay } from '@/components/test-pipeline/ErrorDisplay';
import { TestResultsDisplay } from '@/components/test-pipeline/TestResultsDisplay';
import { submitTestPipeline } from '@/components/test-pipeline/TestPipelineSubmitHandler';

// Helper function extracted from TestPipelinePage to handle form submission
async function handleTestPipelineSubmit(
  event: FormEvent<HTMLFormElement>,
  useRawText: boolean,
  url: string,
  rawText: string,
  isDryRun: boolean,
  setIsLoading: (loading: boolean) => void,
  setResults: (results: TestPipelineResults | undefined) => void,
  setError: (error: string | undefined) => void
) {
  event.preventDefault();
  setIsLoading(true);
  setResults(undefined);
  setError(undefined);

  try {
    const data = await submitTestPipeline({ useRawText, url, rawText, isDryRun });
    setResults(data);
  } catch (error_) {
    const errorMessage = error_ instanceof Error ? error_.message : 'An unknown error occurred';
    setError(errorMessage);
    setResults({ error: errorMessage });
  } finally {
    setIsLoading(false);
  }
}

// Helper function to render stage results
function renderStageResultHelper(stageName: string, result?: StageResult) {
  return <StageResultCard stageName={stageName} result={result} />;
}

export default function TestPipelinePage() {
  const [url, setUrl] = useState<string>('');
  const [rawText, setRawText] = useState<string>('');
  const [useRawText, setUseRawText] = useState<boolean>(false);
  const [isDryRun, setIsDryRun] = useState<boolean>(true);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [results, setResults] = useState<TestPipelineResults | undefined>();
  const [error, setError] = useState<string | undefined>();

  const handleSubmit = (event: FormEvent<HTMLFormElement>) => {
    void handleTestPipelineSubmit(
      event,
      useRawText,
      url,
      rawText,
      isDryRun,
      setIsLoading,
      setResults,
      setError
    );
  };

  return (
    <div className="container mx-auto p-4">
      <TestPipelineForm
        url={url}
        setUrl={setUrl}
        rawText={rawText}
        setRawText={setRawText}
        useRawText={useRawText}
        setUseRawText={setUseRawText}
        isDryRun={isDryRun}
        setIsDryRun={setIsDryRun}
        isLoading={isLoading}
        onSubmit={handleSubmit}
      />
      <ErrorDisplay error={error} />
      <TestResultsDisplay results={results} renderStageResult={renderStageResultHelper} />
    </div>
  );
}

import React from 'react';
