interface UserDisplayData {
  id: string;
  email: string | undefined;
  created_at: string;
  last_sign_in_at: string | undefined;
  role: string;
}

async function getUsersData(): Promise<UserDisplayData[]> {
  if (!supabaseAdmin) {
    throw new Error('Supabase admin client not available');
  }

  const { data, error } = await supabaseAdmin.auth.admin.listUsers();

  if (error !== undefined) {
    console.error('Error fetching users:', error);
    return [];
  }

  const users: User[] = data.users ?? [];

  interface Profile {
    id: string;
    role: string;
  }

  // Fetch profiles to get roles
  const { data: profiles, error: profilesError } = (await supabaseAdmin
    .from('profiles')
    .select('id, role')) as { data: Profile[] | null; error: PostgrestError | null };

  if (profilesError !== undefined) {
    console.error('Error fetching profiles:', profilesError);
    // Continue with users data even if profiles fetch fails
  }

  const profilesMap = new Map<string, string>(profiles?.map((p) => [p.id, p.role]) ?? []);

  return users.map((user) => ({
    id: user.id,
    email: user.email,
    created_at: user.created_at,
    last_sign_in_at: user.last_sign_in_at,
    role: profilesMap.get(user.id) ?? 'user', // Default to 'user' if no profile role
  })) as UserDisplayData[];
}

function PageHeader() {
  return (
    <div className="flex items-center justify-between">
      <h1 className="text-2xl font-bold">User Management</h1>
      <Button asChild>
        <Link href="/admin/users/new">
          <UserPlus className="mr-2 h-4 w-4" />
          Add New User
        </Link>
      </Button>
    </div>
  );
}

function UserListCard({ users }: { users: UserDisplayData[] }) {
  return (
    <Card>
      <CardHeader>
        <CardTitle>System Users</CardTitle>
        <CardDescription>
          Manage user accounts and their roles. ({users.length} total)
        </CardDescription>
      </CardHeader>
      <CardContent>
        <UserTable users={users} />
      </CardContent>
    </Card>
  );
}

export default async function UserManagementPage() {
  const users = await getUsersData();

  return (
    <div className="flex flex-col gap-4">
      <PageHeader />
      <UserListCard users={users} />
    </div>
  );
}

import { NextRequest, NextResponse } from 'next/server';
import {
  verifyAdminAccess,
  handlePostRequest,
  handleGetStatus,
  handleGetSchedules,
  handleGetHistory,
  handleGetPreview,
  handleGetDefault,
} from '@/lib/api/admin/automated-cleanup/handlers';

/**
 * SOTA Automated Data Cleanup API
 * 
 * Provides scheduled and on-demand data cleanup operations
 * with comprehensive monitoring and reporting capabilities
 * 
 * GET /api/admin/automated-cleanup - Get cleanup status and schedule
 * POST /api/admin/automated-cleanup - Run cleanup operations
 */

export async function GET(request: NextRequest): Promise<NextResponse> {
  try {
    const hasAccess = await verifyAdminAccess(request);
    if (!hasAccess) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const { searchParams } = new URL(request.url);
    const action = searchParams.get('action');

    switch (action) {
      case 'status': {
        return await handleGetStatus();
      }
      case 'schedules': {
        return await handleGetSchedules();
      }
      case 'history': {
        return await handleGetHistory(searchParams);
      }
      case 'preview': {
        return await handleGetPreview(searchParams);
      }
      default: {
        return await handleGetDefault();
      }
    }
  } catch (error) {
    console.error('Automated cleanup GET error:', error);
    return NextResponse.json({
      success: false,
      error: 'Failed to process cleanup request',
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 });
  }
}

export async function POST(request: NextRequest): Promise<NextResponse> {
  try {
    const hasAccess = await verifyAdminAccess(request);
    if (!hasAccess) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const body = await request.json();
    return await handlePostRequest(body);
  } catch (error) {
    console.error('Automated cleanup POST error:', error);
    return NextResponse.json({
      success: false,
      error: 'Failed to process cleanup request',
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 });
  }
}

