import React from 'react';
import { AlertTriangle, CheckCircle, Clock, TrendingUp, TrendingDown } from 'lucide-react';
import { StatusMetric } from './useSystemMetrics';

export const getStatusColor = (status: string) => {
  switch (status) {
    case 'healthy': { return 'text-green-600 bg-green-50 border-green-200';
    }
    case 'warning': { return 'text-yellow-600 bg-yellow-50 border-yellow-200';
    }
    case 'error': { return 'text-red-600 bg-red-50 border-red-200';
    }
    default: { return 'text-gray-600 bg-gray-50 border-gray-200';
    }
  }
};

export const getStatusIcon = (status: string) => {
  switch (status) {
    case 'healthy': { return <CheckCircle className="h-4 w-4 text-green-600" />;
    }
    case 'warning': { return <AlertTriangle className="h-4 w-4 text-yellow-600" />;
    }
    case 'error': { return <AlertTriangle className="h-4 w-4 text-red-600" />;
    }
    default: { return <Clock className="h-4 w-4 text-gray-600" />;
    }
  }
};

export const getTrendIcon = (trend?: string) => {
  switch (trend) {
    case 'up': { return <TrendingUp className="h-3 w-3 text-green-600" />;
    }
    case 'down': { return <TrendingDown className="h-3 w-3 text-red-600" />;
    }
    default: { return;
    }
  }
};

import React from 'react';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';

interface SystemAlert {
  id: string;
  type: 'info' | 'warning' | 'error' | 'critical';
  message: string;
  timestamp: string;
  acknowledged?: boolean;
}

interface SystemAlertsProps {
  alerts: SystemAlert[];
  showDetails: boolean;
  onToggleDetails: () => void;
  onAcknowledgeAlert: (alertId: string) => void;
}

export function SystemAlerts({ alerts, showDetails, onToggleDetails, onAcknowledgeAlert }: SystemAlertsProps) {
  if (alerts.length === 0) return null;

  return (
    <div className="mt-4">
      <h4 className="text-sm font-medium text-gray-900 mb-2">Recent Alerts</h4>
      <div className="space-y-2">
        {alerts.slice(0, 3).map((alert) => (
          <div
            key={alert.id}
            className={`p-2 rounded border-l-4 ${(() => {
              if (alert.type === 'critical') return 'border-l-red-500 bg-red-50';
              if (alert.type === 'error') return 'border-l-red-400 bg-red-50';
              return 'border-l-yellow-400 bg-yellow-50';
            })()} ${alert.acknowledged === true ? 'opacity-50' : ''}`}
          >
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-2">
                <Badge variant={alert.type === 'critical' ? 'destructive' : 'secondary'}>
                  {alert.type}
                </Badge>
                <span className="text-sm">{alert.message}</span>
              </div>
              {alert.acknowledged !== true && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => onAcknowledgeAlert(alert.id)}
                >
                  Acknowledge
                </Button>
              )}
            </div>
            <p className="text-xs text-gray-500 mt-1">
              {new Date(alert.timestamp).toLocaleString()}
            </p>
          </div>
        ))}
      </div>
      {alerts.length > 3 && (
        <Button
          variant="outline"
          size="sm"
          className="mt-2"
          onClick={onToggleDetails}
        >
          {showDetails ? 'Hide' : 'Show'} {alerts.length - 3} more alerts
        </Button>
      )}
    </div>
  );
}

