import { cn } from '@/lib/utils';
import { useTooltipLabel } from './chart/useTooltipLabel';
import { TooltipIndicator } from './chart/TooltipIndicator';
import { TooltipItemContent } from './chart/TooltipItemContent';

// Format: { THEME_NAME: CSS_SELECTOR }
const THEMES = { light: '', dark: '.dark' } as const;

export type ChartConfig = {
  [k in string]: {
    label?: React.ReactNode;
    icon?: React.ComponentType;
  } & (
    | { color?: string; theme?: never }
    | { color?: never; theme: Record<keyof typeof THEMES, string> }
  );
};

type ChartContextProps = {
  config: ChartConfig;
};

const ChartContext = React.createContext<ChartContextProps | undefined>(undefined);

function useChart() {
  const context = React.useContext(ChartContext);

  if (context === undefined) {
    throw new Error('useChart must be used within a <ChartContainer />');
  }

  return context;
}

const ChartContainer = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<'div'> & {
    config: ChartConfig;
    children: React.ComponentProps<typeof RechartsPrimitive.ResponsiveContainer>['children'];
  }
>(({ id, className, children, config, ...props }, ref) => {
  const uniqueId = React.useId();
  const chartId = `chart-${id ?? uniqueId.replaceAll(':', '')}`;

  return (
    <ChartContext.Provider value={{ config }}>
      <div
        data-chart={chartId}
        ref={ref}
        className={cn(
          "flex aspect-video justify-center text-xs [&_.recharts-cartesian-axis-tick_text]:fill-muted-foreground [&_.recharts-cartesian-grid_line[stroke='#ccc']]:stroke-border/50 [&_.recharts-curve.recharts-tooltip-cursor]:stroke-border [&_.recharts-dot[stroke='#fff']]:stroke-transparent [&_.recharts-layer]:outline-none [&_.recharts-polar-grid_[stroke='#ccc']]:stroke-border [&_.recharts-radial-bar-background-sector]:fill-muted [&_.recharts-rectangle.recharts-tooltip-cursor]:fill-muted [&_.recharts-reference-line_[stroke='#ccc']]:stroke-border [&_.recharts-sector[stroke='#fff']]:stroke-transparent [&_.recharts-sector]:outline-none [&_.recharts-surface]:outline-none",
          className,
        )}
        {...props}
      >
        <ChartStyle id={chartId} config={config} />
        <RechartsPrimitive.ResponsiveContainer>{children}</RechartsPrimitive.ResponsiveContainer>
      </div>
    </ChartContext.Provider>
  );
});
ChartContainer.displayName = 'Chart';

const ChartStyle = ({ id, config }: { id: string; config: ChartConfig }) => {
  const colorConfig = Object.entries(config).filter(([_, itemConfig]) => (itemConfig.theme ?? itemConfig.color) !== undefined);
  if (colorConfig.length === 0) {
    return null;
  }

  return (
    <style
      dangerouslySetInnerHTML={{
        __html: Object.entries(THEMES)
          .map(
            ([theme, prefix]) => `
${prefix} [data-chart=${id}] {
${colorConfig
  .map(([key, itemConfig]) => {
    const color = itemConfig.theme?.[theme as keyof typeof itemConfig.theme] ?? itemConfig.color;
    return color !== undefined && color !== '' ? `  --color-${key}: ${color};` : undefined;
  })
  .join('\n')}
}
`,
          )
          .join('\n'),
      }}
    />
  );
};

const ChartTooltip = RechartsPrimitive.Tooltip;

const ChartTooltipContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof RechartsPrimitive.Tooltip> &
    React.ComponentProps<'div'> & {
      hideLabel?: boolean;
      hideIndicator?: boolean;
      indicator?: 'line' | 'dot' | 'dashed';
      nameKey?: string;
      labelKey?: string;
    }
>(
  (
    {
      active,
      payload,
      className,
      indicator = 'dot',
      hideLabel = false,
      hideIndicator = false,
      label,
      labelFormatter,
      labelClassName,
      formatter,
      color,
      nameKey,
      labelKey,
    },
    ref,
  ) => {
    const { config } = useChart();

    const tooltipLabel = useTooltipLabel({
      hideLabel,
      payload,
      label,
      labelFormatter,
      labelClassName,
      config,
      labelKey
    });

    if (active !== true || (payload?.length ?? 0) === 0) {
      return null;
    }

    const nestLabel = payload.length === 1 && indicator !== 'dot';

    return (
      <div
        ref={ref}
        className={cn(
          'grid min-w-[8rem] items-start gap-1.5 rounded-lg border border-border/50 bg-background px-2.5 py-1.5 text-xs shadow-xl',
          className,
        )}
      >
        {nestLabel ? undefined : tooltipLabel}
        <div className="grid gap-1.5">
          {(payload as RechartsPrimitive.TooltipPayload[]).map((item, index) => {
            const itemData = item as { name?: string; dataKey?: string; payload?: unknown; color?: string; value?: number };
            const key = `${nameKey ?? itemData.name ?? itemData.dataKey ?? 'value'}`;
            const itemConfig = getPayloadConfigFromPayload(config, item, key);
            const indicatorColor =
              color ??
              (itemData.payload !== undefined && itemData.payload !== null && typeof itemData.payload === 'object' && 'fill' in itemData.payload
                ? String((itemData.payload as Record<string, unknown>).fill)
                : undefined) ??
              itemData.color;

            return (
              <div
                key={itemData.dataKey}
                className={cn(
                  'flex w-full flex-wrap items-stretch gap-2 [&>svg]:h-2.5 [&>svg]:w-2.5 [&>svg]:text-muted-foreground',
                  indicator === 'dot' && 'items-center',
                )}
              >
                <TooltipIndicator
                  indicator={indicator}
                  hideIndicator={hideIndicator}
                  indicatorColor={indicatorColor}
                  nestLabel={nestLabel}
                  itemConfig={itemConfig}
                />
                <TooltipItemContent
                  formatter={formatter}
                  itemData={itemData}
                  item={item}
                  index={index}
                  itemConfig={itemConfig}
                  nestLabel={nestLabel}
                  tooltipLabel={tooltipLabel}
                />
              </div>
            );
          })}
        </div>
      </div>
    );
  },
);
ChartTooltipContent.displayName = 'ChartTooltip';

const ChartLegend = RechartsPrimitive.Legend;

const ChartLegendContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof RechartsPrimitive.Legend> &
    Pick<RechartsPrimitive.LegendProps, 'payload' | 'verticalAlign'> & {
      hideIcon?: boolean;
      nameKey?: string;
    }
>(({ className, hideIcon = false, payload, verticalAlign = 'bottom', nameKey }, ref) => {
  const { config } = useChart();

  if ((payload?.length ?? 0) === 0) {
    return null;
  }

  return (
    <div
      ref={ref}
      className={cn(
        'flex items-center justify-center gap-4',
        verticalAlign === 'top' ? 'pb-3' : 'pt-3',
        className,
      )}
    >
      {(payload as RechartsPrimitive.LegendPayload[]).map((item) => {
        const itemData = item as { dataKey?: string; value?: string; color?: string };
        const keyValue = nameKey ?? (itemData.dataKey !== undefined && itemData.dataKey !== '' ? String(itemData.dataKey) : 'value');
        const key = `${keyValue}`;
        const itemConfig = getPayloadConfigFromPayload(config, item, key);

        return (
          <div
            key={itemData.value as string}
            className={cn(
              'flex items-center gap-1.5 [&>svg]:h-3 [&>svg]:w-3 [&>svg]:text-muted-foreground',
            )}
          >
            {itemConfig?.icon && !hideIcon ? (
              <itemConfig.icon />
            ) : (
              <div
                className="h-2 w-2 shrink-0 rounded-[2px]"
                style={{
                  backgroundColor: itemData.color !== undefined && itemData.color !== '' ? String(itemData.color) : undefined,
                }}
              />
            )}
            {itemConfig?.label}
          </div>
        );
      })}
    </div>
  );
});
ChartLegendContent.displayName = 'ChartLegend';

// Helper to extract item config from a payload.
function getPayloadConfigFromPayload(config: ChartConfig, payload: unknown, key: string) {
  if (typeof payload !== 'object' || payload === null) {
    return;
  }

  const payloadPayload =
    'payload' in payload &&
    typeof (payload as Record<string, unknown>).payload === 'object' &&
    (payload as Record<string, unknown>).payload !== null
      ? (payload as Record<string, unknown>).payload
      : undefined;

  let configLabelKey: string = key;

  if (key in (payload as Record<string, unknown>) && typeof (payload as Record<string, unknown>)[key] === 'string') {
    configLabelKey = (payload as Record<string, unknown>)[key] as string;
  } else if (
    payloadPayload !== undefined &&
    key in (payloadPayload as Record<string, unknown>) &&
    typeof (payloadPayload as Record<string, unknown>)[key] === 'string'
  ) {
    configLabelKey = (payloadPayload as Record<string, unknown>)[key] as string;
  }

  return configLabelKey in config ? config[configLabelKey] : config[key];
}

export {
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
  ChartLegend,
  ChartLegendContent,
  ChartStyle,
};

import React from 'react';
import { cn } from '@/lib/utils';

interface TooltipIndicatorProps {
  indicator: 'line' | 'dot' | 'dashed';
  hideIndicator: boolean;
  indicatorColor?: string;
  nestLabel: boolean;
  itemConfig?: { icon?: React.ComponentType };
}

export function TooltipIndicator({
  indicator,
  hideIndicator,
  indicatorColor,
  nestLabel,
  itemConfig
}: TooltipIndicatorProps) {
  if (itemConfig?.icon) {
    return <itemConfig.icon />;
  }

  if (hideIndicator) {
    return null;
  }

  return (
    <div
      className={cn(
        'shrink-0 rounded-[2px] border-[--color-border] bg-[--color-bg]',
        {
          'h-2.5 w-2.5': indicator === 'dot',
          'w-1': indicator === 'line',
          'w-0 border-[1.5px] border-dashed bg-transparent': indicator === 'dashed',
          'my-0.5': nestLabel && indicator === 'dashed',
        },
      )}
      style={
        {
          '--color-bg': indicatorColor,
          '--color-border': indicatorColor,
        } as React.CSSProperties
      }
    />
  );
}

import React from 'react';
import { cn } from '@/lib/utils';

interface TooltipItemContentProps {
  formatter?: (value: number, name: string, item: unknown, index: number, payload: Record<string, unknown>[]) => React.ReactNode;
  itemData: { name?: string; dataKey?: string; payload?: unknown; color?: string; value?: number };
  item: unknown;
  index: number;
  itemConfig?: { label?: React.ReactNode };
  nestLabel: boolean;
  tooltipLabel: React.ReactNode;
}

export function TooltipItemContent({
  formatter,
  itemData,
  item,
  index,
  itemConfig,
  nestLabel,
  tooltipLabel
}: TooltipItemContentProps) {
  if (formatter !== undefined && itemData?.value !== undefined && itemData.name !== undefined && itemData.name !== '') {
    const payloadArray = Array.isArray(itemData.payload)
      ? (itemData.payload as Record<string, unknown>[])
      : [];
    return <>{formatter(itemData.value, itemData.name, item, index, payloadArray)}</>;
  }

  return (
    <div
      className={cn(
        'flex flex-1 justify-between leading-none',
        nestLabel ? 'items-end' : 'items-center',
      )}
    >
      <div className="grid gap-1.5">
        {nestLabel ? tooltipLabel : undefined}
        <span className="text-muted-foreground">
          {itemConfig?.label ?? itemData.name}
        </span>
      </div>
      {itemData.value !== undefined && itemData.value !== 0 && (
        <span className="font-mono font-medium tabular-nums text-foreground">
          {itemData.value.toLocaleString()}
        </span>
      )}
    </div>
  );
}

import React from 'react';
import { cn } from '@/lib/utils';
import { ChartConfig } from '../chart'; // Assuming ChartConfig is exported from chart.tsx

// Helper to extract item config from a payload.
function getPayloadConfigFromPayload(config: ChartConfig, payload: unknown, key: string) {
  if (typeof payload !== 'object' || payload === null) {
    return;
  }

  const payloadPayload =
    'payload' in payload &&
    typeof (payload as Record<string, unknown>).payload === 'object' &&
    (payload as Record<string, unknown>).payload !== null
      ? (payload as Record<string, unknown>).payload
      : undefined;

  let configLabelKey: string = key;

  if (key in (payload as Record<string, unknown>) && typeof (payload as Record<string, unknown>)[key] === 'string') {
    configLabelKey = (payload as Record<string, unknown>)[key] as string;
  } else if (
    payloadPayload !== undefined &&
    key in (payloadPayload as Record<string, unknown>) &&
    typeof (payloadPayload as Record<string, unknown>)[key] === 'string'
  ) {
    configLabelKey = (payloadPayload as Record<string, unknown>)[key] as string;
  }

  return configLabelKey in config ? config[configLabelKey] : config[key];
}

interface UseTooltipLabelProps {
  hideLabel: boolean;
  payload: unknown[] | undefined;
  label: unknown;
  labelFormatter?: (value: unknown, payload: unknown[]) => React.ReactNode;
  labelClassName?: string;
  config: ChartConfig;
  labelKey?: string;
}

export function useTooltipLabel({
  hideLabel,
  payload,
  label,
  labelFormatter,
  labelClassName,
  config,
  labelKey
}: UseTooltipLabelProps) {
  return React.useMemo(() => {
    if (hideLabel === true || (payload?.length ?? 0) === 0) {
      return null;
    }

    const [item] = payload as unknown[];

    const key = `${labelKey ?? (item as { dataKey?: string; name?: string }).dataKey ?? (item as { dataKey?: string; name?: string }).name ?? 'value'}`;
    const itemConfig = getPayloadConfigFromPayload(config, item, key);
    const value =
      (labelKey === undefined && typeof label === 'string') ? config[label]?.label ?? label : itemConfig?.label;

    if (labelFormatter) {
      return (
        <div className={cn('font-medium', labelClassName)}>{labelFormatter(value, payload as unknown[])}</div>
      );
    }

    if (value === undefined || value === null || value === '') {
      return null;
    }

    return <div className={cn('font-medium', labelClassName)}>{value}</div>;
  }, [label, labelFormatter, payload, hideLabel, labelClassName, config, labelKey]);
}

'use client';

import React from 'react';
import {
  PieChart,
  Pie,
  Cell,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from 'recharts';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';

// SOTA color scheme for data quality categories
const QUALITY_COLORS = {
  high: '#22c55e',    // Green for high quality (80%)
  medium: '#f59e0b',  // Amber for medium quality (60-79%)
  low: '#ef4444',     // Red for low quality (<60%)
};

interface DataQualityStats {
  total_trucks: number;
  avg_quality_score: number;
  high_quality_count: number;
  medium_quality_count: number;
  low_quality_count: number;
  verified_count: number;
  pending_count: number;
  flagged_count: number;
}

interface DataQualityChartsProps {
  qualityStats: DataQualityStats;
}

// Custom tooltip for better accessibility and UX
interface TooltipProps {
  active?: boolean;
  payload?: Array<{
    value: number;
    payload: {
      percentage?: string;
    };
  }>;
  label?: string;
}

const CustomTooltip = ({ active, payload, label }: TooltipProps) => {
  if (active === true && payload != undefined && payload.length > 0) {
    return (
      <div
        className="bg-white p-3 border border-gray-200 rounded-lg shadow-lg"
        role="status"
        aria-live="assertive"
      >
        <p className="font-medium">{`${label ?? 'Unknown'}: ${payload[0].value}`}</p>
        <p className="text-sm text-gray-600">
          {payload[0].payload.percentage != undefined && payload[0].payload.percentage !== '' && `${payload[0].payload.percentage}% of total`}
        </p>
      </div>
    );
  }
  return;
};

// Quality Distribution Pie Chart Component
const QualityDistributionChart: React.FC<{ qualityStats: DataQualityStats }> = ({ qualityStats }) => {
  const data = [
    {
      name: 'High Quality',
      value: qualityStats.high_quality_count,
      percentage: ((qualityStats.high_quality_count / qualityStats.total_trucks) * 100).toFixed(1),
    },
    {
      name: 'Medium Quality',
      value: qualityStats.medium_quality_count,
      percentage: ((qualityStats.medium_quality_count / qualityStats.total_trucks) * 100).toFixed(1),
    },
    {
      name: 'Low Quality',
      value: qualityStats.low_quality_count,
      percentage: ((qualityStats.low_quality_count / qualityStats.total_trucks) * 100).toFixed(1),
    },
  ];

  return (
    <Card>
      <CardHeader>
        <CardTitle>Quality Distribution</CardTitle>
        <CardDescription>
          Breakdown of food trucks by data quality categories
        </CardDescription>
      </CardHeader>
      <CardContent>
        <ResponsiveContainer width="100%" height={300}>
          <PieChart>
            <Pie
              data={data}
              cx="50%"
              cy="50%"
              labelLine={false}
              label={({
                name,
                percentage
              }: {
                name?: string;
                percentage?: string;
              }) => `${name ?? 'Unknown'}: ${percentage ?? '0'}%`}
              outerRadius={80}
              fill="#8884d8"
              dataKey="value"
            >
              {data.map((entry, index) => (
                <Cell 
                  key={`cell-${index}`} 
                  fill={Object.values(QUALITY_COLORS)[index]} 
                />
              ))}
            </Pie>
            <Tooltip content={<CustomTooltip />} />
          </PieChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
};

// Verification Status Bar Chart Component
const VerificationStatusChart: React.FC<{ qualityStats: DataQualityStats }> = ({ qualityStats }) => {
  const data = [
    {
      name: 'Verified',
      count: qualityStats.verified_count,
      percentage: ((qualityStats.verified_count / qualityStats.total_trucks) * 100).toFixed(1),
    },
    {
      name: 'Pending',
      count: qualityStats.pending_count,
      percentage: ((qualityStats.pending_count / qualityStats.total_trucks) * 100).toFixed(1),
    },
    {
      name: 'Flagged',
      count: qualityStats.flagged_count,
      percentage: ((qualityStats.flagged_count / qualityStats.total_trucks) * 100).toFixed(1),
    },
  ];

  return (
    <Card>
      <CardHeader>
        <CardTitle>Verification Status</CardTitle>
        <CardDescription>
          Current verification status of all food trucks
        </CardDescription>
      </CardHeader>
      <CardContent>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip content={<CustomTooltip />} />
            <Bar dataKey="count" fill="#3b82f6" />
          </BarChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
};

// Helper function to get score color
const getScoreColor = (score: number): string => {
  if (score >= 0.8) return 'text-green-600';
  if (score >= 0.6) return 'text-yellow-600';
  return 'text-red-600';
};

// Helper function to get quality threshold label
const getQualityThreshold = (score: number): string => {
  if (score >= 0.8) return 'High';
  if (score >= 0.6) return 'Medium';
  return 'Low';
};

// Quality Score Overview Component
const QualityScoreOverview: React.FC<{ qualityStats: DataQualityStats }> = ({ qualityStats }) => {
  const averageScore = (qualityStats.avg_quality_score * 100).toFixed(1);
  const scoreColor = getScoreColor(qualityStats.avg_quality_score);

  return (
    <Card>
      <CardHeader>
        <CardTitle>Overall Quality Score</CardTitle>
        <CardDescription>
          Average data quality score across all food trucks
        </CardDescription>
      </CardHeader>
      <CardContent className="flex items-center justify-center">
        <div className="text-center">
          <div className={`text-6xl font-bold ${scoreColor}`}>
            {averageScore}%
          </div>
          <p className="text-sm text-muted-foreground mt-2">
            Based on {qualityStats.total_trucks} food trucks
          </p>
          <div className="mt-4 text-sm">
            <div className="flex justify-between items-center">
              <span>Quality Threshold:</span>
              <span className="font-medium">
                {getQualityThreshold(qualityStats.avg_quality_score)}
              </span>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

// Main Data Quality Charts Component
export const DataQualityCharts: React.FC<DataQualityChartsProps> = ({ qualityStats }) => {
  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      <QualityScoreOverview qualityStats={qualityStats} />
      <QualityDistributionChart qualityStats={qualityStats} />
      <VerificationStatusChart qualityStats={qualityStats} />
    </div>
  );
};

export default DataQualityCharts;

import React from 'react';
import { AdminEvent, RealtimeMetrics } from '../useRealtimeAdminEvents.types';
import { parseEventData, setupEventListeners, setupEventSourceAuth } from '../useRealtimeAdminEventsHelpers';
import { useConnectionState } from './useConnectionState';
import { setupEventSourceListeners } from './setupEventSourceListeners';

export function createEventSourceConnection({
  eventSourceRef,
  isConnecting,
  isManuallyDisconnectedRef,
  connectionAttempts,
  maxReconnectAttempts,
  reconnectInterval,
  reconnectTimeoutRef,
  handleEvent,
  connectionState,
  connect
}: {
  eventSourceRef: React.MutableRefObject<EventSource | undefined>;
  isConnecting: boolean;
  isManuallyDisconnectedRef: React.MutableRefObject<boolean>;
  connectionAttempts: number;
  maxReconnectAttempts: number;
  reconnectInterval: number;
  reconnectTimeoutRef: React.MutableRefObject<NodeJS.Timeout | undefined>;
  handleEvent: (event: AdminEvent) => void;
  connectionState: ReturnType<typeof useConnectionState>;
  connect: () => void;
}) {
  if (eventSourceRef.current || isConnecting) {
    return;
  }

  const { setIsConnecting, setConnectionError, setIsConnected, setConnectionAttempts } = connectionState;

  setIsConnecting(true);
  setConnectionError(undefined);
  isManuallyDisconnectedRef.current = false;

  try {
    // Setup authentication
    setupEventSourceAuth();

    const eventSource = new EventSource('/api/admin/realtime-events');

    // Setup all event listeners
    setupEventSourceListeners(
      eventSource,
      handleEvent,
      connectionState,
      isManuallyDisconnectedRef,
      connectionAttempts,
      maxReconnectAttempts,
      reconnectInterval,
      reconnectTimeoutRef,
      connect
    );

    eventSourceRef.current = eventSource;

  } catch (error) {
    console.error('Failed to establish real-time connection:', error);
    setIsConnecting(false);
    setConnectionError(error instanceof Error ? error.message : 'Connection failed');
  }
}

import React from 'react';
import { AdminEvent, RealtimeMetrics } from '../useRealtimeAdminEvents.types';
import { parseEventData, setupEventListeners } from '../useRealtimeAdminEventsHelpers';
import { useConnectionState } from './useConnectionState';

export function setupEventSourceListeners(
  eventSource: EventSource,
  handleEvent: (event: AdminEvent) => void,
  connectionState: ReturnType<typeof useConnectionState>,
  isManuallyDisconnectedRef: React.MutableRefObject<boolean>,
  connectionAttempts: number,
  maxReconnectAttempts: number,
  reconnectInterval: number,
  reconnectTimeoutRef: React.MutableRefObject<NodeJS.Timeout | undefined>,
  connect: () => void
) {
  const { setIsConnected, setIsConnecting, setConnectionError, setConnectionAttempts } = connectionState;

  eventSource.addEventListener('open', () => {
    console.info('Real-time admin events connected');
    setIsConnected(true);
    setIsConnecting(false);
    setConnectionError(undefined);
    setConnectionAttempts(0);
  });

  eventSource.addEventListener('message', (event: MessageEvent) => {
    const adminEvent = parseEventData(event.data as string, 'message');
    if (adminEvent) {
      handleEvent(adminEvent);
    }
  });

  eventSource.addEventListener('error', (error) => {
    console.error('Real-time admin events error:', error);
    setIsConnected(false);
    setIsConnecting(false);
    setConnectionError('Connection error occurred');

    // Attempt reconnection if not manually disconnected
    if (!isManuallyDisconnectedRef.current && connectionAttempts < maxReconnectAttempts) {
      setConnectionAttempts(prev => prev + 1);

      reconnectTimeoutRef.current = setTimeout(() => {
        if (!isManuallyDisconnectedRef.current) {
          connect();
        }
      }, reconnectInterval);
    } else if (connectionAttempts >= maxReconnectAttempts) {
      setConnectionError('Max reconnection attempts reached');
    }
  });

  // Handle specific event types
  setupEventListeners(eventSource, handleEvent);
}

import { useCallback, useRef } from 'react';
import { AdminEvent, RealtimeMetrics } from '../useRealtimeAdminEvents.types';
import { useConnectionState } from './useConnectionState';
import { createEventSourceConnection } from './createEventSourceConnection';

export function useConnectionManagement(
  eventSourceRef: React.MutableRefObject<EventSource | undefined>,
  reconnectTimeoutRef: React.MutableRefObject<NodeJS.Timeout | undefined>,
  isManuallyDisconnectedRef: React.MutableRefObject<boolean>,
  connectionState: ReturnType<typeof useConnectionState>,
  handleEvent: (event: AdminEvent) => void,
  connectionAttempts: number,
  maxReconnectAttempts: number,
  reconnectInterval: number,
  isConnecting: boolean
) {
  const { setIsConnected, setIsConnecting, setConnectionError, setRecentEvents } = connectionState;

  const connect = useCallback(() => {
    createEventSourceConnection({
      eventSourceRef,
      isConnecting,
      isManuallyDisconnectedRef,
      connectionAttempts,
      maxReconnectAttempts,
      reconnectInterval,
      reconnectTimeoutRef,
      handleEvent,
      connectionState,
      connect: () => connect()
    });
  }, [handleEvent, connectionAttempts, maxReconnectAttempts, reconnectInterval, isConnecting, connectionState]);

  const disconnect = useCallback(() => {
    isManuallyDisconnectedRef.current = true;

    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
      reconnectTimeoutRef.current = undefined;
    }

    if (eventSourceRef.current) {
      eventSourceRef.current.close();
      eventSourceRef.current = undefined;
    }

    setIsConnected(false);
    setIsConnecting(false);
    setConnectionError(undefined);
  }, [setIsConnected, setIsConnecting, setConnectionError]);

  const clearEvents = useCallback(() => {
    setRecentEvents([]);
  }, [setRecentEvents]);

  return { connect, disconnect, clearEvents };
}

import { useCallback } from 'react';
import { AdminEvent, RealtimeMetrics } from '../useRealtimeAdminEvents.types';

export function useEventHandlers(
  eventFilter: ((event: AdminEvent) => boolean) | undefined,
  setLastEventTime: (date: Date) => void,
  setLatestMetrics: (metrics: RealtimeMetrics) => void,
  setRecentEvents: React.Dispatch<React.SetStateAction<AdminEvent[]>>
) {
  return useCallback((event: AdminEvent) => {
    // Apply filter if provided
    if (eventFilter && !eventFilter(event)) {
      return;
    }

    setLastEventTime(new Date());

    // Handle different event types
    switch (event.type) {
      case 'heartbeat': {
        if (event.data !== undefined && typeof event.data === 'object') {
          setLatestMetrics(event.data as unknown as RealtimeMetrics);
        }
        break;
      }

      case 'scraping_update':
      case 'data_quality_change':
      case 'system_alert':
      case 'user_activity': {
        setRecentEvents(prev => {
          const newEvents = [event, ...prev].slice(0, 50); // Keep last 50 events
          return newEvents;
        });
        break;
      }
    }
  }, [eventFilter, setLastEventTime, setLatestMetrics, setRecentEvents]);
}

import { useEffect, useRef } from 'react';
import { AdminEvent, RealtimeMetrics } from './useRealtimeAdminEvents.types';
import { useConnectionState } from './realtime/useConnectionState';
import { setupEventSourceListeners } from './realtime/setupEventSourceListeners';
import { createEventSourceConnection } from './realtime/createEventSourceConnection';
import { useEventHandlers } from './realtime/useEventHandlers';
import { useConnectionManagement } from './realtime/useConnectionManagement';
import { useAutoConnect } from './realtime/useAutoConnect';

/**
 * SOTA Real-time Admin Dashboard Hook
 * 
 * Provides real-time updates for admin dashboard using Server-Sent Events (SSE)
 * Implements automatic reconnection, error handling, and event filtering
 */

interface UseRealtimeAdminEventsOptions {
  autoConnect?: boolean;
  reconnectInterval?: number;
  maxReconnectAttempts?: number;
  eventFilter?: (event: AdminEvent) => boolean;
}

interface UseRealtimeAdminEventsReturn {
  // Connection state
  isConnected: boolean;
  isConnecting: boolean;
  connectionError: string | undefined;
  
  // Data
  latestMetrics: RealtimeMetrics | undefined;
  recentEvents: AdminEvent[];
  
  // Controls
  connect: () => void;
  disconnect: () => void;
  clearEvents: () => void;
  
  // Statistics
  connectionAttempts: number;
  lastEventTime: Date | undefined;
}

export function useRealtimeAdminEvents(
  options: UseRealtimeAdminEventsOptions = {}
): UseRealtimeAdminEventsReturn {
  const {
    autoConnect = true,
    reconnectInterval = 5000,
    maxReconnectAttempts = 10,
    eventFilter
  } = options;

  // State management
  const connectionState = useConnectionState();
  const {
    isConnected,
    isConnecting,
    connectionError,
    latestMetrics,
    recentEvents,
    connectionAttempts,
    lastEventTime
  } = connectionState;

  // Refs
  const eventSourceRef = useRef<EventSource | undefined>(undefined);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout | undefined>(undefined);
  const isManuallyDisconnectedRef = useRef(false);

  // Event handlers
  const handleEvent = useEventHandlers(
    eventFilter,
    connectionState.setLastEventTime,
    connectionState.setLatestMetrics,
    connectionState.setRecentEvents
  );

  // Connection management
  const { connect, disconnect, clearEvents } = useConnectionManagement(
    eventSourceRef,
    reconnectTimeoutRef,
    isManuallyDisconnectedRef,
    connectionState,
    handleEvent,
    connectionAttempts,
    maxReconnectAttempts,
    reconnectInterval,
    isConnecting
  );

  // Auto-connect on mount
  useAutoConnect(autoConnect, connect, disconnect);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
      }
      if (eventSourceRef.current) {
        eventSourceRef.current.close();
      }
    };
  }, []);

  return {
    // Connection state
    isConnected,
    isConnecting,
    connectionError,
    
    // Data
    latestMetrics,
    recentEvents,
    
    // Controls
    connect,
    disconnect,
    clearEvents,
    
    // Statistics
    connectionAttempts,
    lastEventTime
  };
}

import { NextRequest, NextResponse } from 'next/server';
import { BatchCleanupService } from '@/lib/data-quality/batchCleanup';
import { DuplicatePreventionService } from '@/lib/data-quality/duplicatePrevention';

interface DataCleanupRequestBody {
  action: string;
  options?: {
    batchSize?: number;
    dryRun?: boolean;
    operations?: string[];
    truckData?: Record<string, unknown>;
    targetId?: string;
    sourceId?: string;
  };
}

export async function handlePostRequest(body: DataCleanupRequestBody): Promise<NextResponse> {
  const { action, options = {} } = body;

  switch (action) {
    case 'full-cleanup': {
      return await handleFullCleanup(options);
    }
    case 'check-duplicates': {
      return await handleCheckDuplicates(options);
    }
    case 'merge-duplicates': {
      return await handleMergeDuplicates(options);
    }
    case 'dry-run': {
      return await handleDryRun(options);
    }
    default: {
      return NextResponse.json(
        { success: false, error: `Unknown action: ${action}` },
        { status: 400 }
      );
    }
  }
}

export async function handleGetRequest(request: NextRequest): Promise<NextResponse> {
  const { searchParams } = new URL(request.url);
  const action = searchParams.get('action');

  switch (action) {
    case 'status': {
      return await handleGetStatus();
    }
    case 'preview': {
      return await handleGetPreview();
    }
    default: {
      return await handleGetDefault();
    }
  }
}

export async function handleFullCleanup(options: DataCleanupRequestBody['options']): Promise<NextResponse> {
  const result = await BatchCleanupService.runFullCleanup({
    batchSize: options?.batchSize ?? 50,
    dryRun: options?.dryRun ?? false,
    operations: options?.operations as any
  });
  
  return NextResponse.json({
    success: true,
    action: 'full-cleanup',
    result,
    message: `Cleanup completed: ${result.summary.trucksImproved} trucks improved, ${result.summary.duplicatesRemoved} duplicates removed`
  });
}

export async function handleCheckDuplicates(options: DataCleanupRequestBody['options']): Promise<NextResponse> {
  const { truckData } = options ?? {};
  if (!truckData) {
    return NextResponse.json(
      { success: false, error: 'Missing truckData for duplicate check' },
      { status: 400 }
    );
  }

  const duplicateCheck = await DuplicatePreventionService.checkForDuplicates(truckData);
  
  return NextResponse.json({
    success: true,
    action: 'check-duplicates',
    result: duplicateCheck
  });
}

export async function handleMergeDuplicates(options: DataCleanupRequestBody['options']): Promise<NextResponse> {
  const { targetId, sourceId } = options ?? {};
  if (targetId === undefined || sourceId === undefined) {
    return NextResponse.json(
      { success: false, error: 'Missing targetId or sourceId for merge operation' },
      { status: 400 }
    );
  }

  const mergedTruck = await DuplicatePreventionService.mergeDuplicates(targetId, sourceId);
  
  return NextResponse.json({
    success: true,
    action: 'merge-duplicates',
    result: mergedTruck,
    message: `Successfully merged truck ${sourceId} into ${targetId}`
  });
}

export async function handleDryRun(options: DataCleanupRequestBody['options']): Promise<NextResponse> {
  const result = await BatchCleanupService.runFullCleanup({
    ...options,
    operations: options?.operations as any,
    dryRun: true
  });
  
  return NextResponse.json({
    success: true,
    action: 'dry-run',
    result,
    message: 'Dry run completed - no changes made to database'
  });
}

