// Helper function to test Firecrawl scraping
export async function testFirecrawlScraping(testUrl: string) {
  console.info('Testing Firecrawl scraping...');
  const scrapeResult = await firecrawl.scrapeUrl(testUrl, {
    formats: ['markdown'],
