      instagram: undefined,
      facebook: undefined,
      twitter: undefined,
      tiktok: undefined,
      yelp: undefined,
    },
    source_urls: [testUrl],
    data_quality_score: 0.8,
    verification_status: 'pending',
  });

  // Create a test scraping job
  const testJob = await ScrapingJobService.createJob({
