// Helper function to format test results
export function formatTestResults(scrapeResult: any, geminiResult: any, supabaseResults: any) {
  return {
    success: true,
    message: 'Integration test completed successfully',
    results: {
      firecrawl: {
        success: scrapeResult.success,
        dataLength: scrapeResult.data?.markdown?.length ?? 0,
      },
      gemini: {
        success: geminiResult.success,
        tokensUsed: geminiResult.tokensUsed,
        categoriesFound: geminiResult.data?.length ?? 0,
      },
      supabase: {
        truckCreated: supabaseResults.testTruck.id,
        jobCreated: supabaseResults.testJob.id,
        queueItemCreated: supabaseResults.queueItem.id,
        nearbyTrucksFound: supabaseResults.nearbyTrucks.length,
      },
    },
    testData: {
      truck: supabaseResults.testTruck,
      processedMenu: geminiResult.data,
      nearbyTrucks: supabaseResults.nearbyTrucks.slice(0, 3),
    },
  };
}

export async function runIntegrationTestSteps(testUrl: string) {
  console.info('Starting integration test...');

  // Step 1: Test Firecrawl scraping
  const firecrawlTest = await testFirecrawlScraping(testUrl);
  if (!firecrawlTest.success) {
    return firecrawlTest;
  }

  // Step 2: Test Gemini processing
  const geminiTest = await testGeminiProcessing();
  if (!geminiTest.success) {
    return geminiTest;
  }

  // Step 3: Test Supabase operations
  console.info('Testing Supabase operations...');
  const supabaseResults = await testSupabaseOperations(testUrl, geminiTest.result);

  // Step 4: Format and return results
  const results = formatTestResults(firecrawlTest.result, geminiTest.result, supabaseResults);
  return { success: true, results };
}

/**
 * SOTA Batch Data Cleanup System
 * Implements automated data quality improvements and cleanup operations
 */

import { FoodTruckService, DataQualityService, type FoodTruck } from '@/lib/supabase';
import { DuplicatePreventionService } from './duplicatePrevention';

export interface CleanupOperation {
  type: 'normalize_phone' | 'fix_coordinates' | 'remove_placeholders' | 'update_quality_scores' | 'merge_duplicates';
  description: string;
  affectedCount: number;
  successCount: number;
  errorCount: number;
  errors: string[];
}

export interface BatchCleanupResult {
  totalProcessed: number;
  operations: CleanupOperation[];
  summary: {
    trucksImproved: number;
    duplicatesRemoved: number;
    qualityScoreImprovement: number;
    placeholdersRemoved: number;
  };
  duration: number;
}

/**
 * Automated Data Quality Cleanup Service
 */
export class BatchCleanupService {
  
  /**
   * Run comprehensive data cleanup operations
   */
  static async runFullCleanup(options: {
    batchSize?: number;
    dryRun?: boolean;
    operations?: CleanupOperation['type'][];
  } = {}): Promise<BatchCleanupResult> {
    const startTime = Date.now();
    const { batchSize = 50, dryRun = false, operations = [
      'remove_placeholders',
      'normalize_phone',
      'fix_coordinates',
      'update_quality_scores',
      'merge_duplicates'
    ] } = options;
    
    console.info(`Starting batch cleanup (${dryRun ? 'DRY RUN' : 'LIVE'})...`);
    
    const result: BatchCleanupResult = {
      totalProcessed: 0,
      operations: [],
      summary: {
        trucksImproved: 0,
        duplicatesRemoved: 0,
        qualityScoreImprovement: 0,
        placeholdersRemoved: 0
      },
      duration: 0
    };
    
    try {
      // Get all trucks for processing
      const allTrucks = await FoodTruckService.getAllTrucks();
      result.totalProcessed = allTrucks.total;
      
      // Process trucks in batches
      for (let i = 0; i < allTrucks.trucks.length; i += batchSize) {
        const batch = allTrucks.trucks.slice(i, i + batchSize);
        
        for (const operation of operations) {
          const opResult = await this.runOperation(operation, batch, dryRun);
          result.operations.push(opResult);
        }
      }
      
      // Calculate summary
      result.summary = this.calculateSummary(result.operations);
      result.duration = Date.now() - startTime;
      
      console.info(`Batch cleanup completed in ${result.duration}ms`);
      return result;
      
    } catch (error) {
      console.error('Batch cleanup failed:', error);
      throw error;
    }
  }
  
  /**
   * Run a specific cleanup operation
   */
  private static async runOperation(
    type: CleanupOperation['type'],
    trucks: FoodTruck[],
    dryRun: boolean
  ): Promise<CleanupOperation> {
    const operation: CleanupOperation = {
      type,
      description: this.getOperationDescription(type),
      affectedCount: 0,
      successCount: 0,
      errorCount: 0,
      errors: []
    };
    
    try {
      switch (type) {
        case 'remove_placeholders': {
          return await this.removePlaceholders(trucks, dryRun, operation);
        }
        case 'normalize_phone': {
          return await this.normalizePhoneNumbers(trucks, dryRun, operation);
        }
        case 'fix_coordinates': {
          return await this.fixCoordinates(trucks, dryRun, operation);
        }
        case 'update_quality_scores': {
          return await this.updateQualityScores(trucks, dryRun, operation);
        }
        case 'merge_duplicates': {
          return await this.mergeDuplicates(trucks, dryRun, operation);
        }
        default: {
          operation.errors.push(`Unknown operation type: ${String(type)}`);
          return operation;
        }
      }
    } catch (error) {
      operation.errors.push(`Operation failed: ${error instanceof Error ? error.message : String(error)}`);
      return operation;
    }
  }
  
  /**
   * Get placeholder detection patterns
   */
  private static getPlaceholderPatterns(): RegExp[] {
    return [
      /undefined/i,
      /placeholder/i,
      /example\.com/i,
      /test\s*truck/i,
      /lorem\s*ipsum/i,
      /\bna\b/i,
      /\bn\/a\b/i,
      /^0+$/,
      /^null$/i
    ];
  }

  /**
   * Check if truck data needs placeholder cleanup
   */
  private static checkForPlaceholders(truck: FoodTruck, patterns: RegExp[]): { updates: Partial<FoodTruck>; needsUpdate: boolean } {
    const updates: Partial<FoodTruck> = {};
    let needsUpdate = false;

    // Check name
    if (truck.name && patterns.some(pattern => pattern.test(truck.name))) {
      updates.name = undefined;
      needsUpdate = true;
    }

    // Check description
    if (truck.description !== undefined && typeof truck.description === 'string' && patterns.some(pattern => pattern.test(truck.description))) {
      updates.description = undefined;
      needsUpdate = true;
    }

    // Check price range
    if (truck.price_range !== undefined && typeof truck.price_range === 'string' && patterns.some(pattern => pattern.test(truck.price_range))) {
      updates.price_range = undefined;
      needsUpdate = true;
    }

    return { updates, needsUpdate };
  }

  /**
   * Remove placeholder and mock data
   */
  private static async removePlaceholders(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<CleanupOperation> {
    const placeholderPatterns = this.getPlaceholderPatterns();

    for (const truck of trucks) {
      const { updates, needsUpdate: initialNeedsUpdate } = this.checkForPlaceholders(truck, placeholderPatterns);
      let needsUpdate = initialNeedsUpdate;

      // Check contact info
      if (truck.contact_info) {
        const cleanContact = { ...truck.contact_info };
        let contactUpdated = false;

        if (cleanContact.phone !== undefined && typeof cleanContact.phone === 'string' && placeholderPatterns.some(pattern => pattern.test(cleanContact.phone))) {
          cleanContact.phone = undefined;
          contactUpdated = true;
        }

        if (cleanContact.website !== undefined && typeof cleanContact.website === 'string' && placeholderPatterns.some(pattern => pattern.test(cleanContact.website))) {
          cleanContact.website = undefined;
          contactUpdated = true;
        }

        if (cleanContact.email !== undefined && typeof cleanContact.email === 'string' && placeholderPatterns.some(pattern => pattern.test(cleanContact.email))) {
          cleanContact.email = undefined;
          contactUpdated = true;
        }

        if (contactUpdated) {
          updates.contact_info = cleanContact;
          needsUpdate = true;
        }
      }

      // Check address
      if (truck.current_location?.address !== undefined && typeof truck.current_location.address === 'string' && placeholderPatterns.some(pattern => pattern.test(truck.current_location.address))) {
        updates.current_location = {
          ...truck.current_location,
          address: undefined
        };
        needsUpdate = true;
      }
      
      if (needsUpdate) {
        operation.affectedCount++;
        
        if (dryRun) {
          operation.successCount++;
        } else {
          try {
            await FoodTruckService.updateTruck(truck.id, updates);
            operation.successCount++;
          } catch (error) {
            operation.errorCount++;
            operation.errors.push(`Failed to update truck ${truck.id}: ${error instanceof Error ? error.message : String(error)}`);
          }
        }
      }
    }
    
    return operation;
  }
  
  /**
   * Normalize phone numbers to consistent format
   */
  private static async normalizePhoneNumbers(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<CleanupOperation> {
    for (const truck of trucks) {
      if (truck.contact_info?.phone !== undefined) {
        const originalPhone = truck.contact_info.phone;
        const normalizedPhone = this.normalizePhone(originalPhone);

        if (normalizedPhone !== undefined && normalizedPhone !== originalPhone) {
          operation.affectedCount++;
          
          if (dryRun) {
            operation.successCount++;
          } else {
            try {
              await FoodTruckService.updateTruck(truck.id, {
                contact_info: {
                  ...truck.contact_info,
                  phone: normalizedPhone
                }
              });
              operation.successCount++;
            } catch (error) {
              operation.errorCount++;
              operation.errors.push(`Failed to normalize phone for truck ${truck.id}: ${error instanceof Error ? error.message : String(error)}`);
            }
          }
        }
      }
    }
    
    return operation;
  }
  
  /**
   * Fix invalid GPS coordinates
   */
  private static async fixCoordinates(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<CleanupOperation> {
    // Charleston, SC default coordinates
    const defaultLat = 32.7767;
    const defaultLng = -79.9311;
    
    for (const truck of trucks) {
      if (truck.current_location != undefined) {
        const { lat, lng } = truck.current_location;
        let needsUpdate = false;
        const updates: Partial<FoodTruck['current_location']> = {};

        // Fix invalid coordinates (0,0 or null)
        if (lat == undefined || lat === 0 || lng == undefined || lng === 0) {
          updates.lat = defaultLat;
          updates.lng = defaultLng;
          needsUpdate = true;
        }

        // Fix coordinates outside reasonable bounds for Charleston area
        if (lat != undefined && lng != undefined && (lat < 32 || lat > 34 || lng > -79 || lng < -81)) {
          updates.lat = defaultLat;
          updates.lng = defaultLng;
          needsUpdate = true;
        }
        
        if (needsUpdate) {
          operation.affectedCount++;
          
          if (dryRun) {
            operation.successCount++;
          } else {
            try {
              await FoodTruckService.updateTruck(truck.id, {
                current_location: {
                  ...truck.current_location,
                  ...updates
                }
              });
              operation.successCount++;
            } catch (error) {
              operation.errorCount++;
              operation.errors.push(`Failed to fix coordinates for truck ${truck.id}: ${error instanceof Error ? error.message : String(error)}`);
            }
          }
        }
      }
    }
    
    return operation;
  }
  
  /**
   * Update quality scores for all trucks
   */
  private static async updateQualityScores(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<CleanupOperation> {
    for (const truck of trucks) {
      // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-call, @typescript-eslint/no-unsafe-member-access
      const qualityAssessment = DataQualityService.calculateQualityScore(truck);
      // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access
      const newScore = qualityAssessment.score;
      const currentScore = truck.data_quality_score ?? 0;
      
      // Only update if score changed significantly (>5% difference)
      if (Math.abs(newScore - currentScore) > 0.05) {
        operation.affectedCount++;
        
        if (dryRun) {
          operation.successCount++;
        } else {
          try {
            // eslint-disable-next-line @typescript-eslint/no-unsafe-call, @typescript-eslint/no-unsafe-member-access
            await DataQualityService.updateTruckQualityScore(truck.id);
            operation.successCount++;
          } catch (error) {
            operation.errorCount++;
            operation.errors.push(`Failed to update quality score for truck ${truck.id}: ${error instanceof Error ? error.message : String(error)}`);
          }
        }
      }
    }
    
    return operation;
  }
  
  /**
   * Identify and merge duplicate trucks
   */
  private static async mergeDuplicates(
    trucks: FoodTruck[],
    dryRun: boolean,
    operation: CleanupOperation
  ): Promise<CleanupOperation> {
    const processedIds = new Set<string>();
    
    for (const truck of trucks) {
      if (processedIds.has(truck.id)) continue;
      
      const duplicateCheck = await DuplicatePreventionService.checkForDuplicates(truck);
      
      if (duplicateCheck.isDuplicate && duplicateCheck.bestMatch) {
        const { bestMatch } = duplicateCheck;
        
        if (bestMatch.confidence === 'high' && bestMatch.recommendation === 'merge') {
          operation.affectedCount++;
          
          if (dryRun) {
            operation.successCount++;
          } else {
            try {
              await DuplicatePreventionService.mergeDuplicates(truck.id, bestMatch.existingTruck.id);
              processedIds.add(bestMatch.existingTruck.id);
              operation.successCount++;
            } catch (error) {
              operation.errorCount++;
              operation.errors.push(`Failed to merge duplicates ${truck.id} and ${bestMatch.existingTruck.id}: ${error instanceof Error ? error.message : String(error)}`);
            }
          }
        }
      }
      
      processedIds.add(truck.id);
    }
    
    return operation;
  }
  
  /**
   * Normalize phone number format
   */
  private static normalizePhone(phone: string): string | undefined {
    if (!phone) return undefined;
    
    // Remove all non-digit characters
    const digits = phone.replaceAll(/\D/g, '');
    
    // Handle US phone numbers
    if (digits.length === 10) {
      return `(${digits.slice(0, 3)}) ${digits.slice(3, 6)}-${digits.slice(6)}`;
    } else if (digits.length === 11 && digits[0] === '1') {
      return `+1 (${digits.slice(1, 4)}) ${digits.slice(4, 7)}-${digits.slice(7)}`;
    }
    
    // Return original if can't normalize
    return phone;
  }
  
  /**
   * Get operation description
   */
  private static getOperationDescription(type: CleanupOperation['type']): string {
    const descriptions = {
      remove_placeholders: 'Remove placeholder and mock data values',
      normalize_phone: 'Normalize phone numbers to consistent format',
      fix_coordinates: 'Fix invalid GPS coordinates',
      update_quality_scores: 'Recalculate data quality scores',
      merge_duplicates: 'Identify and merge duplicate truck entries'
    };
    
    return descriptions[type] ?? 'Unknown operation';
  }
  
  /**
   * Calculate cleanup summary
   */
  private static calculateSummary(operations: CleanupOperation[]): BatchCleanupResult['summary'] {
    return {
      trucksImproved: operations.reduce((sum, op) => sum + op.successCount, 0),
      duplicatesRemoved: operations.find(op => op.type === 'merge_duplicates')?.successCount ?? 0,
      qualityScoreImprovement: operations.find(op => op.type === 'update_quality_scores')?.successCount ?? 0,
      placeholdersRemoved: operations.find(op => op.type === 'remove_placeholders')?.successCount ?? 0
    };
  }
}

import { APIUsageService } from './supabase';
import { APIMonitor } from './monitoring/apiMonitor';
import { GeminiApiClient } from './gemini/geminiApiClient';
import { GeminiUsageLimits } from './gemini/usageLimits';
import { PromptTemplates } from './gemini/promptTemplates';
import { GeminiResponseParser } from './gemini/responseParser';

import {
  MenuCategory,
  LocationData,
  OperatingHours,
  SentimentAnalysisResult,
  EnhancedFoodTruckData,
  ExtractedFoodTruckDetails,
  GeminiResponse,
} from './types';

export class GeminiService {
  private apiClient: GeminiApiClient;
  private dailyRequestLimit = 1500;
  private dailyTokenLimit = 32_000;

  constructor() {
    this.apiClient = new GeminiApiClient();
  }

  async checkUsageLimits() {
    return GeminiUsageLimits.checkUsageLimits({
      dailyRequestLimit: this.dailyRequestLimit,
      dailyTokenLimit: this.dailyTokenLimit,
    });
  }

  private async makeGeminiRequest<T>(
    prompt: string,
    parser: (text: string) => T,
    errorContext: string
  ): Promise<GeminiResponse<T>> {
    return this.apiClient.makeRequestWithParsing(prompt, parser);
  }

  async processMenuData(rawMenuText: string): Promise<GeminiResponse<MenuCategory[]>> {
    const estimatedTokens = Math.ceil(rawMenuText.length / 4) + 500;
    const usageCheck = await GeminiUsageLimits.checkWithMonitoring(estimatedTokens);

    if (!usageCheck.allowed) {
      return {
        success: false,
        error: usageCheck.reason ?? 'API limit reached',
      };
    }

    const prompt = PromptTemplates.menuProcessing(rawMenuText);
    return this.makeGeminiRequest(
      prompt,
      (text: string) => {
        const parsedData = JSON.parse(text) as { categories: MenuCategory[] };
        return parsedData.categories;
      },
      'menu processing'
    );
  }

  async extractLocationFromText(textInput: string): Promise<GeminiResponse<LocationData>> {
    const usageCheck = await this.checkUsageLimits();
    if (!usageCheck.canMakeRequest) {
      return {
        success: false,
        error: 'Daily API limits exceeded',
      };
    }

    const prompt = PromptTemplates.locationExtraction(textInput);
    return this.makeGeminiRequest(
      prompt,
      GeminiResponseParser.parseLocationData,
      'location extraction'
    );
  }

  async standardizeOperatingHours(hoursText: string): Promise<GeminiResponse<OperatingHours>> {
    const usageCheck = await this.checkUsageLimits();
    if (!usageCheck.canMakeRequest) {
      return {
        success: false,
        error: 'Daily API limits exceeded',
      };
    }

    const prompt = PromptTemplates.operatingHours(hoursText);
    return this.makeGeminiRequest(
      prompt,
      GeminiResponseParser.parseOperatingHours,
      'hours standardization'
    );
  }

  async analyzeSentiment(reviewText: string): Promise<GeminiResponse<SentimentAnalysisResult>> {
    const usageCheck = await this.checkUsageLimits();
    if (!usageCheck.canMakeRequest) {
      return {
        success: false,
        error: 'Daily API limits exceeded',
      };
    }

    const prompt = PromptTemplates.sentimentAnalysis(reviewText);
    return this.makeGeminiRequest(
      prompt,
      GeminiResponseParser.parseSentimentAnalysis,
      'sentiment analysis'
    );
  }

  async enhanceFoodTruckData(rawData: unknown): Promise<GeminiResponse<EnhancedFoodTruckData>> {
    const usageCheck = await this.checkUsageLimits();
    if (!usageCheck.canMakeRequest) {
      return {
        success: false,
        error: 'Daily API limits exceeded',
      };
    }

    const prompt = PromptTemplates.dataEnhancement(rawData);
    return this.makeGeminiRequest(
      prompt,
      GeminiResponseParser.parseEnhancedFoodTruckData,
      'data enhancement'
    );
  }

  async batchProcess(
    items: Array<{ type: string; data: unknown }>,
  ): Promise<Array<GeminiResponse<unknown>>> {
    const results: Array<GeminiResponse<unknown>> = [];

    for (const item of items) {
      let result: GeminiResponse<unknown>;
      switch (item.type) {
        case 'menu': {
          result = await this.processMenuData(item.data as string);
          break;
        }
        case 'location': {
          result = await this.extractLocationFromText(item.data as string);
          break;
        }
        case 'hours': {
          result = await this.standardizeOperatingHours(item.data as string);
          break;
        }
        case 'sentiment': {
          result = await this.analyzeSentiment(item.data as string);
          break;
        }
        case 'enhance': {
          result = await this.enhanceFoodTruckData(item.data); // item.data is already unknown
          break;
        }
        default: {
          result = { success: false, error: `Unknown processing type: ${item.type}` };
          break;
        }
      }
      results.push(result);
    }

    return results;
  }

  async getUsageStats(): Promise<{ requests_count: number; tokens_used: number } | undefined> {
    const usage = await APIUsageService.getTodayUsage('gemini');
    return usage ?? undefined;
  }

  async extractFoodTruckDetailsFromMarkdown(
    markdownContent: string,
    sourceUrl?: string,
  ): Promise<GeminiResponse<ExtractedFoodTruckDetails>> {
    const usageCheck = await this.checkUsageLimits();
    if (!usageCheck.canMakeRequest) {
      return {
        success: false,
        error: 'Daily API limits exceeded for Gemini',
      };
    }

    const prompt = PromptTemplates.foodTruckExtraction(markdownContent, sourceUrl);


    const response = await this.makeGeminiRequest(
      prompt,
      (text: string) => {
        const cleanedText = GeminiResponseParser.cleanMarkdownResponse(text);
        return GeminiResponseParser.parseExtractedFoodTruckDetails(cleanedText);
      },
      'food truck extraction'
    );

    // Add promptSent to response for this specific method
    return {
      ...response,
      promptSent: prompt,
    };
  }
}

// Export singleton instance
export const gemini = new GeminiService();

// Centralized Gemini operation dispatcher to eliminate duplication
export async function dispatchGeminiOperation(
  type: 'menu' | 'location' | 'hours' | 'sentiment' | 'enhance',
  data: unknown
): Promise<unknown> {
  switch (type) {
    case 'menu': {
      return await gemini.processMenuData(data as string);
    }
    case 'location': {
      return await gemini.extractLocationFromText(data as string);
    }
    case 'hours': {
      return await gemini.standardizeOperatingHours(data as string);
    }
    case 'sentiment': {
      return await gemini.analyzeSentiment(data as string);
    }
    case 'enhance': {
      return await gemini.enhanceFoodTruckData(data);
    }
    default: {
      throw new Error(`Unknown Gemini operation type: ${String(type)}`);
    }
  }
}

import { APIUsageService } from '../supabase';
import { APIMonitor } from '../monitoring/apiMonitor';

export interface UsageLimits {
  dailyRequestLimit: number;
  dailyTokenLimit: number;
}

export interface UsageCheck {
  canMakeRequest: boolean;
  usage?: {
    requests: { used: number; limit: number; remaining: number };
    tokens: { used: number; limit: number; remaining: number };
  };
}

export class GeminiUsageLimits {
  private static readonly DEFAULT_LIMITS: UsageLimits = {
    dailyRequestLimit: 1500,
    dailyTokenLimit: 32_000,
  };

  static async checkUsageLimits(limits: UsageLimits = this.DEFAULT_LIMITS): Promise<UsageCheck> {
    try {
      const usage = await APIUsageService.getTodayUsage('gemini');

      if (!usage) {
        return { canMakeRequest: true };
      }

      const requestsUsed = usage.requests_count ?? 0;
      const tokensUsed = usage.tokens_used ?? 0;

      const requestsRemaining = limits.dailyRequestLimit - requestsUsed;
      const tokensRemaining = limits.dailyTokenLimit - tokensUsed;

      return {
        canMakeRequest: requestsRemaining > 0 && tokensRemaining > 100, // Keep 100 token buffer
        usage: {
          requests: {
            used: requestsUsed,
            limit: limits.dailyRequestLimit,
            remaining: requestsRemaining,
          },
          tokens: {
            used: tokensUsed,
            limit: limits.dailyTokenLimit,
            remaining: tokensRemaining,
          },
        },
      };
    } catch (error: unknown) {
      console.warn('Error checking Gemini usage limits:', error);
      return { canMakeRequest: false };
    }
  }

  static async checkWithMonitoring(
    estimatedTokens: number,
    limits: UsageLimits = this.DEFAULT_LIMITS
  ): Promise<{ allowed: boolean; reason?: string; waitTime?: number }> {
    const canMakeRequest = await APIMonitor.canMakeRequest('gemini', 1, estimatedTokens);

    if (!canMakeRequest.allowed) {
      return {
        allowed: false,
        reason: `API limit reached: ${canMakeRequest.reason}. Wait time: ${Math.ceil((canMakeRequest.waitTime ?? 0) / 1000 / 60)} minutes`,
      };
    }

    return { allowed: true };
  }
}

/**
 * SOTA Database Query Optimization and Caching
 * Implements Next.js unstable_cache for optimal performance
 */

import { unstable_cache } from 'next/cache';
import { supabaseAdmin, FoodTruckService } from '@/lib/supabase';
import type { FoodTruck } from '@/lib/types';

// Cache configuration
const CACHE_CONFIG = {
  // Short-term cache for frequently changing data
  SHORT_TTL: 60 * 5, // 5 minutes
  // Medium-term cache for semi-static data
  MEDIUM_TTL: 60 * 30, // 30 minutes
  // Long-term cache for static data
  LONG_TTL: 60 * 60 * 24, // 24 hours
} as const;

/**
 * Cached food truck queries with optimized database access
 */
export const CachedFoodTruckService = {
  /**
   * Get all food trucks with caching
   * Cache for 30 minutes since truck data changes moderately
   */
  getAllTrucksCached : unstable_cache(
    async (): Promise<{ trucks: FoodTruck[]; count: number }> => {
      console.info('CachedFoodTruckService: Cache miss - fetching all trucks from database');
      const result = await FoodTruckService.getAllTrucks();
      return { trucks: result.trucks, count: result.total };
    },
    ['all-trucks'],
    {
      revalidate: CACHE_CONFIG.MEDIUM_TTL,
      tags: ['food-trucks', 'all-trucks']
    }
  ),

  /**
   * Get trucks by location with caching
   * Cache for 5 minutes since location-based queries are time-sensitive
   */
  getTrucksByLocationCached : unstable_cache(
    async (lat: number, lng: number, radiusKm: number): Promise<FoodTruck[]> => {
      console.info(`CachedFoodTruckService: Cache miss - fetching trucks near ${lat},${lng} (${radiusKm}km)`);
      return await FoodTruckService.getTrucksByLocation(lat, lng, radiusKm);
    },
    ['trucks-by-location'],
    {
      revalidate: CACHE_CONFIG.SHORT_TTL,
      tags: ['food-trucks', 'location-search']
    }
  ),

  /**
   * Get truck by ID with caching
   * Cache for 30 minutes since individual truck data is relatively stable
   */
  getTruckByIdCached : unstable_cache(
    async (id: string): Promise<FoodTruck | null> => {
      console.info(`CachedFoodTruckService: Cache miss - fetching truck ${id} from database`);
      return await FoodTruckService.getTruckById(id);
    },
    ['truck-by-id'],
    {
      revalidate: CACHE_CONFIG.MEDIUM_TTL,
      tags: ['food-trucks', 'truck-details']
    }
  ),

  /**
   * Search trucks with caching
   * Cache for 5 minutes since search results should be relatively fresh
   */
  searchTrucksCached : unstable_cache(
    async (query: string, filters?: {
      cuisine?: string;
      openNow?: boolean;
      lat?: number;
      lng?: number;
      radius?: number;
    }): Promise<FoodTruck[]> => {
      console.info(`CachedFoodTruckService: Cache miss - searching trucks for "${query}"`);
      
      if (supabaseAdmin == undefined) {
        throw new Error('Supabase admin client not available');
      }

      let dbQuery = supabaseAdmin
        .from('food_trucks')
        .select('*')
        .or(`name.ilike.%${query}%,description.ilike.%${query}%,cuisine_type.cs.{${query}}`);

      // Apply filters
      if (filters?.cuisine != undefined && filters.cuisine !== '') {
        dbQuery = dbQuery.contains('cuisine_type', [filters.cuisine]);
      }

      const { data: trucks, error } = await dbQuery.limit(50);

      if (error != undefined) {
        throw new Error(`Search query failed: ${error.message}`);
      }

      let results = trucks ?? [];

      // Apply location filter if provided
      if (filters?.lat != undefined && filters?.lng != undefined && filters?.radius != undefined) {
        results = results.filter((truck: FoodTruck) => {
          if (truck.current_location?.lat == undefined || truck.current_location?.lng == undefined) {
            return false;
          }
          const distance = calculateDistance(
            filters.lat,
            filters.lng,
            truck.current_location.lat,
            truck.current_location.lng
          );
          return distance <= (filters.radius ?? 10);
        });
      }

      // Apply openNow filter if provided
      if (filters?.openNow === true) {
        const now = new Date();
        const currentDay = now.toLocaleDateString('en-US', { weekday: 'short' }).toLowerCase(); // 'mon', 'tue', etc.
        const currentTime = now.getHours() * 100 + now.getMinutes(); // HHMM format

        results = results.filter((truck: FoodTruck) => {
          const hours = truck.operating_hours?.[currentDay as keyof typeof truck.operating_hours] as { closed?: boolean; open?: string; close?: string } | undefined;
          if (hours == undefined || hours.closed === true) return false;

          const openTime = parseTimeString(hours.open ?? '');
          const closeTime = parseTimeString(hours.close ?? '');

          return currentTime >= openTime && currentTime <= closeTime;
        });
      }

      return results;
    },
    ['search-trucks'],
    {
      revalidate: CACHE_CONFIG.SHORT_TTL,
      tags: ['food-trucks', 'search']
    }
  ),

  /**
   * Get data quality statistics with caching
   * Cache for 24 hours since quality stats change slowly
   */
  getDataQualityStatsCached : unstable_cache(
    async (): Promise<{
      averageScore: number;
      distribution: Record<string, number>;
      totalTrucks: number;
    }> => {
      console.info('CachedFoodTruckService: Cache miss - calculating data quality stats');
      
      if (supabaseAdmin == undefined) {
        throw new Error('Supabase admin client not available');
      }

      const { data: trucks, error } = await supabaseAdmin
        .from('food_trucks')
        .select('data_quality_score');

      if (error) {
        throw new Error(`Quality stats query failed: ${error.message}`);
      }

      const scores = trucks == undefined ? [] : trucks.map((t: { data_quality_score?: number }) => t.data_quality_score ?? 0);
      const averageScore = scores.length > 0
        ? scores.reduce((sum: number, score: number) => sum + score, 0) / scores.length
        : 0;

      const distribution = {
        high: scores.filter((s: number) => s >= 0.8).length,
        medium: scores.filter((s: number) => s >= 0.6 && s < 0.8).length,
        low: scores.filter((s: number) => s < 0.6).length
      };

      return {
        averageScore: Math.round(averageScore * 100) / 100,
        distribution,
        totalTrucks: scores.length
      };
    },
    ['data-quality-stats'],
    {
      revalidate: CACHE_CONFIG.LONG_TTL,
      tags: ['food-trucks', 'data-quality']
    }
  ),
};

/**
 * Cache invalidation utilities
 */
export const CacheManager = {
  /**
   * Invalidate all food truck related caches
   */
  async invalidateAllFoodTruckCaches(): Promise<void> {
    try {
      const { revalidateTag } = await import('next/cache');
      revalidateTag('food-trucks');
      console.info('CacheManager: Invalidated all food truck caches');
    } catch (error) {
      console.warn('CacheManager: Failed to invalidate caches:', error);
    }
  },

  /**
   * Invalidate specific truck cache
   */
  async invalidateTruckCache(truckId: string): Promise<void> {
