export const APIUsageService = {
  async trackUsage(serviceName: string, requests: number, tokens: number): Promise<ApiUsage> {
    if (!supabaseAdmin) {
      throw new Error('Admin operations require SUPABASE_SERVICE_ROLE_KEY');
    }

    try {
      const today = new Date().toISOString().split('T')[0];

      const { data: existing }: PostgrestSingleResponse<ApiUsage> = await supabaseAdmin
        .from('api_usage')
        .select('*')
        .eq('service_name', serviceName)
        .eq('usage_date', today)
        .single();
      if (existing) {
        const { data, error }: PostgrestSingleResponse<ApiUsage> = await supabaseAdmin
          .from('api_usage')
          .update({
            requests_count: (existing.requests_count ?? 0) + requests,
            tokens_used: (existing.tokens_used ?? 0) + tokens,
          })
          .eq('id', existing.id)
          .select()
          .single();

        if (error) throw error;
        return data;
      } else {
        const { data, error }: PostgrestSingleResponse<ApiUsage> = await supabaseAdmin
          .from('api_usage')
          .insert([
            {
              service_name: serviceName,
              usage_date: today,
              requests_count: requests,
              tokens_used: tokens,
            },
          ])
          .select()
          .single();

        if (error) throw error;
        return data;
      }
    } catch (error: unknown) {
      console.warn('Error tracking usage:', error);
      throw error;
    }
  },
  async getTodayUsage(serviceName: string): Promise<ApiUsage | undefined> {
    try {
      const today = new Date().toISOString().split('T')[0];

      const { data, error }: PostgrestSingleResponse<ApiUsage> = await supabase
        .from('api_usage')
        .select('*')
        .eq('service_name', serviceName)
        .eq('usage_date', today)
        .single();

      if (error && error.code !== 'PGRST116') throw error;
      return data ?? undefined;
    } catch (error: unknown) {
      console.warn('Error getting today usage:', error);
      throw error;
    }
  },

  async getAllUsageStats(): Promise<ApiUsage[]> {
    try {
      const { data, error }: PostgrestResponse<ApiUsage> = await supabase
        .from('api_usage')
        .select('*')
        .order('usage_date', { ascending: false })
        .limit(30);

      if (error) throw error;
      return data ?? [];
    } catch (error: unknown) {
      console.warn('Error getting usage stats:', error);
      throw error;
    }
  },
};

export { type MenuItem, type MenuCategory, type OperatingHours, type PriceRange } from './types';

// Helper to prepare menu items for DB insertion
function prepareMenuItemsForInsert(truckId: string, menuData: MenuCategory[] | undefined) {
  if (!menuData || menuData.length === 0) return [];
  return menuData.flatMap((category) =>
    (category.items ?? []).map((item) => ({
      food_truck_id: truckId,
      category: category.name ?? 'Uncategorized',
      name: item.name ?? 'Unknown Item',
      description: item.description ?? undefined,
      price: typeof item.price === 'number' ? item.price : undefined,
      dietary_tags: item.dietary_tags ?? [],
    }))
  );
}

async function insertMenuItems(truckId: string, menuData: MenuCategory[] | undefined) {
  const menuItems = prepareMenuItemsForInsert(truckId, menuData);
  if (menuItems.length === 0) return;
  const { error: menuError } = await supabaseAdmin!.from('menu_items').insert(menuItems);
  if (menuError) {
    console.error('Error inserting menu items for truck', truckId, menuError);
  }
}

import { FoodTruck, TrucksApiResponse } from '@/lib/types/foodTruck';

export const getCurrentDay = () => {
  const days = ['sunday', 'monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday'];
  return days[new Date().getDay()];
};

export const formatPrice = (price: number) => {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
  }).format(price);
};

// Get user's current location or default to San Francisco
export function getUserLocationHelper(
  setUserLocation: (location: { lat: number; lng: number }) => void
) {
  if (typeof navigator !== 'undefined' && navigator.geolocation !== undefined) {
    // eslint-disable-next-line sonarjs/no-intrusive-permissions -- Geolocation is essential for finding nearby food trucks
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setUserLocation({
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        });
      },
      (error) => {
        console.warn('Location access denied:', error);
        // Default to San Francisco
        setUserLocation({ lat: 37.7749, lng: -122.4194 });
      },
    );
  } else {
    // Default to San Francisco if geolocation is not supported
    setUserLocation({ lat: 37.7749, lng: -122.4194 });
  }
}

// Load all food trucks from API
export async function loadFoodTrucksHelper(
  setTrucks: (trucks: FoodTruck[]) => void,
  setLoading: (loading: boolean) => void
) {
  try {
    const response = await fetch('/api/trucks');
    const data: unknown = await response.json();
    if (typeof data === 'object' && data !== null && 'trucks' in data && Array.isArray(data.trucks)) {
      setTrucks(data.trucks as FoodTruck[]);
    } else {
      setTrucks([]);
    }
  } catch (error: unknown) {
    console.error('Failed to load food trucks:', error);
  } finally {
    setLoading(false);
  }
}

// Load nearby food trucks based on user location
export async function loadNearbyTrucksHelper(
  userLocation: { lat: number; lng: number } | undefined,
  setTrucks: (trucks: FoodTruck[]) => void
) {
  if (!userLocation) return;

  try {
    const response = await fetch(
      `/api/trucks?lat=${userLocation.lat}&lng=${userLocation.lng}&radius=10`,
    );
    const data: unknown = await response.json();
    if (typeof data === 'object' && data !== null && 'trucks' in data && Array.isArray(data.trucks)) {
      setTrucks(data.trucks as FoodTruck[]);
    } else {
      setTrucks([]);
    }
  } catch (error: unknown) {
    console.error('Failed to load nearby trucks:', error);
  }
}

// Check if a food truck is currently open
export function isTruckOpen(truck: FoodTruck): boolean {
  const today = getCurrentDay();
  const hours = truck.operating_hours?.[today];

  // Ensure hours and its properties are not null/undefined before accessing
  if (hours === undefined || hours.closed === true || hours.open === undefined || hours.close === undefined) {
    return false;
  }

  try {
    const now = new Date();
    const currentTime = now.getHours() * 100 + now.getMinutes();
    const openTime = Number.parseInt(hours.open.replace(':', ''));
    const closeTime = Number.parseInt(hours.close.replace(':', ''));

    return currentTime >= openTime && currentTime <= closeTime;
  } catch (error: unknown) {
    console.error('Error parsing operating hours for truck', truck.name, error);
    return false;
  }
}

/**
 * SOTA Quality Scoring Utilities
 * Centralized quality scoring functions to eliminate duplication
 * and provide consistent quality metrics across the application
 */

import { isNonEmptyString, isValidNumber, safeNumber, safeString } from './typeGuards';

// Quality score weights (must sum to 1.0)
export const QUALITY_WEIGHTS = {
  CORE_DATA: 0.35,      // 35% - name, description, location
  LOCATION_DATA: 0.25,  // 25% - coordinates, address
  CONTACT_DATA: 0.2,    // 20% - phone, email, website
  MENU_DATA: 0.1,       // 10% - menu items, pricing
  OPERATIONAL_DATA: 0.05, // 5% - hours, social media
  VERIFICATION: 0.05    // 5% - verification status
} as const;

// Quality thresholds
export const QUALITY_THRESHOLDS = {
  HIGH: 0.8,    // 80%+
  MEDIUM: 0.6,  // 60-79%
  LOW: 0       // <60%
} as const;

// Core data quality scoring
export function scoreCoreData(truck: Record<string, unknown>): number {
  let score = 0;
  let maxScore = 0;

  // Name (required - 40% of core score)
  maxScore += 0.4;
  if (isNonEmptyString(truck.name)) {
    score += 0.4;
  }

  // Description (30% of core score)
  maxScore += 0.3;
  if (isNonEmptyString(truck.description)) {
    const desc = safeString(truck.description);
    if (desc.length > 50) {
      score += 0.3;
    } else if (desc.length > 20) {
      score += 0.15;
    }
  }

  // Cuisine type (20% of core score)
  maxScore += 0.2;
  if (isNonEmptyString(truck.cuisine_type)) {
    score += 0.2;
  }

  // Image URL (10% of core score)
  maxScore += 0.1;
  if (isNonEmptyString(truck.image_url)) {
    score += 0.1;
  }

  return maxScore > 0 ? score / maxScore : 0;
}

// Location data quality scoring
export function scoreLocationData(truck: Record<string, unknown>): number {
  let score = 0;
  let maxScore = 0;

  // Coordinates (50% of location score)
  maxScore += 0.5;
  const latitude = safeNumber(truck.latitude);
  const longitude = safeNumber(truck.longitude);
  if (isValidNumber(latitude) && isValidNumber(longitude) && 
      latitude !== 0 && longitude !== 0) {
    score += 0.5;
  }

  // Address (30% of location score)
  maxScore += 0.3;
  if (isNonEmptyString(truck.address)) {
    score += 0.3;
  }

  // City/State (20% of location score)
  maxScore += 0.2;
  if (isNonEmptyString(truck.city) || isNonEmptyString(truck.state)) {
    score += 0.2;
  }

  return maxScore > 0 ? score / maxScore : 0;
}

// Contact data quality scoring
export function scoreContactData(truck: Record<string, unknown>): number {
  let score = 0;
  let maxScore = 0;

  // Phone (40% of contact score)
  maxScore += 0.4;
  if (isNonEmptyString(truck.phone)) {
    const phone = safeString(truck.phone);
    // Basic phone validation - simplified to avoid backtracking
    if (/^\+?[\d\s\-()]{10,}$/.test(phone)) {
      score += 0.4;
    } else if (phone.length > 5) {
      score += 0.2;
    }
  }

  // Email (30% of contact score)
  maxScore += 0.3;
  if (isNonEmptyString(truck.email)) {
    const email = safeString(truck.email);
    // Basic email validation
    if (/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      score += 0.3;
    }
  }

  // Website (30% of contact score)
  maxScore += 0.3;
  if (isNonEmptyString(truck.website)) {
    const website = safeString(truck.website);
    // Basic URL validation
    if (/^https?:\/\/.+\..+/.test(website)) {
      score += 0.3;
    }
  }

  return maxScore > 0 ? score / maxScore : 0;
}

// Menu data quality scoring
export function scoreMenuData(truck: Record<string, unknown>): number {
  let score = 0;
  let maxScore = 0;

  // Menu items (70% of menu score)
  maxScore += 0.7;
  if (isNonEmptyString(truck.menu_items)) {
    const menuItems = safeString(truck.menu_items);
    if (menuItems.length > 100) {
      score += 0.7;
    } else if (menuItems.length > 20) {
      score += 0.35;
    }
  }

  // Price range (30% of menu score)
  maxScore += 0.3;
  if (isNonEmptyString(truck.price_range)) {
    score += 0.3;
  }

  return maxScore > 0 ? score / maxScore : 0;
}

// Operational data quality scoring
export function scoreOperationalData(truck: Record<string, unknown>): number {
  let score = 0;
  let maxScore = 0;

  // Operating hours (60% of operational score)
  maxScore += 0.6;
  if (isNonEmptyString(truck.operating_hours)) {
    score += 0.6;
  }

  // Social media (40% of operational score)
  maxScore += 0.4;
  const socialFields = ['facebook_url', 'twitter_url', 'instagram_url'];
  const socialCount = socialFields.filter(field => isNonEmptyString(truck[field])).length;
  score += (socialCount / socialFields.length) * 0.4;

  return maxScore > 0 ? score / maxScore : 0;
}

// Verification status scoring
export function scoreVerificationStatus(truck: Record<string, unknown>): number {
  const status = safeString(truck.verification_status);
  switch (status) {
    case 'verified': {
      return 1;
    }
    case 'pending': {
      return 0.5;
    }
    default: {
      return 0;
    }
  }
}

// Calculate overall quality score
export function calculateOverallQualityScore(truck: Record<string, unknown>): number {
  const coreScore = scoreCoreData(truck);
  const locationScore = scoreLocationData(truck);
  const contactScore = scoreContactData(truck);
  const menuScore = scoreMenuData(truck);
  const operationalScore = scoreOperationalData(truck);
  const verificationScore = scoreVerificationStatus(truck);

  return (
    coreScore * QUALITY_WEIGHTS.CORE_DATA +
    locationScore * QUALITY_WEIGHTS.LOCATION_DATA +
    contactScore * QUALITY_WEIGHTS.CONTACT_DATA +
    menuScore * QUALITY_WEIGHTS.MENU_DATA +
    operationalScore * QUALITY_WEIGHTS.OPERATIONAL_DATA +
    verificationScore * QUALITY_WEIGHTS.VERIFICATION
  );
}

// Get quality category
export function getQualityCategory(score: number): 'high' | 'medium' | 'low' {
  if (score >= QUALITY_THRESHOLDS.HIGH) return 'high';
  if (score >= QUALITY_THRESHOLDS.MEDIUM) return 'medium';
  return 'low';
}

// Get quality label
export function getQualityLabel(score: number): string {
  const category = getQualityCategory(score);
  switch (category) {
    case 'high': {
      return 'Excellent';
    }
    case 'medium': {
      return 'Good';
    }
    case 'low': {
      return 'Needs Work';
    }
  }
}

// Get quality color
export function getQualityColor(score: number): string {
  const category = getQualityCategory(score);
  switch (category) {
    case 'high': {
      return '#22c55e';
    }
    case 'medium': {
      return '#f59e0b';
    }
    case 'low': {
      return '#ef4444';
    }
  }
}
