import { createRequire } from 'module';
const require = createRequire(import.meta.url);

const globals = require('globals'); // Use require for globals
import tseslint from "typescript-eslint";
import sonarjs from "eslint-plugin-sonarjs";
import unicorn from "eslint-plugin-unicorn";
import nextPlugin from "@next/eslint-plugin-next";
import eslintConfigPrettier from "eslint-config-prettier";

export default tseslint.config(
  // Global ignores - PREVENTION-FOCUSED
  {
    ignores: [
      ".next/",
      "node_modules/",
      "build/",
      "dist/",
      "public/",
      "scripts/", // Ignore scripts directory with JS files
      "lib/database.types.ts", // Ignore auto-generated Supabase types file
      // Temporarily ignore test files while focusing on main application code
      "**/*.test.ts",
      "**/*.test.tsx",
      "tests/**/*",
      "lib/**/*.test.ts",
      "playwright.config.*.ts",
      "jest.config.ts",
      "setup-tests.*",
      "test-*.js",
      // Configuration files
      "**/*.config.js",
      "**/*.config.mjs",
      "**/*.config.ts",
      "coverage/",
      // Legacy files that cause parsing errors
      "setup-tests.cjs",
      "setup-tests.js",
      "test-enhanced-pipeline-api.js",
      "test-enhanced-pipeline.js",
      "test-pipeline-simple.js",
      "playwright.config.test.ts",
      // Quality gate reports
      "eslint-*.json",
      "quality-report.json",
    ],
  },

  // Base ESLint recommended rules
  tseslint.configs.base, // More foundational than eslint.configs.recommended for tseslint.config
  
  // TypeScript configurations
  ...tseslint.configs.recommendedTypeChecked, // Includes recommended and recommended-requiring-type-checking
  {
    languageOptions: {
      parserOptions: {
        project: ["./tsconfig.json"], // Adjusted to match old config
        tsconfigRootDir: import.meta.dirname,
        ecmaFeatures: { jsx: true }, // From old config
      },
    },
  },

  // Next.js specific configurations
  // These replace 'next/core-web-vitals'
  {
    plugins: {
      "@next/next": nextPlugin,
    },
    rules: {
      ...nextPlugin.configs.recommended.rules,
      ...nextPlugin.configs["core-web-vitals"].rules,
    },
    languageOptions: { // Next.js specific globals
        globals: {
            ...globals.browser,
            React: "readonly", // Example, Next.js preset handles this
        }
    }
  },
  
  // SonarJS recommended rules
  sonarjs.configs.recommended,

  // Unicorn recommended rules
  unicorn.configs.recommended,

  // PREVENTION-FOCUSED RULES CONFIGURATION
  {
    rules: {
      // CRITICAL: Type Safety Prevention (60% of errors)
      "@typescript-eslint/no-unsafe-assignment": "error",
      "@typescript-eslint/no-unsafe-call": "error",
      "@typescript-eslint/no-unsafe-member-access": "error",
      "@typescript-eslint/no-unsafe-argument": "error",
      "@typescript-eslint/no-unsafe-return": "error",
      "@typescript-eslint/no-explicit-any": "error",
      "@typescript-eslint/strict-boolean-expressions": "error",
      "@typescript-eslint/prefer-nullish-coalescing": "error",
      "@typescript-eslint/prefer-optional-chain": "error",

      // CRITICAL: Complexity Prevention (25% of errors)
      "sonarjs/cognitive-complexity": ["error", 15],
      "sonarjs/no-nested-conditional": "error",
      "max-lines-per-function": ["error", 50],
      "max-depth": ["error", 4],
      "max-params": ["error", 4],

      // CRITICAL: Consistency Prevention (10% of errors)
      "unicorn/no-null": "error",
      "@typescript-eslint/no-unused-vars": ["error", { "argsIgnorePattern": "^_" }],
      "@typescript-eslint/require-await": "error",

      // IMPORTANT: Code Quality Prevention
      "sonarjs/no-dead-store": "error",
      "sonarjs/prefer-read-only-props": "error",
      "sonarjs/void-use": "error",

      // Filename case configuration
      "unicorn/prevent-abbreviations": "off",
      "unicorn/filename-case": [
        "error",
        {
          "cases": {
            "camelCase": true,
            "pascalCase": true,
          },
          "ignore": [
            /^README\.md$/,
            /^next-env\.d\.ts$/,
            /^postcss\.config\.mjs$/,
            /^tailwind\.config\.ts$/,
            /^next\.config\.mjs$/,
            /^jest\.config\.ts$/,
            /^CODEBASE_RULES\.md$/,
            /^DASHBOARD_PLAN\.md$/,
            /^RISKS\.md$/,
            /^WBS_CHECKLIST\.md$/,
            /^LINTING_PREVENTION_FRAMEWORK\.md$/,
            /^SYSTEMATIC_ERROR_PREVENTION\.md$/
          ],
        },
      ],
      "@typescript-eslint/no-unused-vars": ["warn", { "argsIgnorePattern": "^_" }],
      "@typescript-eslint/explicit-module-boundary-types": "off",
      "no-console": ["warn", { "allow": ["warn", "error", "info"] }],
    },
  },

  // Prettier - must be last to override other formatting rules
  eslintConfigPrettier
);

'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { RefreshCw, Play, AlertCircle, CheckCircle, Clock } from 'lucide-react';
import { Alert, AlertDescription } from '@/components/ui/alert';

interface CronJobStatus {
  id: string;
  name: string;
  schedule: string;
  lastRun: string | null;
  nextRun: string | null;
  status: 'running' | 'idle' | 'error';
  lastResult?: {
    success: boolean;
    message: string;
    trucksProcessed?: number;
    newTrucksFound?: number;
    errors?: number;
  };
}

interface ScrapingMetrics {
  totalRuns: number;
  successfulRuns: number;
  failedRuns: number;
  averageRunTime: number;
  totalTrucksProcessed: number;
  newTrucksToday: number;
}

export default function AutoScrapingDashboard() {
  const [cronJobs, setCronJobs] = useState<CronJobStatus[]>([]);
  const [metrics, setMetrics] = useState<ScrapingMetrics | undefined>();
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string>();

  useEffect(() => {
    void fetchDashboardData();
    // Refresh every 30 seconds
    const interval = setInterval(() => {
      void fetchDashboardData();
    }, 30_000);
    return () => clearInterval(interval);
  }, []);

  const fetchDashboardData = async () => {
    try {
      setIsLoading(true);
      setError(undefined);
      // Fetch cron job statuses
      const cronResponse = await fetch('/api/admin/cron-status');
      if (!cronResponse.ok) {
        throw new Error('Failed to fetch cron status');
      }
      const cronData = (await cronResponse.json()) as unknown;
      setCronJobs(
        Array.isArray((cronData as { jobs?: unknown }).jobs)
          ? (cronData as { jobs: CronJobStatus[] }).jobs
          : [],
      );
      // Fetch scraping metrics
      const metricsResponse = await fetch('/api/admin/scraping-metrics');
      if (!metricsResponse.ok) {
        throw new Error('Failed to fetch metrics');
      }
      const metricsData = (await metricsResponse.json()) as unknown;
      setMetrics((metricsData as { metrics?: ScrapingMetrics }).metrics);
    } catch (error_) {
      setError(error_ instanceof Error ? error_.message : 'Unknown error');
    } finally {
      setIsLoading(false);
    }
  };

  const triggerManualScrape = async () => {
    try {
      setError(undefined);
      const response = await fetch('/api/auto-scrape-initiate', {
        method: 'POST',
      });
      if (!response.ok) {
        throw new Error('Failed to trigger manual scrape');
      }
      setTimeout(() => {
        void fetchDashboardData();
      }, 2000);
    } catch (error_) {
      setError(error_ instanceof Error ? error_.message : 'Failed to trigger scrape');
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'running': {
        return <RefreshCw className="h-4 w-4 animate-spin text-blue-500" />;
      }
      case 'error': {
        return <AlertCircle className="h-4 w-4 text-red-500" />;
      }
      default: {
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      }
    }
  };

  const getStatusBadge = (status: string) => {
    const variants: Record<string, 'default' | 'secondary' | 'destructive'> = {
      running: 'default',
      idle: 'secondary',
      error: 'destructive',
    };
    return <Badge variant={variants[status] ?? 'secondary'}>{status}</Badge>;
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <h1 className="text-3xl font-bold">Auto-Scraping Dashboard</h1>
        </div>
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
          {Array.from({ length: 4 }).map((_, i) => (
            <Card key={i}>
              <CardHeader className="animate-pulse">
                <div className="h-4 bg-gray-200 rounded w-3/4"></div>
                <div className="h-8 bg-gray-200 rounded w-1/2"></div>
              </CardHeader>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-3xl font-bold">Auto-Scraping Dashboard</h1>
        <div className="flex gap-2">
          <Button
            onClick={() => {
              void fetchDashboardData();
            }}
            variant="outline"
            size="sm"
          >
            <RefreshCw className="h-4 w-4 mr-2" />
            Refresh
          </Button>
          <Button
            onClick={() => {
              void triggerManualScrape();
            }}
            size="sm"
          >
            <Play className="h-4 w-4 mr-2" />
            Trigger Manual Scrape
          </Button>
        </div>
      </div>

      {error != undefined && (
        <Alert variant="destructive">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      {/* Metrics Cards */}
      {metrics != undefined && (
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Runs</CardTitle>
              <RefreshCw className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{metrics.totalRuns}</div>
              <p className="text-xs text-muted-foreground">{metrics.successfulRuns} successful</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Success Rate</CardTitle>
              <CheckCircle className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">
                {metrics.totalRuns > 0
                  ? Math.round((metrics.successfulRuns / metrics.totalRuns) * 100)
                  : 0}
                %
              </div>
              <p className="text-xs text-muted-foreground">{metrics.failedRuns} failed runs</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Trucks Processed</CardTitle>
              <Clock className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{metrics.totalTrucksProcessed}</div>
              <p className="text-xs text-muted-foreground">Total trucks processed</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">New Today</CardTitle>
              <Play className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{metrics.newTrucksToday}</div>
              <p className="text-xs text-muted-foreground">New trucks found today</p>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Cron Jobs Status */}
      <Card>
        <CardHeader>
          <CardTitle>Scheduled Jobs</CardTitle>
          <CardDescription>Background scraping jobs and their current status</CardDescription>
        </CardHeader>
        <CardContent>
          <Table className="w-full">
            <TableHeader>
              <TableRow>
                <TableHead>Job Name</TableHead>
                <TableHead>Schedule</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Last Run</TableHead>
                <TableHead>Next Run</TableHead>
                <TableHead>Last Result</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {cronJobs.map((job) => (
                <TableRow key={job.id}>
                  <TableCell className="flex items-center gap-2">
                    {getStatusIcon(job.status)}
                    {job.name}
                  </TableCell>
                  <TableCell className="font-mono text-sm">{job.schedule}</TableCell>
                  <TableCell>{getStatusBadge(job.status)}</TableCell>
                  <TableCell>
                    {job.lastRun ? new Date(job.lastRun).toLocaleString() : 'Never'}
                  </TableCell>
                  <TableCell>
                    {job.nextRun ? new Date(job.nextRun).toLocaleString() : 'Unknown'}
                  </TableCell>
                  <TableCell>
                    {job.lastResult ? (
                      <div className="text-sm">
                        <div className={job.lastResult.success ? 'text-green-600' : 'text-red-600'}>
                          {job.lastResult.success ? 'Success' : 'Failed'}
                        </div>
                        {job.lastResult.trucksProcessed != undefined && (
                          <div className="text-xs text-muted-foreground">
                            {job.lastResult.trucksProcessed} trucks, {job.lastResult.newTrucksFound}{' '}
                            new
                          </div>
                        )}
                      </div>
                    ) : (
                      'No data'
                    )}
                  </TableCell>
                </TableRow>
              ))}
              {cronJobs.length === 0 && (
                <TableRow>
                  <TableCell colSpan={6} className="text-center text-muted-foreground">
                    No scheduled jobs found
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </CardContent>
      </Card>
    </div>
  );
}

import React from 'react';
import { FoodTruckService, FoodTruck, supabase } from '@/lib/supabase';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
// @ts-expect-error TS(2792): Cannot find module 'lucide-react'. Did you mean to... Remove this comment to see the full error message
import { Edit, BarChart3 } from 'lucide-react';
// @ts-expect-error TS(2792): Cannot find module 'next/link'. Did you mean to se... Remove this comment to see the full error message
import Link from 'next/link';
// @ts-expect-error TS(2792): Cannot find module '@/components/ui/data-quality-c... Remove this comment to see the full error message
import { DataQualityCharts } from '@/components/ui/data-quality-charts';
// @ts-expect-error TS(2792): Cannot find module '@/components/ui/simple-quality... Remove this comment to see the full error message
import { SimpleQualityPanel } from '@/components/ui/simple-quality-panel';
import {
  formatQualityScore,
  categorizeQualityScore,
  getQualityBadgeClasses,
  getQualityScoreAriaLabel
} from '@/lib/utils/data-quality-formatters';

// Define the data quality stats type based on the database function
interface DataQualityStats {
  total_trucks: number;
  avg_quality_score: number;
  high_quality_count: number;
  medium_quality_count: number;
  low_quality_count: number;
  verified_count: number;
  pending_count: number;
  flagged_count: number;
}

export default function DataQualityPage() {
  const { trucks } = await FoodTruckService.getAllTrucks(100, 0); // Fetch first 100 trucks

  // Fetch data quality stats using the Supabase function
  const { data: qualityStatsResult, error: qualityError } = await supabase
    .rpc('get_data_quality_stats')
    .single();

  if (qualityError != null) {
    console.error('Error fetching data quality stats:', qualityError);
  }

  const dataQualityStats: DataQualityStats = (qualityStatsResult as DataQualityStats) ?? {
    total_trucks: 0,
    avg_quality_score: 0,
    high_quality_count: 0,
    medium_quality_count: 0,
    low_quality_count: 0,
    verified_count: 0,
    pending_count: 0,
    flagged_count: 0,
  };

  // Sort trucks by data quality score (lowest first for review)
  const sortedTrucks = [...trucks].sort(
    (a: FoodTruck, b: FoodTruck) => (a.data_quality_score ?? 0) - (b.data_quality_score ?? 0),
  );

  return (
    <div className="flex flex-col gap-6">
      <div className="flex items-center justify-between">
        <h1 className="text-3xl font-bold tracking-tight">Data Quality Management</h1>
        <div className="flex items-center gap-2">
          // @ts-expect-error TS(2322): Type '{ children: Element; variant: string; size: ... Remove this comment to see the full error message
          <Button variant="outline" size="sm" asChild>
            <Link href="/admin/data-quality/reports">
              <BarChart3 className="h-4 w-4 mr-2" />
              View Reports
            </Link>
          </Button>
        </div>
      </div>

      {/* Data Quality Charts Section */}
      <DataQualityCharts qualityStats={dataQualityStats} />

      {/* Quality Management Panel */}
      <SimpleQualityPanel />

      {/* Food Truck Quality Table */}
      <Card>
        <CardHeader>
          <CardTitle>Food Truck Data Quality Details</CardTitle>
          <CardDescription>
            Review and manage individual food truck data quality scores. Trucks are sorted by quality score (lowest first for priority review).
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Name</TableHead>
                <TableHead>Verification Status</TableHead>
                <TableHead>Quality Score</TableHead>
                <TableHead>Quality Category</TableHead>
                <TableHead>Last Scraped</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {sortedTrucks.map((truck: FoodTruck) => {
                const qualityCategory = categorizeQualityScore(truck.data_quality_score);
                const badgeClasses = getQualityBadgeClasses(truck.data_quality_score);
                const ariaLabel = getQualityScoreAriaLabel(truck.data_quality_score);

                return (
                  <TableRow key={truck.id}>
                    <TableCell className="font-medium">{truck.name}</TableCell>
                    <TableCell>
                      <Badge
                        variant={truck.verification_status === 'verified' ? 'default' : 'outline'}
                      >
                        {truck.verification_status}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <span aria-label={ariaLabel}>
                        {formatQualityScore(truck.data_quality_score)}
                      </span>
                    </TableCell>
                    <TableCell>
                      <Badge className={badgeClasses}>
                        {qualityCategory.label}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      {truck.last_scraped_at
                        ? new Date(truck.last_scraped_at).toLocaleDateString()
                        : 'N/A'}
                    </TableCell>
                    <TableCell className="text-right">
                      <div className="flex items-center gap-2 justify-end">
                        // @ts-expect-error TS(2322): Type '{ children: Element; variant: string; size: ... Remove this comment to see the full error message
                        <Button variant="outline" size="sm" asChild>
                          <Link href={`/admin/food-trucks/${truck.id}?tab=data-quality`}>
                            <Edit className="h-4 w-4" />
                            <span className="sr-only">Edit {truck.name}</span>
                          </Link>
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        </CardContent>
      </Card>
    </div>
  );
}

import React from 'react';
import { supabase } from '@/lib/supabase';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
// @ts-expect-error TS(2792): Cannot find module 'lucide-react'. Did you mean to... Remove this comment to see the full error message
import { PlusCircle, Edit } from 'lucide-react';
// @ts-expect-error TS(2792): Cannot find module 'next/link'. Did you mean to se... Remove this comment to see the full error message
import Link from 'next/link';

interface Event {
  id: string;
  created_at: string;
  date: string;
  time: string;
  location: string;
  description: string;
  food_truck_id: string;
}

interface FoodTruckSchedule {
  id: string;
  created_at: string;
  food_truck_id: string;
  day_of_week: string;
  start_time: string;
  end_time: string;
  is_recurring: boolean;
  location: string;
}

function getEventsAndSchedules() {
  const { data: events, error: eventsError } = await supabase
    .from('events')
    .select('*')
    .order('date', { ascending: false })
    .order('time', { ascending: false });

  if (eventsError != null) {
    console.error('Error fetching events:', eventsError);
  }

  const { data: schedules, error: schedulesError } = await supabase
    .from('food_truck_schedules')
    .select('*')
    .order('day_of_week', { ascending: true })
    .order('start_time', { ascending: true });

  if (schedulesError != null) {
    console.error('Error fetching schedules:', schedulesError);
  }

  return {
    events: events ?? [],
    schedules: schedules ?? [],
  };
}

export default function EventManagementPage() {
  const { events, schedules } = await getEventsAndSchedules();

  return (
    <div className="flex flex-col gap-4">
      <div className="flex items-center justify-between">
        <h1 className="text-2xl font-bold">Event & Schedule Management</h1>
        <Button asChild>
          <Link href="/admin/events/new">
            <PlusCircle className="mr-2 h-4 w-4" />
            Add New Event/Schedule
          </Link>
        </Button>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Upcoming Events</CardTitle>
          <CardDescription>
            Manage one-time and recurring events for food trucks. ({events.length} total)
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Food Truck ID</TableHead>
                <TableHead>Date</TableHead>
                <TableHead>Time</TableHead>
                <TableHead>Location</TableHead>
                <TableHead>Description</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {events.map((event: Event) => (
                <TableRow key={event.id}>
                  <TableCell className="font-medium">{event.food_truck_id}</TableCell>
                  <TableCell>{event.date}</TableCell>
                  <TableCell>{event.time}</TableCell>
                  <TableCell>{event.location}</TableCell>
                  <TableCell>{event.description}</TableCell>
                  <TableCell className="text-right">
                    // @ts-expect-error TS(2322): Type '{ children: Element; variant: string; size: ... Remove this comment to see the full error message
                    <Button variant="outline" size="sm" asChild>
                      <Link href={`/admin/events/${event.id}`}>
                        <Edit className="h-4 w-4" />
                        <span className="sr-only">Edit</span>
                      </Link>
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Food Truck Schedules</CardTitle>
          <CardDescription>
            Manage regular operating schedules for food trucks. ({schedules.length} total)
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Food Truck ID</TableHead>
                <TableHead>Day of Week</TableHead>
                <TableHead>Time</TableHead>
                <TableHead>Location</TableHead>
                <TableHead>Recurring</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {schedules.map((schedule: FoodTruckSchedule) => (
                <TableRow key={schedule.id}>
                  <TableCell className="font-medium">{schedule.food_truck_id}</TableCell>
                  <TableCell>{schedule.day_of_week}</TableCell>
                  <TableCell>{`${schedule.start_time} - ${schedule.end_time}`}</TableCell>
                  <TableCell>{schedule.location}</TableCell>
                  <TableCell>
                    <Badge variant={schedule.is_recurring ? 'default' : 'outline'}>
                      {schedule.is_recurring ? 'Yes' : 'No'}
                    </Badge>
                  </TableCell>
                  <TableCell className="text-right">
                    // @ts-expect-error TS(2322): Type '{ children: Element; variant: string; size: ... Remove this comment to see the full error message
                    <Button variant="outline" size="sm" asChild>
                      <Link href={`/admin/schedules/${schedule.id}`}>
                        <Edit className="h-4 w-4" />
                        <span className="sr-only">Edit</span>
                      </Link>
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </CardContent>
      </Card>
    </div>
  );
}

