import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import {
  FoodTruckService,
  ScrapingJobService,
  DataProcessingService,
  supabase,
} from '@/lib/supabase';
// @ts-expect-error TS(2792): Cannot find module 'lucide-react'. Did you mean to... Remove this comment to see the full error message
import { Truck, Activity, Settings, AlertTriangle } from 'lucide-react'; // Import icons

// Define the data quality stats type based on the database function
interface DataQualityStats {
  total_trucks: number;
  avg_quality_score: number;
  high_quality_count: number;
  medium_quality_count: number;
  low_quality_count: number;
  verified_count: number;
  pending_count: number;
  flagged_count: number;
}

function getDashboardData() {
  // Fetch total food trucks and verification statuses
  const { trucks: allTrucks } = await FoodTruckService.getAllTrucks(1000, 0); // Fetch a reasonable number for overview
  const totalFoodTrucks = allTrucks.length;
  const pendingVerifications = allTrucks.filter((t) => t.verification_status === 'pending').length;

  // Fetch pipeline status (e.g., pending scraping jobs)
  const pendingScrapingJobs = await ScrapingJobService.getJobsByStatus('pending');
  const runningScrapingJobs = await ScrapingJobService.getJobsByStatus('running');
  const failedScrapingJobs = await ScrapingJobService.getJobsByStatus('failed');

  // Fetch recent errors from data processing queue
  const failedProcessingQueueItems = await DataProcessingService.getQueueByStatus('failed');
  // Fetch data quality stats using the Supabase function
  const { data: qualityStatsResult, error: qualityError } = await supabase
    .rpc('get_data_quality_stats')
    .single();

  if (qualityError != undefined) {
    console.error('Error fetching data quality stats:', qualityError);
  }

  const dataQualityStats: DataQualityStats = (qualityStatsResult as DataQualityStats) ?? {
    total_trucks: 0,
    avg_quality_score: 0,
    high_quality_count: 0,
    medium_quality_count: 0,
    low_quality_count: 0,
    verified_count: 0,
    pending_count: 0,
    flagged_count: 0,
  };

  return {
    totalFoodTrucks,
    pendingVerifications,
    pendingScrapingJobsCount: pendingScrapingJobs.length,
    runningScrapingJobsCount: runningScrapingJobs.length,
    failedScrapingJobsCount: failedScrapingJobs.length,
    failedProcessingQueueItemsCount: failedProcessingQueueItems.length,
    dataQualityStats,
  };
}

export default function AdminDashboard() {
  const {
    totalFoodTrucks,
    pendingVerifications,
    pendingScrapingJobsCount,
    runningScrapingJobsCount,
    failedScrapingJobsCount,
    failedProcessingQueueItemsCount,
    dataQualityStats,
  } = await getDashboardData();

  return (
    <div className="flex flex-col gap-4">
      <h1 className="text-2xl font-bold">Admin Dashboard</h1>

      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Food Trucks</CardTitle>
            <Truck className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{totalFoodTrucks}</div>
            <p className="text-xs text-muted-foreground">
              {pendingVerifications} pending verification
            </p>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Pipeline Status</CardTitle>
            <Activity className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{pendingScrapingJobsCount} pending</div>
            <p className="text-xs text-muted-foreground">
              {runningScrapingJobsCount} running, {failedScrapingJobsCount} failed
            </p>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Data Quality Score</CardTitle>
            <Settings className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {((dataQualityStats.avg_quality_score ?? 0) * 100).toFixed(1)}%
            </div>
            <p className="text-xs text-muted-foreground">
              Average quality score across all trucks
            </p>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Quality Distribution</CardTitle>
            <AlertTriangle className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-green-600">
              {dataQualityStats.high_quality_count ?? 0}
            </div>
            <p className="text-xs text-muted-foreground">
              <span className="text-green-600">{dataQualityStats.high_quality_count ?? 0} high</span>,{' '}
              <span className="text-yellow-600">{dataQualityStats.medium_quality_count ?? 0} medium</span>,{' '}
              <span className="text-red-600">{dataQualityStats.low_quality_count ?? 0} low</span>
            </p>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Recent Errors</CardTitle>
            <AlertTriangle className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{failedProcessingQueueItemsCount}</div>
            <p className="text-xs text-muted-foreground">from data processing queue</p>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

/*
  Test Pipeline Admin Page

  Purpose:
  This page provides an interface for manually testing the entire data pipeline,
  from web scraping with Firecrawl, through data extraction with Gemini,
  to data preparation and (optional) saving to Supabase. It allows for step-by-step
  inspection of outputs from each stage, aiding in debugging, prompt engineering,
  and validation of the pipeline's components.

  How to Use:
  1. Input a url to test the full scraping and processing flow.
  2. Alternatively, select "Use Raw Text Input Instead" and paste Markdown/html
     directly into the textarea to test only the Gemini processing stage onwards.
  3. Use the "Dry Run" checkbox to control whether data is actually saved to
     Supabase or if the save operation is just simulated. By default, it's a dry run.
  4. Click "Run Test" to execute the pipeline with the provided inputs.
  5. Results from each stage (Firecrawl, Gemini, Supabase) will be displayed in tabs,
     including status, raw outputs, processed data, errors, and relevant metadata
     (like prompts sent to Gemini or tokens used).

  Automated Testing:
  As part of a comprehensive testing strategy, placeholder files for automated unit tests
  (e.g., `firecrawl.test.ts`, `gemini.test.ts`, `pipelineProcessor.test.ts`) have been
  added to the `lib/` directory. These outline future tests to be implemented using a
  framework like Jest or Vitest.
*/
'use client';

/*
Admin Dashboard Architecture Proposal (wbs 3.1.2)
--------------------------------------------------
Recommended Approach: Sub-domain or separate route within the existing Next.js app, with robust access control.

- Security: Use Supabase rls and custom roles to restrict access. Consider deploying the admin dashboard at /admin or on a sub-domain (admin.yoursite.com) for separation.
- Separation of Concerns: Keep admin components, logic, and styles in a dedicated /admin directory. Avoid mixing admin and user-facing code.
- Maintainability: Modularize admin features (data editing, pipeline monitoring, quality management) as separate components/pages.
- Authentication: Require login with admin role for all admin routes. Use Supabase Auth or a similar provider.
- Deployment: Can be deployed as part of the main app or as a separate Next.js app if stricter separation is needed in the future.
*/

import { useState, FormEvent } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Checkbox } from '@/components/ui/checkbox';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'; // If you want to use tabs for results
import {
  StageResult,
  TestPipelineResults,
  // FirecrawlOutputData, // Already imported via StageResult's data union if needed directly
  // ExtractedFoodTruckDetails, // Already imported via StageResult's data union
  // FoodTruckSchema // Already imported via StageResult's data union
} from '@/lib/types';

export default function TestPipelinePage() {
  const [url, setUrl] = useState<string>('');
  const [rawText, setRawText] = useState<string>('');
  const [useRawText, setUseRawText] = useState<boolean>(false);
  const [isDryRun, setIsDryRun] = useState<boolean>(true);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [results, setResults] = useState<TestPipelineResults | undefined>();
  const [error, setError] = useState<string | undefined>();

  const handleSubmit = (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    setIsLoading(true);
    setResults(undefined);
    setError(undefined);

    const payload = {
      url: useRawText ? undefined : url,
      rawText: useRawText ? rawText : undefined,
      isDryRun,
    };
    try {
      const response = await fetch('/api/test-pipeline-run', {
        method: 'post',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      const data = (await response.json()) as TestPipelineResults;

      if (!response.ok) {
        throw new Error(data.error ?? 'Test run failed');
      }
      setResults(data);
    } catch (error_) {
      const errorMessage = error_ instanceof Error ? error_.message : 'An unknown error occurred';
      setError(errorMessage);
      setResults({ error: errorMessage });
    } finally {
      setIsLoading(false);
    }
  };
  const renderStageResult = (stageName: string, result?: StageResult) => {
    if (!result) return;
    return (
      <Card className="mt-4">
        <CardHeader>
          <CardTitle>{stageName}</CardTitle>
          <CardDescription>
            Status:{' '}
            <span className={result.status === 'Success' ? 'text-green-500' : 'text-red-500'}>
              {result.status}
            </span>
          </CardDescription>
        </CardHeader>
        <CardContent>
          {result.error != undefined && (
            <p className="text-red-500">
              <strong>Error:</strong> {result.error}
            </p>
          )}
          {result.details != undefined && (
            <p>
              <strong>Details:</strong> {result.details}
            </p>
          )}
          {result.prompt != undefined && (
            <div>
              <strong>Prompt:</strong>
              <Textarea
                readOnly
                value={result.prompt}
                className="mt-1 h-32 bg-gray-50 dark:bg-slate-700"
              />
            </div>
          )}
          {result.rawContent != undefined && (
            <div>
              <strong>Raw Content (Firecrawl):</strong>
              <Textarea
                readOnly
                value={result.rawContent}
                className="mt-1 h-48 bg-gray-50 dark:bg-slate-700"
              />
            </div>
          )}
          {result.data != undefined && (
            <div className="mt-2">
              <strong>Data Output:</strong>{' '}
              <pre className="mt-1 p-2 bg-gray-100 dark:bg-slate-700 rounded-md overflow-x-auto text-sm">
                {/* eslint-disable-next-line unicorn/no-null */}
                {JSON.stringify(result.data, null, 2)}
              </pre>
            </div>
          )}
          {result.preparedData != undefined && (
            <div className="mt-2">
              <strong>Data Prepared for Supabase:</strong>{' '}
              <pre className="mt-1 p-2 bg-gray-100 dark:bg-slate-700 rounded-md overflow-x-auto text-sm">
                {/* eslint-disable-next-line unicorn/no-null */}
                {JSON.stringify(result.preparedData, null, 2)}
              </pre>
            </div>
          )}
          {result.recordId != undefined && (
            <p>
              <strong>Supabase Record id:</strong> {result.recordId}
            </p>
          )}
          {result.tokensUsed !== undefined != undefined && (
            <p>
              <strong>Gemini Tokens Used:</strong> {result.tokensUsed}
            </p>
          )}
          {result.metadata != undefined && (
            <div>
              <strong>Metadata (Firecrawl):</strong>{' '}
              <pre className="mt-1 p-2 bg-gray-100 dark:bg-slate-700 rounded-md overflow-x-auto text-sm">
                {/* eslint-disable-next-line unicorn/no-null */}
                {JSON.stringify(result.metadata, null, 2)}
              </pre>
            </div>
          )}
        </CardContent>
      </Card>
    );
  };

  return (
    <div className="container mx-auto p-4">
      <Card className="mb-6">
        <CardHeader>
          <CardTitle>Test Data Pipeline</CardTitle>
          <CardDescription>
            Use this page to test the data scraping and processing pipeline with a specific url or
            raw text.
          </CardDescription>
        </CardHeader>
        <CardContent>
          <form
            onSubmit={(e) => {
              void handleSubmit(e);
            }}
            className="space-y-6"
          >
            <div>
              <Label htmlFor="url-input">url to Scrape</Label>
              <Input
                id="url-input"
                type="url"
                placeholder="https://example.com"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                disabled={useRawText || isLoading}
                className="mt-1"
              />
            </div>

            <div className="flex items-center space-x-2">
              <Checkbox
                id="use-raw-text-checkbox"
                checked={useRawText}
                onCheckedChange={(checked: any) => setUseRawText(Boolean(checked))}
                disabled={isLoading}
              />
              <Label htmlFor="use-raw-text-checkbox">Use Raw Text Input Instead</Label>
            </div>

            <div>
              <Label htmlFor="raw-text-input">Raw Text (Markdown/html)</Label>
              <Textarea
                id="raw-text-input"
                placeholder="Paste Markdown or html content here..."
                value={rawText}
                onChange={(e) => setRawText(e.target.value)}
                disabled={!useRawText || isLoading}
                className="mt-1 h-40"
              />
            </div>

            <div className="flex items-center space-x-2">
              <Checkbox
                id="dry-run-checkbox"
                checked={isDryRun}
                onCheckedChange={(checked: any) => setIsDryRun(Boolean(checked))}
                disabled={isLoading}
              />
              <Label htmlFor="dry-run-checkbox">Dry Run (Do not save to Supabase)</Label>
            </div>

            <Button type="submit" disabled={isLoading || (useRawText ? !rawText : !url)}>
              {isLoading ? 'Testing...' : 'Run Test'}
            </Button>
          </form>
        </CardContent>
      </Card>

      {error != undefined && (
        <Card className="border-red-500">
          <CardHeader>
            <CardTitle className="text-red-600">Test Failed</CardTitle>
          </CardHeader>
          <CardContent>
            <p>{error}</p>
          </CardContent>
        </Card>
      )}

      {results != undefined && !results.error && (
        <Card>
          <CardHeader>
            <CardTitle>Test Results</CardTitle>
            {results.overallStatus != undefined && (
              <CardDescription>Overall Status: {results.overallStatus}</CardDescription>
            )}
          </CardHeader>
          <CardContent>
            <Tabs defaultValue="firecrawl" className="w-full">
              <TabsList>
                <TabsTrigger value="firecrawl" disabled={!results.firecrawl}>
                  Firecrawl
                </TabsTrigger>
                <TabsTrigger value="gemini" disabled={!results.gemini}>
                  Gemini
                </TabsTrigger>
                <TabsTrigger value="supabase" disabled={!results.supabase}>
                  Supabase
                </TabsTrigger>
              </TabsList>
              <TabsContent value="firecrawl">
                {renderStageResult('Firecrawl Stage', results.firecrawl)}
              </TabsContent>
              <TabsContent value="gemini">
                {renderStageResult('Gemini Processing Stage', results.gemini)}
              </TabsContent>
              <TabsContent value="supabase">
                {renderStageResult('Supabase Interaction Stage', results.supabase)}
              </TabsContent>
            </Tabs>
            {results.logs != undefined && results.logs.length > 0 && (
              <div className="mt-4">
                <h3 className="text-lg font-semibold">Logs:</h3>
                <pre className="mt-1 p-2 bg-gray-100 dark:bg-slate-800 rounded-md overflow-x-auto text-sm">
                  {results.logs.join('\n')}
                </pre>
              </div>
            )}
          </CardContent>
        </Card>
      )}
    </div>
  );
}

import React from 'react';
import { supabaseAdmin } from '@/lib/supabase';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
// @ts-expect-error TS(2792): Cannot find module 'lucide-react'. Did you mean to... Remove this comment to see the full error message
import { UserPlus } from 'lucide-react';
// @ts-expect-error TS(2792): Cannot find module 'next/link'. Did you mean to se... Remove this comment to see the full error message
import Link from 'next/link';
// @ts-expect-error TS(2792): Cannot find module '@supabase/supabase-js'. Did yo... Remove this comment to see the full error message
import { User, PostgrestError } from '@supabase/supabase-js';

interface UserDisplayData {
  id: string;
  email: string | undefined;
  created_at: string;
  last_sign_in_at: string | undefined;
  role: string;
}

async function getUsersData(): Promise<UserDisplayData[]> {
  if (!supabaseAdmin) {
    throw new Error('Supabase admin client not available');
  }

  const { data, error } = await supabaseAdmin.auth.admin.listUsers();

  if (error != null) {
    console.error('Error fetching users:', error);
    return [];
  }

  const users: User[] = data.users ?? [];

  interface Profile {
    id: string;
    role: string;
  }

  // Fetch profiles to get roles
  const { data: profiles, error: profilesError } = (await supabaseAdmin
    .from('profiles')
    .select('id, role')) as { data: Profile[] | null; error: PostgrestError | null };

  if (profilesError != null) {
    console.error('Error fetching profiles:', profilesError);
    // Continue with users data even if profiles fetch fails
  }

  const profilesMap = new Map<string, string>(profiles?.map((p) => [p.id, p.role]) ?? []);

  return users.map((user) => ({
    id: user.id,
    email: user.email,
    created_at: user.created_at,
    last_sign_in_at: user.last_sign_in_at,
    role: profilesMap.get(user.id) ?? 'user', // Default to 'user' if no profile role
  })) as UserDisplayData[];
}

export default async function UserManagementPage() {
  const users = await getUsersData();

  return (
    <div className="flex flex-col gap-4">
