      { status: 401 }
    );
  }

  try {
    const body = await request.json() as { action: string; truckId?: string; limit?: number };
    const { action, truckId, limit } = body;

    switch (action) {
      case 'update_single': {
        if (!truckId) {
          return NextResponse.json(
