          {/* Main Search */}
          <div className="flex flex-wrap items-center gap-2 sm:space-x-2">
            {' '}
            {/* Allow wrapping and adjust gap */}
            <div className="relative flex-grow min-w-[200px]">
              {' '}
              {/* Use flex-grow and min-w for input section */}
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 dark:text-gray-500 h-4 w-4" />
              <Input
                placeholder="Search food trucks, cuisine, or menu items..."
                value={filters.query}
                onChange={(e) => setFilters({ ...filters, query: e.target.value })}
                className="pl-10 w-full dark:bg-slate-700 dark:text-gray-100 dark:placeholder-gray-400" // Ensure input takes full width of its container
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                    handleSearch();
                  }
                }}
              />
            </div>
            <Button onClick={handleSearch} disabled={loading} className="flex-shrink-0">
              {' '}
              {/* Prevent button from shrinking excessively */}
              <Search className="h-4 w-4 mr-2" />
              Search
            </Button>
            <Button
              variant="outline"
              onClick={() => setShowAdvanced(!showAdvanced)}
              className="dark:text-gray-300 dark:border-slate-600 dark:hover:bg-slate-700 flex-shrink-0"
            >
              {' '}
              {/* Prevent button from shrinking */}
              <Filter className="h-4 w-4 mr-2" />
              Filters
              {activeFilterCount > 0 && (
                <Badge variant="secondary" className="ml-2 dark:bg-slate-700 dark:text-gray-300">
                  {activeFilterCount}
                </Badge>
              )}
            </Button>
          </div>

          {/* Advanced Filters */}
          {showAdvanced && (
            <div className="space-y-4 pt-4 border-t dark:border-slate-700">
              {/* Quick Filters */}
              <div className="flex items-center space-x-2">
                <Button
                  variant={filters.openNow ? 'default' : 'outline'}
                  size="sm"
                  onClick={() => setFilters({ ...filters, openNow: !filters.openNow })}
                  className="dark:text-gray-300 dark:border-slate-600 dark:hover:bg-slate-700"
                >
                  <Clock className="h-4 w-4 mr-1" />
                  Open Now
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={clearFilters}
                  className="dark:text-gray-300 dark:border-slate-600 dark:hover:bg-slate-700"
                >
                  Clear All
                </Button>
              </div>

              {/* Cuisine Types */}
              <div>
                <label className="text-sm font-medium mb-2 block dark:text-gray-100">
                  Cuisine Type
                </label>
                <div className="flex flex-wrap gap-2">
                  {cuisineTypes.map((cuisine) => (
                    <Badge
                      key={cuisine}
                      variant={filters.cuisine === cuisine ? 'default' : 'outline'}
                      className="cursor-pointer dark:text-gray-300 dark:border-slate-600 dark:hover:bg-slate-700"
                      onClick={() =>
                        setFilters({
                          ...filters,
                          cuisine: filters.cuisine === cuisine ? '' : cuisine,
                        })
                      }
                    >
                      {cuisine}
                    </Badge>
                  ))}
                </div>
              </div>

              {/* Distance */}
              <div>
                <label className="text-sm font-medium mb-2 block dark:text-gray-100">
                  Distance: {filters.radius} km
                </label>
                <input
                  type="range"
                  min="1"
                  max="50"
                  value={filters.radius}
                  onChange={(e) => setFilters({ ...filters, radius: Number(e.target.value) })}
                  className="w-full accent-blue-600 dark:accent-blue-500"
                />
                <div className="flex justify-between text-xs text-gray-500 dark:text-gray-400 mt-1">
                  <span>1 km</span>
                  <span>50 km</span>
                </div>
              </div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

'use client';

import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
// @ts-expect-error TS(2792): Cannot find module 'lucide-react'. Did you mean to... Remove this comment to see the full error message
import { MapPin, Phone, Star, Clock, Globe, Instagram, Facebook, Twitter, Eye } from 'lucide-react';
// @ts-expect-error TS(2792): Cannot find module 'next/link'. Did you mean to se... Remove this comment to see the full error message
import Link from 'next/link';

interface FoodTruck {
  id: string;
  name: string;
  description: string;
  current_location?: {
    address: string;
  };
  operating_hours?: Record<string, { open: string; close: string; closed: boolean }>;
  contact_info?: {
    phone?: string;
    website?: string;
    email?: string;
  };
  social_media?: {
    instagram?: string;
    facebook?: string;
    twitter?: string;
  };
  average_rating?: number;
  review_count?: number;
  data_quality_score: number;
  verification_status: string;
  menu?: Array<{
    category: string;
    items: Array<{ name: string; price: number }>;
  }>;
}

interface TruckCardProps {
  readonly truck: FoodTruck;
  readonly isOpen: boolean;
  readonly onSelectTruck: () => void;
  readonly formatPrice: (price: number) => string;
  readonly userLocation?: { lat: number; lng: number };
  readonly hideHeader?: boolean; // Add option to hide header when used in accordion
}

export function TruckCard({
  truck,
  isOpen,
  onSelectTruck,
  formatPrice,
  hideHeader = false,
}: TruckCardProps) {
  const getPopularItems = () => {
    if (!truck.menu || truck.menu.length === 0) return [];
    return truck.menu[0]?.items?.slice(0, 3) ?? [];
  };

  // Helper to determine price range fallback
  const getPriceRange = () => {
    // Flatten all prices from all menu items
    const prices = (truck.menu ?? [])
      .flatMap((cat) => cat.items)
      .map((item) => (typeof item.price === 'number' ? item.price : undefined))
      .filter((p): p is number => p !== undefined && !Number.isNaN(p));
    if (prices.length === 0) return;
    const min = Math.min(...prices);
    const max = Math.max(...prices);
    if (max < 10) return '$';
    if (min >= 10 && max <= 20) return '$$';
    if (min > 20) return '$$$';
    if (min < 10 && max > 20) return '$-$$$';
    if (min < 10 && max <= 20) return '$-$$';
    if (min >= 10 && max > 20) return '$$-$$$';
    return '$';
  };

  // Helper to get today's operating hours
  const getTodayHours = () => {
    if (!truck.operating_hours) return;
    const days = ['sunday', 'monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday'];
    const todayKey = days[new Date().getDay()];
    return truck.operating_hours[todayKey];
  };

  const popularItems = getPopularItems();
  const priceRange = getPriceRange();
  const todayHours = getTodayHours();

  // Helper to format operating hours
  const formatHours = (hours: { open: string; close: string; closed: boolean }) => {
    if (hours.closed) return 'Closed';
    return `${hours.open} - ${hours.close}`;
  };

  return (
    <Card
      className={`hover:shadow-md transition-shadow cursor-pointer dark:bg-slate-800 dark:border-slate-700 ${hideHeader ? 'shadow-none border-none bg-transparent dark:bg-transparent' : ''}`}
      onClick={onSelectTruck}
    >
      {!hideHeader && (
        <CardHeader>
          <div className="flex justify-between items-start">
            <div className="flex-1">
              <CardTitle className="text-lg dark:text-gray-100">{truck.name}</CardTitle>
              {truck.current_location?.address && (
                <CardDescription className="flex items-center mt-1 dark:text-gray-400">
                  <MapPin className="h-4 w-4 mr-1" />
                  {truck.current_location.address}
                </CardDescription>
              )}
            </div>
            <div className="flex flex-col items-end space-y-1">
              <Badge variant={isOpen ? 'default' : 'secondary'}>{isOpen ? 'Open' : 'Closed'}</Badge>
