'use client';

import React, { useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { 
  Trash2, 
  RefreshCw, 
  CheckCircle, 
  AlertTriangle, 
  Play,
  Eye,
  Merge,
  Phone,
  MapPin,
  Star
// @ts-expect-error TS(2792): Cannot find module 'lucide-react'. Did you mean to... Remove this comment to see the full error message
} from 'lucide-react';

interface CleanupOperation {
  type: string;
  description: string;
  affectedCount: number;
  successCount: number;
  errorCount: number;
  errors: string[];
}

interface CleanupResult {
  totalProcessed: number;
  operations: CleanupOperation[];
  summary: {
    trucksImproved: number;
    duplicatesRemoved: number;
    qualityScoreImprovement: number;
    placeholdersRemoved: number;
  };
  duration: number;
}

export function DataCleanupDashboard() {
  const [isRunning, setIsRunning] = useState(false);
  const [lastResult, setLastResult] = useState<CleanupResult | null>(null);
  const [previewData, setPreviewData] = useState<any>(null);
  const [selectedOperations, setSelectedOperations] = useState<string[]>([
    'remove_placeholders',
    'normalize_phone',
    'fix_coordinates',
    'update_quality_scores'
  ]);

  const operationConfig = {
    remove_placeholders: {
      name: 'Remove Placeholders',
      description: 'Remove placeholder and mock data values',
      icon: <Trash2 className="h-4 w-4" />,
      color: 'text-red-600'
    },
    normalize_phone: {
      name: 'Normalize Phone Numbers',
      description: 'Format phone numbers consistently',
      icon: <Phone className="h-4 w-4" />,
      color: 'text-blue-600'
    },
    fix_coordinates: {
      name: 'Fix GPS Coordinates',
      description: 'Correct invalid location coordinates',
      icon: <MapPin className="h-4 w-4" />,
      color: 'text-green-600'
    },
    update_quality_scores: {
      name: 'Update Quality Scores',
      description: 'Recalculate data quality scores',
      icon: <Star className="h-4 w-4" />,
      color: 'text-yellow-600'
    },
    merge_duplicates: {
      name: 'Merge Duplicates',
      description: 'Identify and merge duplicate entries',
      icon: <Merge className="h-4 w-4" />,
      color: 'text-purple-600'
    }
  };

  const runCleanup = async (dryRun: boolean = false) => {
    setIsRunning(true);
    try {
      const response = await fetch('/api/admin/data-cleanup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          action: dryRun ? 'dry-run' : 'full-cleanup',
          options: {
            operations: selectedOperations,
            batchSize: 50,
            dryRun
          }
        })
      });

      const data = await response.json();
      
      if (data.success) {
        setLastResult(data.result);
      } else {
        console.error('Cleanup failed:', data.error);
      }
    } catch (error) {
      console.error('Error running cleanup:', error);
    } finally {
      setIsRunning(false);
    }
  };

  const loadPreview = async () => {
    try {
      const response = await fetch('/api/admin/data-cleanup?action=preview');
      const data = await response.json();
      
      if (data.success) {
        setPreviewData(data.preview);
      }
    } catch (error) {
      console.error('Error loading preview:', error);
    }
  };

  const toggleOperation = (operation: string) => {
    setSelectedOperations(prev => 
      prev.includes(operation)
        ? prev.filter(op => op !== operation)
        : [...prev, operation]
    );
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold tracking-tight">Data Cleanup & Quality</h2>
          <p className="text-muted-foreground">
            Automated data quality improvements and duplicate prevention
          </p>
        </div>
        <div className="flex gap-2">
          {/* @ts-expect-error TS(2322): Type '{ children: (string | Element)[]; variant: s... Remove this comment to see the full error message */}
          <Button variant="outline" onClick={loadPreview} disabled={isRunning}>
            <Eye className="h-4 w-4 mr-2" />
            Preview Changes
          </Button>
          {/* @ts-expect-error TS(2322): Type '{ children: (string | Element)[]; variant: s... Remove this comment to see the full error message */}
          <Button variant="outline" onClick={() => runCleanup(true)} disabled={isRunning}>
            <RefreshCw className={`h-4 w-4 mr-2 ${isRunning ? 'animate-spin' : ''}`} />
            Dry Run
          </Button>
          <Button onClick={() => runCleanup(false)} disabled={isRunning}>
            <Play className="h-4 w-4 mr-2" />
            Run Cleanup
          </Button>
        </div>
      </div>

      {/* Operation Selection */}
      <Card>
        <CardHeader>
          <CardTitle>Cleanup Operations</CardTitle>
          <CardDescription>
            Select which cleanup operations to run
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid gap-3 md:grid-cols-2 lg:grid-cols-3">
            {Object.entries(operationConfig).map(([key, config]) => (
              <div
                key={key}
                className={`flex items-center space-x-3 p-3 border rounded-lg cursor-pointer transition-colors ${
                  selectedOperations.includes(key)
                    ? 'border-blue-500 bg-blue-50 dark:bg-blue-950'
                    : 'border-gray-200 hover:border-gray-300'
                }`}
                onClick={() => toggleOperation(key)}
              >
                <div className={config.color}>
                  {config.icon}
                </div>
                <div className="flex-1">
                  <div className="font-medium text-sm">{config.name}</div>
                  <div className="text-xs text-muted-foreground">{config.description}</div>
                </div>
                {selectedOperations.includes(key) && (
                  <CheckCircle className="h-4 w-4 text-blue-600" />
                )}
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Preview Data */}
      {previewData && (
        <Card>
          <CardHeader>
            <CardTitle>Cleanup Preview</CardTitle>
            <CardDescription>
              Estimated changes (based on sample of 10 trucks)
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
              <div className="text-center">
                <div className="text-2xl font-bold text-blue-600">
                  {previewData.estimated_improvements}
                </div>
                <div className="text-sm text-muted-foreground">Trucks to Improve</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-purple-600">
                  {previewData.estimated_duplicates}
                </div>
                <div className="text-sm text-muted-foreground">Duplicates Found</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-green-600">
                  {previewData.operations?.length ?? 0}
                </div>
                <div className="text-sm text-muted-foreground">Operations Ready</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-yellow-600">
                  ~{Math.round((previewData.estimated_improvements / 10) * 100)}%
                </div>
                <div className="text-sm text-muted-foreground">Improvement Rate</div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Last Cleanup Result */}
      {lastResult && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <CheckCircle className="h-5 w-5 text-green-600" />
              Last Cleanup Results
            </CardTitle>
            <CardDescription>
              Completed in {Math.round(lastResult.duration / 1000)}s - {lastResult.totalProcessed} trucks processed
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            {/* Summary Cards */}
            <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
              <div className="text-center p-3 bg-blue-50 dark:bg-blue-950 rounded-lg">
                <div className="text-2xl font-bold text-blue-600">
                  {lastResult.summary.trucksImproved}
                </div>
                <div className="text-sm text-blue-700 dark:text-blue-300">Trucks Improved</div>
              </div>
              <div className="text-center p-3 bg-purple-50 dark:bg-purple-950 rounded-lg">
                <div className="text-2xl font-bold text-purple-600">
                  {lastResult.summary.duplicatesRemoved}
                </div>
                <div className="text-sm text-purple-700 dark:text-purple-300">Duplicates Removed</div>
              </div>
              <div className="text-center p-3 bg-green-50 dark:bg-green-950 rounded-lg">
                <div className="text-2xl font-bold text-green-600">
                  {lastResult.summary.qualityScoreImprovement}
                </div>
                <div className="text-sm text-green-700 dark:text-green-300">Quality Updates</div>
              </div>
              <div className="text-center p-3 bg-red-50 dark:bg-red-950 rounded-lg">
                <div className="text-2xl font-bold text-red-600">
                  {lastResult.summary.placeholdersRemoved}
                </div>
                <div className="text-sm text-red-700 dark:text-red-300">Placeholders Removed</div>
              </div>
            </div>

            {/* Operation Details */}
            <div className="space-y-3">
              <h4 className="font-semibold">Operation Details</h4>
              {lastResult.operations.map((operation, index) => (
                <div key={index} className="border rounded-lg p-3">
                  <div className="flex items-center justify-between mb-2">
                    <div className="flex items-center gap-2">
                      {operationConfig[operation.type as keyof typeof operationConfig]?.icon}
                      <span className="font-medium">{operation.description}</span>
                    </div>
                    <Badge variant={operation.errorCount > 0 ? 'destructive' : 'default'}>
                      {operation.successCount}/{operation.affectedCount}
                    </Badge>
                  </div>
                  
                  {operation.affectedCount > 0 && (
                    <Progress 
                      value={(operation.successCount / operation.affectedCount) * 100} 
                      className="h-2 mb-2"
                    />
                  )}
                  
                  {operation.errors.length > 0 && (
                    <Alert variant="destructive" className="mt-2">
                      <AlertTriangle className="h-4 w-4" />
                      <AlertTitle>Errors ({operation.errors.length})</AlertTitle>
                      <AlertDescription>
                        <div className="max-h-20 overflow-y-auto text-xs">
                          {operation.errors.slice(0, 3).map((error, i) => (
                            <div key={i}>{error}</div>
                          ))}
                          {operation.errors.length > 3 && (
                            <div>... and {operation.errors.length - 3} more</div>
                          )}
                        </div>
                      </AlertDescription>
                    </Alert>
                  )}
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Help & Information */}
      <Card>
        <CardHeader>
          <CardTitle>Cleanup Operations Guide</CardTitle>
          <CardDescription>
            Understanding what each cleanup operation does
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-3 text-sm">
            <div>
              <strong>Remove Placeholders:</strong> Removes test data, "undefined" values, and placeholder content that shouldn't be in production.
            </div>
            <div>
              <strong>Normalize Phone Numbers:</strong> Formats phone numbers consistently (e.g., "(843) 555-0123").
            </div>
            <div>
              <strong>Fix GPS Coordinates:</strong> Corrects invalid coordinates (0,0) and coordinates outside the Charleston area.
            </div>
            <div>
              <strong>Update Quality Scores:</strong> Recalculates data quality scores based on current data completeness.
            </div>
            <div>
              <strong>Merge Duplicates:</strong> Identifies and merges duplicate food truck entries automatically.
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

/**
 * SOTA Real-time Status Indicator Component
 * 
 * Provides visual real-time status updates for admin dashboard
 * with animated indicators, health scores, and alert notifications
 */

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { 
  Activity, 
  AlertTriangle, 
  CheckCircle, 
  Clock, 
  Database, 
  RefreshCw, 
  Server, 
  TrendingUp, 
  TrendingDown,
  Wifi,
  WifiOff,
  Zap
// @ts-expect-error TS(2792): Cannot find module 'lucide-react'. Did you mean to... Remove this comment to see the full error message
} from 'lucide-react';
import { useRealtimeAdminEvents } from '@/hooks/useRealtimeAdminEvents';

interface StatusMetric {
  label: string;
  value: number | string;
  unit?: string;
  trend?: 'up' | 'down' | 'stable';
  status: 'healthy' | 'warning' | 'error';
  icon: React.ReactNode;
}

interface SystemAlert {
  id: string;
  type: 'info' | 'warning' | 'error' | 'critical';
  message: string;
  timestamp: string;
  acknowledged?: boolean;
}

export function RealtimeStatusIndicator() {
  const {
    isConnected,
    isConnecting,
    connectionError,
    latestMetrics,
    recentEvents,
    connect,
    disconnect,
    clearEvents,
    lastEventTime
  } = useRealtimeAdminEvents({
    autoConnect: true,
    reconnectInterval: 5000,
    maxReconnectAttempts: 10
  });

  const [alerts, setAlerts] = useState<SystemAlert[]>([]);
  const [showDetails, setShowDetails] = useState(false);

  // Process recent events into alerts
  useEffect(() => {
    const newAlerts = recentEvents
      .filter(event => event.severity && event.severity !== 'info')
      .map(event => ({
        id: event.id,
        type: event.severity as 'warning' | 'error' | 'critical',
        message: typeof event.data.message === 'string' ? event.data.message : 'System event occurred',
        timestamp: event.timestamp,
        acknowledged: false
      }))
      .slice(0, 5); // Keep only latest 5 alerts

    setAlerts(newAlerts);
  }, [recentEvents]);

  // Calculate system metrics
  const systemMetrics: StatusMetric[] = [
    {
      label: 'Connection Status',
      value: isConnected ? 'Connected' : isConnecting ? 'Connecting...' : 'Disconnected',
      status: isConnected ? 'healthy' : connectionError ? 'error' : 'warning',
      icon: isConnected ? <Wifi className="h-4 w-4" /> : <WifiOff className="h-4 w-4" />
    },
    {
      label: 'Active Jobs',
      value: latestMetrics?.scrapingJobs.active ?? 0,
      status: (latestMetrics?.scrapingJobs.active ?? 0) > 0 ? 'healthy' : 'warning',
      icon: <Activity className="h-4 w-4" />
    },
    {
      label: 'System Health',
      value: latestMetrics?.systemHealth.status ?? 'unknown',
      status: latestMetrics?.systemHealth.status === 'healthy' ? 'healthy' : 
             latestMetrics?.systemHealth.status === 'warning' ? 'warning' : 'error',
      icon: <Server className="h-4 w-4" />
    },
    {
      label: 'Data Quality',
      value: latestMetrics?.dataQuality.averageScore ?? 0,
      unit: '%',
      trend: 'stable',
      status: (latestMetrics?.dataQuality.averageScore ?? 0) >= 80 ? 'healthy' : 
             (latestMetrics?.dataQuality.averageScore ?? 0) >= 60 ? 'warning' : 'error',
      icon: <Database className="h-4 w-4" />
    }
  ];

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'healthy': { return 'text-green-600 bg-green-50 border-green-200';
      }
      case 'warning': { return 'text-yellow-600 bg-yellow-50 border-yellow-200';
      }
      case 'error': { return 'text-red-600 bg-red-50 border-red-200';
      }
      default: { return 'text-gray-600 bg-gray-50 border-gray-200';
      }
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'healthy': { return <CheckCircle className="h-4 w-4 text-green-600" />;
      }
      case 'warning': { return <AlertTriangle className="h-4 w-4 text-yellow-600" />;
      }
      case 'error': { return <AlertTriangle className="h-4 w-4 text-red-600" />;
      }
      default: { return <Clock className="h-4 w-4 text-gray-600" />;
      }
    }
  };

  const getTrendIcon = (trend?: string) => {
    switch (trend) {
      case 'up': { return <TrendingUp className="h-3 w-3 text-green-600" />;
      }
      case 'down': { return <TrendingDown className="h-3 w-3 text-red-600" />;
      }
      default: { return;
      }
    }
  };

  const acknowledgeAlert = (alertId: string) => {
    setAlerts(prev => prev.map(alert => 
      alert.id === alertId ? { ...alert, acknowledged: true } : alert
    ));
  };

  return (
    <div className="space-y-4">
      {/* Connection Status Header */}
      <Card className={`border-l-4 ${isConnected ? 'border-l-green-500' : 'border-l-red-500'}`}>
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <CardTitle className="text-lg flex items-center gap-2">
              <Zap className="h-5 w-5" />
              Real-time System Status
            </CardTitle>
            <div className="flex items-center gap-2">
              {isConnected && (
                <div className="flex items-center gap-1 text-sm text-green-600">
                  <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse" />
                  Live
                </div>
              )}
              <Button
                // @ts-expect-error TS(2322): Type '{ children: (string | Element)[]; variant: s... Remove this comment to see the full error message
                variant="outline"
                size="sm"
                onClick={isConnected ? disconnect : connect}
                disabled={isConnecting}
              >
                <RefreshCw className={`h-4 w-4 mr-2 ${isConnecting ? 'animate-spin' : ''}`} />
                {isConnected ? 'Disconnect' : isConnecting ? 'Connecting...' : 'Connect'}
              </Button>
            </div>
          </div>
          {lastEventTime && (
            <p className="text-sm text-muted-foreground">
              Last update: {lastEventTime.toLocaleTimeString()}
            </p>
          )}
        </CardHeader>
        <CardContent>
          {connectionError && (
            <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-md">
              <p className="text-sm text-red-600">{connectionError}</p>
            </div>
          )}

          {/* System Metrics Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {systemMetrics.map((metric, index) => (
              <div
                key={index}
                className={`p-3 rounded-lg border ${getStatusColor(metric.status)}`}
              >
                <div className="flex items-center justify-between mb-2">
                  <div className="flex items-center gap-2">
                    {metric.icon}
                    <span className="text-sm font-medium">{metric.label}</span>
                  </div>
                  {getStatusIcon(metric.status)}
                </div>
                <div className="flex items-center gap-2">
                  <span className="text-lg font-bold">
                    {metric.value}{metric.unit}
                  </span>
                  {getTrendIcon(metric.trend)}
                </div>
              </div>
            ))}
          </div>

          {/* Scraping Jobs Progress */}
          {latestMetrics?.scrapingJobs && (
            <div className="mt-4 p-3 bg-blue-50 border border-blue-200 rounded-lg">
              <h4 className="text-sm font-medium text-blue-900 mb-2">Scraping Jobs Status</h4>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                <div>
                  <span className="text-blue-600">Active:</span>
                  <span className="ml-1 font-medium">{latestMetrics.scrapingJobs.active}</span>
                </div>
                <div>
                  <span className="text-green-600">Completed:</span>
                  <span className="ml-1 font-medium">{latestMetrics.scrapingJobs.completed}</span>
                </div>
                <div>
                  <span className="text-yellow-600">Pending:</span>
                  <span className="ml-1 font-medium">{latestMetrics.scrapingJobs.pending}</span>
                </div>
                <div>
                  <span className="text-red-600">Failed:</span>
                  <span className="ml-1 font-medium">{latestMetrics.scrapingJobs.failed}</span>
                </div>
              </div>
            </div>
          )}

          {/* System Alerts */}
          {alerts.length > 0 && (
            <div className="mt-4">
              <h4 className="text-sm font-medium text-gray-900 mb-2">Recent Alerts</h4>
              <div className="space-y-2">
                {alerts.slice(0, 3).map((alert) => (
                  <div
                    key={alert.id}
                    className={`p-2 rounded border-l-4 ${
                      alert.type === 'critical' ? 'border-l-red-500 bg-red-50' :
                      alert.type === 'error' ? 'border-l-red-400 bg-red-50' :
                      'border-l-yellow-400 bg-yellow-50'
                    } ${alert.acknowledged ? 'opacity-50' : ''}`}
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-2">
                        <Badge variant={alert.type === 'critical' ? 'destructive' : 'secondary'}>
                          {alert.type}
                        </Badge>
                        <span className="text-sm">{alert.message}</span>
                      </div>
                      {!alert.acknowledged && (
                        <Button
                          // @ts-expect-error TS(2322): Type '{ children: string; variant: string; size: s... Remove this comment to see the full error message
                          variant="ghost"
                          size="sm"
                          onClick={() => acknowledgeAlert(alert.id)}
                        >
                          Acknowledge
                        </Button>
                      )}
                    </div>
                    <p className="text-xs text-gray-500 mt-1">
                      {new Date(alert.timestamp).toLocaleString()}
                    </p>
                  </div>
                ))}
              </div>
              {alerts.length > 3 && (
                <Button
                  // @ts-expect-error TS(2322): Type '{ children: (string | number)[]; variant: st... Remove this comment to see the full error message
                  variant="outline"
                  size="sm"
                  className="mt-2"
                  onClick={() => setShowDetails(!showDetails)}
                >
                  {showDetails ? 'Hide' : 'Show'} {alerts.length - 3} more alerts
                </Button>
              )}
            </div>
          )}

          {/* Event Controls */}
          <div className="mt-4 flex items-center gap-2">
            <Button
              // @ts-expect-error TS(2322): Type '{ children: (string | number)[]; variant: st... Remove this comment to see the full error message
              variant="outline"
              size="sm"
              onClick={clearEvents}
              disabled={recentEvents.length === 0}
            >
              Clear Events ({recentEvents.length})
            </Button>
            <Badge variant="secondary">
              {recentEvents.length} events in buffer
            </Badge>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { 
  Activity, 
  AlertTriangle, 
  CheckCircle, 
  XCircle, 
  RefreshCw,
  TrendingUp,
  Clock,
  Zap
// @ts-expect-error TS(2792): Cannot find module 'lucide-react'. Did you mean to... Remove this comment to see the full error message
} from 'lucide-react';

interface APIUsage {
  requests: { used: number; limit: number; percentage: number };
  tokens?: { used: number; limit: number; percentage: number };
}

interface APIAlert {
  service: string;
  level: 'info' | 'warning' | 'critical';
  message: string;
  usage: { current: number; limit: number; percentage: number };
  timestamp: string;
  recommendations: string[];
}

interface MonitoringData {
  canMakeRequest: boolean;
  alerts: APIAlert[];
  usage: Record<string, APIUsage>;
  recommendations: string[];
}

export function APIMonitoringDashboard() {
  const [data, setData] = useState<MonitoringData | null>(null);
  const [loading, setLoading] = useState(true);
  const [lastUpdate, setLastUpdate] = useState<Date | null>(null);
  const [autoRefresh, setAutoRefresh] = useState(true);

  const fetchMonitoringData = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/monitoring/api-usage');
      const result = await response.json();
      
      if (result.success) {
        setData(result.data);
        setLastUpdate(new Date());
      } else {
        console.error('Failed to fetch monitoring data:', result.error);
      }
    } catch (error) {
      console.error('Error fetching monitoring data:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchMonitoringData();
  }, []);

  useEffect(() => {
    if (!autoRefresh) return;

    const interval = setInterval(fetchMonitoringData, 30_000); // Refresh every 30 seconds
    return () => clearInterval(interval);
  }, [autoRefresh]);

  const getStatusColor = (percentage: number) => {
    if (percentage >= 95) return 'text-red-600';
    if (percentage >= 80) return 'text-yellow-600';
    return 'text-green-600';
  };

  const getStatusIcon = (percentage: number) => {
    if (percentage >= 95) return <XCircle className="h-4 w-4 text-red-600" />;
    if (percentage >= 80) return <AlertTriangle className="h-4 w-4 text-yellow-600" />;
    return <CheckCircle className="h-4 w-4 text-green-600" />;
  };

  const getAlertIcon = (level: string) => {
    switch (level) {
      case 'critical': { return <XCircle className="h-4 w-4 text-red-600" />;
      }
      case 'warning': { return <AlertTriangle className="h-4 w-4 text-yellow-600" />;
      }
      default: { return <CheckCircle className="h-4 w-4 text-blue-600" />;
      }
    }
  };

  const getAlertVariant = (level: string): "default" | "destructive" => {
    return level === 'critical' ? 'destructive' : 'default';
  };

  if (loading && !data) {
    return (
      <div className="flex items-center justify-center p-8">
        <RefreshCw className="h-6 w-6 animate-spin mr-2" />
        <span>Loading API monitoring data...</span>
      </div>
    );
  }

  if (!data) {
    return (
      <Alert variant="destructive">
        <XCircle className="h-4 w-4" />
        <AlertTitle>Error</AlertTitle>
        <AlertDescription>
          Failed to load API monitoring data. Please try refreshing the page.
        </AlertDescription>
      </Alert>
    );
  }

  const criticalAlerts = data.alerts.filter(a => a.level === 'critical');
  const warningAlerts = data.alerts.filter(a => a.level === 'warning');

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold tracking-tight">API Monitoring Dashboard</h2>
          <p className="text-muted-foreground">
            Real-time monitoring of external API usage and limits
          </p>
        </div>
        <div className="flex items-center gap-2">
          <Button
            // @ts-expect-error TS(2322): Type '{ children: (string | Element)[]; variant: s... Remove this comment to see the full error message
            variant="outline"
            size="sm"
            onClick={fetchMonitoringData}
            disabled={loading}
          >
            <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
            Refresh
          </Button>
          <Button
            // @ts-expect-error TS(2322): Type '{ children: (string | Element)[]; variant: s... Remove this comment to see the full error message
            variant={autoRefresh ? "default" : "outline"}
            size="sm"
            onClick={() => setAutoRefresh(!autoRefresh)}
          >
            <Activity className="h-4 w-4 mr-2" />
            Auto Refresh
          </Button>
        </div>
      </div>

      {/* Status Overview */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              {data.canMakeRequest ? (
                <CheckCircle className="h-5 w-5 text-green-600" />
              ) : (
                <XCircle className="h-5 w-5 text-red-600" />
              )}
              <div>
                <p className="text-sm font-medium">API Status</p>
                <p className={`text-lg font-bold ${data.canMakeRequest ? 'text-green-600' : 'text-red-600'}`}>
                  {data.canMakeRequest ? 'Healthy' : 'Limited'}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <AlertTriangle className="h-5 w-5 text-yellow-600" />
              <div>
                <p className="text-sm font-medium">Active Alerts</p>
                <p className="text-lg font-bold">{data.alerts.length}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <XCircle className="h-5 w-5 text-red-600" />
              <div>
                <p className="text-sm font-medium">Critical</p>
                <p className="text-lg font-bold text-red-600">{criticalAlerts.length}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <Clock className="h-5 w-5 text-gray-600" />
              <div>
                <p className="text-sm font-medium">Last Update</p>
                <p className="text-sm text-gray-600">
                  {lastUpdate ? lastUpdate.toLocaleTimeString() : 'Never'}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Critical Alerts */}
      {criticalAlerts.length > 0 && (
        <Alert variant="destructive">
          <XCircle className="h-4 w-4" />
          <AlertTitle>Critical API Usage Alerts</AlertTitle>
          <AlertDescription>
            {criticalAlerts.length} critical alert{criticalAlerts.length > 1 ? 's' : ''} require immediate attention.
          </AlertDescription>
        </Alert>
      )}

      {/* API Usage Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {Object.entries(data.usage).map(([service, usage]) => (
          <Card key={service}>
            <CardHeader className="pb-3">
              <div className="flex items-center justify-between">
                <CardTitle className="text-lg capitalize">{service}</CardTitle>
                {getStatusIcon(usage.requests.percentage)}
              </div>
              <CardDescription>API usage and limits</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              {/* Requests */}
              <div>
                <div className="flex justify-between text-sm mb-1">
                  <span>Requests</span>
                  <span className={getStatusColor(usage.requests.percentage)}>
                    {usage.requests.used.toLocaleString()} / {usage.requests.limit.toLocaleString()}
                  </span>
                </div>
                <Progress 
                  value={usage.requests.percentage} 
                  className="h-2"
                />
                <p className="text-xs text-gray-500 mt-1">
                  {usage.requests.percentage.toFixed(1)}% used
                </p>
              </div>

              {/* Tokens (if applicable) */}
              {usage.tokens && (
                <div>
                  <div className="flex justify-between text-sm mb-1">
                    <span>Tokens</span>
                    <span className={getStatusColor(usage.tokens.percentage)}>
                      {usage.tokens.used.toLocaleString()} / {usage.tokens.limit.toLocaleString()}
                    </span>
                  </div>
                  <Progress 
                    value={usage.tokens.percentage} 
                    className="h-2"
                  />
                  <p className="text-xs text-gray-500 mt-1">
                    {usage.tokens.percentage.toFixed(1)}% used
                  </p>
                </div>
              )}
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Alerts */}
      {data.alerts.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <AlertTriangle className="h-5 w-5" />
              Active Alerts
            </CardTitle>
            <CardDescription>
              Current API usage alerts and recommendations
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-3">
            {data.alerts.map((alert, index) => (
              <Alert key={index} variant={getAlertVariant(alert.level)}>
                <div className="flex items-start gap-3">
                  {getAlertIcon(alert.level)}
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-1">
                      <AlertTitle className="text-sm">{alert.message}</AlertTitle>
                      <Badge variant={alert.level === 'critical' ? 'destructive' : 'secondary'}>
                        {alert.level}
                      </Badge>
                    </div>
                    <AlertDescription className="text-xs">
                      Service: {alert.service} | Usage: {alert.usage.percentage.toFixed(1)}%
                    </AlertDescription>
                    {alert.recommendations.length > 0 && (
                      <div className="mt-2">
                        <p className="text-xs font-medium mb-1">Recommendations:</p>
                        <ul className="text-xs space-y-1">
                          {alert.recommendations.slice(0, 2).map((rec, i) => (
                            <li key={i} className="flex items-center gap-1">
                              <Zap className="h-3 w-3" />
                              {rec}
                            </li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </div>
                </div>
              </Alert>
            ))}
          </CardContent>
        </Card>
      )}

      {/* Recommendations */}
      {data.recommendations.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <TrendingUp className="h-5 w-5" />
              Optimization Recommendations
            </CardTitle>
            <CardDescription>
              Suggested actions to optimize API usage
            </CardDescription>
          </CardHeader>
          <CardContent>
            <ul className="space-y-2">
              {data.recommendations.map((rec, index) => (
                <li key={index} className="flex items-center gap-2 text-sm">
                  <Zap className="h-4 w-4 text-blue-600" />
                  {rec}
                </li>
              ))}
            </ul>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

