/**
 * Utility functions
 */
function calculateDistance(lat1: number, lng1: number, lat2: number, lng2: number): number {
  const R = 6371; // Earth's radius in kilometers
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLng = (lng2 - lng1) * Math.PI / 180;
  const a = 
    Math.sin(dLat/2) * Math.sin(dLat/2) +
    Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
    Math.sin(dLng/2) * Math.sin(dLng/2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
  return R * c;
}

function parseTimeString(timeStr: string): number {
  if (!timeStr) return 0;
  const [hours, minutes] = timeStr.split(':').map(Number);
  return hours * 100 + (minutes ?? 0);
}

/**
 * SOTA Core Web Vitals Monitoring Implementation
 * Tracks LCP, FID, CLS, FCP, and TTFB metrics for performance optimization
 */

// @ts-expect-error TS(2792): Cannot find module 'web-vitals'. Did you mean to s... Remove this comment to see the full error message
import { getCLS, getFCP, getFID, getLCP, getTTFB, Metric } from 'web-vitals';

// Performance thresholds based on Google's Core Web Vitals standards
export const PERFORMANCE_THRESHOLDS = {
  LCP: { good: 2500, needsImprovement: 4000 }, // Largest Contentful Paint
  FID: { good: 100, needsImprovement: 300 },   // First Input Delay
  CLS: { good: 0.1, needsImprovement: 0.25 },  // Cumulative Layout Shift
  FCP: { good: 1800, needsImprovement: 3000 }, // First Contentful Paint
  TTFB: { good: 800, needsImprovement: 1800 }  // Time to First Byte
} as const;

export type MetricName = keyof typeof PERFORMANCE_THRESHOLDS;

export interface PerformanceMetric {
  name: MetricName;
  value: number;
  rating: 'good' | 'needs-improvement' | 'poor';
  timestamp: number;
  url: string;
  userAgent: string;
}

// In-memory storage for metrics (in production, send to analytics service)
const metricsStore: PerformanceMetric[] = [];

/**
 * Categorizes metric value based on thresholds
 */
function getRating(name: MetricName, value: number): 'good' | 'needs-improvement' | 'poor' {
  const thresholds = PERFORMANCE_THRESHOLDS[name];
  if (value <= thresholds.good) return 'good';
  if (value <= thresholds.needsImprovement) return 'needs-improvement';
  return 'poor';
}

/**
 * Processes and stores a web vital metric
 */
function handleMetric(metric: Metric) {
  const performanceMetric: PerformanceMetric = {
    name: metric.name as MetricName,
    value: metric.value,
    rating: getRating(metric.name as MetricName, metric.value),
    timestamp: Date.now(),
    url: globalThis.location.href,
    userAgent: navigator.userAgent
  };

  // Store metric locally
  metricsStore.push(performanceMetric);

  // Send to analytics endpoint (non-blocking)
  sendMetricToAnalytics(performanceMetric).catch(error => {
    console.warn('Failed to send metric to analytics:', error);
  });

  // Log performance issues
  if (performanceMetric.rating === 'poor') {
    console.warn(`Poor ${metric.name} performance:`, {
      value: metric.value,
      threshold: PERFORMANCE_THRESHOLDS[metric.name as MetricName],
      url: performanceMetric.url
    });
  }
}

/**
 * Sends metric data to analytics endpoint
 */
async function sendMetricToAnalytics(metric: PerformanceMetric): Promise<void> {
  try {
    await fetch('/api/analytics/web-vitals', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(metric),
    });
  } catch (error) {
    // Silently fail - don't impact user experience
    console.debug('Analytics endpoint unavailable:', error);
  }
}

/**
 * Initializes Core Web Vitals monitoring
 * Call this in your app's root component or _app.tsx
 */
export function initWebVitalsMonitoring(): void {
  try {
    getCLS(handleMetric);
    getFCP(handleMetric);
    getFID(handleMetric);
    getLCP(handleMetric);
    getTTFB(handleMetric);
  } catch (error) {
    console.warn('Failed to initialize web vitals monitoring:', error);
  }
}

/**
 * Gets current performance metrics summary
 */
export function getPerformanceMetrics(): {
  metrics: PerformanceMetric[];
  summary: Record<MetricName, {
    latest: number | null;
    average: number;
    rating: 'good' | 'needs-improvement' | 'poor' | 'no-data';
    count: number;
  }>;
} {
  const summary = {} as Record<MetricName, {
    latest: number | null;
    average: number;
    rating: 'good' | 'needs-improvement' | 'poor' | 'no-data';
    count: number;
  }>;

  // Initialize summary for all metrics
  for (const metricName of Object.keys(PERFORMANCE_THRESHOLDS)) {
    const name = metricName as MetricName;
    const metricData = metricsStore.filter(m => m.name === name);
    
    if (metricData.length === 0) {
      summary[name] = {
        latest: undefined,
        average: 0,
        rating: 'no-data',
        count: 0
      };
    } else {
      const latest = metricData.at(-1);
      const average = metricData.reduce((sum, m) => sum + m.value, 0) / metricData.length;
      
      summary[name] = {
        // @ts-expect-error TS(2532): Object is possibly 'undefined'.
        latest: latest.value,
        average: Math.round(average),
        rating: getRating(name, average),
        count: metricData.length
      };
    }
  }

  return {
    metrics: [...metricsStore],
    summary
  };
}

/**
 * Performance budget checker
 */
export function checkPerformanceBudget(): {
  passed: boolean;
  violations: Array<{
    metric: MetricName;
    value: number;
    threshold: number;
    severity: 'warning' | 'critical';
  }>;
} {
  const { summary } = getPerformanceMetrics();
  const violations: Array<{
    metric: MetricName;
    value: number;
    threshold: number;
    severity: 'warning' | 'critical';
  }> = [];

  for (const [metricName, data] of Object.entries(summary)) {
    const name = metricName as MetricName;
    if (data.latest === undefined) continue;

    const thresholds = PERFORMANCE_THRESHOLDS[name];
    
    if (data.latest > thresholds.needsImprovement) {
      violations.push({
        metric: name,
        value: data.latest,
        threshold: thresholds.needsImprovement,
        severity: 'critical'
      });
    } else if (data.latest > thresholds.good) {
      violations.push({
        metric: name,
        value: data.latest,
        threshold: thresholds.good,
        severity: 'warning'
      });
    }
  }

  return {
    passed: violations.length === 0,
    violations
  };
}

/**
 * Performance optimization suggestions based on metrics
 */
export function getPerformanceOptimizationSuggestions(): Array<{
  metric: MetricName;
  issue: string;
  suggestions: string[];
  priority: 'high' | 'medium' | 'low';
}> {
  const { summary } = getPerformanceMetrics();
  const suggestions: Array<{
    metric: MetricName;
    issue: string;
    suggestions: string[];
    priority: 'high' | 'medium' | 'low';
  }> = [];

  for (const [metricName, data] of Object.entries(summary)) {
    const name = metricName as MetricName;
    if (data.rating === 'poor' || data.rating === 'needs-improvement') {
      switch (name) {
        case 'LCP': {
          suggestions.push({
            metric: name,
            issue: `Largest Contentful Paint is ${data.latest}ms (target: <2500ms)`,
            suggestions: [
              'Optimize images with Next.js Image component',
              'Implement lazy loading for non-critical content',
              'Use CDN for static assets',
              'Optimize server response times',
              'Preload critical resources'
            ],
            priority: 'high'
          });
          break;
        }
        case 'FID': {
          suggestions.push({
            metric: name,
            issue: `First Input Delay is ${data.latest}ms (target: <100ms)`,
            suggestions: [
              'Reduce JavaScript bundle size',
              'Implement code splitting',
              'Use web workers for heavy computations',
              'Optimize third-party scripts',
              'Defer non-critical JavaScript'
            ],
            priority: 'high'
          });
          break;
        }
        case 'CLS': {
          suggestions.push({
            metric: name,
            issue: `Cumulative Layout Shift is ${data.latest} (target: <0.1)`,
            suggestions: [
              'Set explicit dimensions for images and videos',
              'Reserve space for dynamic content',
              'Use CSS aspect-ratio for responsive images',
              'Avoid inserting content above existing content',
              'Use transform animations instead of layout changes'
            ],
            priority: 'medium'
          });
          break;
        }
        case 'FCP': {
          suggestions.push({
            metric: name,
            issue: `First Contentful Paint is ${data.latest}ms (target: <1800ms)`,
            suggestions: [
              'Optimize critical rendering path',
              'Inline critical CSS',
              'Minimize render-blocking resources',
              'Use resource hints (preload, prefetch)',
              'Optimize web fonts loading'
            ],
            priority: 'medium'
          });
          break;
        }
        case 'TTFB': {
          suggestions.push({
            metric: name,
            issue: `Time to First Byte is ${data.latest}ms (target: <800ms)`,
            suggestions: [
              'Optimize server response times',
              'Use CDN for global distribution',
              'Implement server-side caching',
              'Optimize database queries',
              'Use edge computing for dynamic content'
            ],
            priority: 'high'
          });
          break;
        }
      }
    }
  }

  return suggestions.sort((a, b) => {
    const priorityOrder = { high: 3, medium: 2, low: 1 };
    return priorityOrder[b.priority] - priorityOrder[a.priority];
  });
}

// lib/pipelineManager.ts
// Unified Pipeline Manager - Consolidates all pipeline operations

import { ScrapingJobService } from './supabase';
import { discoveryEngine } from './discoveryEngine';
import { processScrapingJob } from './pipelineProcessor';
import { ensureDefaultTrucksAreScraped } from './autoScraper';

// Configuration interfaces
export interface PipelineConfig {
  type: 'discovery' | 'processing' | 'full' | 'maintenance';
  params: {
    maxUrls?: number;
    maxUrlsToProcess?: number;
    targetCities?: string[];
    priority?: number;
    skipDiscovery?: boolean;
    retryFailedJobs?: boolean;
  };
}

export interface DiscoveryParams {
  cities: string[];
  maxUrls: number;
  searchTerms?: string[];
}

export interface ProcessingOptions {
  maxJobs: number;
  priority: number;
  retryFailedJobs: boolean;
}

// Result interfaces
export interface PipelineResult {
  success: boolean;
  type: string;
  phase: string;
  summary: {
    urlsDiscovered?: number;
    urlsProcessed?: number;
    jobsCreated?: number;
    trucksCreated?: number;
    errors?: number;
    duration: number;
  };
  details: unknown;
  timestamp: string;
}

export interface DiscoveryResult {
  success: boolean;
  urlsDiscovered: number;
  urlsStored: number;
  urlsDuplicate: number;
  errors: string[];
  duration: number;
}

export interface ProcessingResult {
  success: boolean;
  jobsProcessed: number;
  jobsSuccessful: number;
  jobsFailed: number;
  trucksCreated: number;
  errors: string[];
  duration: number;
}

export interface MaintenanceResult {
  success: boolean;
  trucksProcessed: number;
  newTrucksFound: number;
  errors: string[];
  duration: number;
}

/**
 * Unified Pipeline Manager
 *
 * Consolidates all pipeline operations into a single, modular system:
 * - Discovery: Find new food truck URLs using Tavily
 * - Processing: Process URLs through Firecrawl + Gemini pipeline
 * - Full: Combined discovery + processing
 * - Maintenance: Check existing trucks for stale data
 */
export class PipelineManager {
  /**
   * Run the complete pipeline based on configuration
   */
  async runPipeline(config: PipelineConfig): Promise<PipelineResult> {
    const startTime = Date.now();

    try {
      console.info(`🚀 PipelineManager: Starting ${config.type} pipeline...`);

      let result: unknown;

      switch (config.type) {
        case 'discovery': {
          result = await this.runDiscovery({
            cities: config.params.targetCities ?? [],
            maxUrls: config.params.maxUrls ?? 50,
            searchTerms: ['food truck', 'food cart', 'mobile food'],
          });
          break;
        }

        case 'processing': {
          result = await this.processJobs({
            maxJobs: config.params.maxUrlsToProcess ?? 20,
            priority: config.params.priority ?? 5,
            retryFailedJobs: config.params.retryFailedJobs ?? false,
          });
          break;
        }

        case 'full': {
          result = await this.runFullPipeline(config);
          break;
        }

        case 'maintenance': {
          result = await this.runMaintenance();
          break;
        }

        default: {
          throw new Error(`Unknown pipeline type: ${String(config.type)}`);
        }
      }

      const duration = Date.now() - startTime;

      return {
        success: true,
        type: config.type,
        phase: 'completed',
        summary: {
          ...(typeof result === 'object' && result !== null
            ? (result as Record<string, unknown>)
            : {}),
          duration,
        },
        details: result,
        timestamp: new Date().toISOString(),
      };
    } catch (error) {
      const duration = Date.now() - startTime;
      console.error(`❌ PipelineManager: ${config.type} pipeline failed:`, error);

      return {
        success: false,
        type: config.type,
        phase: 'failed',
        summary: {
          errors: 1,
          duration,
        },
        details: {
          error: error instanceof Error ? error.message : 'Unknown error',
        },
        timestamp: new Date().toISOString(),
      };
    }
  }
  /**
   * Run URL discovery using Tavily search
   */
  async runDiscovery(_params: DiscoveryParams): Promise<DiscoveryResult> {
    const startTime = Date.now();

    try {
      console.info(`🔍 PipelineManager: Starting discovery process...`);

      // Use the existing discovery engine's method
      const discoveryResult = await discoveryEngine.discoverNewFoodTrucks();

      const duration = Date.now() - startTime;

      return {
        success: discoveryResult.errors.length === 0,
        urlsDiscovered: discoveryResult.urls_discovered,
        urlsStored: discoveryResult.urls_stored,
        urlsDuplicate: discoveryResult.urls_duplicates,
        errors: discoveryResult.errors,
        duration,
      };
    } catch (error) {
      const duration = Date.now() - startTime;
      const errorMsg = error instanceof Error ? error.message : 'Unknown discovery error';
      console.error('❌ PipelineManager: Discovery failed:', error);

      return {
        success: false,
        urlsDiscovered: 0,
        urlsStored: 0,
        urlsDuplicate: 0,
        errors: [errorMsg],
        duration,
      };
    }
  }

  /**
   * Process existing scraping jobs
   */
  async processJobs(options: ProcessingOptions): Promise<ProcessingResult> {
    const startTime = Date.now();
    const errors: string[] = [];

    try {
      console.info(`⚙️ PipelineManager: Processing up to ${options.maxJobs} jobs...`);

      // Get pending jobs
      const pendingJobs = await ScrapingJobService.getJobsByStatus('pending');
      if (pendingJobs == undefined || pendingJobs.length === 0) {
        console.info('No pending jobs to process');
        return {
          success: true,
          jobsProcessed: 0,
          jobsSuccessful: 0,
          jobsFailed: 0,
          trucksCreated: 0,
          errors: [],
          duration: Date.now() - startTime,
        };
      }

      // Process jobs up to maxJobs limit
      const jobsToProcess = pendingJobs.slice(0, options.maxJobs);
      let jobsSuccessful = 0;
      let jobsFailed = 0;
      let trucksCreated = 0;

      for (const job of jobsToProcess) {
        try {
          console.info(`Processing job ${job.id} for URL: ${job.target_url}`);

          // Process the job
          await processScrapingJob(job.id);

          // Check if job resulted in truck creation
          const updatedJob = await ScrapingJobService.getJobsByStatus('completed').then((jobs) =>
            jobs?.find((j) => j.id === job.id),
          );

          if (updatedJob?.data_collected?.truck_id) {
            trucksCreated++;
          }

          jobsSuccessful++;
        } catch (jobError) {
          const errorMsg = `Job ${job.id} failed: ${jobError instanceof Error ? jobError.message : 'Unknown error'}`;
          console.warn(errorMsg);
          errors.push(errorMsg);
          jobsFailed++;
        }
      }

      const duration = Date.now() - startTime;

      return {
        success: jobsFailed === 0,
        jobsProcessed: jobsToProcess.length,
        jobsSuccessful,
        jobsFailed,
        trucksCreated,
        errors,
        duration,
      };
    } catch (error) {
      const duration = Date.now() - startTime;
      const errorMsg = error instanceof Error ? error.message : 'Unknown processing error';
      console.error('❌ PipelineManager: Job processing failed:', error);

      return {
        success: false,
        jobsProcessed: 0,
        jobsSuccessful: 0,
        jobsFailed: 0,
        trucksCreated: 0,
        errors: [errorMsg],
        duration,
      };
    }
  }

  /**
   * Run full pipeline: discovery + processing
   */
  private async runFullPipeline(config: PipelineConfig): Promise<Record<string, unknown>> {
    const results: {
      discovery: DiscoveryResult | undefined;
      processing: ProcessingResult | undefined;
    } = {
      discovery: undefined,
      processing: undefined,
    };

    // Step 1: Discovery (unless skipped)
    if (config.params.skipDiscovery !== true) {
      results.discovery = await this.runDiscovery({
        cities: config.params.targetCities ?? ['Charleston', 'Columbia', 'Greenville'],
        maxUrls: config.params.maxUrls ?? 50,
        searchTerms: ['food truck', 'food cart', 'mobile food'],
      });
    }

    // Step 2: Processing
    results.processing = await this.processJobs({
      maxJobs: config.params.maxUrlsToProcess ?? 20,
      priority: config.params.priority ?? 5,
      retryFailedJobs: config.params.retryFailedJobs ?? false,
    });

    // Combine results
    return {
      urlsDiscovered: results.discovery?.urlsDiscovered ?? 0,
      urlsStored: results.discovery?.urlsStored ?? 0,
      urlsDuplicate: results.discovery?.urlsDuplicate ?? 0,
      jobsProcessed: results.processing?.jobsProcessed ?? 0,
      trucksCreated: results.processing?.trucksCreated ?? 0,
      errors: [...(results.discovery?.errors ?? []), ...(results.processing?.errors ?? [])],
    };
  }

  /**
   * Run maintenance checks on existing trucks
   */
  async runMaintenance(): Promise<MaintenanceResult> {
    const startTime = Date.now();

    try {
      console.info('🔧 PipelineManager: Running maintenance checks...');

      // Use the existing autoScraper functionality
      const result = await ensureDefaultTrucksAreScraped();

      const duration = Date.now() - startTime;

      return {
        success: result.errors.length === 0,
        trucksProcessed: result.trucksProcessed,
        newTrucksFound: result.newTrucksFound,
        errors: result.errors.map((e) => e.url + ': ' + (e.details ?? 'Unknown error')),
        duration,
      };
    } catch (error) {
      const duration = Date.now() - startTime;
      const errorMsg = error instanceof Error ? error.message : 'Unknown maintenance error';
      console.error('❌ PipelineManager: Maintenance failed:', error);

      return {
        success: false,
        trucksProcessed: 0,
        newTrucksFound: 0,
        errors: [errorMsg],
        duration,
      };
    }
  }
}

