import { supabaseAdmin } from '@/lib/supabase';

export interface AuditLogEntry {
  user_id: string;
  user_email: string;
  action: string;
  resource_type: string;
  resource_id?: string;
  details?: Record<string, unknown>;
  ip_address?: string;
  user_agent?: string;
  session_id?: string;
  timestamp: string;
  severity: 'info' | 'warning' | 'error' | 'critical';
}

export interface SecurityEvent {
  event_type: 'login_attempt' | 'login_success' | 'login_failure' | 'logout' | 'permission_denied' | 'data_access' | 'data_modification' | 'admin_action';
  user_id?: string;
  user_email?: string;
  ip_address?: string;
  user_agent?: string;
  details?: Record<string, unknown>;
  severity: 'info' | 'warning' | 'error' | 'critical';
}

/**
 * Audit Logger Service
 */
export class AuditLogger {
  /**
   * Log admin action with full audit trail
   */
  static async logAdminAction(
    userId: string,
    userEmail: string,
    action: string,
    resourceType: string,
    resourceId?: string,
    details?: Record<string, unknown>,
    request?: {
      ip?: string;
      userAgent?: string;
      sessionId?: string;
    }
  ): Promise<void> {
    const auditEntry: AuditLogEntry = {
      user_id: userId,
      user_email: userEmail,
      action,
      resource_type: resourceType,
      resource_id: resourceId,
      details,
      ip_address: request?.ip,
      user_agent: request?.userAgent,
      session_id: request?.sessionId,
      timestamp: new Date().toISOString(),
      severity: this.determineSeverity(action, resourceType)
    };

    await this.writeAuditLog(auditEntry);
    
    // Log to console for immediate monitoring
    console.info('Admin Action Audit:', {
      user: userEmail,
      action,
      resource: `${resourceType}${resourceId ? `:${resourceId}` : ''}`,
      timestamp: auditEntry.timestamp
    });
  }

  /**
   * Log security event
   */
  static async logSecurityEvent(event: SecurityEvent): Promise<void> {
    const logEntry = {
      event_type: event.event_type,
      user_id: event.user_id,
      user_email: event.user_email,
      ip_address: event.ip_address,
      user_agent: event.user_agent,
      details: event.details,
      severity: event.severity,
      timestamp: new Date().toISOString()
    };

    try {
      if (supabaseAdmin) {
        await supabaseAdmin
          .from('security_events')
          .insert(logEntry);
      }
    } catch (error) {
      console.error('Failed to log security event:', error);
    }

    // Always log security events to console
    console.info('Security Event:', logEntry);

    // Alert on critical security events
    if (event.severity === 'critical' || event.severity === 'error') {
      console.warn('SECURITY ALERT:', logEntry);
    }
  }

  /**
   * Log authentication events
   */
  static async logAuthEvent(
    eventType: 'login_attempt' | 'login_success' | 'login_failure' | 'logout',
    userEmail?: string,
    userId?: string,
    request?: {
      ip?: string;
      userAgent?: string;
    },
    details?: Record<string, unknown>
  ): Promise<void> {
    const severity = eventType === 'login_failure' ? 'warning' : 'info';
    
    await this.logSecurityEvent({
      event_type: eventType,
      user_id: userId,
      user_email: userEmail,
      ip_address: request?.ip,
      user_agent: request?.userAgent,
      details,
      severity
    });
  }

  /**
   * Log data access events
   */
  static async logDataAccess(
    userId: string,
    userEmail: string,
    resourceType: string,
    resourceId?: string,
    action: 'read' | 'search' | 'export' = 'read',
    request?: {
      ip?: string;
      userAgent?: string;
    }
  ): Promise<void> {
    await this.logSecurityEvent({
      event_type: 'data_access',
      user_id: userId,
      user_email: userEmail,
      ip_address: request?.ip,
      user_agent: request?.userAgent,
      details: {
        resource_type: resourceType,
        resource_id: resourceId,
        action
      },
      severity: 'info'
    });
  }

  /**
   * Write audit log to database
   */
  private static async writeAuditLog(entry: AuditLogEntry): Promise<void> {
    try {
      if (supabaseAdmin) {
        await supabaseAdmin
          .from('audit_logs')
          .insert(entry);
      }
    } catch (error) {
      console.error('Failed to write audit log:', error);
      // Don't throw - audit logging should not break application flow
    }
  }

  /**
   * Determine severity based on action and resource type
   */
  private static determineSeverity(action: string, resourceType: string): 'info' | 'warning' | 'error' | 'critical' {
    // Critical actions
    if (action.includes('delete') || action.includes('remove')) {
      return 'critical';
    }
    
    // Warning actions
    if (action.includes('update') || action.includes('modify') || action.includes('change')) {
      return 'warning';
    }
    
    // Admin-specific actions
    if (resourceType === 'user' || resourceType === 'admin' || resourceType === 'system') {
      return 'warning';
    }
    
    // Default to info
    return 'info';
  }

  /**
   * Get audit logs for a specific user
   */
  static async getUserAuditLogs(
    userId: string,
    limit: number = 100,
    offset: number = 0
  ): Promise<AuditLogEntry[]> {
    try {
      if (!supabaseAdmin) {
        return [];
      }

      const { data, error } = await supabaseAdmin
        .from('audit_logs')
        .select('*')
        .eq('user_id', userId)
        .order('timestamp', { ascending: false })
        .range(offset, offset + limit - 1);

      if (error) {
        console.error('Failed to fetch user audit logs:', error);
        return [];
      }

      return data ?? [];
    } catch (error) {
      console.error('Error fetching user audit logs:', error);
      return [];
    }
  }

  /**
   * Get recent security events
   */
  static async getRecentSecurityEvents(
    hours: number = 24,
    severity?: 'info' | 'warning' | 'error' | 'critical'
  ): Promise<any[]> {
    try {
      if (!supabaseAdmin) {
        return [];
      }

      const startTime = new Date();
      startTime.setHours(startTime.getHours() - hours);

      let query = supabaseAdmin
        .from('security_events')
        .select('*')
        .gte('timestamp', startTime.toISOString())
        .order('timestamp', { ascending: false });

      if (severity) {
        query = query.eq('severity', severity);
      }

      const { data, error } = await query.limit(500);

      if (error) {
        console.error('Failed to fetch security events:', error);
        return [];
      }

      return data ?? [];
    } catch (error) {
      console.error('Error fetching security events:', error);
      return [];
    }
  }
}

/**
 * Security monitoring utilities
 */
export const SecurityMonitor = {
  /**
   * Check for suspicious activity patterns
   */
  async checkSuspiciousActivity(userId: string): Promise<{
    suspicious: boolean;
    reasons: string[];
    riskLevel: 'low' | 'medium' | 'high';
  }> {
    const reasons: string[] = [];
    let riskLevel: 'low' | 'medium' | 'high' = 'low';

    try {
      // Check recent failed login attempts
      const recentEvents = await AuditLogger.getRecentSecurityEvents(1, 'warning');
      const failedLogins = recentEvents.filter(
        event => event.event_type === 'login_failure' && event.user_id === userId
      );

      if (failedLogins.length > 5) {
        reasons.push('Multiple failed login attempts');
        riskLevel = 'high';
      } else if (failedLogins.length > 2) {
        reasons.push('Recent failed login attempts');
        riskLevel = 'medium';
      }

      // Check for unusual access patterns
      const auditLogs = await AuditLogger.getUserAuditLogs(userId, 50);
      const recentActions = auditLogs.filter(
        log => new Date(log.timestamp) > new Date(Date.now() - 60 * 60 * 1000) // Last hour
      );

      if (recentActions.length > 20) {
        reasons.push('High activity volume');
        riskLevel = riskLevel === 'high' ? 'high' : 'medium';
      }

      return {
        suspicious: reasons.length > 0,
        reasons,
        riskLevel
      };
    } catch (error) {
      console.error('Error checking suspicious activity:', error);
      return {
        suspicious: false,
        reasons: [],
        riskLevel: 'low'
      };
    }
  },
};

/**
 * SOTA Rate Limiting Implementation
 * Prevents brute force attacks and API abuse with intelligent rate limiting
 */

interface RateLimitEntry {
  count: number;
  resetTime: number;
  blocked: boolean;
  blockUntil?: number;
}

interface RateLimitConfig {
  windowMs: number;
  maxRequests: number;
  blockDurationMs: number;
  skipSuccessfulRequests?: boolean;
}

// In-memory rate limit store (in production, use Redis)
const rateLimitStore = new Map<string, RateLimitEntry>();

// Rate limit configurations for different endpoints
export const RATE_LIMIT_CONFIGS = {
  // Authentication endpoints - strict limits
  auth: {
    windowMs: 15 * 60 * 1000, // 15 minutes
    maxRequests: 5, // 5 attempts per 15 minutes
    blockDurationMs: 30 * 60 * 1000, // Block for 30 minutes
    skipSuccessfulRequests: true
  },
  // API endpoints - moderate limits
  api: {
    windowMs: 60 * 1000, // 1 minute
    maxRequests: 60, // 60 requests per minute
    blockDurationMs: 5 * 60 * 1000, // Block for 5 minutes
    skipSuccessfulRequests: false
  },
  // Admin endpoints - very strict limits
  admin: {
    windowMs: 60 * 1000, // 1 minute
    maxRequests: 20, // 20 requests per minute
    blockDurationMs: 15 * 60 * 1000, // Block for 15 minutes
    skipSuccessfulRequests: false
  }
} as const;

export type RateLimitType = keyof typeof RATE_LIMIT_CONFIGS;

/**
 * Rate Limiter Service
 */
export class RateLimiter {
  /**
   * Check if request should be rate limited
   */
  static checkRateLimit(
    identifier: string,
    type: RateLimitType = 'api'
  ): {
    allowed: boolean;
    remaining: number;
    resetTime: number;
    retryAfter?: number;
  } {
    const config = RATE_LIMIT_CONFIGS[type];
    const now = Date.now();
    const key = `${type}:${identifier}`;
    
    // Clean up expired entries
    this.cleanupExpiredEntries();
    
    let entry = rateLimitStore.get(key);
    
    // Initialize entry if it doesn't exist
    if (!entry) {
      entry = {
        count: 0,
        resetTime: now + config.windowMs,
        blocked: false
      };
      rateLimitStore.set(key, entry);
    }
    
    // Check if currently blocked
    if (entry.blocked && entry.blockUntil && now < entry.blockUntil) {
      return {
        allowed: false,
        remaining: 0,
        resetTime: entry.resetTime,
        retryAfter: Math.ceil((entry.blockUntil - now) / 1000)
      };
    }
    
    // Reset window if expired
    if (now >= entry.resetTime) {
      entry.count = 0;
      entry.resetTime = now + config.windowMs;
      entry.blocked = false;
      entry.blockUntil = undefined;
    }
    
    // Check if limit exceeded
    if (entry.count >= config.maxRequests) {
      entry.blocked = true;
      entry.blockUntil = now + config.blockDurationMs;
      
      return {
        allowed: false,
        remaining: 0,
        resetTime: entry.resetTime,
        retryAfter: Math.ceil(config.blockDurationMs / 1000)
      };
    }
    
    // Increment counter
    entry.count++;
    rateLimitStore.set(key, entry);
    
    return {
      allowed: true,
      remaining: config.maxRequests - entry.count,
      resetTime: entry.resetTime
    };
  }
  
  /**
   * Record successful request (for auth endpoints)
   */
  static recordSuccess(identifier: string, type: RateLimitType = 'api'): void {
    const config = RATE_LIMIT_CONFIGS[type];
    if (!config.skipSuccessfulRequests) return;
    
    const key = `${type}:${identifier}`;
    const entry = rateLimitStore.get(key);
    
    if (entry) {
      // Reset counter on successful auth
      entry.count = 0;
      entry.blocked = false;
      entry.blockUntil = undefined;
      rateLimitStore.set(key, entry);
    }
  }
  
  /**
   * Get rate limit status without incrementing
   */
  static getStatus(
    identifier: string,
    type: RateLimitType = 'api'
  ): {
    remaining: number;
    resetTime: number;
    blocked: boolean;
    retryAfter?: number;
  } {
    const config = RATE_LIMIT_CONFIGS[type];
    const now = Date.now();
    const key = `${type}:${identifier}`;
    const entry = rateLimitStore.get(key);
    
    if (!entry) {
      return {
        remaining: config.maxRequests,
        resetTime: now + config.windowMs,
        blocked: false
      };
    }
    
    // Check if blocked
    if (entry.blocked && entry.blockUntil && now < entry.blockUntil) {
      return {
        remaining: 0,
        resetTime: entry.resetTime,
        blocked: true,
        retryAfter: Math.ceil((entry.blockUntil - now) / 1000)
      };
    }
    
    // Check if window expired
    if (now >= entry.resetTime) {
      return {
        remaining: config.maxRequests,
        resetTime: now + config.windowMs,
        blocked: false
      };
    }
    
    return {
      remaining: Math.max(0, config.maxRequests - entry.count),
      resetTime: entry.resetTime,
      blocked: false
    };
  }
  
  /**
   * Clean up expired entries to prevent memory leaks
   */
  private static cleanupExpiredEntries(): void {
    const now = Date.now();
    
    for (const [key, entry] of rateLimitStore.entries()) {
      // Remove entries that are expired and not blocked
      if (now >= entry.resetTime && (!entry.blocked || !entry.blockUntil || now >= entry.blockUntil)) {
        rateLimitStore.delete(key);
      }
    }
  }
  
  /**
   * Clear all rate limit data for an identifier
   */
  static clearLimits(identifier: string, type?: RateLimitType): void {
    if (type) {
      const key = `${type}:${identifier}`;
      rateLimitStore.delete(key);
    } else {
      // Clear all types for this identifier
      for (const limitType of Object.keys(RATE_LIMIT_CONFIGS)) {
        const key = `${limitType}:${identifier}`;
        rateLimitStore.delete(key);
      }
    }
  }
  
  /**
   * Get rate limit statistics
   */
  static getStats(): {
    totalEntries: number;
    blockedEntries: number;
    entriesByType: Record<string, number>;
  } {
    const stats = {
      totalEntries: rateLimitStore.size,
      blockedEntries: 0,
      entriesByType: {} as Record<string, number>
    };
    
    for (const [key, entry] of rateLimitStore.entries()) {
      const type = key.split(':')[0];
      stats.entriesByType[type] = (stats.entriesByType[type] ?? 0) + 1;
      
      if (entry.blocked) {
        stats.blockedEntries++;
      }
    }
    
    return stats;
  }
}

/**
 * Utility function to get client identifier from request
 */
export function getClientIdentifier(request: Request): string {
  // Try to get IP address from various headers
  const forwarded = request.headers.get('x-forwarded-for');
  const realIp = request.headers.get('x-real-ip');
  const cfConnectingIp = request.headers.get('cf-connecting-ip');
  
  // Use the first available IP
  const ip = forwarded?.split(',')[0]?.trim() || realIp || cfConnectingIp ?? 'unknown';
  
  // Include user agent for more specific identification
  const userAgent = request.headers.get('user-agent') ?? 'unknown';
  
  // Create a hash of IP + User Agent for better identification
  return `${ip}:${userAgent.slice(0, 50)}`;
}

/**
 * Rate limiting middleware for API routes
 */
export function withRateLimit(
  handler: (request: Request) => Promise<Response>,
  type: RateLimitType = 'api'
) {
  return async (request: Request): Promise<Response> => {
    const identifier = getClientIdentifier(request);
    const result = RateLimiter.checkRateLimit(identifier, type);
    
    if (!result.allowed) {
      const headers = new Headers({
        'X-RateLimit-Limit': RATE_LIMIT_CONFIGS[type].maxRequests.toString(),
        'X-RateLimit-Remaining': '0',
        'X-RateLimit-Reset': new Date(result.resetTime).toISOString(),
      });
      
      if (result.retryAfter) {
        headers.set('Retry-After', result.retryAfter.toString());
      }
      
      return new Response(
        JSON.stringify({
          error: 'Rate limit exceeded',
          message: `Too many requests. Try again ${result.retryAfter ? `in ${result.retryAfter} seconds` : 'later'}.`,
          retryAfter: result.retryAfter
        }),
        {
          status: 429,
          headers: {
            'Content-Type': 'application/json',
            ...Object.fromEntries(headers.entries())
          }
        }
      );
    }
    
    // Add rate limit headers to successful responses
    const response = await handler(request);
    
    response.headers.set('X-RateLimit-Limit', RATE_LIMIT_CONFIGS[type].maxRequests.toString());
    response.headers.set('X-RateLimit-Remaining', result.remaining.toString());
    response.headers.set('X-RateLimit-Reset', new Date(result.resetTime).toISOString());
    
    return response;
  };
}

/**
 * SOTA Data Quality Formatting Utilities
 * Provides consistent formatting and categorization for data quality metrics
 */

export interface QualityThresholds {
  high: number;
  medium: number;
  low: number;
}

export interface QualityCategory {
  label: 'High' | 'Medium' | 'Low';
  color: string;
  bgColor: string;
  textColor: string;
}

// SOTA quality thresholds based on industry standards
export const QUALITY_THRESHOLDS: QualityThresholds = {
  high: 0.8,    // 80%+ = High quality
  medium: 0.6,  // 60-79% = Medium quality
  low: 0.6      // <60% = Low quality
};

// SOTA color scheme for accessibility and visual hierarchy
export const QUALITY_CATEGORIES: Record<string, QualityCategory> = {
  high: {
    label: 'High',
    color: '#22c55e',
    bgColor: 'bg-green-100',
    textColor: 'text-green-800'
  },
  medium: {
    label: 'Medium',
    color: '#f59e0b',
    bgColor: 'bg-yellow-100',
    textColor: 'text-yellow-800'
  },
  low: {
    label: 'Low',
    color: '#ef4444',
    bgColor: 'bg-red-100',
    textColor: 'text-red-800'
  }
};

/**
 * Formats a quality score as a percentage with proper precision
 * @param score - Quality score (0-1 range)
 * @param precision - Number of decimal places (default: 1)
 * @returns Formatted percentage string
 */
export function formatQualityScore(score: number | null | undefined, precision: number = 1): string {
  if (score === null || score === undefined || Number.isNaN(score)) {
    return 'N/A';
  }
  
  // Ensure score is in 0-1 range
  const normalizedScore = Math.max(0, Math.min(1, score));
  return `${(normalizedScore * 100).toFixed(precision)}%`;
}

/**
 * Categorizes a quality score into high/medium/low categories
 * @param score - Quality score (0-1 range)
 * @returns Quality category object
 */
export function categorizeQualityScore(score: number | null | undefined): QualityCategory {
  if (score === null || score === undefined || Number.isNaN(score)) {
    return QUALITY_CATEGORIES.low;
  }

  if (score >= QUALITY_THRESHOLDS.high) {
    return QUALITY_CATEGORIES.high;
  } else if (score >= QUALITY_THRESHOLDS.medium) {
    return QUALITY_CATEGORIES.medium;
  } else {
    return QUALITY_CATEGORIES.low;
  }
}

/**
 * Gets the appropriate CSS classes for a quality score badge
 * @param score - Quality score (0-1 range)
 * @returns CSS class string for badge styling
 */
export function getQualityBadgeClasses(score: number | null | undefined): string {
  const category = categorizeQualityScore(score);
  return `${category.bgColor} ${category.textColor}`;
}

/**
 * Calculates quality score trend indicator
 * @param currentScore - Current quality score
 * @param previousScore - Previous quality score
 * @returns Trend object with direction and percentage change
 */
export function calculateQualityTrend(
  currentScore: number | null | undefined,
  previousScore: number | null | undefined
): {
  direction: 'up' | 'down' | 'stable' | 'unknown';
  change: number;
  changeText: string;
} {
  if (!currentScore || !previousScore) {
    return {
      direction: 'unknown',
      change: 0,
      changeText: 'N/A'
    };
  }

  const change = currentScore - previousScore;
  const changePercentage = Math.abs(change * 100);

  if (Math.abs(change) < 0.01) { // Less than 1% change
    return {
      direction: 'stable',
      change: 0,
      changeText: 'No change'
    };
  }

  return {
    direction: change > 0 ? 'up' : 'down',
    change: changePercentage,
    changeText: `${change > 0 ? '+' : '-'}${changePercentage.toFixed(1)}%`
  };
}

/**
 * Generates quality improvement suggestions based on score
 * @param score - Quality score (0-1 range)
 * @returns Array of improvement suggestions
 */
export function getQualityImprovementSuggestions(score: number | null | undefined): string[] {
  if (!score || score >= QUALITY_THRESHOLDS.high) {
    return ['Quality score is excellent! Continue maintaining data standards.'];
  }

  const suggestions: string[] = [];

  if (score < QUALITY_THRESHOLDS.medium) {
    suggestions.push(
      'Critical: Add missing core information (name, location, contact details)',
      'Verify and update GPS coordinates for accurate location data',
      'Add comprehensive menu information and pricing',
      'Update operating hours and schedule information'
    );
  } else {
    suggestions.push(
      'Add missing optional fields (website, social media, ratings)',
      'Enhance menu descriptions and categories',
      'Update recent photos and promotional content',
      'Verify contact information accuracy'
    );
  }

  return suggestions;
}

/**
 * Formats quality statistics for display
 * @param stats - Raw quality statistics from database
 * @returns Formatted statistics object
 */
export function formatQualityStats(stats: {
  total_trucks: number;
  avg_quality_score: number;
  high_quality_count: number;
  medium_quality_count: number;
  low_quality_count: number;
  verified_count: number;
  pending_count: number;
  flagged_count: number;
}) {
  return {
    totalTrucks: stats.total_trucks,
    averageScore: formatQualityScore(stats.avg_quality_score),
    averageScoreRaw: stats.avg_quality_score,
    distribution: {
      high: {
        count: stats.high_quality_count,
        percentage: ((stats.high_quality_count / stats.total_trucks) * 100).toFixed(1)
      },
      medium: {
        count: stats.medium_quality_count,
        percentage: ((stats.medium_quality_count / stats.total_trucks) * 100).toFixed(1)
      },
      low: {
        count: stats.low_quality_count,
        percentage: ((stats.low_quality_count / stats.total_trucks) * 100).toFixed(1)
      }
    },
    verification: {
      verified: {
        count: stats.verified_count,
        percentage: ((stats.verified_count / stats.total_trucks) * 100).toFixed(1)
      },
      pending: {
        count: stats.pending_count,
        percentage: ((stats.pending_count / stats.total_trucks) * 100).toFixed(1)
      },
      flagged: {
        count: stats.flagged_count,
        percentage: ((stats.flagged_count / stats.total_trucks) * 100).toFixed(1)
      }
    }
  };
}

/**
 * Validates if a quality score is within acceptable range
 * @param score - Quality score to validate
 * @returns Boolean indicating if score is valid
 */
export function isValidQualityScore(score: number | null | undefined): boolean {
  return typeof score === 'number' && !Number.isNaN(score) && score >= 0 && score <= 1;
}

/**
 * Generates accessibility-friendly description for quality score
 * @param score - Quality score (0-1 range)
 * @returns Screen reader friendly description
 */
export function getQualityScoreAriaLabel(score: number | null | undefined): string {
  if (!isValidQualityScore(score)) {
    return 'Quality score not available';
  }

  const category = categorizeQualityScore(score);
  const percentage = formatQualityScore(score);
  
  return `Data quality score: ${percentage}, categorized as ${category.label.toLowerCase()} quality`;
}

/**
 * SOTA Quality Scoring Utilities
 * Centralized quality scoring functions to eliminate duplication
 * and provide consistent quality metrics across the application
 */

import { isNonEmptyString, isValidNumber, safeNumber, safeString } from './typeGuards';

// Quality score weights (must sum to 1.0)
export const QUALITY_WEIGHTS = {
  CORE_DATA: 0.35,      // 35% - name, description, location
  LOCATION_DATA: 0.25,  // 25% - coordinates, address
  CONTACT_DATA: 0.2,    // 20% - phone, email, website
  MENU_DATA: 0.1,       // 10% - menu items, pricing
  OPERATIONAL_DATA: 0.05, // 5% - hours, social media
  VERIFICATION: 0.05    // 5% - verification status
} as const;

// Quality thresholds
export const QUALITY_THRESHOLDS = {
  HIGH: 0.8,    // 80%+
  MEDIUM: 0.6,  // 60-79%
  LOW: 0       // <60%
} as const;

// Core data quality scoring
export function scoreCoreData(truck: Record<string, unknown>): number {
  let score = 0;
  let maxScore = 0;

  // Name (required - 40% of core score)
  maxScore += 0.4;
  if (isNonEmptyString(truck.name)) {
    score += 0.4;
  }

  // Description (30% of core score)
  maxScore += 0.3;
  if (isNonEmptyString(truck.description)) {
    const desc = safeString(truck.description);
    if (desc.length > 50) {
      score += 0.3;
    } else if (desc.length > 20) {
      score += 0.15;
    }
  }

  // Cuisine type (20% of core score)
  maxScore += 0.2;
  if (isNonEmptyString(truck.cuisine_type)) {
    score += 0.2;
  }

  // Image URL (10% of core score)
  maxScore += 0.1;
  if (isNonEmptyString(truck.image_url)) {
    score += 0.1;
  }

  return maxScore > 0 ? score / maxScore : 0;
}

// Location data quality scoring
export function scoreLocationData(truck: Record<string, unknown>): number {
  let score = 0;
  let maxScore = 0;

  // Coordinates (50% of location score)
  maxScore += 0.5;
  const latitude = safeNumber(truck.latitude);
  const longitude = safeNumber(truck.longitude);
  if (isValidNumber(latitude) && isValidNumber(longitude) && 
      latitude !== 0 && longitude !== 0) {
    score += 0.5;
  }

  // Address (30% of location score)
  maxScore += 0.3;
  if (isNonEmptyString(truck.address)) {
    score += 0.3;
  }

  // City/State (20% of location score)
  maxScore += 0.2;
  if (isNonEmptyString(truck.city) || isNonEmptyString(truck.state)) {
    score += 0.2;
  }

  return maxScore > 0 ? score / maxScore : 0;
}

// Contact data quality scoring
export function scoreContactData(truck: Record<string, unknown>): number {
  let score = 0;
  let maxScore = 0;

  // Phone (40% of contact score)
  maxScore += 0.4;
  if (isNonEmptyString(truck.phone)) {
    const phone = safeString(truck.phone);
    // Basic phone validation - simplified to avoid backtracking
    if (/^\+?[\d\s\-()]{10,}$/.test(phone)) {
      score += 0.4;
    } else if (phone.length > 5) {
      score += 0.2;
    }
  }

  // Email (30% of contact score)
  maxScore += 0.3;
  if (isNonEmptyString(truck.email)) {
    const email = safeString(truck.email);
    // Basic email validation
    if (/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      score += 0.3;
    }
  }

  // Website (30% of contact score)
  maxScore += 0.3;
  if (isNonEmptyString(truck.website)) {
    const website = safeString(truck.website);
    // Basic URL validation
    if (/^https?:\/\/.+\..+/.test(website)) {
      score += 0.3;
    }
  }

  return maxScore > 0 ? score / maxScore : 0;
}

// Menu data quality scoring
export function scoreMenuData(truck: Record<string, unknown>): number {
  let score = 0;
  let maxScore = 0;

  // Menu items (70% of menu score)
  maxScore += 0.7;
  if (isNonEmptyString(truck.menu_items)) {
    const menuItems = safeString(truck.menu_items);
    if (menuItems.length > 100) {
      score += 0.7;
    } else if (menuItems.length > 20) {
      score += 0.35;
    }
  }

  // Price range (30% of menu score)
  maxScore += 0.3;
  if (isNonEmptyString(truck.price_range)) {
    score += 0.3;
  }

  return maxScore > 0 ? score / maxScore : 0;
}

// Operational data quality scoring
export function scoreOperationalData(truck: Record<string, unknown>): number {
  let score = 0;
  let maxScore = 0;

  // Operating hours (60% of operational score)
  maxScore += 0.6;
  if (isNonEmptyString(truck.operating_hours)) {
    score += 0.6;
  }

  // Social media (40% of operational score)
  maxScore += 0.4;
  const socialFields = ['facebook_url', 'twitter_url', 'instagram_url'];
  const socialCount = socialFields.filter(field => isNonEmptyString(truck[field])).length;
  score += (socialCount / socialFields.length) * 0.4;

  return maxScore > 0 ? score / maxScore : 0;
}

// Verification status scoring
export function scoreVerificationStatus(truck: Record<string, unknown>): number {
  const status = safeString(truck.verification_status);
  switch (status) {
    case 'verified': {
      return 1;
    }
    case 'pending': {
      return 0.5;
    }
    default: {
      return 0;
    }
  }
}

// Calculate overall quality score
export function calculateOverallQualityScore(truck: Record<string, unknown>): number {
  const coreScore = scoreCoreData(truck);
  const locationScore = scoreLocationData(truck);
  const contactScore = scoreContactData(truck);
  const menuScore = scoreMenuData(truck);
  const operationalScore = scoreOperationalData(truck);
  const verificationScore = scoreVerificationStatus(truck);

  return (
    coreScore * QUALITY_WEIGHTS.CORE_DATA +
    locationScore * QUALITY_WEIGHTS.LOCATION_DATA +
    contactScore * QUALITY_WEIGHTS.CONTACT_DATA +
    menuScore * QUALITY_WEIGHTS.MENU_DATA +
    operationalScore * QUALITY_WEIGHTS.OPERATIONAL_DATA +
    verificationScore * QUALITY_WEIGHTS.VERIFICATION
  );
}

// Get quality category
export function getQualityCategory(score: number): 'high' | 'medium' | 'low' {
  if (score >= QUALITY_THRESHOLDS.HIGH) return 'high';
  if (score >= QUALITY_THRESHOLDS.MEDIUM) return 'medium';
  return 'low';
}

// Get quality label
export function getQualityLabel(score: number): string {
  const category = getQualityCategory(score);
  switch (category) {
    case 'high': {
      return 'Excellent';
    }
    case 'medium': {
      return 'Good';
    }
    case 'low': {
      return 'Needs Work';
    }
  }
}

// Get quality color
export function getQualityColor(score: number): string {
  const category = getQualityCategory(score);
  switch (category) {
    case 'high': {
      return '#22c55e';
    }
    case 'medium': {
      return '#f59e0b';
    }
    case 'low': {
      return '#ef4444';
    }
  }
}
